INSERT INTO `t_han_nga` VALUES (1, '黨;曉;哲;知', '昌', '黨[昌]、曉、哲，知也。楚謂之黨，1或曰曉，齊宋之閒謂之哲。', '[郭注-1]：黨朗也，解寤貌。', '卷一', '方言', '1', DEFAULT, DEFAULT, '6562;4195;838;3315', '4237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2, '虔;𠐛;慧;謾;貍;倢;隋;黠;鬼', '丯;疌;詑', '虔[丯]、𠐛，1慧也。2秦謂之謾，34晉謂之㦟{貍}，5宋楚之閒謂之倢[疌]，6楚或謂之䜏{隋}[詑]。78自關而東趙魏之閒謂之黠，或謂之鬼。9', '[音義-1]：音翾。\n[郭注-2]：謂慧了[憭]，\n[郭注-3]：言謾詑。\n[音義-4]：謾，莫錢反，又亡山反。𧦧，大和反。\n[音義-5]：音悝，或莫佳反。\n[郭注-6]：言便倢也。\n[郭注-7]：亦今通語。\n[音義-8]：他和反。\n[郭注-9]：言鬼衇也。', '卷一', '方言', '1;6;7', DEFAULT, DEFAULT, '3111;4972;6733;1599;6124;5050;2670;6559;5819', '2817;1074;1598', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3, '娥;嬴;好;媌;姣;姝;丰;妍', '', '娥、㜲{嬴}，好也。秦曰娥，1宋衞之閒謂之㜲，23秦晉之閒凡好而輕者謂之娥。自關而東河濟之閒謂之媌，45或謂之姣。67趙魏燕代之閒曰姝，89或曰妦{丰}。1011自關而西秦晉之故都曰妍。1213好，其通語也。', '[郭注-1]：言娥娥也。\n[郭注-2]：言㜲㜲也。\n[音義-3]：㜲音盈。\n[郭注-4]：今關西人呼好爲媌。\n[音義-5]：莫交反。\n[郭注-6]：言姣潔也。\n[音義-7]：音狡。\n[郭注-8]：亦四方通語。\n[音義-9]：姝，昌朱反，又音株。\n[郭注-10]：言妦容也。\n[音義-11]：音蜂。\n[郭注-12]：秦舊都，今扶風雍丘也。晉舊都，今太原晉陽縣也。其俗通呼好爲妍。\n[音義-13]：五千反。一作忓。', '卷一', '方言', '', DEFAULT, DEFAULT, '8166;8114;8189;8196;8193;8188;3875;8297', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4, '烈;𣡌;餘;肄', '', '烈、枿{𣡌}，餘也。1陳鄭之閒曰枿，2晉衞之閒曰烈，秦晉之閒曰肄，34或曰烈。', '[郭注-1]：謂遺餘也。\n[音義-2]：五割反。\n[郭注-3]：傳曰：「夏肄是屏。」\n[音義-4]：音謚。', '卷一', '方言', '', DEFAULT, DEFAULT, '6425;3802;3245;1938', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (5, '台;胎;陶;鞠;養;艾', '宧;匋;𡫬', '台[宧]、胎、陶[匋]、鞠[𡫬]，養也。晉衞燕魏曰台，12陳楚韓鄭之閒曰鞠，秦或曰陶，汝潁梁宋之閒曰胎，或曰艾。3', '[郭注-1]：台猶頤也。\n[音義-2]：音怡。\n[郭注-3]：爾雅云：「艾，養也。」。', '卷一', '方言', '1;3;4', DEFAULT, DEFAULT, '868;2596;9687;1810;3219;389', '4560;3287;4616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (6, '憮;奄;憐;牟;愛', '𢜮;㤅', '憮、㤿{奄}、憐、牟[𢜮]，愛[㤅]也。韓鄭曰憮，1晉衞曰㤿，23汝潁之閒曰憐，宋魯之閒曰牟，或曰憐。憐，通語也。', '[音義-1]：亡輔反。\n[郭注-2]：㤿憸，多意氣也。\n[音義-3]：音淹。', '卷一', '方言', '4;5', DEFAULT, DEFAULT, '6778;6599;6960;751;3367', '6785;6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (7, '夌;憮;矜;悼;憐;哀', '憐;憐', '㥄{夌}[憐]、憮、矜[憐]、悼、憐，哀也。齊魯之閒曰矜，陳楚之閒曰悼，趙魏燕代之閒曰㥄，12自楚之北郊曰憮，秦晉之閒或曰矜，或曰悼。', '[郭注-1]：㥄亦憐耳。\n[音義-2]：音陵。', '卷一', '方言', '1;3', DEFAULT, DEFAULT, '3364;6778;9510;6941;6960;936', '6960;6960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (8, '咺;唏;勺;怛;痛;唴良;噭咷;喑;惄', '咷', '咺、唏、㣿{勺}[咷]、1怛，痛也。凡哀泣而不止曰咺，哀而不泣曰唏。於方：則楚言哀曰唏，2燕之外鄙朝鮮洌水之閒少兒泣而不止曰咺。3456自關而西秦晉之閒凡大人少兒泣而不止謂之唴。哭極音絶亦謂之唴。平原謂啼極無聲謂之唴哴{唴良}。78楚謂之噭咷。9齊宋之閒謂之喑，10或謂之惄。11', '[音義-1]：音的，一音灼。\n[音義-2]：虛几反。\n[郭注-3]：鄙，邊邑名。朝鮮，今樂浪郡是也。洌水，在遼東，\n[音義-4]：音烈。\n[郭注-5]：少兒，猶言小兒。\n[音義-6]：咺，香遠反。\n[郭注-7]：今關西語亦然。\n[音義-8]：唴，丘尙反。哴，音亮。\n[音義-9]：叫逃兩音。字或作叴，音求。\n[音義-10]：音蔭。\n[音義-11]：惄，奴歴反。', '卷一', '方言', '3', DEFAULT, DEFAULT, '796;850;9465;6898;4697;797/3339;781/798;799;6803', '798', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (9, '悼;惄;悴;憖;傷', '', '悼、惄、悴、憖，傷也。自關而東汝潁陳楚之閒通語也。汝謂之惄，秦謂之悼，宋謂之悴，楚潁之閒謂之憖。12', '[郭注-1]：詩曰：「不憖遺一老。」亦恨傷之言也。\n[音義-2]：魚吝反。', '卷一', '方言', '', DEFAULT, DEFAULT, '6941;6803;6928;6751;5156', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (10, '愼;濟;憯;惄;溼;桓;憂', '疾;咺', '愼、濟[疾]、䁮{憯}、惄、溼、桓[咺]，憂也。宋衞或謂之愼，或曰䁮。12陳楚或曰溼，或曰濟。自關而西秦晉之閒或曰惄，或曰溼。自關而西秦晉之閒凡志而不得、欲而不獲、高而有墜、得而中亡謂之溼，34或謂之惄。', '[郭注-1]：䁮者，憂而不動也。\n[音義-2]：作念反。\n[郭注-3]：溼者，失意潛沮之名。\n[音義-4]：沮，一作阻。', '卷一', '方言', '2;6', DEFAULT, DEFAULT, '6712;7093;6899;6803;7345;3671;3366', '4694;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (11, '鬱悠;懷;惄;惟;慮;願;念;靖;愼;思', '', '鬱悠、懷、惄、惟、慮、願、念、靖、愼，思也。晉宋衞魯之閒謂之鬱悠。1惟，凡思也。慮，謀思也。願，欲思也。念，常思也。東齊海岱之閒曰靖，2秦晉或曰愼。凡思之貌亦曰愼，3或曰惄。', '[郭注-1]：鬱悠，猶鬱陶也。\n[郭注-2]：岱，太山。\n[郭注-3]：謂感思者之容。', '卷一', '方言', '', DEFAULT, DEFAULT, '3848/6927;6762;6803;6761;6702;5633;6719;6684;6712;6701', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (12, '敦;豐;厖;𡗦;幠;般;嘏;奕;戎;京;奘;將;大;夏', '惇;𣞤;鞶;濃;壯;嘏', '敦[惇]、豐、厖、𡗦、幠[𣞤]、般[鞶]、1嘏、奕、戎[濃]、京、奘、將[壯]，大也。凡物之大貌曰豐。厖，2深之大也。東齊海岱之閒曰𡗦，3或曰幠。4宋魯陳衞之閒謂之嘏，5或曰戎。秦晉之閒凡物壯大謂之嘏，或曰夏[嘏]。秦晉之閒凡人之大謂之奘，6或謂之壯。燕之北鄙齊楚之郊或曰京，或曰將。皆古今語也。7初別國不相往來之言也，今或同，而舊書雅記故俗語不失其方，8而後人不知，故爲之作釋也。9', '[音義-1]：音般桓。\n[音義-2]：音鴟𪁪。\n[音義-3]：音介。\n[音義-4]：海狐反。\n[音義-5]：音賈。\n[音義-6]：在朗反。\n[郭注-7]：語聲轉耳。\n[郭注-8]：皆本其言之所出也。雅，爾雅也。\n[郭注-9]：釋詁、釋言之屬。', '卷一', '方言', '1;5;6;9;12;14', DEFAULT, DEFAULT, '2039;3103;5995;6608;4898;5446;1463;6667;8377;3329;6668;1983;6596;3371', '6727;3847;1800;7331;228;1463', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (13, '假;格;懷;摧;詹;戾;艐;至', '徦;瞻', '假[徦]、𢓜{格}、懷、摧、詹[瞻]、戾、艐，至也。邠唐冀兗之閒曰假，12或曰𢓜。3齊楚之會郊或曰懷。4摧、詹、戾，楚語也。5艐，6宋語也。皆古雅之别語也，7今則或同。', '[郭注-1]：邠，今在始平漆縣。唐，今在太原晉陽縣。\n[音義-2]：假，音駕。\n[郭注-3]：古格字。\n[郭注-4]：兩境之閒。\n[郭注-5]：詩曰：「先祖于摧」「六日不詹」「魯侯戾止」之謂也。此亦方國之語，不專在楚也。\n[郭注-6]：古屆字。\n[郭注-7]：雅，謂風雅。', '卷一', '方言', '1;5', DEFAULT, DEFAULT, '5074;3601;6762;7851;713;6354;5443;7706', '1248;2157', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (14, '嫁;逝;徂;適;往', '', '嫁、逝、徂、適，往也。自家而出謂之嫁，由女而出爲嫁也。逝，秦晉語也。徂，齊語也。適，宋魯語也。往，凡語也。', '', '卷一', '方言', '', DEFAULT, DEFAULT, '8125;1105;1106;1109;1229', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (15, '謾台;脅鬩;懼;嘽咺', '憚吅', '謾台、脅鬩，懼也。燕代之閒曰謾台，1齊楚之閒曰脅鬩。宋衞之閒凡怒而噎噫，23謂之脅鬩。45南楚江湘之閒謂之嘽咺[憚吅]。67', '[音義-1]：蠻怡二音。\n[郭注-2]：噎噫謂憂也。\n[音義-3]：噫，央媚反。\n[郭注-4]：脅鬩猶𤄎沭也。\n[音義-5]：鬩，呼鬲反。\n[郭注-6]：湘，水名，今在零陵。\n[音義-7]：咺，音香遠反。', '卷一', '方言', '4', DEFAULT, DEFAULT, '1599/868;2617/1900;6772;821/796', '6940/971', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (16, '虔;劉;慘;惏;殺;殘;貪;歁', '丯', '虔[丯]、劉、慘、㨆{惏}，12殺也。秦晉宋衞之閒謂殺曰劉，晉之北鄙亦曰劉。秦晉之北鄙、燕之北郊、翟縣之郊謂賊爲虔。3晉魏河内之北謂㨆曰殘，楚謂之貪，南楚江湘之閒謂之歁。4', '[郭注-1]：今關西人呼打爲㨆。\n[音義-2]：音廩，或洛感反。\n[郭注-3]：今上黨潞縣卽古翟國。\n[郭注-4]：言歁㨆難猒也。', '卷一', '方言', '1', DEFAULT, DEFAULT, '3111;9444;6900;6859;1979;2550;3982;5571', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (17, '亟;憐;憮;奄;愛', '㥛;㤅', '亟[㥛]、憐、憮、㤿{奄}，愛[㤅]也。東齊海岱之閒曰亟。自關而西秦晉之閒凡相敬愛謂之亟，1陳楚江淮之閒曰憐，宋衞邠陶之閒曰憮，或曰㤿。2', '[音義-1]：欺革反。\n[郭注-2]：陶唐，晉都處。', '卷一', '方言', '1;5', DEFAULT, DEFAULT, '9014;6960;6778;6599;3367', '6812;6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (18, '眉;棃;耋;老;耉鮐', '', '眉、棃、耋、鮐，老也。東齊曰眉，1燕代之北鄙曰棃，2宋衞兗豫之内曰耋，34秦晉之郊陳兗之會曰耉鮐。56', '[郭注-1]：言秀眉也。\n[郭注-2]：言面色似凍棃。\n[郭注-3]：八十爲耋。\n[音義-4]：音絰。\n[郭注-5]：言背皮如鮐魚。\n[音義-6]：耉，音垢。', '卷一', '方言', '', DEFAULT, DEFAULT, '2218;3416;5376;5375;5379/7650', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (19, '脩;駿;融;繹;尋;延;長;充;永', '攸;峻', '脩[攸]、駿[峻]、融、繹、尋、延，長也。陳楚之閒曰脩，海岱大野之閒曰尋，1宋衞荆吳之閒曰融。自關而西秦晉梁益之閒凡物長謂之尋。周官之法，度廣爲尋，2幅廣爲充。3延、永，長也。凡施于年者謂之延，施于衆長謂之永。4', '[郭注-1]：大野，今高平鉅野。\n[郭注-2]：度，謂絹帛橫廣。\n[郭注-3]：爾雅曰：「緇廣充幅。」\n[郭注-4]：各隨事爲義。', '卷一', '方言', '1;2', DEFAULT, DEFAULT, '2685;6168;1863;8518;1984;1265;6067;5460;7495', '2034;5876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (20, '允;訦;恂;展;諒;穆;信', '眞;睦', '允、訦、恂、展[眞]、諒、穆[睦]，信也。齊魯之閒曰允，燕代東齊曰訦，1宋衞汝潁之閒曰恂，2荆吳淮汭之閒曰展，34西甌毒屋黃石野之閒曰穆。567衆信曰諒，8周南召南衞之語也。', '[音義-1]：音諶。\n[音義-2]：音荀。\n[郭注-3]：汭，水口也。\n[音義-4]：音芮。\n[郭注-5]：西甌，駱越别種也，\n[音義-6]：音嘔。\n[郭注-7]：其餘皆未詳所在。\n[音義-8]：諒，音亮。', '卷一', '方言', '4;6', DEFAULT, DEFAULT, '5458;1525;6759;5405;1481;4428;1524', '5213;2156', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (21, '碩;沈;巨;濯;訏;敦;夏;于;大;寇;夥;過;僉;劒;弩;郴', '甚;鉅;卓;惇;嘏;僉;怒;闖', '碩、沈[甚]、巨[鉅]、濯[卓]、訏、敦[惇]、夏[嘏]、于，大也。齊宋之閒曰巨，曰碩。凡物盛多謂之寇。1齊宋之郊、楚魏之際曰夥。2自關而西秦晉之閒凡人語而過謂之𨘌{過}，3或曰僉。東齊謂之劒[僉]，或謂之弩[怒]。弩猶怒也。陳鄭之閒曰敦，荆吳揚甌之郊曰濯，中齊西楚之閒曰訏。45自關而西秦晉之閒凡物之壯大者而愛偉之謂之夏，周鄭之閒謂之假。6郴[闖]，齊語也。7于，通語也。', '[郭注-1]：今江東有小鳬，其多無數，俗謂之寇鳧。\n[音義-2]：音禍。\n[音義-3]：于果反。\n[郭注-4]：西楚，謂今汝南彭城。\n[音義-5]：訏亦作芋，音義同耳，香于反。\n[音義-6]：音賈。\n[音義-7]：洛含反。', '卷一', '方言', '2;3;4;6;7;14;15;16', DEFAULT, DEFAULT, '5628;7318;3032;7419;1671;2039;3371;3069;6596;2043;4335;1110;3270;2813;8497;4111', '3041;9446;5222;6727;1463;3270;6877;7786', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (22, '抵;致;會', '', '抵、𠊷{致}，會也。雍梁之閒曰抵，秦晉亦曰抵。1凡會物謂之𠊷。2', '[音義-1]：音觸牴。\n[音義-2]：𠊷，音致。', '卷一', '方言', '', DEFAULT, DEFAULT, '7850;3365;3274', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (23, '華;𠌶;盛', '', '華、荂{𠌶}，晠{盛}也。齊楚之閒或謂之華，或謂之荂。12', '[郭注-1]：荂亦華别名。\n[音義-2]：音誇。', '卷一', '方言', '', DEFAULT, DEFAULT, '3884;3882;3140', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (24, '墳;大', '歕', '墳[歕]，地大也。青幽之閒凡土而高且大者謂之墳。1', '[郭注-1]：卽大陵也。', '卷一', '方言', '1', DEFAULT, DEFAULT, '9141;6596', '5533', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (25, '𩫖;幕', '', '張小使大謂之廓{𩫖}，陳楚之閒謂之摸{幕}。1', '[音義-1]：音莫。', '卷一', '方言', '', DEFAULT, DEFAULT, '3327;4881', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (26, '嬛;蟬;臽;撚;未;續', '繯;嬗', '𡣱{嬛}[繯]、蟬[嬗]、䌪{臽}、1撚、2未，續也。楚曰𡣱。3蟬，出也。4楚曰蟬，或曰未及也。', '[音義-1]：音剡。\n[音義-2]：諾典反。\n[音義-3]：火全反。\n[郭注-4]：別異義。', '卷一', '方言', '1;2', DEFAULT, DEFAULT, '8211;8867;4526;8069;9799;8548', '8573;8251', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (27, '𨃚;䠛;𧿳;跳;折;蹠', '𧼪', '𨃚、䠛、𧿳，1跳也。楚曰䟷{折}[𧼪]。23陳鄭之閒曰䠛，4楚曰蹠。自關而西秦晉之閒曰跳，或曰𨃚。56', '[音義-1]：音拂。\n[郭注-2]：亦中州語。\n[音義-3]：勑厲反。\n[音義-4]：音逍遙。\n[郭注-5]：古塌字。\n[音義-6]：他匣切。', '卷一', '方言', '5', DEFAULT, DEFAULT, '1375;1376;1373;1370;628;1374', '1036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (28, '躡;郅;跂;格;躋;䟑;登', '陟', '躡、郅[陟]、跂、𢓜{格}、躋、䠯{䟑}，1登也。自關而西秦晉之閒曰躡，東齊海岱之閒謂之躋，2魯衞曰郅，3梁益之閒曰𢓜，4或曰跂。5', '[音義-1]：音踊躍。\n[音義-2]：音濟渡。\n[音義-3]：音質。\n[郭注-4]：𢓜亦訓來。\n[音義-5]：跂，音企。', '卷一', '方言', '2', DEFAULT, DEFAULT, '1352;4071;1394;3601;1347;1034;1079', '9641', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (29, '逢;逆;迎', '', '逢、逆，迎也。自關而東曰逆，自關而西或曰迎，或曰逢。', '', '卷一', '方言', '', DEFAULT, DEFAULT, '1130;1124;1125', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (30, '尋;搴;摭;挻;取;篡', '', '撏{尋}、攓{搴}、摭、挻，取也。南楚曰攓，1陳宋之閒曰摭，2衞魯揚徐荆衡之郊曰撏。34自關而西秦晉之閒凡取物而逆謂之篡，5楚部或謂之挻。6', '[音義-1]：音蹇。\n[音義-2]：音盜蹠。\n[郭注-3]：衡，衡山，南岳名，今在長沙。\n[音義-4]：撏，常含反。\n[音義-5]：音饌。\n[音義-6]：羊羶反。', '卷一', '方言', '', DEFAULT, DEFAULT, '1984;8000;7984;7900;1926;5844', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (31, '餥;飵;食;䬯;䭡䭓', '', '餥、飵，食也。陳楚之内相謁而食麥饘謂之餥，12楚曰飵。3凡陳楚之郊南楚之外相謁而飧或曰飵，4或曰䬯。5秦晉之際河陰之閒曰䭡䭓。67此秦語也。8', '[郭注-1]：謁，請也。饘，糜也，\n[音義-2]：音旃。餥，音非。\n[音義-3]：音昨。\n[郭注-4]：晝飯爲飧。\n[音義-5]：音黏。\n[郭注-6]：今馮翊郃陽河東龍門是其處也。\n[音義-7]：䭡，惡恨反。䭓，五恨反。\n[郭注-8]：今關西人呼食欲飽爲䭡䭓。', '卷一', '方言', '', DEFAULT, DEFAULT, '3216;3235;3204;3236;3237/3238', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (32, '勉;薄努;勉努;愐釗;勖茲', '迫努;愐劭;勖孜', '釗、薄，勉也。1秦晉曰釗，或曰薄。故其鄙語曰薄努，猶勉努也。2南楚之外曰薄努[迫努]，自關而東周鄭之閒曰勔釗{愐釗}[愐劭]，3齊魯曰勖茲[勖孜]。4', '[郭注-1]：相勸勉也。\n[郭注-2]：如今人言努力也。\n[音義-3]：勔，音沈湎。釗，居遼反。\n[郭注-4]：勖勔，亦訓勉也。', '卷一', '方言', '2;4;5', DEFAULT, DEFAULT, '9220;561/9254;9220/9254;6788/2794;9222/526', '1182/9254;6788/9221;9222/2013', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (33, '陗;嫽;好', '', '釥{陗}、嫽，好也。靑徐海岱之閒曰釥，1或謂之嫽。23好，凡通語也。', '[音義-1]：錯眇反。\n[郭注-2]：今通呼小姣潔喜好者爲嫽釥。\n[音義-3]：洛夭反。', '卷二', '方言', '', DEFAULT, DEFAULT, '9636;8174;8189', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (34, '朦;厖;豐;伃;杼', '厖;眝;眝', '朦[厖]、厖，豐也。自關而西秦晉之閒凡大貌謂之朦，1或謂之厖；2豐，其通語也。趙魏之郊、燕之北鄙凡大人謂之豐人。燕記曰：豐人杼首。杼首，長首也。楚謂之伃[眝]，燕謂之杼[眝]。3燕趙之閒言圍大謂之豐。4', '[音義-1]：忙紅反；\n[音義-2]：音鴟𪁪；\n[音義-3]：音序；\n[郭注-4]：謂度圍物也。', '卷二', '方言', '1;4;5', DEFAULT, DEFAULT, '4281;5995;3103;4970;3725', '5995;2176;2176', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (35, '娃;嫷;窕;豔;美;好;娥;窈', '', '娃、嫷、窕、豔，美也。吳楚衡淮之閒曰娃，1南楚之外曰嫷，23宋衞晉鄭之閒曰豔，陳楚周南之閒曰窕。4自關而西秦晉之閒凡美色或謂之好，或謂之窕。故吳有館娃之宮，秦有㯃娥之臺。56秦晉之閒美貌謂之娥，7美狀爲窕，8美色爲豔，9美心爲窈。10', '[音義-1]：烏佳反；\n[郭注-2]：言婑嫷也；\n[音義-3]：諾過反；\n[音義-4]：徒了反；\n[郭注-5]：皆戰國時諸侯所立也。\n[音義-6]：㯃，音七；\n[郭注-7]：言娥娥也；\n[郭注-8]：言閑都也；\n[郭注-9]：言光豔也；\n[郭注-10]：言幽靜也。', '卷二', '方言', '', DEFAULT, DEFAULT, '8298;8187;4669;3104;2355;8189;8166;4676', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (36, '奕;僷;容', '僷', '奕[僷]、僷，容也。1自關而西凡美容謂之奕，或謂之僷。2宋衞曰僷，陳楚汝潁之閒謂之奕。', '[郭注-1]：皆輕麗之貌。\n[音義-2]：音葉。', '卷二', '方言', '1', DEFAULT, DEFAULT, '6667;4976;4587', '4976', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (37, '𥌗;鑠;盱;揚;𠈪;雙;順;𥌭', '繚;𠈪;訓', '䫵{𥌗}、鑠[繚]、盱、揚[𠈪]、𥉋{𠈪}、雙也。南楚江淮之閒曰䫵，1或曰𥉋。2好目謂之順[訓]，3黸瞳之子謂之𥌣{𥌭}。4宋衞韓鄭之閒曰鑠。56燕代朝鮮洌水之閒曰盱，78或謂之揚。9', '[音義-1]：音緜。下作𥌣，音字同耳；\n[音義-2]：音滕；\n[郭注-3]：言流澤也；\n[郭注-4]：黸，黑也；言𥌣邈也；\n[郭注-5]：言光明也；\n[音義-6]：舒灼反；\n[郭注-7]：謂舉眼也；\n[音義-8]：香于反；\n[郭注-9]：詩曰：「美目揚兮」是也。此本論雙耦，因廣其訓，復言目耳。', '卷二', '方言', '2;4;7', DEFAULT, DEFAULT, '2104;9279;2134;7956;5107;2364;5652;2102', '8569;5107;1496', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (38, '嫢;笙;揫;摻;細;徥', '', '嫢、笙、揫、摻，細也。自關而西秦晉之閒凡細而有容謂之嫢，12或曰徥。34凡細貌謂之笙，斂物而細謂之揫，5或曰摻。6', '[郭注-1]：嫢嫢，小成貌；\n[音義-2]：羌箠反；\n[郭注-3]：言徥𢔡也。\n[音義-4]：度指反。\n[音義-5]：揫，音遒；\n[音義-6]：摻，素㩜反；', '卷二', '方言', '', DEFAULT, DEFAULT, '8227;2987;3396;8093;8559;1237', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (39, '傀;渾;濞;䑋;廫;泡;盛', '葆/飽', '𠐤{傀}、渾、12䑄{濞}、34䑋、𠐋{廫}、泡[葆/飽]，盛也。𠐤，5自關而西秦晉之閒語也。陳宋之閒曰𠐋，67江淮之閒曰泡，89秦晉或曰䑋，梁益之閒凡人言盛及其所愛，偉其肥晠謂之䑋。1011', '[郭注-1]：們渾，肥滿也。\n[音義-2]：狐本反；\n[郭注-3]：䑄呬，充壯也。\n[音義-4]：匹四反；\n[郭注-5]：言瓌瑋也；\n[郭注-6]：𠐋𠈭，麤大貌；\n[音義-7]：恪膠反；\n[郭注-8]：泡肥，洪張貌；\n[音義-9]：音庖；\n[郭注-10]：肥䑋多肉。\n[音義-11]：音壤；', '卷二', '方言', '6', DEFAULT, DEFAULT, '4979;7195;7167;2648;5969;7072;3140', '674/3242', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (40, '私;策;纖;兌;稺;杪;小;綾;靡;蔑;葼', '厶;朿;𦺶;糜', '私[厶]、策[朿]、纖、䓲{兌}[𦺶]、稺、杪，小也。自關而西秦晉之郊梁益之閒凡物小者謂之私，或曰纖，繒帛之細者謂之纖。東齊言布帛之細者曰綾，1秦晉曰靡[糜]。2凡草生而初達謂之䓲。34稺，5年小也。木細枝謂之杪，67江淮陳楚之内謂之蔑，8青齊兗冀之閒謂之葼，9燕之北鄙朝鮮洌水之間謂之策。故傳曰：慈母之怒子也，雖折葼笞之，其惠存焉。10', '[音義-1]：音凌；\n[郭注-2]：靡，細好也；\n[郭注-3]：鋒萌始出；\n[音義-4]：音銳；\n[郭注-5]：古稚字；\n[郭注-6]：言杪梢也；\n[音義-7]：莫召反；\n[郭注-8]：蔑，小貌也；\n[音義-9]：音馬𩭤；\n[郭注-10]：言敎在其中也。', '卷二', '方言', '1;2;4;9', DEFAULT, DEFAULT, '4429;2972;8558;5459;4422;3581;704;8603;7696;2331;508', '1905;4395;496;4360', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (41, '奄;枼;微', '', '殗{奄}、殜{枼}，微也。宋衞之閒曰殗。自關而西秦晉之閒凡病而不甚曰殗殜。12', '[郭注-1]：病半臥半起也。\n[音義-2]：殗，於怯反；殜，音葉；', '卷二', '方言', '', DEFAULT, DEFAULT, '6599;3814;1236', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (42, '臺;敵;匹', '持', '臺[持]、敵，匹也。1東齊海岱之閒曰臺，自關而西秦晉之閒物力同者謂之臺敵。', '[音義-1]：匹，一作疋。', '卷二', '方言', '1', DEFAULT, DEFAULT, '7710;2029;8422', '7856', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (43, '捊嬎;耦', '勽嬎', '抱㛯{捊嬎}[勽嬎]，耦也。1荆吳江湖之閒曰抱㛯，2宋潁之閒或曰㛯。', '[郭注-1]：耦亦匹，互見其義耳。\n[音義-2]：抱，音赴；㛯，孚萬反，一作婏；', '卷二', '方言', '1', DEFAULT, DEFAULT, '7909/8136;2821', '5807/8136', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (44, '倚;踦;奇', '奇', '倚[奇]、踦，奇也。1自關而西秦晉之閒凡全物而體不具謂之倚，2梁楚之閒謂之踦，雍梁之西郊凡嘼支體不具者謂之踦。3', '[音義-1]：音奇耦。\n[音義-2]：於寄反；\n[音義-3]：卻奇反。', '卷二', '方言', '1', DEFAULT, DEFAULT, '5045;1331;3059', '3059', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (45, '逴;𤡯;透;驚', '倏', '逴、𤡯、透[倏]，驚也。1自關而西秦晉之閒凡蹇者或謂之逴，23體而偏長短亦謂之逴。宋衞南楚凡相驚曰𤡯，4或曰透。5', '[郭注-1]：皆驚貌也。\n[郭注-2]：行略逴也；\n[音義-3]：勑略反；\n[音義-4]：音鑠；\n[音義-5]：式六反；', '卷二', '方言', '3', DEFAULT, DEFAULT, '1201;6331;1218;6216', '6348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (46, '儀;格;來;懷', '', '儀、𢓜{格}，來也。陳潁之閒曰儀，自關而東周鄭之郊齊魯之閒或謂之𢓜，或曰懷。', '', '卷二', '方言', '', DEFAULT, DEFAULT, '5082;3601;3346;6762', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (47, '䵒;𡫽;黏', '', '䵑{䵒}、𪏮{𡫽}，黏也。齊魯靑徐自關而東或曰䵑，12或曰𪏮。3', '[郭注-1]：言黏䵑也。\n[音義-2]：音昵；\n[音義-3]：音汝；', '卷二', '方言', '', DEFAULT, DEFAULT, '4512;4687;4510', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (48, '餬;託;庇;寓;𠈪;寄', '', '餬、託、庇、寓、媵{𠈪}，寄也。齊衞宋魯陳晉汝潁荆州江淮之閒曰庇，1或曰寓。寄食爲餬。23凡寄爲託，寄物爲媵。4', '[郭注-1]：庇蔭；\n[郭注-2]：傳曰：「餬其口于四方。」是也。\n[音義-3]：音胡；\n[音義-4]：音孕；', '卷二', '方言', '', DEFAULT, DEFAULT, '3239;1567;5955;4610;5107;4609', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (49, '逞;苦;了;快', '憭', '逞、苦、了[憭]，快也。自山而東或曰逞，楚曰苦，1秦曰了。23', '[郭注-1]：苦而爲快者，猶以臭爲香、治爲亂、徂爲存，此訓義之反覆用之是也；\n[郭注-2]：今江東呼快爲愃，\n[音義-3]：相緣反。', '卷二', '方言', '3', DEFAULT, DEFAULT, '1196;323;9779;6716', '6734', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (50, '每;匿;赧;愧', '瞢;𢘋', '挴{每}[瞢]、㥾{匿}[𢘋]、赧，愧也。晉曰挴，或曰㥾。秦晉之閒凡愧而見上謂之赧，1梁宋曰㥾。23', '[郭注-1]：小雅曰：「面赤愧曰赧。」；\n[郭注-2]：敕㥾，亦慙貌也。\n[音義-3]：音匿。', '卷二', '方言', '1;2', DEFAULT, DEFAULT, '235;8418;6588;8344', '2329;6819', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (51, '叨;惏;殘', '', '叨、1惏，2殘也。陳楚曰惏。', '[音義-1]：託高反；\n[音義-2]：洛含反。', '卷二', '方言', '', DEFAULT, DEFAULT, '3250;6859;2550', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (52, '馮;齘;苛;怒', '訶', '馮、齘、苛[訶]，怒也。楚曰馮，1小怒曰齘。2陳謂之苛。3', '[郭注-1]：馮，恚盛貌。楚辭曰：「康回馮怒。」；\n[郭注-2]：言噤齘也；\n[郭注-3]：相苛責也。', '卷二', '方言', '3', DEFAULT, DEFAULT, '6201;1283;543;6877', '1684', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (53, '朿;剌;痛', '', '憡{朿}、剌，痛也。自關而西秦晉之閒或曰憡。12', '[郭注-1]：㦧憡，小痛也。\n[音義-2]：音策。', '卷二', '方言', '', DEFAULT, DEFAULT, '4395;3900;4697', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (54, '撟捎;選', '', '撟捎，選也。1自關而西秦晉之閒凡取物之上謂之撟捎。2', '[郭注-1]：此妙擇積聚者也。\n[音義-2]：矯騷兩音。', '卷二', '方言', '', DEFAULT, DEFAULT, '7964/7965;1143', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (55, '僩;梗;爽;猛', '更/庚', '撊{僩}、梗[更/庚]、爽，猛也。晉魏之閒曰撊，12韓趙之閒曰梗，3齊晉曰爽。', '[郭注-1]：傳曰：「撊然登埤。」\n[音義-2]：呼旱反；\n[音義-3]：音魚鯁；', '卷二', '方言', '2', DEFAULT, DEFAULT, '5007;3541;2091;6343', '2021/9753', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (56, '瞯;睇;睎;䀩;眄', '', '瞯、睇、睎、䀩，眄也。陳楚之閒南楚之外曰睇，1東齊靑徐之閒曰睎，吳揚江淮之閒或曰瞯，2或曰䀩，3自關而西秦晉之閒曰眄。', '[音義-1]：音悌；\n[音義-2]：音閑；\n[音義-3]：音略。', '卷二', '方言', '', DEFAULT, DEFAULT, '2190;2178;2174;2179;2199', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (57, '息;喙;呬;息', '喟', '䭒{息}、喙[喟]、呬，息也。周鄭宋沛之閒曰䭒，自關而西秦晉之閒或曰喙，1或曰䭒，2東齊曰呬。3', '[音義-1]：音口喙；\n[音義-2]：音消息；\n[音義-3]：呬，許四反。', '卷二', '方言', '2', DEFAULT, DEFAULT, '6704;783;824;6704', '830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (58, '𠂢;規;裁;斲', '撝', '䤨{𠂢}、摫{規}[撝]，裁也。1梁益之閒裁木爲器曰䤨，2裂帛爲衣曰摫。3䤨又斲也，晉趙之閒謂之䤨。', '[郭注-1]：皆析破之名也。\n[音義-2]：音劈歷；\n[音義-3]：摫，音規；', '卷二', '方言', '2', DEFAULT, DEFAULT, '7497;6675;5253;9480', '8020', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (59, '鐫;椓', '', '鐫，㧻{椓}也。晉趙謂之鐫。12', '[郭注-1]：謂鏨鐫也。\n[音義-2]：鐫，子旋反。', '卷二', '方言', '', DEFAULT, DEFAULT, '9340;3798', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (60, '鍇;稽;堅', '', '鍇、𨯀{稽}，堅也。自關而西秦晉之閒曰鍇，1吳揚江淮之閒曰𨯀。2', '[郭注-1]：音楷；\n[音義-2]：音啓。', '卷二', '方言', '', DEFAULT, DEFAULT, '9269;3889;1951', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (61, '揄鋪;𢅡巫;帗縷;葉褕;毳', '𢅡幠', '揄鋪、𢅡𢃀{𢅡巫}[𢅡幠]、帗縷、葉褕，毳也。12荆揚江湖之閒曰揄鋪，3楚曰𢅡𢃀，4陳宋鄭衞之閒謂之帗縷，5燕之北郊朝鮮洌水之閒曰葉褕。67', '[郭注-1]：皆謂物之扞蔽也；\n[音義-2]：音脆。\n[音義-3]：音敷；\n[音義-4]：音藍；\n[音義-5]：音拂；\n[郭注-6]：今名短度絹爲葉褕也。\n[音義-7]：音臾；', '卷二', '方言', '2', DEFAULT, DEFAULT, '7968/9434;4874/3035;4858/8674;495/5256;5398', '4874/4898', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (62, '孑;㶳;餘;俊;遵', '尊', '孑、藎{㶳}，餘也。1周鄭之閒曰藎，或曰孑。靑徐楚之閒曰孑。自關而西秦晉之閒炊薪不盡曰藎。2孑，俊也。遵[尊]，俊也。3', '[郭注-1]：謂遺餘；\n[音義-2]：昨吝反；\n[郭注-3]：廣異語耳。', '卷二', '方言', '5', DEFAULT, DEFAULT, '9780;6478;3245;4960;1108', '9879', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (63, '翿;幢;翳', '橦', '翿、幢[橦]，翳也。楚曰翿，12關西關東皆曰幢。3', '[郭注-1]：舞者所以自蔽翳也。\n[音義-2]：音濤；\n[音義-3]：徒江反；', '卷二', '方言', '2', DEFAULT, DEFAULT, '2272;4916;2273', '3673', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (64, '𢯱;略;求;攈;拓', '', '𢯱、略，求也。秦晉之閒曰𢯱，就室曰𢯱，於道曰略。略，强取也。攈、1摭{拓}，2取也。此通語也。', '[郭注-1]：古捃字；\n[音義-2]：音盜蹠。', '卷二', '方言', '', DEFAULT, DEFAULT, '8090;9190;5373;7985;7984', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (65, '朚;矜;奄;遽', '拮;弇', '茫{朚}、矜[拮]、奄[弇]，遽也。1吳揚曰茫，23陳潁之閒曰奄，秦晉或曰矜，或曰遽。', '[郭注-1]：謂遽矜也；\n[郭注-2]：今北方通然也。\n[音義-3]：莫光反。', '卷二', '方言', '2;3', DEFAULT, DEFAULT, '4322;9510;6599;1206', '8030;1760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (66, '速;逞;搖扇;疾', '', '速、逞、搖扇，疾也。東齊海岱之閒曰速，燕之外鄙朝鮮洌水之閒曰搖扇，楚曰逞。', '', '卷二', '方言', '', DEFAULT, DEFAULT, '1121;1196;7949/7722;4694', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (67, '予;賴;讎', '讕', '予、賴[讕]，讎也。南楚之外曰賴，1秦晉曰讎。', '[郭注-1]：賴亦惡名。', '卷二', '方言', '2', DEFAULT, DEFAULT, '2509;3962;1488', '1708', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (68, '恒慨;蓡綏;羞繹;紛毋;廣大', '', '恒慨、蔘綏{蓡綏}、羞繹、紛毋，言既廣又大也。荆揚之閒凡言廣大者謂之恒慨，東甌之閒謂之蔘綏，12或謂之羞繹、3紛毋。', '[郭注-1]：東甌亦越地，今臨海永寧是也。\n[音義-2]：蔘，索含反；\n[音義-3]：繹，音奕；', '卷二', '方言', '', DEFAULT, DEFAULT, '9015/6729;315/8762;9790/8518;8707/8355;5938/6596', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (69, '劋;蹶;獪;蔿;姡', '訬;獪;僞;獪', '劋[訬]、蹶[獪]，獪也。秦晉之閒曰獪；1楚謂之劋，2或曰蹶；3楚鄭曰蔿[僞]，45或曰姡[獪]。67', '[郭注-1]：古狡㹟字；\n[音義-2]：劋，雀燎反，又子了反；\n[音義-3]：音踣蹶；\n[郭注-4]：亦獪聲之轉也；\n[音義-5]：音指撝。\n[郭注-6]：言黠姡也，今建平郡人呼狡爲姡。\n[音義-7]：胡刮切。', '卷二', '方言', '1;2;4;5', DEFAULT, DEFAULT, '2788;1369;6340;447;8225', '1667;6340;5134;6340', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (70, '釐孶;連子;孿生;嫁子', '𠩺孶', '陳楚之閒凡人嘼乳而雙產謂之釐孶[𠩺孶]，1秦晉之間謂之僆子{連子}，2自關而東趙魏之間謂之孿生。3女謂之嫁子。4', '[音義-1]：音茲；\n[音義-2]：音輦；\n[音義-3]：蘇官反；\n[郭注-4]：言往適人。', '卷三', '方言', '1', DEFAULT, DEFAULT, '9168/9774;1168/9764;9769/3874;8125/9764', '2056/9774', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (71, '知;倩', '壻', '東齊之間聟{知}[壻]謂之倩。12', '[郭注-1]：言可借倩也。\n[音義-2]：今俗呼女壻爲卒便是也。', '卷三', '方言', '1', DEFAULT, DEFAULT, '3315;4969', '227', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (72, '娠', '', '燕齊之閒養馬者謂之娠，1官婢女廝謂之娠。23', '[郭注-1]：今之温厚也。\n[郭注-2]：女廝，婦人給使者，亦名娠。\n[音義-3]：音振。', '卷三', '方言', '', DEFAULT, DEFAULT, '8134', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (73, '亭父;亭公;卒;弩父;褚', '', '楚東海之間亭父謂之亭公。1卒謂之弩父，2或謂之褚。34', '[郭注-1]：亭民；\n[郭注-2]：主擔幔弩導幨，因名云；\n[郭注-3]：言衣赤也。\n[音義-4]：音赭。', '卷三', '方言', '', DEFAULT, DEFAULT, '3320/1908;3320/716;5359;8497/1908;5360', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (74, '臧;甬;侮;獲', '庸', '臧、甬[庸]、侮、獲，奴婢賤稱也。1荆淮海岱雜齊之閒，2罵奴曰臧，罵婢曰獲。齊之北鄙燕之北郊凡民男而聟婢謂之臧，女而婦奴謂之獲；亡奴謂之臧，亡婢謂之獲。皆異方罵奴婢之醜稱也。自關而東陳魏宋楚之閒保庸謂之甬。34秦晉之閒罵奴婢曰侮。', '[郭注-1]：皆爲人所輕弄。\n[郭注-2]：俗不純爲雜；\n[郭注-3]：保，言可保信也；\n[音義-4]：音勇；', '卷三', '方言', '2', DEFAULT, DEFAULT, '1955;4344;5148;6362', '2084', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (75, '蔿;譌;譁;涅;化', '化;化', '蔿[化]、1譌[化]、2譁、34涅，化也。燕朝鮮洌水之閒曰涅，或曰譁。雞伏卵而未孚、始化之時謂之涅。5', '[音義-1]：音花；\n[音義-2]：音訛言；\n[郭注-3]：皆化聲之轉也；\n[音義-4]：五瓜反；\n[音義-5]：音赴。', '卷三', '方言', '1;2', DEFAULT, DEFAULT, '447;1661;1659;7232;5214', '5214;5214', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (76, '斟;協;汁', '汁', '斟、協[汁]，汁也。1北燕朝鮮洌水之閒曰斟，自關而東曰協，關西曰汁。', '[郭注-1]：謂和協也，或曰潘汁，所未能詳。', '卷三', '方言', '2', DEFAULT, DEFAULT, '9497;9258;7394', '7394', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (77, '蘇;芥;草;莽;荏;公蕡;害;䖆柔', '丯;艸;茻;丯', '蘇、芥[丯]，草[艸]也。江淮南楚之閒曰蘇，1自關而西或曰草，或曰芥。2南楚江湘之閒謂之莽[茻]。3蘇亦荏也。4關之東西或謂之蘇，或謂之荏。56周鄭之閒謂之公蕡。7沅湘之南或謂之𦵯{害}[丯]，8910其小者謂之䖆葇{䖆柔}。11', '[郭注-1]：漢書曰：「樵蘇而爨。」蘇猶蘆，語轉也；\n[郭注-2]：或言菜也；\n[音義-3]：嫫母反。\n[郭注-4]：荏屬也。爾雅曰：「蘇，桂荏也。」；\n[郭注-5]：今江東人呼荏爲䓊。\n[音義-6]：音魚；\n[音義-7]：蕡，音翡翠。\n[郭注-8]：今長沙人呼野蘇爲𦵯。\n[音義-9]：音車轄。\n[郭注-10]：沅，水名，在武陵；\n[郭注-11]：荲葇也，亦蘇之種類，因名云。', '卷三', '方言', '2;3;4;7', DEFAULT, DEFAULT, '256;632;680;702;257;716/575;4614;268/3607', '2817;240;700;2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (78, '豐;蕘;蕪菁;大芥;辛芥;幽芥;蘆菔;拉遝', '葑', '蘴{豐}[葑]、蕘，蕪菁也。1陳楚之郊謂之蘴；2魯齊之郊謂之蕘；3關之東西謂之蕪菁；趙魏之郊謂之大芥，其小者謂之辛芥，或謂之幽芥，其紫華者謂之蘆菔。4東魯謂之菈𦿚{拉遝}。5', '[郭注-1]：今江東名爲温菘，實如小豆。\n[音義-2]：蘴，舊音蜂，今江東音嵩，字作菘也；\n[音義-3]：音鈐鐃；\n[音義-4]：羅匐二音；\n[音義-5]：洛荅、徒合兩反。', '卷三', '方言', '1', DEFAULT, DEFAULT, '3103;621;544/276;6596/632;9754/632;2501/632;277/278;7852/1116', '399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (79, '役;芡;雞頭;鴈頭;烏頭', '', '䓈{役}、芡，雞頭也。北燕謂之䓈，1青徐淮泗之閒謂之芡，2南楚江湘之閒謂之雞頭，或謂之鴈頭，或謂之烏頭。3', '[郭注-1]：今江東亦呼䓈耳；\n[音義-2]：音儉；\n[郭注-3]：狀似烏頭，故轉以名之。', '卷三', '方言', '', DEFAULT, DEFAULT, '1977;415;2290/5599;2422/5599;2487/5599', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (80, '茦;壯;梗;劌;刺;棘', '戕', '凡草木刺人，北燕朝鮮之閒謂之茦。1或謂之壯[戕]。2自關而東或謂之梗，3或謂之劌，45自關而西謂之刺。江湘之閒謂之棘。67', '[郭注-1]：爾雅曰：「茦，刺也。」；\n[郭注-2]：今淮南人亦呼壯。壯，傷也。山海經謂刺爲傷也；\n[郭注-3]：今之梗榆；\n[郭注-4]：劌者，傷割人名。\n[音義-5]：音鱥魚也；\n[郭注-6]：楚辭曰：「曾枝剡棘。」亦通語耳。\n[音義-7]：音己力反。', '卷三', '方言', '2', DEFAULT, DEFAULT, '397;228;3541;2763;2805;4397', '8388', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (81, '瘌;癆;瞑;眩', '', '凡飲藥傅藥而毒，南楚之外謂之瘌，1北燕朝鮮之閒謂之癆，23東齊海岱之閒謂之瞑，或謂之眩。4自關而西謂之毒。瘌，痛也。', '[音義-1]：音乖瘌；\n[郭注-2]：癆、瘌皆辛螫也。\n[音義-3]：音澇；\n[郭注-4]：瞑眩，亦今通語耳。', '卷三', '方言', '', DEFAULT, DEFAULT, '4789;4790;2182;2099', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (82, '逞;曉;恔;苦;快', '', '逞、曉、恔、苦，快也。自關而東或曰曉，或曰逞。江淮陳楚之閒曰逞，宋鄭周洛韓魏之閒曰苦，東齊海岱之閒曰恔，1自關而西曰快。', '[郭注-1]：恔卽狡，狡戲亦快事也。', '卷三', '方言', '', DEFAULT, DEFAULT, '1196;4195;6735;323;6716', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (83, '膠;譎;謬;詐', '', '謬、譎，詐也。涼州西南之閒曰膠，自關而東西或曰譎，或曰謬。詐，通語也。12', '[郭注-1]：汝南人呼欺爲讉[囮]，亦曰詒。\n[音義-2]：讉，託回反。詒，音殆。', '卷三', '方言', '', DEFAULT, DEFAULT, '2728;1669;1664;1670', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (84, '揠;擢;拂;戎;拔', '拔', '揠、擢、拂[拔]、戎，拔也。自關而西或曰拔，或曰擢。自關而東江淮南楚之閒或曰戎。東齊海岱之閒曰揠。12', '[郭注-1]：今呼拔草心爲揠。\n[音義-2]：烏拔反。', '卷三', '方言', '3', DEFAULT, DEFAULT, '7996;7994;8060;8377;7995', '7995', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (85, '慰;廛;度;凥;踐', '宅', '慰、廛、度[宅]，凥也。江淮靑徐之閒曰慰，東齊海岱之閒或曰度，或曰廛，12或曰踐。', '[郭注-1]：周官云：「夫一廛。」宅也。\n[音義-2]：音纏約。', '卷三', '方言', '3', DEFAULT, DEFAULT, '6781;5943;1929;9469;1357', '4556', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (86, '萃;雜;集', '', '萃、雜，集也。東齊曰聚。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '540;5333;2367', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (87, '𨽿;遝;及', '', '迨{𨽿}、遝，及也。東齊曰迨，1關之東西曰遝，或曰及。', '[音義-1]：音殆。', '卷三', '方言', '', DEFAULT, DEFAULT, '1947;1116;1918', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (88, '荄;杜;根;茇', '土', '荄、12杜[土]，根也。東齊曰杜，3或曰茇。4', '[郭注-1]：今俗名韭根爲荄。\n[音義-2]：音陔。\n[郭注-3]：詩曰：「徹彼桑杜。」是也；\n[音義-4]：音撥。', '卷三', '方言', '2', DEFAULT, DEFAULT, '515;3431;3562;517', '9019', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (89, '班;徹;列', '', '班、徹，列也。北燕曰班，東齊曰徹。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '222;1997;2771', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (90, '瘼;復;病;湛', '', '瘼、癁{復}，病也。東齊海岱之閒曰瘼，1或曰癁，2秦曰瘎{湛}。3', '[音義-1]：音莫；\n[郭注-2]：謂勞復也；\n[音義-3]：音諶。', '卷三', '方言', '', DEFAULT, DEFAULT, '4703;1226;4695;7294', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (91, '掩;醜;掍;𪓌;同', '雔', '掩、醜[雔]、掍、綷{𪓌}，同也。江淮南楚之閒曰掩；宋衞之閒曰綷，1或曰掍；2東齊曰醜。', '[音義-1]：作㥽反。\n[音義-2]：音衮衣；', '卷三', '方言', '2', DEFAULT, DEFAULT, '8033;5834;8089;4946;4801', '2362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (92, '裕;猷;道', '邎;邎', '裕[邎]、猷[邎]，道也。東齊曰裕，或曰猷。', '', '卷三', '方言', '1;2', DEFAULT, DEFAULT, '5335;6373;1205', '1100;1100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (93, '虔;散;殺', '丯', '虔[丯]、散，殺也。東齊曰散，靑徐淮楚之閒曰虔。', '', '卷三', '方言', '1', DEFAULT, DEFAULT, '3111;2720;1979', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (94, '氾;浼;㶄;洼;洿;潢', '㶄', '氾[㶄]、浼、㶄、洼，洿也，1潢2自關而東或曰洼，3或曰氾；4東齊海岱之閒或曰浼，5或曰㶄。6', '[郭注-1]：皆洿池也；荆州呼\n[郭注-2]：也。\n[音義-3]：烏蛙反；\n[音義-4]：音汎；\n[音義-5]：音漫；\n[音義-6]：音湯㶄；', '卷三', '方言', '1', DEFAULT, DEFAULT, '7180;7428;7427;7253;7347;7255', '7427', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (95, '庸;恣;侹;更;佚;遞;代', '佽;迭', '庸、恣[佽]、1比、2侹、更、佚[迭]、遞，代也。齊曰佚，3江淮陳楚之閒曰侹，4餘四方之通語也。', '[郭注-1]：今俗亦名更代作爲恣作也。\n[音義-2]：音比次；\n[音義-3]：音蹉跌；\n[音義-4]：音挺直；', '卷三', '方言', '2;5', DEFAULT, DEFAULT, '2084;6846;5011;2021;5142;1133;5081', '5048;1166', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (96, '氓;民', '', '氓，民也。12', '[郭注-1]：民之總名。\n[音義-2]：氓，音萌；', '卷三', '方言', '', DEFAULT, DEFAULT, '8358;8357', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (97, '仇;仇', '', '㐜{仇}，仇也。12', '[郭注-1]：謂怨仇也。\n[音義-2]：朹，音舊；', '卷三', '方言', '', DEFAULT, DEFAULT, '5171;5171', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (98, '寓;寄', '', '寓，寄也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '4610;4609', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (99, '露;敗', '', '露，敗也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '7561;2041', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (100, '别;治', '', '别，治也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '2566;7087', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (101, '棖;法', '', '棖，法也。1', '[郭注-1]：救傾之法。', '卷三', '方言', '', DEFAULT, DEFAULT, '3734;6260', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (102, '謫;怒', '', '謫，怒也。12', '[郭注-1]：相責怒也。\n[音義-2]：謫，音責；', '卷三', '方言', '', DEFAULT, DEFAULT, '1691;6877', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (103, '閒;非', '', '閒，非也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '7760;7694', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (104, '格;正', '', '格，正也。', '', '卷三', '方言', '', DEFAULT, DEFAULT, '3601;1087', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (105, '𣀷;數', '', '𣀷，數也。1', '[郭注-1]：偶物爲麗，故云數也。', '卷三', '方言', '', DEFAULT, DEFAULT, '2010;2011', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (106, '軫;戾', '紾', '軫[紾]，戾也。12', '[郭注-1]：相了戾也。\n[音義-2]：軫，江東音善。', '卷三', '方言', '1', DEFAULT, DEFAULT, '9540;6354', '8572', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (107, '㞕;潔', '絜', '㞕，潔[絜]也。12', '[郭注-1]：謂潔淸也。\n[音義-2]：㞕，音薛；', '卷三', '方言', '2', DEFAULT, DEFAULT, '5404;7467', '8752', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (108, '諄;罪', '敦', '諄[敦]，罪也。12', '[郭注-1]：謂罪惡也。\n[音義-2]：諄，章順反；', '卷三', '方言', '1', DEFAULT, DEFAULT, '1504;4822', '2039', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (109, '俚;聊', '僇', '俚，聊[僇]也。12', '[郭注-1]：謂苟且也。\n[音義-2]：俚，音吏；', '卷三', '方言', '2', DEFAULT, DEFAULT, '5004;7800', '5170', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (110, '梱;就', '稛', '梱[稛]，就也。12', '[郭注-1]：梱梱，成就貌。\n[音義-2]：梱，恪本反；', '卷三', '方言', '1', DEFAULT, DEFAULT, '3661;3330', '4467', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (111, '笠;圂', '', '苙{笠}，圂也。12', '[郭注-1]：謂蘭圂也。\n[音義-2]：苙，音立；', '卷三', '方言', '', DEFAULT, DEFAULT, '2967;3930', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (112, '叜;隱', '縮', '廋{叜}[縮]，隱也。12', '[郭注-1]：謂隱匿也。\n[音義-2]：廋，音捜索也；', '卷三', '方言', '1', DEFAULT, DEFAULT, '1909;9667', '8563', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (113, '銛;取', '', '銛，取也。12', '[郭注-1]：謂挑取物。\n[音義-2]：銛，音忝；', '卷三', '方言', '', DEFAULT, DEFAULT, '9342;1926', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (114, '棖;隨', '', '棖，隨也。1', '[郭注-1]：棖柱令相隨也。', '卷三', '方言', '', DEFAULT, DEFAULT, '3734;1102', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (115, '嬯;服;辟', '僰', '儓{嬯}、𧟵{服}[僰]，農夫之醜稱也。南楚凡罵傭賤謂之田儓，12或謂之𧟵，34或謂之辟。辟，商人醜稱也。56', '[郭注-1]：㑣儓，駑鈍貌。或曰「僕臣儓」，亦至賤之號也；\n[音義-2]：音臺；\n[郭注-3]：𧟵，丁健貌也。\n[音義-4]：音僰，廣雅以爲奴，字作僰，音同；\n[郭注-5]：辟辟，便黠貌也。\n[音義-6]：音擘。', '卷三', '方言', '2', DEFAULT, DEFAULT, '8316;5447;5796', '5188', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (116, '庸;松', '庸', '庸謂之倯{松}[庸]，轉語也。12', '[郭注-1]：倯，猶保倯也。今隴右人名孏爲倯。\n[音義-2]：相容反。', '卷三', '方言', '2', DEFAULT, DEFAULT, '2084;3543', '2084', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (117, '敗;須捷;褸裂;襤褸;挾斯', '須𧚨;挾澌', '褸裂、須捷、挾斯，敗也。南楚凡人貧衣被醜敝謂之須捷[須𧚨]。1或謂之褸裂，23或謂之襤褸，故左傳曰：「篳路襤褸，以启山林。」殆謂此也。4或謂之挾斯[挾澌]。器物敝亦謂之挾斯。5', '[郭注-1]：須捷，狎翣也；\n[郭注-2]：衣壞貌；\n[音義-3]：音縷；\n[郭注-4]：篳路，柴車；\n[郭注-5]：挾斯，猶挾變也。', '卷三', '方言', '2;5', DEFAULT, DEFAULT, '2041;5706/8087;5267/5338;5281/5267;7869/9483', '5706/5268;7869/7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (118, '㯷;鋌;澌;盡;賜', '澌', '𢷏{㯷}、鋌、澌，盡也。南楚凡物盡生者曰𢷏生。12物空盡者曰鋌；3鋌、賜[澌]、4𢷏、澌皆盡也。賜、澌，語之轉也。', '[郭注-1]：今種物皆生云𢷏地生也；\n[音義-2]：音打𢷏；\n[音義-3]：音挺；\n[郭注-4]：亦中國之通語也。', '卷三', '方言', '5', DEFAULT, DEFAULT, '3492;9287;7338;3156;3959', '7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (119, '㯷;翕;葉;聚', '吸;疊', '𢷏{㯷}、翕[吸]、葉[疊]，聚也。楚謂之𢷏，1或謂之翕。葉，楚通語也。', '[郭注-1]：𢷏屬，藂相著貌。', '卷三', '方言', '2;3', DEFAULT, DEFAULT, '3492;2257;495;5236', '827;4317', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (120, '斟;益', '', '斟，益也。1南楚凡相益而又少謂之不斟。凡病少愈而加劇亦謂之不斟，或謂之何斟。2', '[郭注-1]：言斟酌益之；\n[郭注-2]：言雖少損無所益也。', '卷三', '方言', '', DEFAULT, DEFAULT, '9497;3154', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (121, '瘥;閒;知;瘉;慧;憭;瘳;蠲;除', '', '差{瘥}、閒、知，愈{瘉}也。南楚病愈者謂之差，或謂之閒，1或謂之知。知，通語也。或謂之慧，或謂之憭，2或謂之瘳，或謂之蠲，34或謂之除。', '[郭注-1]：言有閒隙；\n[郭注-2]：慧、憭，皆意精明；\n[郭注-3]：蠲，亦除也。\n[音義-4]：音涓，一音圭。', '卷三', '方言', '', DEFAULT, DEFAULT, '4791;7760;3315;4793;6733;6734;4794;8827;9690', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (122, '襌衣;褋;左衣;裎衣;深衣', '', '襌衣，江淮南楚之閒謂之褋，12關之東西謂之襌衣。有袌者，34趙魏之閒謂之袏衣{左衣}；無袌者謂之裎衣。5古謂之深衣。6', '[郭注-1]：楚辭曰：「遺余褋兮澧浦。」\n[音義-2]：音簡牒；\n[郭注-3]：前施袌囊也。\n[音義-4]：房報反；\n[音義-5]：音逞；\n[郭注-6]：制見禮記。', '卷四', '方言', '', DEFAULT, DEFAULT, '5321/5252;5275;3027/5252;5345/5252;7034/5252', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (123, '襜褕;童容;裋褕;襤褸;充屈;緻', '𧝉', '襜褕，江淮南楚謂之𧝎褣{童容}，1自關而西謂之襜褕，其短者謂之裋褕，2以布而無緣、敝而紩之謂之襤褸。自關而西謂之䘪䘿{充屈}，34其敝者謂之緻[𧝉]。56', '[音義-1]：𧝎，裳凶反；\n[音義-2]：裋，音豎；\n[郭注-3]：俗名䘿掖。\n[音義-4]：音倔；\n[郭注-5]：緻，縫納敝，故名之也。\n[音義-6]：丁履反。', '卷四', '方言', '6', DEFAULT, DEFAULT, '5290/5256;1747/4587;5355/5256;5281/5267;5460/5428;8764', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (124, '汗襦;曾;袛裯;甲襦;襜襦;襌襦', '', '汗襦，12江淮南楚之閒謂之䙢{曾}；3自關而西或謂之袛裯；4自關而東謂之甲襦；陳魏宋楚之閒謂之襜襦，或謂之襌襦。5', '[郭注-1]：亦呼爲掩汗也；\n[音義-2]：襦，廣雅作褕；\n[音義-3]：音甑；\n[音義-4]：袛，音氐。裯，丁牢反。\n[郭注-5]：今或呼衫爲襌襦。', '卷四', '方言', '', DEFAULT, DEFAULT, '7434/5318;710;5279/5280;9739/5318;5290/5318;5321/5318', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (125, '帬;帔;罷', '帔', '帬，陳魏之閒謂之帔，1自關而東或謂之襬{罷}[帔]。23', '[音義-1]：音披；\n[郭注-2]：今關西語然也。\n[音義-3]：音碑。', '卷四', '方言', '3', DEFAULT, DEFAULT, '4869;4868;4841', '4868', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (126, '蔽厀;褘;袚;大巾;冉', '襜', '蔽厀，江淮之閒謂之褘，1或謂之袚，2魏宋南楚之閒謂之大巾，自關東西謂之蔽厀，齊魯之郊謂之袡{冉}[襜]。3', '[音義-1]：音葦，或暉；\n[音義-2]：音沸；\n[音義-3]：袡，昌詹反。', '卷四', '方言', '5', DEFAULT, DEFAULT, '550/5783;5270;5362;6596/4854;6073', '5290', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (127, '襦;曲領', '', '襦，1西南蜀漢謂之曲領，或謂之襦。', '[郭注-1]：字亦作褕，又襦無右也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5318;8442/5618', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (128, '㡓;𢃓', '', '褌{㡓}，陳楚江淮之閒謂之䘴{𢃓}。1', '[音義-1]：䘴，息勇反。', '卷四', '方言', '', DEFAULT, DEFAULT, '4872;4873', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (129, '絝;褰', '', '袴{絝}，齊魯之閒謂之䙭{褰}，12或謂之襱。34關西謂之袴。', '[郭注-1]：傳曰：「徵䙭與襦。」\n[音義-2]：音騫；\n[郭注-3]：今俗呼袴踦爲襱。\n[音義-4]：音鮦魚。', '卷四', '方言', '', DEFAULT, DEFAULT, '8659;5297', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (130, '褕;袖', '', '褕謂之袖。1', '[郭注-1]：襦䙭有袖者，因名云。', '卷四', '方言', '', DEFAULT, DEFAULT, '5256;5285', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (131, '及;褗', '襋', '衱{及}[襋]謂之褗。12', '[郭注-1]：卽衣領也。\n[音義-2]：衱褗，劫偃兩音。', '卷四', '方言', '1', DEFAULT, DEFAULT, '1918;5264', '5262', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (132, '圭;裾', '', '袿{圭}謂之裾。12', '[郭注-1]：衣後裾也。\n[音義-2]：或作袪，廣雅云「衣袖」。', '卷四', '方言', '', DEFAULT, DEFAULT, '9149;5294', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (133, '褸;衽', '', '褸謂之衽。1', '[郭注-1]：衣襟也。或曰裳際也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5267;5266', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (134, '褸;緻', '𧝉', '褸謂之緻[𧝉]。1', '[郭注-1]：襤褸，綴結也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '5267;8764', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (135, '裯;襤', '', '裯謂之襤。1', '[郭注-1]：袛裯，敝衣，亦謂襤褸；', '卷四', '方言', '', DEFAULT, DEFAULT, '5280;5281', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (136, '襤;䙃;鼻', '', '無緣之衣謂之襤。無袂之衣謂之䙃。12無𧙥之袴謂之襣{鼻}。3', '[郭注-1]：袂，衣袖也，\n[音義-2]：音藝。䙃，音慢惰；\n[郭注-3]：袴無踦者，卽今犢鼻褌也。𧙥亦襱，字異耳。', '卷四', '方言', '', DEFAULT, DEFAULT, '5281;5282;2232', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (137, '綃;占', '㡇', '䘯{綃}謂之袩{占}[㡇]。12', '[郭注-1]：未詳其義。\n[音義-2]：所交、丁俠兩反。', '卷四', '方言', '2', DEFAULT, DEFAULT, '8522;2079', '4915', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (138, '䘳;交', '', '衿{䘳}謂之交。1', '[郭注-1]：衣交領也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5269;6625', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (139, '裺;襦', '', '裺謂之襦。1', '[音義-1]：裺，尖劍反。', '卷四', '方言', '', DEFAULT, DEFAULT, '5265;5318', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (140, '襜;掖', '', '襜謂之䘸{掖}。1', '[郭注-1]：衣掖下也。', '卷四', '方言', '', DEFAULT, DEFAULT, '5290;8092', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (141, '佩紟;裎', '綎', '佩紟謂之裎[綎]。12', '[郭注-1]：所以系玉佩帶也。\n[音義-2]：紟，音禁。', '卷四', '方言', '2', DEFAULT, DEFAULT, '4958/8656;5345', '8653', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (142, '褸;占', '㡇', '褸謂之袩{占}[㡇]。1', '[郭注-1]：卽衣衽也。', '卷四', '方言', '2', DEFAULT, DEFAULT, '5267;2079', '4915', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (143, '覆卒;襌衣;偏襌;襌襦', '', '覆䘹{覆卒}謂之襌衣。1偏襌謂之襌襦。2', '[音義-1]：䘹，作憒反；\n[郭注-2]：卽衫也。', '卷四', '方言', '', DEFAULT, DEFAULT, '4852/5359;5321/5252;5118/5321;5321/5318', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (144, '約纏;襌', '', '𧘑繵{約纏}謂之襌。12', '[郭注-1]：今又呼爲涼衣也。\n[音義-2]：𧘑襌，灼纏兩音。', '卷四', '方言', '', DEFAULT, DEFAULT, '8568/8570;5321', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (145, '袒飾;直領', '', '袒飾謂之直袊{直領}。12', '[郭注-1]：婦人初嫁，所著上衣直袊也。\n[音義-2]：袒音但。', '卷四', '方言', '', DEFAULT, DEFAULT, '5340/4894;8410/5618', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (146, '襃明;袍', '', '襃明謂之袍。1', '[郭注-1]：廣雅云：「襃明，長襦也。」。', '卷四', '方言', '', DEFAULT, DEFAULT, '5301/4321;5273', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (147, '繞領;帬', '', '繞袊{繞領}謂之帬。1', '[郭注-1]：俗人呼接下，江東通言下裳。', '卷四', '方言', '', DEFAULT, DEFAULT, '8571/5618;4869', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (148, '縣裺;緣', '', '懸裺{縣裺}謂之緣。12', '[郭注-1]：衣縫緣也。\n[音義-2]：裺，音掩。', '卷四', '方言', '', DEFAULT, DEFAULT, '5705/5265;8657', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (149, '絜襦;蔽厀', '', '絜襦謂之蔽厀。1', '[郭注-1]：廣異名也。', '卷四', '方言', '', DEFAULT, DEFAULT, '8752/5318;550/5783', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (150, '陶矞;袖', '韜繘', '裪𥛯{陶矞}[韜繘]謂之袖。12', '[郭注-1]：衣褾。江東呼䘼，\n[音義-2]：音婉。𥛯音橘；', '卷四', '方言', '1', DEFAULT, DEFAULT, '9687/1453;5285', '3386/8718', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (151, '扈表;被巾', '扈襮', '帍裱{扈表}[扈襮]謂之被巾。12', '[郭注-1]：婦人領巾也。\n[音義-2]：裱，方廟反；', '卷四', '方言', '1', DEFAULT, DEFAULT, '4023/5259;5323/4854', '4023/5263', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (152, '繞盾;𧛔裺', '', '繞䋸{繞盾}謂之𧞶裺{𧛔裺}。12', '[郭注-1]：衣督脊也。\n[音義-2]：䋸，音循。', '卷四', '方言', '', DEFAULT, DEFAULT, '8571/2220;5283/5265', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (153, '厲;帶', '烈', '厲[烈]謂之帶。1', '[郭注-1]：小爾雅曰：「帶之垂者爲厲。」', '卷四', '方言', '1', DEFAULT, DEFAULT, '5983;4865', '6425', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (154, '幡卷;幭', '幡㠾', '襎裷{幡卷}[幡㠾]謂之幭。12', '[郭注-1]：卽帊幞也。\n[音義-2]：襎裷，煩冤兩音；幭，亡别反。', '卷四', '方言', '1', DEFAULT, DEFAULT, '4891/5784;4897', '4891/4890', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (155, '繄絡;䙔', '', '繄袼{繄絡}謂之䙔。12', '[郭注-1]：卽小兒㳄衣也。\n[音義-2]：翳、洛、漚三音。', '卷四', '方言', '', DEFAULT, DEFAULT, '8687/8725;5356', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (156, '襤;褸;緻;充屈', '𧝉', '楚謂無緣之衣曰襤，紩衣謂之褸，秦謂之緻[𧝉]。自關而西秦晉之閒無緣之衣謂之䘪䘿{充屈}。1', '[郭注-1]：嫌上說有未了，故復分明之。', '卷四', '方言', '3', DEFAULT, DEFAULT, '5281;5267;8764;5460/5428', '5342', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (157, '複襦;裋;桶褺', '', '複襦，江湘之閒謂之𧞀{裋}，1或謂之筩褹{桶褺}。2', '[音義-1]：音豎；\n[郭注-2]：今筩袖之襦也。褹卽袂字耳。', '卷四', '方言', '', DEFAULT, DEFAULT, '5305/5318;5355;3760/5315', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (158, '絝;到頓;交尞', '', '{絝}大袴謂之倒頓{到頓}，1小袴謂之䘨𧘈{交尞}。23楚通語也。', '[郭注-1]：今雹袴也；\n[郭注-2]：今𧝜袴也。\n[音義-3]：皎了兩音。', '卷四', '方言', '', DEFAULT, DEFAULT, '8659;7707/5658;6625/6420', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (159, '𢄐;巾;𢁥;帤', '', '𢄐，巾也。1大巾謂之𢁥。23嵩嶽之南，4陳潁之閒謂之帤，5亦謂之𢄐。', '[郭注-1]：巾，主覆者，故名𢄐也；\n[郭注-2]：江東通呼巾𢁥耳。\n[音義-3]：音芬；\n[郭注-4]：嵩高，中岳山也，今在河南陽城縣；\n[音義-5]：奴豬反；', '卷四', '方言', '', DEFAULT, DEFAULT, '4896;4854;4855;4861', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (160, '絡頭;佰頭;鈔繢;䰎帶;采帶;𣦼;奄;幧頭', '帴;裛', '絡頭，帞頭{佰頭}也。紗繢{鈔繢}、䰎帶、䰂帶{采帶}、𢂺{𣦼}[帴]、㡋{奄}[裛]，幧頭也。1自關而西秦晉之郊曰絡頭，南楚江湘之閒曰帞頭，2自河以北趙魏之閒曰幧頭，或謂之𢂺，3或謂之㡋。4其偏者謂之䰎帶。5或謂之䰂帶。67', '[郭注-1]：今之偏疊幧頭也；\n[音義-2]：音貊；\n[音義-3]：音綪，亦千；\n[音義-4]：于怯反；\n[音義-5]：羌位反；\n[郭注-6]：䰂亦結也。\n[音義-7]：音菜；', '卷四', '方言', '6;7', DEFAULT, DEFAULT, '8725/5599;5068/5599;9436/8538;5750/4865;3792/4865;2524;6599;4920/5599', '4871;5353', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (161, '覆結;幘巾;承露;覆采', '覆䯰', '覆結[覆䯰]謂之幘巾，或謂之承露，或謂之覆䰂{覆采}。皆趙魏之閒通語也。1', '[郭注-1]：今結籠是也。', '卷四', '方言', '1', DEFAULT, DEFAULT, '4852/8575;4866/4854;7912/7561;4852/3792', '4852/5751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (162, '屝;屨;𧆓;履;複舄;夗下;鞮;不借;䩕角;妥;䋀', '', '屝、屨、麤{𧆓}，履也。徐兗之郊謂之屝，1自關而西謂之屨。中有木者謂之複舄，自關而東謂之複履。其庳者謂之𩊁下{夗下}，2襌者謂之鞮，3絲作之者謂之履，麻作之者謂之不借，麤者謂之屨，東北朝鮮洌水之閒謂之䩕角。4南楚江沔之閒總謂之麤。5西南梁益之閒或謂之㞜{妥}，6或謂之䋀。7履，其通語也。徐土邳圻之閒，89大麤謂之䩕角。10', '[音義-1]：音翡；\n[音義-2]：音婉；\n[郭注-3]：今韋鞮也；\n[音義-4]：音卭；\n[郭注-5]：沔水，今在襄陽；\n[音義-6]：他囘反，字或作「𡲾」，音同；\n[音義-7]：乎瓦反，一音畫；\n[郭注-8]：今下邳也。\n[音義-9]：圻，音祈；\n[郭注-10]：今漆履有齒者。', '卷四', '方言', '', DEFAULT, DEFAULT, '5416;5431;607;5430;5305/2488;4328/9;1805;7704/5075;1804/2826;8347;8749', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (163, '緉;爽;絞', '雙', '緉、𦄍{爽}[雙]，絞也。關之東西或謂之緉，1或謂之𦄍。2絞，通語也。34', '[音義-1]：緉，音兩；\n[音義-2]：音爽；\n[郭注-3]：謂履中絞也。\n[音義-4]：音校。', '卷四', '方言', '2', DEFAULT, DEFAULT, '8751;2091;6627', '2364', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (164, '纑;鬒', '', '纑謂之縝{鬒}。12', '[郭注-1]：謂纑縷也。\n[音義-2]：縝，音振。', '卷四', '方言', '', DEFAULT, DEFAULT, '8734;5713', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (165, '鍑;鑊;錪;餅;錡;鏤;鬲;釜', '', '鍑，12或謂之鑊。北燕朝鮮洌水之閒或謂之錪，3或謂之鉼{餅}。4江淮陳楚之閒謂之錡，56或謂之鏤。吳揚之閒謂之鬲。7釜，自關而西或謂之釜，或謂之鍑。8', '[郭注-1]：釜屬也。\n[音義-2]：音富；\n[音義-3]：音腆；\n[音義-4]：音餅；\n[郭注-5]：或曰三腳釜也。\n[音義-6]：音技；\n[音義-7]：音歴。\n[郭注-8]：鍑亦釜之總名。', '卷五', '方言', '', DEFAULT, DEFAULT, '9299;9298;9301;3212;9329;9271;1854;1861', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (166, '甑;甗;鬵;酢餾', '飵餾', '甑，自關而東謂之甗，1或謂之鬵，23或謂之酢餾[飵餾]。4', '[音義-1]：音言；\n[郭注-2]：涼州呼鉹；\n[音義-3]：𩱕音岑。\n[音義-4]：音屋霤。', '卷五', '方言', '4', DEFAULT, DEFAULT, '8454;8455;1859;9860/3206', '3235/3206', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (167, '盂;盌;銚銳;櫂;柯;卷', '銚;閜', '盂，1宋楚魏之閒或謂之盌。2盌謂之盂，或謂之銚銳。3盌謂之櫂[銚]，盂謂之柯[閜]。4海岱東齊北燕之閒或謂之𥁠{卷}。5', '[音義-1]：音于；\n[音義-2]：烏管反；\n[音義-3]：銚，謠音；\n[郭注-4]：轉相釋者，廣異語也；\n[音義-5]：音書卷。', '卷五', '方言', '4;5', DEFAULT, DEFAULT, '3138;3139;9307/9367;3839;3744;5784', '9307;7756', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (168, '疋;椷;醆;氾;閜;楊;麻;桮', '雅;㔶', '㿿{疋}[雅]、椷[㔶]、盞{醆}、䀀{氾}、閜、𥂸{楊}、𥂓{麻}，桮也。秦晉之郊謂之㿿；12自關而東趙魏之閒曰椷，3或曰盞，45或曰䀀。6其大者謂之閜，7吳越之閒曰𥂸，8齊右平原以東或謂之𥂓。9桮，其通語也。', '[郭注-1]：所謂伯㿿者也；\n[音義-2]：音雅；\n[音義-3]：音封緘；\n[郭注-4]：最小桮也。\n[音義-5]：音酒醆；\n[音義-6]：薄淹反；\n[音義-7]：呼雅反；\n[音義-8]：又章反；\n[音義-9]：音摩；', '卷五', '方言', '1;2', DEFAULT, DEFAULT, '1422;3708;9833;7180;7756;3510;4534;3703', '2279;8429', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (169, '蠡;簞;戲;瓢', '', '㼖{蠡}，12陳楚宋魏之閒或謂之簞，或謂之𣟵{戲}，或謂之瓢。34', '[郭注-1]：瓠勺也。\n[音義-2]：音麗；\n[郭注-3]：今江東通呼勺爲𣟵。\n[音義-4]：音羲。', '卷五', '方言', '', DEFAULT, DEFAULT, '8965;2924;8383;4553', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (170, '案;寫', '', '案，陳楚宋魏之閒謂之𣞐{寫}，自關東西謂之案。', '', '卷五', '方言', '', DEFAULT, DEFAULT, '3706;4598', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (171, '桮笿;豆筥', '', '桮𥯛{桮笿}，1陳楚宋衞之閒謂之桮𥯛，又謂之豆筥；自關東西謂之桮𥯛。', '[郭注-1]：盛桮器籠也。', '卷五', '方言', '', DEFAULT, DEFAULT, '3703/2933;3095/2921', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (172, '箸筩;䈰;籯;桶蔥', '箸籠', '箸筩[箸籠]，1陳楚宋魏之閒謂之筲{䈰}，2或謂之籯；34自關而西謂之桶𣞷{桶蔥}。56', '[郭注-1]：盛朼箸籫也；\n[音義-2]：音鞭鞘；\n[郭注-3]：漢書曰：「遺子黃金滿籯。」\n[音義-4]：音盈也；\n[郭注-5]：今俗亦通呼小籠爲桶𣞷。\n[音義-6]：桶，或作筩，音籠冠。𣞷，蘇勇反。', '卷五', '方言', '1', DEFAULT, DEFAULT, '2928/2946;2920;2937;3760/633', '2928/2956', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (173, '亢;冘;廡;䍃;奠;從;𦉈;瓮;瓿婁;瓨;疑;罌', '䍌婁', '㼚{亢}、瓭{冘}、𤮢{廡}、䍃、㽀{奠}、㼻{從}、甀{𦉈}、瓮、瓿甊{瓿婁}[䍌婁]、瓨、㽈{疑}，甖{罌}也。靈桂之郊謂之㼚，12其小者謂之瓭；3周魏之閒謂之𤮢；45秦之舊都謂之㽀；6淮汝之閒謂之䍃；7江湘之閒謂之㼻。8自關而西晉之舊都河汾之閒，9其大者謂之甀，10其中者謂之瓿甊；11自關而東趙魏之郊謂之瓮，或謂之甖；東齊海岱之閒謂之㽈。12甖，其通語也。13', '[郭注-1]：今江東通呼大瓮爲㼚；\n[音義-2]：㼚，音岡；\n[音義-3]：瓭，都感反，亦音沈；\n[郭注-4]：今江東亦呼甖爲𤮢子；\n[音義-5]：𤮢，音舞；\n[音義-6]：㽀音鄭；\n[音義-7]：䍃音由；\n[音義-8]：㼻，胙江反；\n[郭注-9]：汾水出太原，經絳北，西南入河。\n[音義-10]：甀，度睡反；\n[音義-11]：瓿，音部。甊，洛口反；\n[音義-12]：㽈，牛志反；\n[音義-13]：甖，于庚反；', '卷五', '方言', '9', DEFAULT, DEFAULT, '6653;3324;5930;3298;3026;5225;3289;8459;8465/8321;8460;9778;3288', '3290/8321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (174, '罃;俞;朱;長;詹;𦉈', '臾;臾', '罃，陳魏宋楚之閒曰㼶{俞}[臾]，1或曰㼡{朱}[臾]；2燕之東北朝鮮洌水之閒謂之瓺{長}；3齊之東北海岱之閒謂之儋{詹}；45周洛韓鄭之閒謂之甀{𦉈}，或謂之罃。', '[音義-1]：音臾；\n[音義-2]：音殊；\n[音義-3]：音暢，亦腸；\n[郭注-4]：所謂家無儋石之儲者也。\n[音義-5]：音儋荷，字或作甔。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3294;5437;3561;6067;713;3289', '608;608', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (175, '罃;㼰', '', '罃謂之㼰。1', '[音義-1]：音鼓鼙。', '卷五', '方言', '', DEFAULT, DEFAULT, '3294;8463', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (176, '𦉥;斯', '', '𦉨{𦉥}謂之㽄{斯}。', '', '卷五', '方言', '', DEFAULT, DEFAULT, '3292;9483', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (177, '缶;瓿禺;缾', '䍌禺', '缶謂之瓿㼴{瓿禺}[䍌禺]，12其小者謂之瓶{缾}。', '[郭注-1]：卽盆也。\n[音義-2]：音偶。', '卷五', '方言', '2', DEFAULT, DEFAULT, '3285;8465/5842;3291', '3290/5842', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (178, '罃甈;盎;盆;升甌;甂;是', '榹', '罃甈謂之盎。12自關而西或謂之盆，或謂之盎；其小者謂之升甌。3甂，4陳魏宋楚之閒謂之㼵{是}[榹]；5自關而西謂之甂，其大者謂之甌。', '[郭注-1]：案爾雅甈，康瓠，而方言以爲盆，未詳也。\n[音義-2]：甈，卻罽反。盎，烏浪反。\n[音義-3]：惡牟反，亦音憂。\n[音義-4]：音邊；\n[郭注-5]：今河北人呼小盆爲㼵子。㼵，杜啓反。', '卷五', '方言', '6', DEFAULT, DEFAULT, '3294/8469;3146;3147;9505/8458;8464;1089', '3705', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (179, '注斛;鬲;注箕;羅', '', '所以注斛，1陳魏宋楚之閒謂之䈪{鬲}，234自關而西謂之注箕，陳魏宋楚之閒謂之籮{羅}。5', '[郭注-1]：盛米穀寫斛中者也；\n[郭注-2]：今江東亦呼爲䈪，\n[音義-3]：音巫覡\n[郭注-4]：；\n[郭注-5]：䈪亦籮屬也，形小而高，無耳。', '卷五', '方言', '', DEFAULT, DEFAULT, '7275/9490;1854;7275/3018;4831', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (180, '炊䉛;縮;叜;㕣', '籔;籔;匴', '炊䉛謂之縮[籔]，1或謂之𥰞{叜}[籔]，2或謂之㔯{㕣}[匴]。34', '[郭注-1]：漉米䉛也；\n[音義-2]：音藪；\n[郭注-3]：江東呼淅籖。\n[音義-4]：音旋。', '卷五', '方言', '2;3;4', DEFAULT, DEFAULT, '6455/2916;8563;1909;959', '2917;2917;8428', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (181, '篝;牆居', '', '篝，1陳楚宋魏之閒謂之牆居。', '[郭注-1]：今薰籠也。', '卷五', '方言', '', DEFAULT, DEFAULT, '2932;3345/5402', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (182, '扇;箑', '', '扇，自關而東謂之箑，12自關而西謂之扇。', '[郭注-1]：今江東亦通名扇爲箑。\n[音義-2]：音䈉。', '卷五', '方言', '', DEFAULT, DEFAULT, '7722;2955', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (183, '碓機;梴;磑;妻', '碓磑', '碓機[碓磑]，1陳魏宋楚自關而東謂之梴。2磑或謂之䃀{妻}。34', '[郭注-1]：碓梢也；\n[音義-2]：音𢌛；\n[郭注-3]：卽磨也。\n[音義-4]：錯碓反。', '卷五', '方言', '1', DEFAULT, DEFAULT, '6049/3723;3597;6048;8129', '6049/6048', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (184, '繘;綆;絡', '', '繘，1自關而東周洛韓魏之閒謂之綆，或謂之絡。2關西謂之繘。3', '[郭注-1]：汲水索也。\n[音義-2]：音洛。\n[音義-3]：音橘。', '卷五', '方言', '', DEFAULT, DEFAULT, '8718;8719;8725', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (185, '歷;宿;草', '翮;槽;槽', '櫪{歷}[翮]，1梁宋齊楚北燕之閒或謂之樎{宿}[槽]，2或謂之皁{草}[槽]。3', '[郭注-1]：養馬器也；\n[音義-2]：音縮；\n[郭注-3]：皁隸之名，於此乎出？', '卷五', '方言', '1;2;3', DEFAULT, DEFAULT, '1070;4600;680', '2253;3758;3758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (186, '飤馬槖;裺囊;裺篼;婁篼;振', '裛囊;裛篼', '飤馬槖，自關而西謂之裺囊[裛囊]，1或謂之裺篼[裛篼]；或謂之㡞篼{婁篼}；2燕齊之間謂之帪{振}。3', '[音義-1]：音䳺；\n[音義-2]：音樓；\n[郭注-3]：廣雅作「振」，字音同耳。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3222/6135/3902;5265/3903;5265/2962;8321/2962;7961', '5353/3903;5353/2962', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (187, '鉤;鹿觡;鉤格;微', '', '鉤，1宋楚陳魏之閒謂之鹿觡，2或謂之鉤格；自關而西謂之鉤，或謂之䥩{微}。3', '[郭注-1]：懸物者；\n[郭注-2]：或呼鹿角；\n[音義-3]：音微。', '卷五', '方言', '', DEFAULT, DEFAULT, '1461;6261/2848;1461/3601;1236', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (188, '臿;𣂀;華;韋;畚;喿;㭒', '㭉;䤥;銚', '臿，燕之東北朝鮮洌水之閒謂之𣂀；12宋魏之閒謂之鏵{華}[㭉]，或謂之鍏{韋}[䤥]；3江淮南楚之閒謂之臿；沅湘之閒謂之畚；趙魏之閒謂之喿[銚]；45東齊謂之梩{㭒}。6', '[郭注-1]：此亦鍫聲轉也。\n[音義-2]：湯料反；\n[音義-3]：音韋；\n[郭注-4]：字亦作鍫也；江東又呼鍫刃爲鐅，\n[音義-5]：普蔑反。\n[音義-6]：音駭。', '卷五', '方言', '3;4;6', DEFAULT, DEFAULT, '4524;9504;3884;3382;8447;1427;3687', '3686;9344;9307', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (189, '杷;渠挐;渠疏', '𣚏挐;𣚏挐', '杷，12宋魏之閒謂之渠挐[𣚏挐]，34或謂之渠疏[𣚏挐]。5', '[郭注-1]：有齒曰杷，無齒爲朳；\n[音義-2]：朳音八。\n[郭注-3]：今江東名亦然。\n[音義-4]：挐，諾豬反；\n[郭注-5]：語轉也。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '3693;7263/7877;7263/9787', '3685/7877;3685/7877', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (190, '僉;𣠞殳;度;棓;柫;柍;孛', '柫', '僉，1宋魏之閒謂之欇殳{𣠞殳}，23或謂之度，45自關而西謂之棓，6或謂之柫；7齊楚江淮之閒謂之柍，89或謂之桲{孛}[柫]。10', '[郭注-1]：今連枷，所以打穀者；\n[郭注-2]：亦杖名也；\n[音義-3]：音殊。\n[郭注-4]：今江東呼打爲度。\n[音義-5]：音量度也；\n[郭注-6]：蒲項反；\n[音義-7]：音拂；\n[郭注-8]：此皆打之別名也；\n[音義-9]：音悵怏，亦音車鞅。\n[音義-10]：音勃。', '卷五', '方言', '7', DEFAULT, DEFAULT, '3270;3574/1959;1929;3741;3696;3439;3870', '3696', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (191, '乂鉤;鉊;過;鎌;鍥', '戈', '刈鉤{乂鉤}，江淮陳楚之閒謂之鉊，1或謂之鐹{過}[戈]；2自關而西或謂之鉤，或謂之鎌，或謂之鍥。3', '[音義-1]：音昭；\n[音義-2]：音果；\n[音義-3]：音結。', '卷五', '方言', '3', DEFAULT, DEFAULT, '8360/1461;9356;1110;9354;9355', '8373', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (192, '薄;䒼;匊;蓬薄', '䒼', '薄，宋魏陳楚江淮之閒謂之䒼，或謂之麴{匊}[䒼]；1自關而西謂之薄，南楚謂之蓬薄。', '[郭注-1]：此直語楚聲轉耳。', '卷五', '方言', '3', DEFAULT, DEFAULT, '561;618;5803;671/561', '618', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (193, '橜;段', '', '橛{橜}，燕之東北朝鮮洌水之閒謂之椴{段}。12', '[郭注-1]：揭杙也。江東呼都。\n[音義-2]：音段。', '卷五', '方言', '', DEFAULT, DEFAULT, '3737;1972', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (194, '槌;植;羊;栚;帶;㭙;僉;巽;𦇏;𤪹', '㭙;鐉;𦇏', '槌，1宋魏陳楚江淮之閒謂之植[㭙]，2自關而西謂之槌，3齊謂之样{羊}。4其橫，關西曰㮳{栚}，56宋魏陳楚江淮之閒謂之㯂{帶}，7齊部謂之㭙。8所以縣㯂，關西謂之䌞{僉}，9東齊海岱之閒謂之繏{巽}[鐉]，10宋魏陳楚江淮之閒謂之𦇏，11或謂之𤪹[𦇏]。12', '[郭注-1]：縣蠶薄柱也。\n[音義-2]：音值；\n[音義-3]：度畏反；\n[音義-4]：音陽；\n[郭注-5]：亦名校，\n[音義-6]：音交。㮳，音朕。\n[音義-7]：音帶；\n[音義-8]：丁革反；\n[音義-9]：力冉反；\n[音義-10]：相卞反；\n[音義-11]：音𢸃甲；\n[音義-12]：音𣞲。', '卷五', '方言', '2;8;10', DEFAULT, DEFAULT, '3715;3648;2332;3717;4865;3716;3270;3024;8573;108', '3716;9435;8573', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (195, '簟;笙;籧䒼;㪿;籧篨;盇炎', '盇簟', '簟，宋魏之閒謂之笙，1或謂之籧䒼；自關而西或謂之簟，或謂之䇽{㪿}。2其粗者謂之籧篨，34自關而東或謂之篕掞{盇炎}[盇簟]。5', '[郭注-1]：今江東通言笙；\n[郭注-2]：今云䇽，篾篷也；\n[郭注-3]：江東呼籧篨爲䉬。\n[音義-4]：音廢。\n[音義-5]：音剡。', '卷五', '方言', '6', DEFAULT, DEFAULT, '2911;2987;2912/618;628;2912/2913;3180/6532', '3180/2911', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (196, '行唐;倚羊', '', '筕篖{行唐}，123自關而東周洛楚魏之閒謂之倚佯{倚羊}，4自關而西謂之筕篖，南楚之外謂之篖。', '[郭注-1]：似籧篨，直文而粗。江東呼笪，\n[音義-2]：音靼\n[郭注-3]：；\n[音義-4]：音羊。', '卷五', '方言', '', DEFAULT, DEFAULT, '1266/878;5045/2332', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (197, '牀;簀;笫;杠;樹;趙;𣓀;版;牒;牑', '桃;䡊', '牀，1齊魯之閒謂之簀，2陳楚之閒或謂之笫。3其杠，北燕朝鮮之閒謂之樹，自關而西秦晉之閒謂之杠，南楚之閒謂之趙[桃]，4東齊海岱之閒謂之𣘘{𣓀}。5其上版[䡊]，衞之北郊趙魏之閒謂之牒，6或曰牑。7', '[郭注-1]：牀版也。\n[音義-2]：音迮；\n[音義-3]：音滓，又音姊；\n[郭注-4]：趙，當作桃，聲之轉也。中國亦呼杠爲桃牀，皆通語也；\n[音義-5]：音詵；\n[音義-6]：簡牒；\n[音義-7]：履屬。', '卷五', '方言', '6;8', DEFAULT, DEFAULT, '3677;2908;2909;3674;3558;1030;3426;4399;4402;4403', '3424;9532', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (198, '俎;几;止;桯;椸;𧇽', '㮐;㮐', '俎，几也。西南蜀漢之郊曰杫{止}{㮐}。1榻前几，江沔之閒曰桯，23趙魏之閒謂之椸[㮐]。4几，其高者謂之虡{𧇽}。56', '[音義-1]：音賜；\n[郭注-2]：今江東呼爲承。\n[音義-3]：音刑；\n[音義-4]：音易；\n[郭注-5]：卽筍虡也。\n[音義-6]：音巨。', '卷五', '方言', '3;5', DEFAULT, DEFAULT, '9472;9467;1064;3675;3836;3116', '3701;3701', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (199, '籆;袁;格', '爰;檷', '籆，榬{袁}[爰]也。12兗豫河濟之閒謂之榬。絡謂之格[檷]。3', '[郭注-1]：所以絡絲者也。\n[音義-2]：音爰；\n[郭注-3]：所以轉籆絡車也。', '卷五', '方言', '2;3', DEFAULT, DEFAULT, '2902;5313;3601', '2516;3722', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (200, '繀車;歷鹿車;道軌', '歷録車', '繀車，1趙魏之閒謂之轣轆車{歷鹿車}[歷録車]，東齊海岱之閒謂之道軌。', '[音義-1]：蘇對反。', '卷五', '方言', '2', DEFAULT, DEFAULT, '8529/9512;1070/6261/9512;1205/9582', '1070/9276/9512', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (201, '鍵;𨷲', '', '戸鑰，自關而東陳楚之閒謂之鍵，1自關而西謂之鑰{𨷲}。', '[音義-1]：巨蹇反。', '卷五', '方言', '', DEFAULT, DEFAULT, '9312;7772', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (202, '簙;蔽;箘;箭裏;簙毒;夗專;尊旋;棊;枰;廣平;局;曲道;弈', '簙筑', '簙謂之蔽，或謂之箘。1秦晉之閒謂之簙；吳楚之閒或謂之蔽，或謂之箭裏，或謂之簙毒[簙筑]，2或謂之夗專，3或謂之𠥙璇{尊旋}，45或謂之棊。所以投簙謂之枰。6或謂之廣平。所以行棊謂之局，或謂之曲道。圍棊謂之弈，自關而東齊魯之閒皆謂之弈。', '[音義-1]：音囷；\n[郭注-2]：簙箸，一名箭，廣雅云。\n[音義-3]：夗，于辯反。專，音轉；\n[郭注-4]：或曰竹器，所以整頓簙者。\n[音義-5]：銓旋兩音；\n[音義-6]：音評論。', '卷五', '方言', '5', DEFAULT, DEFAULT, '3003;550;2867;2866/5260;3003/236;4328/1985;9879/4306;3754;3803;5938/3073;958;8442/1205;1771', '3003/2997', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (203, '𢕈;𤟌;欲', '', '聳{𢕈}、奬{𤟌}，欲也。1荊吳之閒曰聳，2晉趙曰奬。自關而西秦晉之閒相勸曰聳，或曰奬。中心不欲而由旁人之勸語亦曰聳。凡相被飾亦曰奬。', '[郭注-1]：皆强欲也。\n[音義-2]：山頂反；', '卷六', '方言', '', DEFAULT, DEFAULT, '7812;6324;5540', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (204, '𢕈;䏁;聾;𦘍;隓;䎳', '', '聳{𢕈}、䏁，聾也。半聾，梁益之閒謂之䏁；12秦晉之閒聽而不聰、聞而不達謂之䏁。生而聾，陳楚江淮之閒謂之聳。3荆揚之閒及山之東西雙聾者謂之聳。聾之甚者，秦晉之閒謂之𦘍。45吳楚之外郊凡無耳者亦謂之𦘍。其言𦘍者，若秦晉中土謂𡐦{隓}耳者䎳也。6', '[郭注-1]：言胎䏁煩憒也。\n[音義-2]：音宰；\n[郭注-3]：言無所聞，常聳耳也；\n[郭注-4]：言朏無所聞知也，外傳：「聾聩司火」，\n[音義-5]：音蒯聵。𦘍，五刮反，\n[音義-6]：䎳，五刮反。', '卷六', '方言', '', DEFAULT, DEFAULT, '7812;7813;7811;7817;9651;7816', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (205, '陂;㑾;衺', '', '陂、傜{㑾}，衺也。陳楚荆揚曰陂，1自山而西凡物細大不純者謂之傜。23', '[音義-1]：音偏頗；\n[郭注-2]：言俄傜也。\n[音義-3]：音逍遙；', '卷六', '方言', '', DEFAULT, DEFAULT, '9625;5144;5347', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (206, '邎迪;正', '', '由迪{邎迪}，正也。東齊青徐之閒相正謂之由迪。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '1100/1132;1087', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (207, '㥏;恧;慙;䀣', '', '㥏、恧，慙也。荆揚青徐之閒曰㥏，1若梁益秦晉之閒言心内慙矣；山之東西自愧曰恧；23趙巍之閒謂之䀣。4', '[音義-1]：音腆；\n[郭注-2]：小雅曰：「心愧爲恧」；\n[音義-3]：人力反，又女六反；\n[音義-4]：音密，亦祕。', '卷六', '方言', '', DEFAULT, DEFAULT, '6955;6958;6957;2124', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (208, '蹇;展;難;憚;燀', '蹇;憚', '謇{蹇}、展[蹇]，難也。齊晉曰謇。1山之東西凡難貌曰展。荆吳之人相難謂之展，若秦晉之言相憚矣。齊魯曰燀[憚]。23', '[音義-1]：音蹇；\n[郭注-2]：難而雄也。\n[音義-3]：昌羨反。', '卷六', '方言', '2;5', DEFAULT, DEFAULT, '1395;5405;2401;6940;6454', '1395;6940', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (209, '胥;邎;輔', '', '胥、由{邎}，輔也。1吳越曰胥，2燕之北鄙曰由。3', '[郭注-1]：皆謂輔持也。\n[郭注-2]：相也；\n[郭注-3]：正也。', '卷六', '方言', '', DEFAULT, DEFAULT, '2698;1100;9565', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (210, '蛩㤨;戰栗;恐', '', '蛩㤨，戰慄{戰栗}也。荆吳曰蛩㤨。蛩㤨，又恐也。1', '[音義-1]：鞏恭兩音。', '卷六', '方言', '', DEFAULT, DEFAULT, '8927/6946;8382/4391;6942', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (211, '錪;錘;重', '', '錪、錘，重也。東齊之閒曰錪，1宋魯曰錘。2', '[音義-1]：吐本反；\n[音義-2]：直睡反。', '卷六', '方言', '', DEFAULT, DEFAULT, '9301;9376;5242', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (212, '含;龕;受;盛', '堪', '鋡{含}、龕[堪]，受也。1齊楚曰鋡，2揚越曰龕。受，盛也，猶秦晉言容盛也。', '[郭注-1]：今云龕囊，依此名也。\n[音義-2]：音含；', '卷六', '方言', '2', DEFAULT, DEFAULT, '815;7689;2518;3140', '9052', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (213, '矔;眮', '', '矔、眮，轉目也。梁益之閒瞋目曰矔，轉目顧視亦曰矔，1吳楚曰眮。2', '[音義-1]：音慣習；\n[音義-2]：音侹侗。', '卷六', '方言', '', DEFAULT, DEFAULT, '2117;2123', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (214, '逴;騷;逴;蹇', '簫', '逴、騷[簫]、𡯴{逴}，蹇也。1吳楚偏蹇曰騷，2齊楚晉曰逴。34', '[郭注-1]：跛者行𧿒踔也；\n[音義-2]：先牢反；\n[郭注-3]：行略逴也。\n[音義-4]：勑略反。', '卷六', '方言', '2', DEFAULT, DEFAULT, '1201;6229;1201;1395', '2990', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (215, '𤺊;嗌;噎', '', '𤺊、嗌，噎也。12楚曰𤺊；3秦晉或曰嗌，4又曰噎。', '[郭注-1]：皆謂咽痛也。\n[音義-2]：音翳。\n[音義-3]：音斯；\n[音義-4]：惡介反；', '卷六', '方言', '', DEFAULT, DEFAULT, '4719;790;881', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (216, '怠;阤;壞', '殆', '怠[殆]、阤，壞也。12', '[郭注-1]：謂壞落也。\n[音義-2]：阤，音蟲豸；', '卷六', '方言', '1', DEFAULT, DEFAULT, '6837;9650;9118', '2548', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (217, '涅;墊;下', '墊', '㘿{涅}[墊]、墊，下也。凡柱而下曰㘿，1屋而下曰墊。2', '[郭注-1]：音涅；\n[音義-2]：丁念反。', '卷六', '方言', '1', DEFAULT, DEFAULT, '7232;9085;9', '9085', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (218, '勿;邈;離;越;遠', '', '伆{勿}、邈，離也。1楚謂之越，或謂之遠；吳越曰伆。2', '[郭注-1]：謂乖離也。\n[音義-2]：音刎；', '卷六', '方言', '', DEFAULT, DEFAULT, '6071;1214;2293;990;1198', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (219, '顚;頂;上', '', '顚、頂，上也。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '5606;5607;6', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (220, '誣;㛪与;阿与', '', '誣、䛳，与也。吳越曰誣；荆齊曰䛳与{㛪与}，1猶秦晉言阿与。2', '[音義-1]：於劍反；\n[郭注-2]：相阿与者，所以致誣䛳。', '卷六', '方言', '', DEFAULT, DEFAULT, '1610;8333/9466;9624/9466', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (221, '掩;𡩡;取;狙', '揜;抯', '掩[揜]、𡩡，取也。自關而東曰掩，自關而西曰𡩡，或曰狙[抯]。1', '[郭注-1]：狙伺也。', '卷六', '方言', '1;4', DEFAULT, DEFAULT, '8033;4615;1926;6374', '7910;7981', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (222, '䁙;䀩;視', '', '䁙、䀩，視也。東齊曰䁙，吳揚曰䀩。12凡以目相戲曰䁙。3', '[郭注-1]：今中國亦云目䀩也。\n[音義-2]：音略；\n[音義-3]：烏拔反；', '卷六', '方言', '', DEFAULT, DEFAULT, '2169;2179;5475', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (223, '遙;廣;遠', '繇', '遙[繇]、廣，遠也。梁楚曰遙。', '', '卷六', '方言', '1', DEFAULT, DEFAULT, '1222;5938;1198', '8514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (224, '汩;遙;疾行', '𡿯;搖', '汩[𡿯]、遙[搖]，疾行也。南楚之外曰汩，12或曰遙。', '[郭注-1]：汩汩，急貌也。\n[音義-2]：汩，于筆反；', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '7448;1222;4694/1266', '7485;7949', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (225, '蹇;妯;𢺕', '怞', '蹇、妯[怞]，𢺕也。1人不靜曰妯，秦晉曰蹇，齊宋曰妯。2', '[郭注-1]：謂躁夒也；\n[音義-2]：音迪。', '卷六', '方言', '2', DEFAULT, DEFAULT, '1395;8287;7933', '6784', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (226, '絓;挈;煢;介;特;雙;乘', '佳/閨;赹', '絓[佳/閨]、挈、𠎽{煢}[赹]、介，特也。楚曰𠎽，1晉曰絓，2秦曰挈，3物無耦曰特，獸無耦曰介。4飛鳥曰雙，鴈曰乘。', '[郭注-1]：𠎽，古煢字；\n[郭注-2]：音乖；\n[郭注-3]：口八反；\n[郭注-4]：傳曰：「逢澤有介麋」。', '卷六', '方言', '1;3', DEFAULT, DEFAULT, '8527;7857;7700;714;731;2364;3410', '4977/7735;1015', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (227, '台;旣;失', '駘', '台[駘]、旣，失也。宋魯之閒曰台。', '', '卷六', '方言', '1', DEFAULT, DEFAULT, '868;3197;7976', '6232', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (228, '旣;隱;據;定', '𤔌', '旣、隱[𤔌]、據，定也。', '', '卷六', '方言', '2', DEFAULT, DEFAULT, '3197;9667;7865;4574', '2521', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (229, '稟;浚;敬', '凜;夋', '稟[凜]、浚[夋]，敬也。秦晉之閒曰稟，齊曰浚。吳楚之閒自敬曰稟。', '', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '3341;7372;5818', '3362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (230, '悛;懌;改', '譯', '悛、懌[譯]，改也。自山而東或曰悛，1或曰懌。23', '[音義-1]：音銓；\n[郭注-2]：論語曰：「悅而不懌」。\n[音義-3]：音奕；', '卷六', '方言', '2', DEFAULT, DEFAULT, '6792;6978;2019', '1723', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (231, '坻;坥;場', '', '坻、坥，場也。梁宋之閒蚍蜉䵓鼠之場謂之坻。12螾場謂之坥。34', '[郭注-1]：䵓鼠，蚡鼠也；\n[音義-2]：音水汦；\n[郭注-3]：螾，蛐蟮也，其糞名坥。\n[音義-4]：螾音引。場，音傷；音癰疽；', '卷六', '方言', '', DEFAULT, DEFAULT, '9086;9134;9145', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (232, '徥;用;行', '', '徥、用，行也。朝鮮洌水之閒或曰徥。12', '[郭注-1]：徥徥，行貌。\n[音義-2]：度揩反。', '卷六', '方言', '', DEFAULT, DEFAULT, '1237;2082;1266', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (233, '鋪頒;索;斗藪', '布班;𡩡', '鋪頒[布班]，索[𡩡]也。東齊曰鋪頒，1猶秦晉言抖藪{斗藪}也。2', '[音義-1]：鋪音敷。\n[郭注-2]：謂斗藪舉索物也。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '9434/5629;3871;9489/563', '4910/222;4615', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (234, '參;蠡;分;離', '', '參、蠡，分也。1齊曰參，楚曰蠡，2秦晉曰離。', '[郭注-1]：謂分割也。\n[音義-2]：音麗；', '卷六', '方言', '', DEFAULT, DEFAULT, '4315;8965;708;2293', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (235, '𤺊;披;散;釁;㠲', '柀', '𤺊、披[柀]，散也。東齊聲散曰𤺊，器破曰披，秦晉聲變曰𤺊，器破而不殊其音亦謂之𤺊。器破而未離謂之璺{釁}，1南楚之閒謂之𢻹{㠲}。2', '[音義-1]：音問；\n[音義-2]：妨美反,一音圯塞。', '卷六', '方言', '2', DEFAULT, DEFAULT, '4719;7945;2720;1790;4882', '3464', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (236, '緍;緜;施', '', '緍、緜，施也。秦曰緍，趙曰緜，吳越之閒脫衣相被謂之緍緜。12', '[郭注-1]：相覆及之名也。\n[音義-2]：緍音旻；', '卷六', '方言', '', DEFAULT, DEFAULT, '8723;8513;4301', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (237, '涌;畐;滿', '', '悀{涌}、愊{畐}，滿也。凡以器盛而滿謂之悀，12腹滿曰偪。34', '[郭注-1]：言涌出也；\n[音義-2]：音踊；\n[郭注-3]：言勅偪也。\n[音義-4]：妨逼反；', '卷六', '方言', '', DEFAULT, DEFAULT, '7190;3338;7217', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (238, '徯醯;冉鎌;危', '', '徯醯、冉鐮{冉鎌}，危也。東齊㨳物而危謂之徯醯，1僞物謂之冉鐮。2', '[音義-1]：㨳，居枝反。醯音醢酢；\n[音義-2]：音髯；', '卷六', '方言', '', DEFAULT, DEFAULT, '1244/3152;6073/9354;6007', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (239, '紕;繹;督;雉;理', '比;夷', '紕[比]、繹、督、雉[夷]，理也。秦晉之閒曰纰，1凡物曰督之，2絲曰繹之。34', '[音義-1]：音毗；\n[郭注-2]：言正理也；\n[郭注-3]：言解繹也。\n[音義-4]：音亦；', '卷六', '方言', '1;4', DEFAULT, DEFAULT, '8759;8518;2172;2288;147', '5227;6613', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (240, '矤;呂;長', '', '矤、呂，長也。東齊曰矤，1宋魯曰呂。', '[郭注-1]：古矧字。', '卷六', '方言', '', DEFAULT, DEFAULT, '3314;4630;6067', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (241, '屈;呂;力', '𠢤', '䠇{屈}[𠢤]、膐{呂}，力也。東齊曰䠇，1宋魯曰膐。膐，田力也。2', '[郭注-1]：律䠇，多力貌；\n[郭注-2]：謂耕墾也。', '卷六', '方言', '1', DEFAULT, DEFAULT, '5428;4630;9210', '9224', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (242, '瘱;帶;審', '諟', '瘱、𧫚{帶}[諟]，審也。齊楚曰瘱，1秦晉曰𧫚。2', '[音義-1]：音瘞埋，又翳；\n[音義-2]：音瓜蔕。', '卷六', '方言', '2', DEFAULT, DEFAULT, '6736;4865;722', '1516', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (243, '壹帶;諟', '瘱諟', '𧬇𧫚{壹帶}[瘱諟]，諟也。12吳越曰𧬇𧫚。3', '[郭注-1]：諟亦審，互見其義耳。\n[音義-2]：音帝。\n[音義-3]：𧬇音翳；𧫚亦音蔕；', '卷六', '方言', '1', DEFAULT, DEFAULT, '6642/4865;1516', '6736/1516', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (244, '音;揜;錯;摩;藏', '揜;臧', '揞{音}[揜]、揜、錯、摩，藏[臧]也。荆楚曰揞，1吳揚曰揜，周秦曰錯，2陳之東鄙曰摩。', '[音義-1]：烏感反；\n[音義-2]：音酢。', '卷六', '方言', '1;5', DEFAULT, DEFAULT, '1739;7910;9327;8009;695', '7910;1955', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (245, '去莫;去', '', '袪摸{去莫}，去也。齊趙之總語也，袪摸猶言持去也。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '3164/701;3164', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (246, '舒勃;展', '', '舒勃，展也。東齊之閒凡展物謂之舒勃。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '2510/9244;5405', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (247, '摳揄;旋', '', '摳揄，旋也。秦晉凡物樹稼早成熟謂之旋，燕齊之閒謂之摳揄。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '7835/7968;4306', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (248, '絙;筳;竟', '', '絙、筳，竟也。秦晉或曰絙，1或曰竟，楚曰筳。2', '[音義-1]：岡鄧反；\n[音義-2]：湯丁反。', '卷六', '方言', '', DEFAULT, DEFAULT, '8654;2903;1744', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (249, '閻;業;續', '接', '𢸴{閻}、𠟪{業}[接]，續也。秦晉續折謂之𢸴，1繩索謂之𠟪。2', '[音義-1]：音剡；\n[音義-2]：音妾。', '卷六', '方言', '2', DEFAULT, DEFAULT, '7740;1750;8548', '7917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (250, '擘;紉', '襞', '擘[襞]，1楚謂之紉。23', '[音義-1]：音檗；\n[郭注-2]：今亦以綫貫針爲紉。\n[音義-3]：音刃。', '卷六', '方言', '1', DEFAULT, DEFAULT, '8019;8691', '5336', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (251, '閻苫;開;闓', '', '閻苫，開也。東齊開戸謂之閻苫，楚謂之闓。1', '[郭注-1]：亦開字也。', '卷六', '方言', '', DEFAULT, DEFAULT, '7740/587;7754;7755', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (252, '杼;柚;作', '軸', '杼、柚[軸]，作也。東齊土作謂之杼，水作謂之柚。', '', '卷六', '方言', '2', DEFAULT, DEFAULT, '3725;3414;5073', '9543', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (253, '厲;卬;爲', '', '厲、卬，爲也。1甌越曰卬，吳曰厲。', '[郭注-1]：爾雅曰：「俶、厲，作。」作，亦爲也。', '卷六', '方言', '', DEFAULT, DEFAULT, '5983;5221;1882', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (254, '戲;憚;怒', '', '戲、憚，怒也。齊曰戲，楚曰憚。', '', '卷六', '方言', '', DEFAULT, DEFAULT, '8383;6940;6877', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (255, '爰;爰;恚', '咺;咺', '爰[咺]、喛{爰}[咺]，恚也。1楚曰爰，秦晉曰喛，皆不欲譍而强畣[荅]之意也。', '[郭注-1]：謂悲恚也。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '2516;2516;6875', '796;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (256, '叜;艾;長;老;公;翁;父;父老;皇;母姼;父姼', '母媞;父媞', '𠋢{叜}、艾，長，老也。東齊魯衞之閒凡尊老謂之𠋢，或謂之艾；1周晉秦隴謂之公，或謂之翁；南楚謂之父，或謂之父老。南楚𤄗洭之閒，23母謂之媓{皇}，謂婦妣曰母姼[母媞]，稱婦考曰父姼[父媞]。45', '[郭注-1]：禮記曰：「五十曰艾」；\n[郭注-2]：洭水在桂陽。\n[音義-3]：㬥匡兩音。\n[郭注-4]：古者通以考妣爲生存之稱。\n[音義-5]：音多。', '卷六', '方言', '10;11', DEFAULT, DEFAULT, '1909;389;6067;5375;716;2248;1908;1908/5375;80;8139/8157;1908/8157', '8139/8228;1908/8228', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (257, '巍;嶢;崝;險;高', '', '巍、嶢、崝、嶮{險}，高也。1', '[郭注-1]：嶕嶢、崝嵤，皆高峻之貌也。', '卷六', '方言', '', DEFAULT, DEFAULT, '5847;5897;5891;9629;3318', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (258, '猒;塞;安', '懕;㥶', '猒[懕]、塞[㥶]，安也。1', '[郭注-1]：物足則定。', '卷六', '方言', '1;2', DEFAULT, DEFAULT, '3040;9093;4576', '6796;6758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (259, '夌;𢜮;憐', '憐', '㥄{夌}[憐]、𢜮，憐也。12', '[郭注-1]：㥄音夌；\n[音義-2]：𢜮，亡主反。', '卷六', '方言', '1', DEFAULT, DEFAULT, '3364;6785;6960', '6960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (260, '掩;翳;𥴨', '', '掩、翳，薆{𥴨}也。12', '[郭注-1]：謂蔽薆也，詩曰：「薆而不見」。\n[音義-2]：音愛。', '卷六', '方言', '', DEFAULT, DEFAULT, '8033;2273;3005', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (261, '佚愓;緩', '', '佚愓，緩也。1', '[音義-1]：跌唐兩音。', '卷六', '方言', '', DEFAULT, DEFAULT, '5142/6847;8779', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (262, '諄憎;疾;可惡', '敦憎', '諄憎[敦憎]，所疾也。宋魯凡相惡謂之諄憎，1若秦晉言可惡矣。', '[音義-1]：諄，之潤反。', '卷七', '方言', '1', DEFAULT, DEFAULT, '1504/6881;4694;3058/6880', '2039/6881', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (263, '杜;蹻;歰', '', '杜、蹻，歰也。趙曰杜，1山之東西或曰蹻。23', '[郭注-1]：今俗語通言歰如杜，杜棃子歰，因名之；\n[郭注-2]：卻蹻，燥歰貌。\n[音義-3]：蹻，音笑噱。', '卷七', '方言', '', DEFAULT, DEFAULT, '3431;1343;1077', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (264, '佻;抗;縣', '䄪/到', '𠄏[䄪/到]、抗，縣也。趙魏之閒曰𠄏，自山之東西曰抗；燕趙之郊縣物于臺之上謂之𠄏。12', '[郭注-1]：了𠄏，縣物貌。\n[音義-2]：丁小反。', '卷七', '方言', '1', DEFAULT, DEFAULT, '5127;8066;5705', '4448/7707', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (265, '發;稅;舍車', '挽', '發、稅[挽]，舍車也。1東齊海岱之閒謂之發，2宋趙陳魏之閒謂之稅。3', '[音義-1]：舍，宜音寫；\n[郭注-2]：今通言發寫也；\n[郭注-3]：稅猶脫也。', '卷七', '方言', '2', DEFAULT, DEFAULT, '8502;4486;3273/9512', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (266, '肖;類;法;哨', '嗾', '肖、類，法也。齊曰類，西楚梁益之閒曰肖。秦晉之西鄙自冀隴而西使犬曰哨[嗾]，12西南梁益之閒凡言相類者亦謂之肖。3', '[郭注-1]：冀縣，今在天水；\n[音義-2]：音騷；\n[郭注-3]：肖者，似也。', '卷七', '方言', '4', DEFAULT, DEFAULT, '2643;6369;6260;929', '943', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (267, '憎;讓;憚', '', '憎、懹{讓}，憚也。1陳曰懹。', '[郭注-1]：相畏憚也。', '卷七', '方言', '', DEFAULT, DEFAULT, '6881;1693;6940', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (268, '譙;讙;讓', '', '譙、讙，讓也。齊楚宋衞荆陳之閒曰譙，1自關而西秦晉之閒凡言相責讓曰譙讓，北燕曰讙。2', '[郭注-1]：字或作誚；\n[音義-2]：火袁反。', '卷七', '方言', '', DEFAULT, DEFAULT, '1694;1658;1693', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (269, '僉;胥;皆', '', '僉、胥，皆也。自山而東五國之郊曰僉，1東齊曰胥。', '[郭注-1]：六國惟秦在山西。', '卷七', '方言', '', DEFAULT, DEFAULT, '3270;2698;2226', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (270, '侔莫;强', '勖;彊', '侔莫[勖慔]，强[彊]也。北燕之外郊凡勞而相勉若言努力者謂之侔莫。', '', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '5037/701;8824', '9222;8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (271, '巩松;罵', '巩庸', '𠌖倯{巩松}[巩庸]，罵也。1燕之北郊曰𠌖倯。2', '[郭注-1]：羸小可憎之名也。\n[音義-2]：𠌖，音邛竹。', '卷七', '方言', '1', DEFAULT, DEFAULT, '1888/3543;4845', '1888/2084', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (272, '展;惇;信', '眞', '展[眞]、惇，信也。東齊海岱之閒曰展，燕曰惇。1', '[郭注-1]：惇亦誠信貌。', '卷七', '方言', '1', DEFAULT, DEFAULT, '5405;6727;1524', '5213', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (273, '斯;播;離', '', '斯、𨤏{播}，離也。齊陳曰斯，燕之外郊朝鮮洌水之閒曰𨤏。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '9483;8036;2293', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (274, '蝎;噬;逮', '逮;逮', '蝎[逮]、噬[逮]，逮也。東齊曰蝎，1北燕曰噬。2逮，通語也。', '[音義-1]：音曷；\n[音義-2]：音卜筮。', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '8823;811;1147', '1147;1147', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (275, '皮傅;彈憸;强', '彊', '皮傅、彈憸，强[彊]也。1秦晉言非其事謂之皮傅，東齊陳宋江淮之閒曰彈憸。2', '[郭注-1]：謂强語也。\n[音義-2]：音僉；', '卷七', '方言', '3', DEFAULT, DEFAULT, '1988/5042;8501/6805;8824', '8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (276, '膊;曬;晞;㬥', '', '膊、曬、晞，㬥也。東齊及秦之西鄙言相㬥僇爲膊。12燕之外郊朝鮮洌水之閒凡㬥肉發人之私、披牛羊之五藏謂之膊。㬥五穀之類，秦晉之閒謂之曬，3東齊北燕海岱之郊謂之晞。', '[郭注-1]：㬥僇，謂相㬥𣩊惡事。\n[音義-2]：音膊脯；\n[音義-3]：霜智反；', '卷七', '方言', '', DEFAULT, DEFAULT, '2688;4247;4249;4246', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (277, '熬;取;煎;備;鞏', '𩱦;𤐸;烘', '熬、㷅{取}[𩱦]、煎、㷶{備}[𤐸]、鞏[烘]，火乾也。凡以火而乾五穀之類，自山而東、齊楚以往謂之熬，關西隴冀以往謂之㷶，1秦晉之閒或謂之㷅；23凡有汁而乾謂之煎，東齊謂之鞏。4', '[音義-1]：皮力反；\n[郭注-2]：卽𩱈字也。\n[音義-3]：創眇反；\n[音義-4]：音拱手。', '卷七', '方言', '2;4;5', DEFAULT, DEFAULT, '6460;1926;6459;5028;1801', '1876;6464;6456', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (278, '胹;飪;亨;爛;糦;酋;酷;熟', '秋;焅', '胹、飪、亨、爛、糦、酋[秋]、酷[焅]，熟也。自關而西秦晉之郊曰胹，1徐揚之閒曰飪，2嵩嶽以南陳潁之閒曰亨。自河以北趙魏之閒火熟曰爛，氣熟曰糦，3久熟曰酋，4穀熟曰酷。熟，其通語也。', '[音義-1]：音而；\n[音義-2]：音荏；\n[音義-3]：音熾；\n[音義-4]：音囚。', '卷七', '方言', '6;7', DEFAULT, DEFAULT, '2699;3207;3331;6468;3217;9878;9827;1886', '4491;6519', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (279, '嫢盈;怒', '', '嫢盈，怒也。燕之外郊朝鮮洌水之閒，凡言呵叱者謂之嫢盈。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '8227/3155;6877', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (280, '長敄;豈企;立', '', '䠆𨂣{長敄}、隑企{豈企}，立也。東齊海岱北燕之郊跪謂之䠆𨂣，12委痿謂之隑企。34', '[郭注-1]：今東郡人亦呼長跽爲䠆𨂣；\n[音義-2]：𨂣，音務；\n[郭注-3]：腳躄不能行也。\n[音義-4]：企，欺豉反；', '卷七', '方言', '', DEFAULT, DEFAULT, '6067/2001;3092/4952;6677', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (281, '瀧涿;霑漬', '', '瀧涿謂之霑漬。12', '[郭注-1]：瀧涿猶瀨滯也。\n[音義-2]：音籠。', '卷七', '方言', '', DEFAULT, DEFAULT, '7312/7311;7553/7325', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (282, '晞;鑠;摩;呂', '鑢', '希{晞}、鑠，摩也。燕齊摩鋁{呂}[鑢]謂之希。1', '[音義-1]：鋁音慮。', '卷七', '方言', '4', DEFAULT, DEFAULT, '4249;9279;8009;4630', '9370', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (283, '平均;賦', '', '平均，賦也。燕之北鄙、東齊北郊凡相賦斂謂之平均。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '3073/9028;3981', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (284, '羅;離', '', '羅謂之離，離謂之羅。1', '[郭注-1]：皆行列物也。', '卷七', '方言', '', DEFAULT, DEFAULT, '4831;2293', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (285, '釗;超;遠', '迢;迢', '釗[迢]、超[迢]，遠也。燕之北郊曰釗，東齊曰超。', '', '卷七', '方言', '1;2', DEFAULT, DEFAULT, '2794;983;1198', '1220;1220', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (286, '漢漫;辰眩;懣', '熯懣', '漢漫[熯懣]、䀼眩{辰眩}，懣也。朝鮮洌水之閒煩懣謂之漢漫，顚眴謂之䀼眩。1', '[音義-1]：䀼，音瞋恚，眩，音懸。', '卷七', '方言', '1', DEFAULT, DEFAULT, '7007/1911;9793/2099;6890', '6432/6890', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (287, '憐職;愛', '㤅', '憐職，愛[㤅]也，言相愛憐者吳越之閒謂之憐職。', '', '卷七', '方言', '2', DEFAULT, DEFAULT, '6960/7805;3367', '6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (288, '茹;食', '', '茹，食也。吳越之閒凡貪飲食者謂之茹。12', '[郭注-1]：今俗呼能粗食者爲茹。\n[音義-2]：音勝如。', '卷七', '方言', '', DEFAULT, DEFAULT, '614;3204', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (289, '竘;貌;治', '', '竘、貌，治也。1吳越飾貌爲竘，2或謂之巧。3', '[郭注-1]：謂治作也。\n[音義-2]：恪垢反。\n[郭注-3]：語楚聲轉耳。', '卷七', '方言', '', DEFAULT, DEFAULT, '6686;5466;7087', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (290, '煦;叚;熱;乾', '煦', '煦、煆{叚}[煦]，熱也，乾也。1吳越曰煦煆。2', '[郭注-1]：熱則乾燥。\n[音義-2]：煦，音州吁；煆，呼夏反；', '卷七', '方言', '2', DEFAULT, DEFAULT, '6431;1928;6510;9741', '6431', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (291, '嬴;膂;賀;幐;儋;負佗', '何', '攍{嬴}、膂、賀[何]、幐，儋也。12齊楚陳宋之閒曰攍；34燕之外郊、越之垂甌、吳之外鄙謂之膂；5南楚或謂之攍；自關而西、隴冀以往謂之賀，6凡以驢馬馲駝載物者謂之負佗，7亦謂之賀。', '[郭注-1]：今江東呼儋兩頭有物爲幐。\n[音義-2]：音鄧；\n[郭注-3]：莊子曰：攍糧而赴之；\n[音義-4]：音盈；\n[郭注-5]：儋者用膂力，因名云；\n[郭注-6]：今江東語亦然；\n[音義-7]：音大。', '卷七', '方言', '3', DEFAULT, DEFAULT, '8114;4630;3945;4903;5024;3963/5022', '5023', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (292, '樹植;立', '', '樹植，立也。燕之外郊朝鮮洌水之閒凡言置立者謂之樹植。', '', '卷七', '方言', '', DEFAULT, DEFAULT, '3558/3648;6677', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (293, '過度;涉濟', '', '過度謂之涉濟。1', '[郭注-1]：猶今云濟度。', '卷七', '方言', '', DEFAULT, DEFAULT, '1110/1929;7475/7093', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (294, '福祿;祓戩', '', '福祿謂之祓戩。1', '[音義-1]：廢箭兩音。', '卷七', '方言', '', DEFAULT, DEFAULT, '20/15;49/8393', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (295, '祭;眙;逗', '刹', '傺{祭}[刹]、眙，逗也。南楚謂之傺，1西秦謂之眙。23逗，其通語也。4', '[音義-1]：音際；\n[郭注-2]：西秦，酒泉、敦煌、張掖是也。眙，謂住視也。\n[音義-3]：敕吏反；\n[郭注-4]：逗，卽今住字也；', '卷七', '方言', '1', DEFAULT, DEFAULT, '30;2177;1153', '2810', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (296, '虎;李父;李耳;於䖘;伯都', '', '虎，陳魏宋楚之閒或謂之李父；江淮南楚之閒謂之李耳，1或謂之於䖘；2345自關東西或謂之伯都。6', '[郭注-1]：虎食物值耳卽止，以觸其諱故；\n[郭注-2]：今江南山夷呼虎爲䖘，\n[音義-3]：音狗竇\n[郭注-4]：；\n[音義-5]：於，音烏。\n[郭注-6]：俗曰伯都事神虎說。', '卷八', '方言', '', DEFAULT, DEFAULT, '3117;3423/1908;3423/7792;2487/3133;4965/4005', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (297, '貔;來;否;貍', '', '貔，12陳楚江淮之閒謂之𧳟{來}，3北燕朝鮮之閒謂之𧳏{否}，45關西謂之狸{貍}。6', '[郭注-1]：狸别名也，未聞語所出。\n[音義-2]：音毗。\n[音義-3]：音來；\n[郭注-4]：今江南呼爲𧳏狸。\n[音義-5]：音丕；\n[郭注-6]：此通名耳。', '卷八', '方言', '', DEFAULT, DEFAULT, '6114;3346;934;6124', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (298, '貛;貒', '', '貛，12關西謂之貒。3', '[郭注-1]：貛，豚也。\n[音義-2]：音歡；\n[音義-3]：音波湍。', '卷八', '方言', '', DEFAULT, DEFAULT, '6126;6125', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (299, '雞;鸊氏;割雞;從;抱;鷇;涅', '鸊鷉', '雞，陳楚宋魏之閒謂之鸊䲬{鸊氏}[鸊鷉]；1桂林之中謂之割雞，或曰𩀨{從}。2北燕朝鮮洌水之閒謂伏雞曰抱。34爵子及雞雛皆謂之鷇。56其卵伏而未孚始化謂之涅。', '[音義-1]：僻祇兩音；\n[音義-2]：音從；\n[郭注-3]：江東呼蓲，\n[音義-4]：央富反；抱，房奧反。\n[郭注-5]：關西曰鷇，\n[音義-6]：音狗竇。鷇，恪遘反。', '卷八', '方言', '2', DEFAULT, DEFAULT, '2290;2429/8367;2777/2290;5225;7909;2479;7232', '2429/2430', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (300, '豬;豭;彘;豕;豨;豚;豯;豬子;竲', '', '豬，北燕朝鮮之閒謂之豭；1關東西或謂之彘，或謂之豕；南楚謂之豨。2其子或謂之豚，或謂之豯。吳揚之閒謂之豬子。其檻及蓐曰橧{竲}。34', '[郭注-1]：猶云豭斗也；\n[音義-2]：音奚；\n[郭注-3]：爾雅曰：「所寢，橧。」\n[音義-4]：音繒。', '卷八', '方言', '', DEFAULT, DEFAULT, '6077;6084;6104;6076;6093;6108;6079;6077/9764;6695', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (301, '布穀;結誥;擊穀', '秸𪈓', '布穀，1自關東西梁楚之閒謂之結誥[秸𪈓]，周魏之閒謂之擊穀，自關而西或謂之布穀。', '[郭注-1]：今江東呼爲穫穀。', '卷八', '方言', '2', DEFAULT, DEFAULT, '4910/4483;8575/1529;8064/4483', '4472/2380', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (302, '旱鴠;定甲;獨舂;城旦;倒縣;渴鴠', '', '䳚鴠{旱鴠}，12周魏齊宋楚之閒謂之定甲，或謂之獨舂；3自關而東謂之城旦，4或謂之倒縣，5或謂之䳚鴠；自關而西秦隴之内謂之鶡鴠{渴鴠}。', '[郭注-1]：鳥似雞，五色，冬無毛，赤倮，晝夜鳴。\n[音義-2]：侃旦兩音；\n[郭注-3]：好自低仰；\n[郭注-4]：言其辛苦有似于罪謫者；\n[郭注-5]：好自縣于樹也。', '卷八', '方言', '', DEFAULT, DEFAULT, '4228/2382;4574/9739;6355/4522;9081/4283;5200/5705;7343/2382', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (303, '鳩;良皋;鸊皋;匊鳩;鳻鳩;役鳩;癸鳩;孚鳩;鶻鳩;鷦', '頒鳩', '鳩，自關而東周鄭之郊、韓魏之都謂之𪁜鷎{良皋}，1其𪁛鳩謂之鸊鷎{鸊皋}；自關而西秦漢之閒謂之鵴鳩{匊鳩}，2其大者謂之鳻鳩[頒鳩]，3其小者謂之𪁛鳩{役鳩}、4或謂之䳫鳩{癸鳩}、5或謂之䳕鳩{孚鳩}、6或謂之鶻鳩；梁宋之閒謂之鷦。', '[音義-1]：郎皋。\n[音義-2]：音菊花；\n[音義-3]：鳻音斑；\n[郭注-4]：今荆鳩也；\n[音義-5]：䳫音葵；\n[音義-6]：䳕音浮。', '卷八', '方言', '5', DEFAULT, DEFAULT, '2375;3339/6660;2429/6660;5803/2375;2482/2375;1977/2375;9763/2375;1881/2375;2378/2375;2398', '5629/2375', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (304, '尸鳩;畐丕;戴任;戴南;鶭澤;戴鳻;戴勝;任;服畐;复畐', '戴任;𪀐鵖', '䲩鳩{尸鳩}，12燕之東北朝鮮洌水之閒謂之鶝䲹{畐丕}。3自關而東謂之戴鵀{戴任}。東齊海岱之閒謂之戴南[戴任]，南猶鵀也；4或謂之鶭鸅{鶭澤}；5或謂之戴鳻；或謂之戴勝。6東齊吳揚之閒謂之鵀{任}。自關而西謂之服鶝{服畐}，或謂之𪃃鶝{复畐}[𪀐鵖]。燕之東北朝鮮洌水之閒謂之𪂉。7', '[郭注-1]：按爾雅卽布穀，非戴勝也，或云鸇，皆失之也；\n[音義-2]：音尸；\n[音義-3]：福丕兩音；\n[郭注-4]：此亦語楚聲轉也；\n[郭注-5]：按爾雅說戴鵀，下鶭鸅自別一鳥名，方言似依此義，又失也；\n[郭注-6]：勝所以纏絍；\n[音義-7]：音域。', '卷八', '方言', '4;10', DEFAULT, DEFAULT, '5400/2375;3338/4;1779/5087;1779/3873;2391/7220;1779/2482;1779/9227;5087;5447/3338;3363/3338', '1779/5087;2434/2435', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (305, '蝙蝠;服翼;飛鼠;老鼠;㒨鼠;戠墨', '', '蝙蝠，1自關而東謂之服翼，或謂之飛鼠，或謂之老鼠，或謂之㒨鼠；自關而西秦隴之閒謂之蝙蝠；北燕謂之蟙䘃{戠墨}。2', '[音義-1]：邊福兩音；\n[音義-2]：職墨兩音。', '卷八', '方言', '', DEFAULT, DEFAULT, '8929/8930;5447/7693;7692/6391;5375/6391;5186/6391;8397/9076', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (306, '鴈;鴚䳘;䳘;倉鴚', '', '鴈，自關而東謂之鴚䳘；南楚之外謂之䳘，或謂之倉鴚。12', '[郭注-1]：今江東通呼爲鴚。\n[音義-2]：音加；', '卷八', '方言', '', DEFAULT, DEFAULT, '2422;2420/2421;2421;3277/2420', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (307, '桑飛;工爵;過𦟀;女匠;寧鴂;懱爵', '', '桑飛，1自關而東謂之工爵，或謂之過鸁{過𦟀}，2或謂之女𪀘{女匠}。3自關而東謂之鸋鴂{寧鴂}；4自關而西謂之桑飛，或謂之懱爵。56', '[郭注-1]：卽鷦鷯也，又名鷦鸎；\n[音義-2]：音螺；\n[郭注-3]：今亦名爲巧婦，江東呼布母；\n[郭注-4]：按爾雅云：「鸋鴃，鴟鴞。鴟屬。」非此小雀明矣。\n[郭注-5]：言懱𢧵也。\n[音義-6]：甯玦兩音；', '卷八', '方言', '', DEFAULT, DEFAULT, '3858/7692;3029/3201;1110/2729;8109/8424;3056/2389;6826/3201', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (308, '麗黃;倉庚;黃鳥;楚雀', '離黃', '𪈳黃{麗黃}[離黃]，自關而東謂之鶬鶊{倉庚}；1自關而西謂之𪈳黃，2或謂之黃鳥，或謂之楚雀。', '[郭注-1]：又名商庚；\n[郭注-2]：其色黧黑而黃，因名之。', '卷八', '方言', '1', DEFAULT, DEFAULT, '6286/9201;3277/9753;9201/2368;3849/2285', '2293/9201', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (309, '守宮;盧廛;蜥易;易易;蛇醫;榮蚖;虒侯;祝延;蛤解', '', '守宮，秦晉西夏謂之守宮，或謂之蠦𧔊{盧廛}，1或謂之蜥易；23其在澤中者謂之易蜴{易易}。南楚謂之蛇醫，或謂之蠑螈{榮蚖}。4東齊海岱謂之螔䗔{虒侯}。56北燕謂之祝蜒{祝延}。7桂林之中守宮大者而能鳴謂之蛤解。8910', '[音義-1]：盧纏兩音；\n[郭注-2]：南陽人又呼蝘蜓；\n[音義-3]：蜴，音析；\n[音義-4]：榮元兩音；\n[郭注-5]：似蜤易大而有鱗，今所在通言蛇醫耳。\n[音義-6]：斯侯兩音；\n[音義-7]：音延；\n[郭注-8]：似蛇醫而短，身有鱗采，江東人呼爲蛤蚧，\n[音義-9]：音頜頷\n[郭注-10]：，汝潁人直名爲蛤，解音懈，誤聲也。', '卷八', '方言', '', DEFAULT, DEFAULT, '4594/4628;3143/5943;8801/6132;6132/6132;8993/9853;3536/8804;3130/3311;47/1265;8900/2850', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (310, '鼠;隹', '', '宛野謂鼠爲𪕪{隹}。12', '[郭注-1]：宛、新野，今皆在南陽。\n[音義-2]：音錐。', '卷八', '方言', '', DEFAULT, DEFAULT, '6391;2278', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (311, '雞雛;秋子', '', '雞雛，徐魯之閒謂之䨂子{秋子}。12', '[郭注-1]：徐，今下邳僮縣東南大徐城是也。\n[音義-2]：䨂，子幽反。', '卷八', '方言', '', DEFAULT, DEFAULT, '2290/2291;4491/9764', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (312, '戟;孑;戭;戈;鏝胡;鉤孑鏝胡', '', '戟，楚謂之孑。1凡戟而無刃秦晉之閒謂之孑，或謂之鏔{戭}；2吳揚之閒謂之戈。東齊秦晉之閒謂其大者曰鏝胡，其曲者謂之鉤孑鏝胡。34', '[郭注-1]：取名于鉤孑也；\n[音義-2]：音寅；\n[郭注-3]：卽今雞鳴，鉤孑戟也。\n[音義-4]：音泥鏝；', '卷九', '方言', '', DEFAULT, DEFAULT, '8375;9780;8391;8373;9368/2677;1461/9780/9368/2677', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (313, '三刃枝;匽戟;柲;殳', '', '三刃枝，1南楚宛郢謂之匽戟。2345其柄自關而西謂之柲，6或謂之殳。7', '[郭注-1]：今戟中有小孑刺者，所謂雄戟也；\n[郭注-2]：郢，今江陵也，\n[音義-3]：余整反\n[郭注-4]：；\n[音義-5]：匽，音偃。\n[音義-6]：音祕；\n[音義-7]：音殊。', '卷九', '方言', '', DEFAULT, DEFAULT, '77/2811/3569;8420/8375;3746;1959', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (314, '矛;鉈;鋋;鏦;矜', '', '矛，吳揚江淮南楚五湖之閒謂之鍦{鉈}，12或謂之鋋，3或謂之鏦。45其柄謂之矜。6', '[郭注-1]：五湖，今吳興大湖也。先儒處之多亦不了，所未能詳者；\n[音義-2]：嘗蛇反。\n[音義-3]：音蝉；\n[郭注-4]：漢書曰：「鏦殺吳王。」\n[音義-5]：錯工反；\n[音義-6]：矜，今字作𥎊，巨巾反。', '卷九', '方言', '', DEFAULT, DEFAULT, '9506;9402;9400;9403;9510', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (315, '箭;矢;鍭', '', '箭，自關而東謂之矢，江淮之閒謂之鍭，1關西曰箭。2', '[音義-1]：音侯；\n[郭注-2]：箭者竹名，因以爲號。', '卷九', '方言', '', DEFAULT, DEFAULT, '2866;3307;9409', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (316, '鑽;耑', '䤸;䤸', '鑽[䤸]謂之鍴{耑}[䤸]。1', '[音義-1]：音端。', '卷九', '方言', '1;2', DEFAULT, DEFAULT, '9369;4538', '9403;9403', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (317, '矜;杖', '', '矜謂之杖。1', '[郭注-1]：矛戟𥎊，卽杖也。', '卷九', '方言', '', DEFAULT, DEFAULT, '9510;3739', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (318, '室;郭;削;鞞', '', '劍削，自河而北燕趙之閒謂之室；自關而東或謂之廓{郭}，或謂之削；自關而西謂之𩏂{鞞}。1', '[音義-1]：方婢反。', '卷九', '方言', '', DEFAULT, DEFAULT, '4557;4150;2746;1813', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (319, '盾;瞂;干', '𢧀', '盾，自關而東或謂之瞂，1或謂之干[𢧀]；2關西謂之盾。', '[音義-1]：音伐；\n[郭注-2]：干者，扞也。', '卷九', '方言', '3', DEFAULT, DEFAULT, '2220;2221;1442', '8379', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (320, '失;畢;綦', '紩;䩛', '車下鉄{失}[紩]，陳宋淮楚之閒謂之畢[䩛]，大者謂之綦。12', '[郭注-1]：鹿車也。\n[音義-2]：音忌。', '卷九', '方言', '1;2', DEFAULT, DEFAULT, '7976;2491;8627', '8679;1817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (321, '轊;龍', '', '車轊，12齊謂之𨏠{龍}。3', '[郭注-1]：車軸頭也。\n[音義-2]：于厲反；\n[郭注-3]：又名𨎬。', '卷九', '方言', '', DEFAULT, DEFAULT, '9552;7687', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (322, '枸簍;恢;蛩籠;灼;冥;畚;逢;隆屈', '槶;穹隆;糸;轒', '車枸簍，12宋魏陳楚之閒謂之䈐{恢}[槶]，34或謂之𥳎籠{蛩籠}[穹隆]。5其上約謂之𥭓{灼}，67或謂之䈿{冥}[糸]。8秦晉之閒自關而西謂之枸簍；西隴謂之㮥{畚}[轒]；910南楚之外謂之篷{逢}，11或謂之隆屈。12', '[郭注-1]：卽車弓也。\n[音義-2]：音縷；\n[郭注-3]：今呼車子弓爲䈐。\n[音義-4]：音巾幗；\n[郭注-5]：穹隆兩音；\n[郭注-6]：卽軬帶也。\n[音義-7]：音瓜瓝；\n[音義-8]：音覛；\n[郭注-9]：卽軬字。\n[音義-10]：薄晩反；\n[郭注-11]：今亦通呼篷；\n[郭注-12]：尾屈。', '卷九', '方言', '2;3;5;6', DEFAULT, DEFAULT, '3501/2929;6740;8927/2956;6473;4311;8447;1130;3877/5428', '3732;4670/3877;8515;9600', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (323, '輪;軑;軝;㚇', '鏓', '輪，1韓楚之閒謂之軑，2或謂之軝。34關西謂之𨍈{㚇}[鏓]。5', '[郭注-1]：車輅也；\n[音義-2]：音大；\n[郭注-3]：詩曰：「約軝錯衡。」\n[音義-4]：音祇；\n[音義-5]：音總。', '卷九', '方言', '4', DEFAULT, DEFAULT, '9547;9554;9550;3373', '9391', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (324, '輑;軸', '', '輑謂之軸。1', '[音義-1]：輑，牛忿反。', '卷九', '方言', '', DEFAULT, DEFAULT, '9539;9543', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (325, '轅;輈', '', '轅，楚衞之閒謂之輈。1', '[音義-1]：張由反。', '卷九', '方言', '', DEFAULT, DEFAULT, '9556;9557', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (326, '箱;篚', '', '箱謂之輫{篚}。1', '[音義-1]：音俳。', '卷九', '方言', '', DEFAULT, DEFAULT, '2968;2969', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (327, '軫;枕', '', '軫謂之枕。1', '[郭注-1]：車後橫木。', '卷九', '方言', '', DEFAULT, DEFAULT, '9540;3678', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (328, '紂;秋;曲匋;曲綸', '緧;曲絛', '車紂，自關而東周洛韓鄭汝潁而東謂之䋺{秋}[緧]，1或謂之曲綯{曲匋}[曲絛]，2或謂之曲綸；34自關而西謂之紂。', '[音義-1]：音秋；\n[郭注-2]：綯亦䋲名，詩曰：「宵爾索綯。」\n[郭注-3]：今江東通呼索綸。\n[音義-4]：音倫。', '卷九', '方言', '2;3', DEFAULT, DEFAULT, '8708;4491;8442/3287;8442/8652', '8709;8442/8664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (329, '輨;軑;鍊𨬍', '輨軑', '輨、軑，鍊𨬍[輨軑]也。關之東西曰輨，1南楚曰軑，2趙魏之閒曰鍊𨬍。3', '[音義-1]：音管；\n[音義-2]：音大；\n[音義-3]：鍊音柬，𨬍音度果反。', '卷九', '方言', '3', DEFAULT, DEFAULT, '9555;9554;9280/9350', '9555/9554', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (330, '釭;楇;昆', '', '車釭，齊燕海岱之閒謂之鍋{楇}，1或謂之錕{昆}；2自關而西謂之釭，盛膏者乃謂之鍋。', '[音義-1]：音戈；\n[音義-2]：音衮衣。', '卷九', '方言', '', DEFAULT, DEFAULT, '9416;3779;4254', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (331, '鉤腸;羊頭;錍;鈀;甲鑪;飛䖟;平題;箙;鞬;韇丸', '', '凡箭鏃胡合嬴者，1四鐮，2或曰鉤腸，三鐮者謂之羊頭，其廣長而薄鐮謂之錍，3或謂之鈀。4箭，其小而長中穿二孔者謂之鉀鑪{甲鑪}。56其三鐮長六尺者謂之飛䖟，7禸者謂之平題。8所以藏箭弩謂之箙。9弓謂之鞬，10或謂之𥀲丸{韇丸}。11', '[郭注-1]：胡鏑在于喉下，嬴，邊也；\n[郭注-2]：稜也。\n[音義-3]：普蹄反；\n[音義-4]：音葩；\n[郭注-5]：今箭錍鑿空兩邊者也。\n[音義-6]：㗐嚧兩音；\n[郭注-7]：此謂今射箭也；\n[郭注-8]：今戲射箭頭題猶羊頭也。\n[郭注-9]：盛弩箭器也。外傳曰：「檿弧箕箙。」\n[音義-10]：音犍牛；\n[音義-11]：音牛犢。', '卷九', '方言', '', DEFAULT, DEFAULT, '1461/2611;2332/5599;9338;9378;9739/9322;7692/8963;3073/5609;2977;1841;1842/6003', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (332, '鶴厀;鉤孑;矛;孑;錟;鈹;骹;銎;鐏;釬', '', '矛骹細如鴈脛者謂之鶴厀。1有小枝刃者謂之鉤𨥂{鉤孑}。矛或謂之𨥂{孑}。錟謂之鈹。23骹謂之銎。45鐏謂之釬。67', '[郭注-1]：今江東呼爲鈐釘；\n[郭注-2]：今江東呼大矛爲鈹，\n[音義-3]：音彼。錟，音𦕃；\n[郭注-4]：卽矛刃下口。\n[音義-5]：音凶；\n[郭注-6]：或名爲鐓，\n[音義-7]：音頓。釬，音扞。', '卷九', '方言', '', DEFAULT, DEFAULT, '2411/5783;1461/9780;9506;9780;9404;9333;2581;9336;9407;9412', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (333, '舟;船;𣃚;舸;差;冒宿;艇;帶;符;巩;欚', '', '舟，自關而西謂之船；自關而東或謂之舟，或謂之航{𣃚}。1南楚江湘凡船大者謂之舸，2小舸謂之艖{差}。34艖謂之艒䑿{冒宿}，5小艒䑿謂之艇。6艇長而薄者謂之艜{帶}，7短而深者謂之䒀{符}，89小而深者謂之㮪{巩}。1011東南丹陽會稽之閒謂艖爲欚。12', '[音義-1]：音行伍；\n[音義-2]：姑可反；\n[郭注-3]：今江東呼艖小底者也。\n[音義-4]：音叉；\n[音義-5]：目宿二音；\n[郭注-6]：𦨴也；\n[音義-7]：音衣帶；\n[郭注-8]：今江東呼艖䒀者。\n[音義-9]：音步；\n[郭注-10]：卽長舼也。\n[音義-11]：音卭竹；\n[音義-12]：音禮。', '卷九', '方言', '', DEFAULT, DEFAULT, '5436;5438;5454;5449;3028;4807/4600;5450;4865;2898;1888;3789', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (334, '泭;椑;伐;薦', '橃', '泭謂之𥴖{椑}，1𥴖謂之筏{伐}[橃]。2筏，秦晉之通語也。江淮家居𥴖中謂之薦。3', '[音義-1]：泭音敷；\n[音義-2]：音伐；\n[音義-3]：音箭。', '卷九', '方言', '3', DEFAULT, DEFAULT, '7282;3712;5165;6259', '3787', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (335, '方舟;㶇;艁舟;浮梁', '', '方舟謂之㶇，12艁舟謂之浮梁。3', '[郭注-1]：揚州人呼渡津舫爲杭，荆州人呼㶇。\n[音義-2]：音橫；\n[郭注-3]：卽今浮橋。', '卷九', '方言', '', DEFAULT, DEFAULT, '5453/5436;7281;1114/5436;7178/3785', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (336, '楫;橈;櫂;䉃;緝;篙;維;鼎;首;閤閭;益首;舳;僞;扤', '鶃首;𦨉', '楫謂之橈，1或謂之櫂。2所以隱櫂謂之𥷃{䉃}，34所以縣櫂謂之緝，5所以刺船謂之㰏{篙}。6維之謂之鼎，7首謂之閤閭，8或謂之艗艏{益首}[鶃首]。910後曰舳，舳，制水也。1112儰{僞}謂之扤[𦨉]，扤，不安也。1314', '[音義-1]：如寮反；\n[郭注-2]：今云櫂歌，依此名也；\n[郭注-3]：摇樐小橛也。江東又名爲胡人。\n[音義-4]：音奬；\n[郭注-5]：繫櫂頭索也；\n[音義-6]：音高；\n[郭注-7]：繫船爲維；\n[郭注-8]：今江東呼船頭屋謂之飛間是也；\n[郭注-9]：鷁，鳥名也。今江東貴人船前作靑雀，是其像也。\n[音義-10]：音亦；\n[郭注-11]：舳，今江東呼柁爲舳。\n[音義-12]：音軸；\n[郭注-13]：船動摇之貌也。\n[音義-14]：扤，吾㪍反。儰音訛。', '卷九', '方言', '11;14', DEFAULT, DEFAULT, '3788;3591;3839;2888;8731;3016;8701;4407;5701;7736/7739;3154/5701;5440;5134;8039', '2441/5701;5442', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (337, '媱;愓;遊;僖', '', '媱、愓，遊也。江沅之閒謂戲爲媱，或謂之愓，1或謂之嬉{僖}。2', '[音義-1]：音羊；\n[音義-2]：香其反。', '卷十', '方言', '', DEFAULT, DEFAULT, '8210;6847;4304;5090', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (338, '曾;訾;何', '', '曾、訾，何也。湘潭之原荆之南鄙謂何爲曾，123或謂之訾，4若中夏言何爲也。', '[郭注-1]：潭，水名，出武陵，\n[音義-2]：音譚，亦音淫\n[郭注-3]：；\n[郭注-4]：今江東人語亦云訾，爲聲如斯。', '卷十', '方言', '', DEFAULT, DEFAULT, '710;1627;5023', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (339, '央亡;墨杘;𡜶;獪;無賴;㺒;娗;滑', '獪;謬', '央亡、嚜杘{墨杘}、𡜶[獪]，獪也。江湘之閒或謂之無賴，或謂之㺒[謬]。12凡小兒多詐而獪謂之央亡，或謂之嚜杘，34或謂之𡜶。56𡜶，娗也。7或謂之猾{滑}。8皆通語也。', '[郭注-1]：㤍𢙘，多智也。\r\n[音義-2]：恪交反；\n[郭注-3]：潛潛狡也；\n[音義-4]：嚜，音目；杘，丑夷反；\n[郭注-5]：言黠𡜶也；\n[音義-6]：胡𠜜反；\n[郭注-7]：言恫娗也；\n[音義-8]：音滑。', '卷十', '方言', '3;6', DEFAULT, DEFAULT, '3325/8411;9076/3748;8225;6340;8414/3962;6322;8340;7218', '6340;1664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (340, '子;子', '', '崽{子}者，子也。湘沅之會，12凡言是子者謂之崽，若東齊言子矣。3', '[郭注-1]：两水合處也。\n[音義-2]：音獪。\n[音義-3]：崽音枲，聲之轉也；聲如宰。', '卷十', '方言', '', DEFAULT, DEFAULT, '9764;9764', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (341, '來;亡;秕', '', '誺{來}，不知也。沅澧之閒，12凡相問而不知答曰誺，3使之而不肯答曰吂{亡}。45粃{秕}，不知也。6', '[郭注-1]：澧水，今在長沙。\n[音義-2]：音禮。\n[音義-3]：江東曰咨，此亦知聲之轉也；音癡眩。\n[郭注-4]：今中國語亦然；\n[音義-5]：音茫。\n[郭注-6]：今淮楚閒語呼聲如非也。', '卷十', '方言', '', DEFAULT, DEFAULT, '3346;8411;4475', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (342, '果;火;𤈦', '火', '㷄{果}[火]，1火也。楚轉語也，猶齊言𤈦，2火也。', '[音義-1]：呼隗反；\n[音義-2]：音毁。', '卷十', '方言', '1', DEFAULT, DEFAULT, '3566;6414;6416', '6414', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (343, '嘳;無寫;憐;思;人兮', '', '嘳、無寫，憐也。1沅澧之原凡言相憐哀謂之嘳，2或謂之無寫，江濱謂之思。3皆相見驩喜有得亡之意也。九疑湘潭之閒謂之人兮。4', '[郭注-1]：皆南鄙之代語也；\n[音義-2]：音蒯；\n[郭注-3]：濱，水邊也；\n[郭注-4]：九疑，山名，今在零陵營道縣。', '卷十', '方言', '', DEFAULT, DEFAULT, '830;8414/4598;6960;6701;4948/3063', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (344, '岸;嫧;鮮;好', '妍;鱻', '婩{岸}[妍]、1嫧、2鮮[鱻]，好也。南楚之外通語也。', '[音義-1]：魚踐反；\n[音義-2]：音策。', '卷十', '方言', '1;3', DEFAULT, DEFAULT, '5916;8244;7646;8189', '8297;7680', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (345, '闌牢;謰謱;拏;支主;占謕;惹;㛪', '支主', '㘓哰{闌牢}、謰謱，拏也。東齊周晉之鄙曰㘓哰，㘓哰亦通語也；12南楚曰謰謱，3或謂之支註{支主}，4或謂之詀謕{占謕}[支主]，轉語也；5拏，揚州會稽之語也，67或謂之惹，89或謂之䛳{㛪}。10', '[郭注-1]：平原人好㘓哰也；\n[音義-2]：闌牢二音；\n[音義-3]：上音連，下力口反；\n[音義-4]：支，之䜴反。註，音注；\n[音義-5]：上託兼反，下音蹄；\n[郭注-6]：言譇拏也。\n[音義-7]：奴加反；\n[郭注-8]：言情惹也。\n[音義-9]：汝邪反，一音若；\n[郭注-10]：言誣䛳也。', '卷十', '方言', '5', DEFAULT, DEFAULT, '7766/757;1602/1603;8077;1935/3183;2079/1656;6975;8333', '1935/3183', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (346, '𠄌;𠻮;貪;吝', '', '亄{𠄌}、𠻮，貪也。1荆汝江湘之郊凡貪而不施謂之亄，23或謂之𠻮，或謂之悋{吝}。悋，恨也。4', '[郭注-1]：謂慳貪也；\n[郭注-2]：亦中國之通語；\n[音義-3]：音懿；\n[郭注-4]：慳者多惜恨也。', '卷十', '方言', '', DEFAULT, DEFAULT, '8404;3344;3982;932', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (347, '遙;窕;淫', '媱;姚;婬', '遙[媱]、窕[姚]，淫[婬]也。九疑荆郊之鄙謂淫曰遙，1沅湘之閒謂之窕。2', '[郭注-1]：言心遙蕩也；\n[郭注-2]：窈窕冶容。', '卷十', '方言', '1;2;3', DEFAULT, DEFAULT, '1222;4669;7221', '8210;8115;8336', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (348, '潛;涵;沈;游', '湛', '潛、涵，沈[湛]也。楚郢以南曰涵，1或曰潛。潛又游也。2', '[音義-1]：音含，或古南反；\n[郭注-2]：潛行水中亦爲游也。', '卷十', '方言', '3', DEFAULT, DEFAULT, '7288;7321;7318;4304', '7294', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (349, '𡧯;安;靜', '', '𡧘{𡧯}、安，靜也。江湘九疑之郊謂之𡧘。1', '[音義-1]：音寂。', '卷十', '方言', '', DEFAULT, DEFAULT, '4580;4576;3189', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (350, '半;棄;敲;投', '播', '拌{半}[播]，棄也。楚凡揮棄物謂之拌，1或謂之敲；23淮汝之閒謂之投。45', '[音義-1]：音伴，又普槃反；\n[郭注-2]：今汝潁間語亦然。或云撅也；\n[音義-3]：恪校反。\n[郭注-4]：江東又呼擫。\n[音義-5]：音黶，又音揞。', '卷十', '方言', '1', DEFAULT, DEFAULT, '725;2493;2053;7925', '8036', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (351, '椓;愬', '', '諑{椓}，愬也。楚以南謂之諑。1', '[郭注-1]：諑、譖亦通語也。', '卷十', '方言', '', DEFAULT, DEFAULT, '3798;1687', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (352, '戲;泄;歇;奄', '羲', '戲[羲]、泄，歇也。楚謂之戲泄。1奄，息也。楚揚謂之泄。', '[音義-1]：戲，音羲。', '卷十', '方言', '1', DEFAULT, DEFAULT, '8383;7063;5534;6599', '3065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (353, '弗;曬', '', '昲{弗}、1曬，乾物也。揚楚通語也。2', '[音義-1]：音費。\n[郭注-2]：亦皆北方常語耳。或云㬓。', '卷十', '方言', '', DEFAULT, DEFAULT, '8361;4247', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (354, '匪;卒', '猝', '䕁{匪}，卒[猝]也。1江湘之閒凡卒相見謂之䕁，2相見或曰突。3', '[郭注-1]：謂倉卒也；\n[音義-2]：音斐；\n[音義-3]：他骨反。', '卷十', '方言', '2', DEFAULT, DEFAULT, '8430;5359', '6316', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (355, '迹迹;㞕㞕;塞塞;省省', '', '迹迹、㞕㞕，不安也。1江沅之閒謂之迹迹；秦晉謂之㞕㞕，或謂之塞塞，或謂之省省，不安之語也。', '[郭注-1]：皆往來之貌也。', '卷十', '方言', '', DEFAULT, DEFAULT, '1093/1093;5404/5404;9093/9093;2219/2219', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (356, '鬩沭;正伀;遑遽', '鬩怵;䋫㤨', '𤄎沭{鬩沭}[鬩怵]、佂伀{正伀}[䋫㤨]，遑遽也。江湘之閒凡窘猝怖遽謂之𤄎沭，12或謂之佂伀。', '[郭注-1]：喘𠴲貌也。\n[音義-2]：𤄎，音鬩；', '卷十', '方言', '1;2', DEFAULT, DEFAULT, '1900/7079;1087/4971;1212/1206', '1900/6944;8693/6946', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (357, '翥;舉', '', '翥，舉也。1楚謂之翥。', '[郭注-1]：謂軒翥也。', '卷十', '方言', '', DEFAULT, DEFAULT, '2256;7957', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (358, '衂怩;慙歰;蹙咨', '恧怩', '忸怩{衂怩}[恧怩]，慙歰也。1楚郢江湘之閒謂之忸怩，或謂之㗤咨{蹙咨}。2', '[郭注-1]：歰猶苦者；\n[音義-2]：子六、莊伊二反。', '卷十', '方言', '1', DEFAULT, DEFAULT, '3172/6968;6957/1077;1417/841', '6958/6968', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (359, '垤;封;場', '', '垤、封，場也。楚郢以南蟻土謂之封。垤，中齊語也。', '', '卷十', '方言', '', DEFAULT, DEFAULT, '9133;9074;9145', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (360, '謫;過;衇;慧', '', '讁{謫}，過也。1南楚以南凡相非議人謂之讁，2或謂之衇。衇，又慧也。34', '[郭注-1]：謂罪過也。\n[音義-2]：音賾，亦音適，罪罰也；\n[郭注-3]：今名黠爲鬼衇。\n[音義-4]：音血脈；', '卷十', '方言', '', DEFAULT, DEFAULT, '1691;1110;7498;6733', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (361, '膊;兄;蠡', '䨣;剺', '膊[䨣]，兄也。荆揚之鄙謂之膊。桂林之中謂之⿰彖孟{劙}{蠡}[剺]。1', '[郭注-1]：此音義所未詳。', '卷十', '方言', '1;3', DEFAULT, DEFAULT, '2688;5462;8965', '7557;2778', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (362, '蹇;極;吃;軋;歰', '㥛;乙', '𧮈{蹇}、極[㥛]，吃也。楚語也。1或謂之軋[乙]，23或謂之歰。45', '[郭注-1]：亦北方通語也；\n[郭注-2]：鞅軋，氣不利也。\n[音義-3]：烏八反；\n[郭注-4]：語歰難也。今江南又名吃爲𠿚，\n[音義-5]：若葉反。', '卷十', '方言', '2;4', DEFAULT, DEFAULT, '1395;3624;888;9579;1077', '6812;9740', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (363, '啙;罷;短;齊;㾈', '䇑', '啙、矲{罷}[䇑]，短也。江湘之會謂之啙。1凡物生而不長大亦謂之啙，又曰癠{齊}。23桂林之中謂短矲。4矲，通語也。5東陽之閒謂之㾈。6', '[音義-1]：昨啓反；\n[郭注-2]：今俗呼小爲癠。\n[音義-3]：音薺菜；\n[郭注-4]：言矲雉也。\n[音義-5]：蒲楷反；\n[郭注-6]：言俯視之，因名。', '卷十', '方言', '2', DEFAULT, DEFAULT, '1084;4841;3313;4393;4730', '6694', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (364, '鉗;反;敝;惡', '', '鉗、㽹{反}、憋{敝}，1惡也。南楚凡人殘罵謂之鉗，2又謂之㽹。34', '[郭注-1]：憋怤，急性也，妨滅反；\n[郭注-2]：殘猶惡也。鉗害，又惡也；\n[郭注-3]：㽹恎，惡腹也。\n[音義-4]：妨反反；', '卷十', '方言', '', DEFAULT, DEFAULT, '9361;1920;4941;6880', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (365, '癡;騃;耳;斫', '譺懝;佁', '癡[譺懝]，騃[佁]也。1揚越之郊凡人相侮以爲無知謂之眲{耳}，眲，耳目不相信也。23或謂之斫。45', '[音義-1]：吾駭反；\n[郭注-2]：因字名也；\n[音義-3]：諾革反；\n[郭注-4]：斫，頑直之貌，今關西語亦皆然。\n[音義-5]：音斫卻。', '卷十', '方言', '1;2', DEFAULT, DEFAULT, '4795;6203;7792;9477', '1605/6830;5132', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (366, '混;誖;頓愍;惛;氐惆;眠眩', '溷', '惃{混}[溷]、𢠜{誖}、頓愍，惛也。1楚揚謂之惃，2或謂之𢠜；3江湘之閒謂之頓愍，4或謂之氐惆。56南楚飲毒藥懣謂之氐惆，亦謂之頓愍，猶中齊言眠眩也。愁恚憒憒、毒而不發謂之氐惆。', '[郭注-1]：謂迷惛也；\n[音義-2]：音衮衣；\n[音義-3]：音㪍；\n[郭注-4]：頓愍猶頓悶也；\n[郭注-5]：氐惆，猶懊憹也。\n[音義-6]：丁弟、丁牢二反；', '卷十', '方言', '1', DEFAULT, DEFAULT, '7141;1618;5658/6906;6867;8369/6893;2182/2099', '7205', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (367, '挩;舒;蘓', '', '悅{挩}、舒，蘓也。1楚通語也。', '[郭注-1]：謂蘇息也。', '卷十', '方言', '', DEFAULT, DEFAULT, '7977;2510;256', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (368, '眠娗;脈痬;賜施;茭媞;亶謾;詈詑;欺謾', '', '眠娗、1脈蜴{脈痬}、2賜施、3茭媞、4譠謾{亶謾}、5㦒忚{詈詑}，6皆欺謾之語也。楚郢以南東揚之郊通語也。7', '[音義-1]：莫典、塗殄二反；\n[音義-2]：蜴，音析；\n[音義-3]：音輕易；\n[音義-4]：恪校、得懈二反；\n[音義-5]：託蘭、莫蘭二反；\n[音義-6]：麗、醯二音；\n[郭注-7]：六者亦中國相輕易蚩弄之言也。', '卷十', '方言', '', DEFAULT, DEFAULT, '2182/8340;7498/4774;3959/4301;612/8228;3342/1599;4844/1598;5586/1599', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (369, '旃;頟;顔;顙', '', '𩔣{旃}、頟、顔，顙也。湘江之閒謂之𩔣，12中夏謂之頟，東齊謂之顙，汝潁淮泗之閒謂之顔。', '[郭注-1]：今建平人呼頟爲𩔣。\n[音義-2]：音旃裘；', '卷十', '方言', '', DEFAULT, DEFAULT, '4297;5610;5600;5608', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (370, '頷;頤;頜', '顄', '頷[顄]、頤，頜也。1南楚謂之頷，2秦晉謂之頜。頤，其通語也。', '[郭注-1]：謂頷車也；\n[郭注-2]：亦今通語耳。', '卷十', '方言', '1', DEFAULT, DEFAULT, '5647;7825;5615', '5616', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (371, '紛怡;喜;巸巳', '媐巳/饎巳', '紛怡，喜也。湘潭之閒曰紛怡，或曰巸巳[媐巳/饎巳]。1', '[音義-1]：嬉怡二音。', '卷十', '方言', '3', DEFAULT, DEFAULT, '8707/6744;3074;7826/9795', '8231/9795/3217/9795', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (372, '邯;或', '', '𣵷{邯}，或也。沅澧之閒凡言或如此者曰𣵷如是。12', '[郭注-1]：此亦憨聲之轉耳。\n[音義-2]：音酒酣；', '卷十', '方言', '', DEFAULT, DEFAULT, '4064;8385', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (373, '䍃;療;治;憂', '', '愮{䍃}、療，治也。江湘郊會謂醫治之曰愮。12愮又憂也。3或曰療。', '[郭注-1]：俗云厭愮病。\n[音義-2]：音曜；\n[郭注-3]：博異義也。', '卷十', '方言', '', DEFAULT, DEFAULT, '3298;4788;7087;3366', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (374, '芔;莽;草', '茻;艸', '芔、莽[茻]，草[艸]也。東越揚州之閒曰芔，1南楚曰莽。2', '[音義-1]：凶位反；\n[音義-2]：音嫫母。', '卷十', '方言', '2;3', DEFAULT, DEFAULT, '629;702;680', '700;240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (375, '悈;思;乾;都;耇;革;老', '', '悈、1鰓{思}、2乾、3都、耇、4革，老也。5皆南楚江湘之閒代語也。6', '[音義-1]：音良愅；\n[音義-2]：音魚鰓；\n[音義-3]：音干；\n[音義-4]：音垢；\n[郭注-5]：皆老者皮色枯瘁之形也；\n[郭注-6]：凡以異語相易謂之代也。', '卷十', '方言', '', DEFAULT, DEFAULT, '6753;6701;9741;4005;5379;1791;5375', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (376, '柲;抌;椎;忽;攩', '', '㧙{柲}、抌，1椎也。南楚凡相椎搏曰㧙，2或曰㧾{忽}；3沅涌𣿆幽之語或曰攩。4567', '[音義-1]：都感反，亦音甚；\n[音義-2]：音神祕；\n[音義-3]：苦骨反；\n[郭注-4]：𣿆水，今在桂陽。\n[音義-5]：音扶。\n[郭注-6]：涌水，今在南郡華容縣也；今江東人亦名推爲攩，\n[音義-7]：音晃。', '卷十', '方言', '', DEFAULT, DEFAULT, '3746;8062;3743;6843;7916', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (377, '食閻;慫涌;勸', '', '食閻、慫慂{慫涌}，勸也。南楚凡己不欲喜而㫄人說之、不欲怒而㫄人怒之謂之食閻，1或謂之慫慂。2', '[音義-1]：音鹽；\n[音義-2]：上子竦反，下音涌。', '卷十', '方言', '', DEFAULT, DEFAULT, '3204/7740;6840/7190;9223', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (378, '欸;殹;然', '唉', '欸[唉]、𧫦{殹}，然也。南楚凡言然者曰欸，1或曰𧫦。2', '[音義-1]：音醫，或音塵埃；\n[音義-2]：音鳧鷖。', '卷十', '方言', '1', DEFAULT, DEFAULT, '5555;1971;6421', '855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (379, '緤;末;紀;緒;端', '', '緤、末、紀，緒也。南楚皆曰緤，1或曰端、或曰紀、或曰末，皆楚轉語也。', '[音義-1]：音薛。', '卷十', '方言', '', DEFAULT, DEFAULT, '8715;3564;8540;8519;6680', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (380, '葼;𠭥;闚;占;占;伺;視;司;瞻', '䚕;窺;覘;覘;司', '䁓{葼}、𣛒{𠭥}[䚕]、闚[窺]、䀡{占}[覘]、占[覘]、伺[司]，視也。凡相竊視南楚謂之闚、或謂之䁓、1或謂之䀡、2或謂之占、或謂之𣛒。𣛒，中夏語也。34闚，其通語也。自江而北謂之䀡、或謂之覗{司}。凡相候謂之占，占猶瞻也。', '[音義-1]：音總；\n[音義-2]：敕纖反；\n[郭注-3]：亦言𥉹也。\n[音義-4]：音麗；', '卷十', '方言', '2;3;4;5;6', DEFAULT, DEFAULT, '508;1923;7777;2079;2079;5207;5475;5771;2157', '5476;4659;5496;5496;5771', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (381, '邕;濃;盛;多', '', '郺{邕}、𨑊{濃}、晠{盛}，多也。南楚凡大而多謂之郺，1或謂之𨑊。2凡人語言過度及妄施行亦謂之𨑊。', '[音義-1]：惡孔反；\n[音義-2]：奴動反。', '卷十', '方言', '', DEFAULT, DEFAULT, '7487;7331;3140;4334', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (382, '抯;𠭯;取', '', '抯、摣{𠭯}，取也。南楚之閒凡取物溝泥中謂之抯，1或謂之摣。2', '[音義-1]：音柤棃；\n[音義-2]：仄加反。', '卷十', '方言', '', DEFAULT, DEFAULT, '7981;1915;1926', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (383, '汎;僄;輕', '', '𠆩{汎}、僄，輕也。楚凡相輕薄謂之相𠆩，1或謂之僄。2', '[音義-1]：音汎；\n[音義-2]：音飄零。', '卷十', '方言', '', DEFAULT, DEFAULT, '7162;5137;9519', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (384, '多蚗;螇鹿;蟪蛄;蛉蛄;蛁勞;是勞;蜓木', '𧉅蚗;蛁尞', '蛥蚗{多蚗}[𧉅蚗]，1齊謂之螇螰{螇鹿}；2楚謂之蟪蛄，3或謂之蛉蛄；4秦謂之蛥蚗；自關而東謂之虭蟧{蛁勞}[蛁尞]，56或謂之蝭蟧{是勞}，7或謂之蜓蚞{蜓木}，8西楚與秦通名也。', '[音義-1]：蛥，音折。蚗，于列反，一音玦；\n[音義-2]：奚鹿二音；\n[郭注-3]：莊子曰：「蟪蛅不知春秋也」；\n[音義-4]：音零；\n[郭注-5]：江東人呼嘄蟧。\n[音義-6]：貂料二音；\n[音義-7]：音啼；\n[音義-8]：廷木二音；', '卷十一', '方言', '1;5', DEFAULT, DEFAULT, '4334/8870;8869/6261;8939/8832;8874/8832;8793/9234;1089/9234;8803/3411', '8861/8870;8793/6420', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (385, '蟬;蜩;唐蜩;蜋蜩;奇;勞;蝒馬;麥札;蜻蜻;尐;戔;蜺;蟊蜩;應;寒蜩;瘖蜩', '𧉄馬;麥蠽', '蟬，楚謂之蜩，1宋衞之閒謂之螗蜩{唐蜩}，2陳鄭之閒謂之蜋蜩，3秦晉之閒謂之蟬，海岱之閒謂之䗁{奇}。45其大者謂之蟧{勞}，或謂之蝒馬{𧉄馬}；6其小者謂之麥蚻{麥札}[麥蠽]，789有文者謂之蜻蜻，10其雌蜻謂之尐，11大而黑者謂之䗃[戔]，12黑而赤者謂之蜺。13蜩蟧謂之𧓿蜩{蟊蜩}。14𧕄{應}謂之寒蜩，寒蜩，瘖蜩也。1516', '[音義-1]：音調；\n[郭注-2]：今胡蟬也，似蟬而小，鳴聲淸亮，江南呼螗蛦；\n[音義-3]：音良；\n[郭注-4]：齊人呼爲巨䗁。\n[音義-5]：音技；\n[郭注-6]：案爾雅：「蝒者，馬蜩。」非別名蝒馬也，此方言誤耳；\n[郭注-7]：如蟬而小，靑色。今關西呼麥蠽，\n[音義-8]：音癰𤻵之𤻵\n[郭注-9]：；\n[郭注-10]：卽蚻也，爾雅云耳；\n[音義-11]：祖一反；\n[音義-12]：音棧；\n[音義-13]：音雲霓。\n[郭注-14]：江東呼爲𧓿蠽也。\n[郭注-15]：案爾雅以蜺爲寒蜩，月令亦曰「寒蜩鳴」，知寒蜩非瘖者也。此諸蜩名，通出爾雅而多駮雜，未可詳據也。寒蜩，螿也，似小蟬而色靑。\n[音義-16]：𧕄，音應。', '卷十一', '方言', '7;8', DEFAULT, DEFAULT, '8867;8866;878/8866;8842/8866;3059;9234;8839/6135;3348/3768;8873/8873;706;8398;8868;8954/8866;6711;4613/8866;4722/8866', '8871/6135;3348/8952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (386, '蛄諸;杜各;螻室;螻蛄;象蛉;杜狗;𠯑螻', '', '蛄諸謂之杜蛒{杜各}。1螻𧍱{螻室}謂之螻蛄，2或謂之蟓蛉{象蛉}。3南楚謂之杜狗，或謂之𧋣螻{𠯑螻}。', '[音義-1]：音格；\n[音義-2]：螻𧍱，音窒塞；\n[音義-3]：象鈴二音。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8832/1489;3431/933;8831/4557;8831/8832;6133/8874;3431/6302;942/8831', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (387, '蜻蛚;蟋率;共;王孫', '悉𧍓', '蜻蛚，12楚謂之蟋蟀{蟋率}[悉𧍓]，或謂之蛬{共}；34南楚之閒謂之蚟孫{王孫}。5', '[郭注-1]：卽趨織也。\n[音義-2]：精列二音；\n[郭注-3]：梁國呼蛬。\n[音義-4]：音鞏；\n[音義-5]：孫，一作𠬆。', '卷十一', '方言', '2', DEFAULT, DEFAULT, '8873/8872;8943/8783;1776;78/8512', '723/8838', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (388, '堂蜋;髦;丁;芈芈', '', '螳螂{堂蜋}謂之髦，1或謂之虰{丁}，2或謂之蝆蝆{芈芈}。', '[郭注-1]：有斧蟲也。江東呼爲石蜋，又名齕肬；\n[郭注-2]：案爾雅云：「螳蜋，蛑。」「虰」義自應下屬。方言依此說，失其指也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9054/8842;5734;9745;2333/2333', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (389, '蛄䗐;強羊', '', '姑䗐{蛄䗐}謂之強蛘{強羊}。123', '[郭注-1]：米中小黑甲蟲也。江東謂之𧉪，\n[音義-2]：音加。\n[郭注-3]：建平人呼羊子，羊卽蛘也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8832/8847;8824/2332', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (390, '莽;𧎢;蟅莽;螣', '蜢;虴蜢', '蟒{莽}[蜢]，12宋魏之閒謂之𧊇{𧎢}，3南楚之外謂之蟅蟒{蟅莽}[虴蜢]，4或謂之蟒，或謂之螣。5', '[郭注-1]：卽蝗也。\n[音義-2]：莫鯁反；\n[音義-3]：音貸；\n[音義-4]：蟅音近詐，亦呼虴蛨；\n[音義-5]：音滕。', '卷十一', '方言', '1;3', DEFAULT, DEFAULT, '702;8807;8864/702;8786', '8942;8941/8942', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (391, '蜻蛉;即蛉', '', '蜻蛉謂之蝍蛉{即蛉}。12', '[郭注-1]：六足四翼蟲也。江東名爲狐黎，淮南人呼䗧𧉅。\n[音義-2]：蛉音靈。䗧，音康。𧉅，音伊。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8873/8874;3196/8874', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (392, '舂黍;從蝑', '蜙蝑', '舂黍謂之𧐱蝑{從蝑}[蜙蝑]。12', '[郭注-1]：又名蚣𧐱，江東呼虴蛨。\n[音義-2]：𧐱音葼，蝑音思沮反。', '卷十一', '方言', '2', DEFAULT, DEFAULT, '4522/4507;5225/8863', '8862/8863', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (393, '資就;尺蠖', '', '蠀𧑙{資就}謂之蚇蠖{尺蠖}。12', '[郭注-1]：又呼步屈。\n[音義-2]：卽踧二音。蠖，烏郭反。', '卷十一', '方言', '', DEFAULT, DEFAULT, '3940/3330;5424/8829', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (394, '蠭;蠓螉;翳螉;幼蛻;壺蠭', '', '蠭，燕趙之閒謂之蠓螉。1其小者謂之蠮螉{翳螉}，23或謂之蚴蛻{幼蛻}；4其大而蜜謂之壺蠭。5', '[音義-1]：蒙翁二音；\n[郭注-2]：小細腰蠭也。\n[音義-3]：蠮螉，音鯁噎；\n[音義-4]：幽悅二音；\n[郭注-5]：今黑蠭穿竹木作孔亦有蜜者，或呼笛師。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8959;8875/8790;2273/8790;2498/8888;6640/8959', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (395, '蠅;羊', '', '蠅，東齊謂之羊，1陳楚之閒謂之蠅，自關而西秦晉之閒謂之羊。', '[郭注-1]：此亦語轉耳。今江東人呼羊聲如蠅，凡此之類，皆不宜別立名也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9006;2332', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (396, '蚍蜉;蚼象;玄蚼;蛾蛘;坻;垤', '', '蚍蜉，12齊魯之閒謂之蚼蟓{蚼象}，3西南梁益之閒謂之玄蚼，4燕謂之蛾蛘。56其場謂之坻，7或謂之垤。8', '[郭注-1]：亦呼蟞蜉。\n[音義-2]：𣬈浮二音。\n[音義-3]：駒養二音；\n[郭注-4]：法言云：「玄駒之步」是。\n[郭注-5]：建平人呼蚳，\n[音義-6]：音侈。蟻養二音。\n[音義-7]：直尸反；\n[郭注-8]：亦名冢也。', '卷十一', '方言', '', DEFAULT, DEFAULT, '8973/8967;8926/6133;2506/8926;8834/8892;9086;9133', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (397, '資𧖤;賁;蝤資;卷屬;宣㱿;各;蝎;蛭各;蠹;天螻', '𧓉𧕐;蝤𧓉', '蠀螬{資𧖤}[𧓉𧕐]謂之蟦{賁}。1自關而東謂之蝤蠀{蝤資}[蝤𧓉]，2或謂之䖭蠾{卷屬}，3或謂之蝖𧏚{宣㱿}；45梁益之閒謂之蛒{各}，或謂之蝎，或謂之蛭蛒{蛭各}；6秦晉之閒謂之蠹，或謂之天螻。7四方異語而通者也。', '[音義-1]：音翡翠；\n[音義-2]：酋餈兩音；\n[音義-3]：音書卷；\n[郭注-4]：亦呼當齊，或呼地蠶，或呼蟦蝖。\n[音義-5]：喧斛兩音；\n[音義-6]：蛭，音質；蛒，音格；\n[郭注-7]：按爾雅云：「𧏚，天螻。」謂螻蛄耳，而方言以爲蝎，未詳其義也。', '卷十一', '方言', '1;2', DEFAULT, DEFAULT, '3940/8956;3944;8821/3940;5784/5427;4558/1963;933;8823;8809/933;8964;3/8831', '8822/8956;8821/8822', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (398, '由延;螾衍;入耳;長麗;蚨于;衂怩', '恧怩', '蚰蜒{由延}，12自關而東謂之螾𧍢{螾衍}，3或謂之入耳，或謂之䗅𧕯{長麗}；4趙魏之閒或謂蚨虶{蚨于}；5北燕謂之䖡蚭{衂怩}[恧怩]。6', '[郭注-1]：江東又呼蛩。\n[音義-2]：音鞏。由延二音；\n[音義-3]：螾，音引；\n[音義-4]：𧕯，音麗；\n[音義-5]：扶于二音；\n[音義-6]：䖡，奴六反。蚭，音尼。', '卷十一', '方言', '6', DEFAULT, DEFAULT, '8514/1265;8789/7136;3279/7792;6067/6286;8911/3069;3172/6968', '6958/6968', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (399, '䵹鼄;鼄蝥;屬蝓;毒余', '', '䵹鼄，1鼄蝥也。2自關而西秦晉之閒謂之鼄蝥；34自關而東趙魏之郊謂之鼅鼄，或謂之蠾蝓{屬蝓}。5蠾蝓者，侏儒語之轉也。北燕朝鮮洌水之閒謂之蝳蜍{毒余}。67', '[音義-1]：知株二音；\n[音義-2]：蝥，音無；\n[郭注-3]：今江東呼蝃蝥。\n[音義-4]：音掇；\n[音義-5]：燭臾二音；\n[郭注-6]：齊人又呼社公，亦言罔工。\n[音義-7]：音毒餘。', '卷十一', '方言', '', DEFAULT, DEFAULT, '9007/9008;9008/8859;5427/8905;236/718', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (400, '蜉流;渠略', '浮游;螶𧎾', '蜉䖻{蜉流}[浮游]，12秦晉之閒謂之蟝𧐯{渠略}[螶𧎾]。', '[郭注-1]：似天牛而小，有甲角，出糞土中，朝生夕死。\n[音義-2]：浮由二音；', '卷十一', '方言', '1;2', DEFAULT, DEFAULT, '8967/7474;7263/9190', '7178/4304;8961/8876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (401, '馬玄;且渠;馬由', '馬蠲', '馬蚿{馬玄}[馬蠲]，1北燕謂之蛆蟝{且渠}，2其大者謂之馬蚰{馬由}。34', '[音義-1]：音弦；\n[音義-2]：音蝍蛆；\n[郭注-3]：今關西云。\n[音義-4]：蚰，音逐。', '卷十一', '方言', '1', DEFAULT, DEFAULT, '6135/2506;9471/7263;6135/8514', '6135/8827', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (402, '爰;爰;哀', '咺;咺', '爰[咺]、喛{爰}[咺]，12哀也。', '[郭注-1]：喛，哀而恚也。\n[音義-2]：音段。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '2516;2516;936', '796;796', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (403, '儒輸;愚', '孺輸', '儒輸[孺輸]，愚也。1', '[郭注-1]：儒輸猶懦撰也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '4959/9576;6827', '9770/9576', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (404, '爰;諒;知', '', '愋{爰}、諒，知也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '2516;1481;3315', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (405, '拊;撫;疾', '駙', '拊[駙]、1撫，疾也。2', '[郭注-1]：音府；\n[郭注-2]：謂急疾也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '7890;7920;4694', '6191', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (406, '悲;惄;悵', '', '蕜{悲}、1惄，悵也。2', '[郭注-1]：音翡；\n[郭注-2]：謂惋惆也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6903;6803;6894', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (407, '鬱;熙;長', '巸', '鬱、熙[巸]，1長也。2', '[音義-1]：音怡；\n[郭注-2]：謂壯大也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '3848;6525;6067', '7826', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (408, '娋;孟;姊', '嬃', '娋[嬃]、1孟，2姊也。', '[郭注-1]：音義未詳。\n[郭注-2]：外傳曰：「孟啖我。」是也。今江東山越閒呼姊聲如市，此因字誤遂俗也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '8285;9772;8147', '8170', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (409, '築里;匹;耦', '妯里/妯𠩺', '築娌{築里}[妯里/妯𠩺]，12匹也。娌，耦也。', '[郭注-1]：今關西兄弟婦相呼爲築娌。\n[音義-2]：築，度六反，廣雅作「妯」。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '3617/9167;8422;2821', '8287/9167/8287/2056', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (410, '嬴;裔;習', '習', '䃷{嬴}、1裔[習]，習也。2', '[音義-1]：音盈；\n[郭注-2]：謂玩習也。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '8114;5310;2239', '2239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (411, '躔;逡;循;歴;行', '', '躔、1逡，2循也。躔、歴，行也。日運爲躔，3月運爲逡。4', '[音義-1]：度展反；\n[音義-2]：音逡巡。\n[郭注-3]：躔猶踐也；\n[郭注-4]：運猶行也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '1356;1161;1233;1070;1266', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (412, '逭;育;轉;步', '', '逭、逳{育}，轉也。逭、1逳，2步也。3', '[音義-1]：音換，亦管；\n[音義-2]：陽六反；\n[郭注-3]：轉相訓耳。', '卷十二', '方言', '', DEFAULT, DEFAULT, '1171;9786;9575;1081', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (413, '㷭;虞;望', '', '㷭，1虞，望也。', '[郭注-1]：今云烽火是也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6522;3109;8413', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (414, '俞;隋;脫', '輸;挩', '揄{俞}[輸]，撱{隋}，脫[挩]也。', '', '卷十二', '方言', '1;3', DEFAULT, DEFAULT, '5437;2670;2652', '9576;7977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (415, '解;輸;梲', '挩', '解、輸，梲[挩]也。1', '[郭注-1]：梲猶脫耳。', '卷十二', '方言', '3', DEFAULT, DEFAULT, '2850;9576;3742', '7977', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (416, '賦;與;操', '', '賦、與，操也。1', '[郭注-1]：謂操持也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '3981;1782;7861', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (417, '漉;歇;涸', '', '𣿍{漉}、1歇，2涸也。34', '[音義-1]：音鹿；\n[音義-2]：音泄气；\n[郭注-3]：謂渴也。\n[音義-4]：音鶴。', '卷十二', '方言', '', DEFAULT, DEFAULT, '7374;5534;7340', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (418, '潎;澂;淸', '', '潎、1澂，2淸也。', '[郭注-1]：妨計反；\n[郭注-2]：音澄。', '卷十二', '方言', '', DEFAULT, DEFAULT, '7421;7199;7200', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (419, '逯;遡;行', '', '逯、1遡，2行也。', '[音義-1]：音鹿，亦録；\n[音義-2]：音素。', '卷十二', '方言', '', DEFAULT, DEFAULT, '1164;7285;1266', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (420, '墾;牧;司;力;飤;監;督;察', '', '墾、牧，司也。墾，力也。1牧，飤也。2監、牧、督，察也。', '[郭注-1]：耕墾用力。\n[郭注-2]：謂放飤牛馬也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '9156;2068;5771;9210;3222;5245;2172;4581', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (421, '雚;始;化', '幻', '𡚊{雚}[幻]，始也。𡚊，化也。12', '[郭注-1]：別異訓也。\n[音義-2]：𡚊音歡；', '卷十二', '方言', '1', DEFAULT, DEFAULT, '2323;8182;5214', '2511', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (422, '鋪;脾;止', '罷', '鋪、脾[罷]，止也。12', '[郭注-1]：義有不同，故異訓之。\n[音義-2]：鋪，妨孤反。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '9434;2606;1064', '4841', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (423, '攘;掩;止', '', '攘、掩，止也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '7839;8033;1064', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (424, '幕;覆', '', '幕，覆也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '4881;4852', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (425, '侗;同;狀', '', '侗、1胴{同}，2狀也。3', '[音義-1]：他動反；\n[音義-2]：音挺挏；\n[郭注-3]：謂形狀也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '4992;4801;6333', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (426, '尐;杪;小', '', '尐、杪，小也。1', '[郭注-1]：樹細枝爲杪也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '706;3581;704', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (427, '㞕;往;勞', '', '㞕、往，勞也。1', '[郭注-1]：㞕㞕、往來，皆劬勞也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '5404;1229;9234', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (428, '㞕;往;獪', '泆;誑', '㞕[泆]、𢛛{往}[誑]，1獪也。2', '[音義-1]：音王相；\n[音義-2]：巿獪。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '5404;1229;6340', '7223;1606', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (429, '皦;烓;明', '炯', '皦、1烓[炯]，2明也。', '[音義-1]：音皎；\n[音義-2]：口類反。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4937;6452;4321', '6503', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (430, '湊;將;威', '', '湊、將，威也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '7293;1983;8145', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (431, '嬀;娗;謾', '僞', '嬀[僞]、1娗，2僈{謾}也。3', '[音義-1]：居僞反；\n[音義-2]：音挻；\n[郭注-3]：爛僈，健狡也。', '卷十二', '方言', '1', DEFAULT, DEFAULT, '8116;8340;1599', '5134', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (432, '𠐛;虔;謾', '丯', '𠐛、虔[丯]，謾也。1', '[郭注-1]：謂惠黠也，莫錢反。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '4972;3111;1599', '2817', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (433, '𠐛;佻;疾', '𧾎;跳', '𠐛[𧾎]、佻[跳]，1疾也。2', '[音義-1]：音糶。\n[郭注-2]：謂輕疾也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '4972;5127;4694', '1023;1370', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (434, '鞅;孛;强;鞅;孛;懟', '怏;怏;艴;艴', '鞅[怏]、侼{孛}[艴]，1强也。2鞅[怏]、3侼{孛}[艴]，懟也。4', '[音義-1]：音勃；\n[郭注-2]：謂强戾也。\n[郭注-3]：鞅猶怏也。\n[郭注-4]：亦爲怨懟。', '卷十二', '方言', '1;2;3;4', DEFAULT, DEFAULT, '1846;3870;8824;1846;3870;6886', '6889;6889;5792;5792', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (435, '追;末;隨', '', '追、末，隨也。', '', '卷十二', '方言', '', DEFAULT, DEFAULT, '1177;3564;1102', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (436, '僉;怚;劇;夥', '勮', '僉、怚，劇[勮]也。12僉，夥也。34', '[郭注-1]：謂勤劇。\n[音義-2]：音驕怚。\n[郭注-3]：僉者同，故爲多。\n[音義-4]：音禍。', '卷十二', '方言', '3', DEFAULT, DEFAULT, '3270;6820;2809;4335', '9235', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (437, '夸;烝;婬', '', '夸、烝，婬也。1', '[郭注-1]：上婬爲烝。', '卷十二', '方言', '', DEFAULT, DEFAULT, '6600;6429;8336', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (438, '𣬈;賓;懣', '𤼤;𤼤', '𣬈[𤼤]、顮{賓}[𤼤]，1懣也。2', '[音義-1]：音頻；\n[郭注-2]：謂憤懣也。', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '6700;3966;6890', '4729;4729', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (439, '煢;激;淸', '瀅', '煢[瀅]、激，淸也。', '', '卷十二', '方言', '1', DEFAULT, DEFAULT, '7700;7186;7200', '7252', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (440, '紓;退;緩', '', '紓、1退，緩也。2', '[音義-1]：音舒；\n[郭注-2]：謂寛緩也。', '卷十二', '方言', '', DEFAULT, DEFAULT, '8554;1249;8779', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (441, '淸;躡;急', '䋫;㛼', '淸[䋫]、躡[㛼]，急也。', '', '卷十二', '方言', '1;2', DEFAULT, DEFAULT, '7200;1352;6809', '8693;8313', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (442, '抒;瘛;豸;解', '𤸪', '抒、1瘛[𤸪]、2豸，解也。', '[音義-1]：音抒井；\n[音義-2]：胡計反。', '卷十二', '方言', '2', DEFAULT, DEFAULT, '7980;4784;6110;2850', '7946', 'null', 'null');
