INSERT INTO `t_han_nga` VALUES (602, '曉;過;嬴', '贏', '曉，過也。曉，嬴[贏]也。', '', '卷十三', '方言', '3', DEFAULT, DEFAULT, '4195;1110;8114', '3961', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (603, '䆯;短', '', '𠭴{䆯}，短也。12', '[郭注-1]：蹶𠭴，短小貌；\n[音義-2]：音疣贅。', '卷十三', '方言', '', DEFAULT, DEFAULT, '8268;3313', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (604, '豈;陭', '', '隑{豈}，陭也。12', '[郭注-1]：江南人呼梯爲隑，所以隑物而登者也；\n[音義-2]：隑，音剴切也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3092;9677', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (605, '迒;長;迹', '', '迒，長也。12迒，迹也。3', '[郭注-1]：謂長短也。\n[音義-2]：迒，胡郎反。\n[郭注-3]：爾雅以爲兔迹。', '卷十三', '方言', '', DEFAULT, DEFAULT, '1207;6067;1093', NULL, 'null', 'null');
