INSERT INTO `t_han_nga` VALUES (607, '薀;饒', '', '蘊{薀}，饒也。1', '[音義-1]：蘊音温。', '卷十三', '方言', '', DEFAULT, DEFAULT, '552;3244', NULL, 'null', 'null');
