INSERT INTO `t_han_nga` VALUES (610, '盛;腯', '', '𦛙{盛}，腯也。12', '[郭注-1]：腯腯，肥充也；\n[音義-2]：腯音腞，亦突。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3140;2675', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (611, '盬;雜;猝', '疌', '盬、1雜[疌]，猝也。2', '[郭注-1]：音古；\n[郭注-2]：皆倉卒也。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '7718;5333;6316', '1074', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (612, '蘥;行', '躍', '𨈅{蘥}[躍]，行也。12', '[郭注-1]：言跳𨈅也。\n[音義-2]：音藥。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '417;1266', '1348', 'null', 'null');
