INSERT INTO `t_han_nga` VALUES (621, '臆;滿', '𢡃', '臆[𢡃]，滿也。1', '[郭注-1]：愊臆，氣滿之也。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2615;7217', '6767', 'null', 'null');
