INSERT INTO `t_han_nga` VALUES (624, '珇;好;美', '袓', '珇[袓]，好也。珇，美也。12', '[郭注-1]：美好等互見義耳。\n[音義-2]：珇音祖。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '132;8189;2355', '5330', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (625, '嫗;色', '煦', '嫗[煦]，色也。1', '[郭注-1]：嫗煦[姁]，好色貌。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '8140;5791', '6431', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (626, '閻;開', '', '閻，開也。1', '[郭注-1]：謂開門也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '7740;7754', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (627, '摩;滅', '', '摩，滅也。1', '[音義-1]：或作攠滅字，音麋。', '卷十三', '方言', '', DEFAULT, DEFAULT, '8009;7441', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (628, '菲;薄', '', '菲，薄也。12', '[郭注-1]：謂微薄也。\n[音義-2]：菲音翡。', '卷十三', '方言', '', DEFAULT, DEFAULT, '641;561', NULL, 'null', 'null');
