INSERT INTO `t_han_nga` VALUES (2935, '𪊺', '', '𪊺，狼題肉角，含仁懷義，音中鐘呂，行步中規，折還中榘，遊必擇土，翔必後處，不履生蟲，不折生草，不羣居，不旅行，不入陷穽，不羅罘罔，文章彬彬。', '', '釋獸', '廣雅', '', DEFAULT, DEFAULT, '6269', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2936, '隹鼠;䶂鼠;鼫鼠;偃鼠;鼢鼠;鼠狼;鼬;陽鼠;𪕎鼠;䶄鼠;𪕶鼠;韱𪕱;尃唐;冋令;白𪖇;㱿;業攴;夋鼠;耳鼠;鼲鼠;䶃鼠;𪕚鼠', '斬𪕱', '𪕪鼠{隹鼠}1。䶂鼠2。鼫鼠。鼴鼠{偃鼠}，鼢鼠3。鼠狼，鼬4。陽鼠。𪕎鼠5。䶄鼠6。𪕶鼠7。𪖎𪕱{韱𪕱}[斬𪕱]8。䶈𪕹{尃唐}9。𪕍𪕌{冋令}10。白𪖇11。𪕷{㱿}12。𪖊𪔿{業攴}13。𪕞鼠{夋鼠}14。𪕔〔鼠〕{耳鼠}15。鼲鼠16。𪕛鼠{䶃鼠}17。𪕚鼠18。', '[音義-1]：隹\n[音義-2]：爵\n[音義-3]：墳\n[音義-4]：由溜\n[音義-5]：如勇\n[音義-6]：瓶\n[音義-7]：於革\n[音義-8]：讒乎\n[音義-9]：博唐\n[音義-10]：古熒。零\n[音義-11]：煩\n[音義-12]：谷\n[音義-13]：卜攴\n[音義-14]：音俊\n[音義-15]：耳\n[音義-16]：古門\n[音義-17]：胡貪\n[音義-18]：柳', '釋獸', '廣雅', '12', DEFAULT, DEFAULT, '2278/6391;6406/6391;6398/6391;5154/6391;6394/6391;6391/6377;6405;9622/6391;6407/6391;6395/6391;6400/6391;4543/6410;1986/878;3322/5777;4929/6392;1963;1750/1995;3362/6391;7792/6391;6409/6391;6404/6391;6397/6391', '9608/6410', 'null', 'null');
