INSERT INTO `t_han_nga` VALUES (2939, '驙;馼;飛黃;騶吾;吉量;朱駮;飛兔;金喙;要褭;走狐;駃騠;飛鴻;野麋;腹丹;騏驥;緑耳;華駵;北𤛿;汗血;驒騱;巨虛;騀鹿', '騶虞', '白馬黑脊，驙1。白馬朱鬛，馼。飛黃。騶吾{騶虞}。吉𣊹{吉量}。朱駮。飛兔。金喙、騕褭{要褭}。走狐。駃騠2。飛鴻。野麋。腹丹。騏驥。騄駬{緑耳}。驊騮{華駵}。駣𩥴{北𤛿}3。汗血。驒騱4。巨虛。騀鹿。', '[音義-1]：大安、知連\n[音義-2]：決啼\n[音義-3]：力兮\n[音義-4]：顛', '釋嘼', '廣雅', '4', DEFAULT, DEFAULT, '6224;6177;7692/9201;6234/837;876/5243;3561/6241;7692/6293;9259/783;1785/5368;979/6380;6242/6243;7692/2414;9169/6270;2630/3185;6141/6167;8611/7792;3884/6145;5229/761;7434/3167;6247/6248;3032/5232;6194/6261', '6234/3109', 'null', 'null');
