INSERT INTO `t_han_nga` VALUES (2947, '初;哉;首;基;肈;祖;元;胎;俶;落;權輿;始', '才;肁;路;灌渝', '初、哉[才]1、首、基、肈[肁]23、祖、元、胎4、俶56、落[路]、權輿[灌渝]7，始也。89', '[釋文-1]：子來反，亦作栽\n[音釋-2]：兆。\n[釋文-3]：音趙\n[釋文-4]：天才反，孫炎大才反，本或作台\n[音釋-5]：昌叔。\n[釋文-6]：字又作（⿰亻𠦑），尺叔反\n[釋文-7]：巨貟反。音余\n[郭注-8]：尚書曰：「三月哉生魄。」詩曰：「令終有俶。」又曰：「俶載南畝。」又曰：「訪予落止。」又曰：「胡不承權輿。」胚胎未成，亦物之始也。其餘皆義之常行者耳。此所以釋古今之異言，通方俗之殊語。\n[釋文-9]：令，力政反。胚，字又作衃，同，普才反，又四尤反。淮南子及文子並云：「婦孕三月而胚。」說文云：「胚，婦孕一月也。」「衃，凝血也。」', '釋詁弟一', '爾雅', '2;5;10;11', DEFAULT, DEFAULT, '2753;856;5701;9044;8374;36;2;2596;5015;549;3519/9526;8182', '3856;7726;1410;7042/7439', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2948, '林;烝;天;帝;皇;王;后;辟;公;侯;君', '', '林、烝1、天、帝、皇、王、后、辟23、公、侯，君也。45', '[釋文-1]：本又作蒸，同，之仍反\n[音釋-2]：并亦。\n[釋文-3]：必亦反\n[郭注-4]：詩曰：「有壬有林。」又曰：「文王烝哉。」其餘義皆通見詩、書。\n[釋文-5]：壬，而心反。見，賢遍反，下迄卷終注悉放此。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3846;6429;3;7;80;78;5769;5796;716;3311;839', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2949, '弘;郭;宏;溥;介;純;夏;幠;厖;墳;嘏;丕;奕;洪;誕;戎;駿;假;京;碩;濯;訏;宇;穹;壬;路;淫;甫;景;廢;壯;冢;𥳑;菿;昄;至;將;業;蓆;大', '廣;𡗦;𡗥;嘏;𣞤;濃;峻;嘏;卓;任;嘏;父;京;㚕;僩/橌;𢧤;壯', '弘、廓{郭}[廣]、宏1、溥2、介[𡗦]3、純[𡗥]、夏[嘏]4、幠[𣞤]56、厖78、墳9、嘏10、丕11、奕12、洪、誕1314、戎[濃]、駿[峻]15、假[嘏]16、京、碩、濯[卓]17、訏1819、宇、穹20、壬[任]、路[嘏]、淫、甫[父]、景[京]、廢[㚕]、壯21、冢、𥳑[僩/橌]、菿2223、昄2425、晊{至}[𢧤]2627、將[壯]、業、蓆28，大也。2930', '[釋文-1]：戸萌反\n[釋文-2]：音普\n[釋文-3]：音界\n[釋文-4]：戸雅反\n[音釋-5]：呼。\n[釋文-6]：火吳反，下同\n[音釋-7]：亡江。\n[釋文-8]：亡江反，又亡項反，方言云：「厖，深之大也」\n[釋文-9]：符云反\n[釋文-10]：古雅反\n[釋文-11]：字又作㔻，同，普悲反\n[釋文-12]：以昔反\n[音釋-13]：但。\n[釋文-14]：音但\n[釋文-15]：子浚反，又荀閏反\n[釋文-16]：古雅反\n[釋文-17]：直角反\n[音釋-18]：吁。\n[釋文-19]：本又作盱，同，香于反\n[釋文-20]：起弓反\n[釋文-21]：側狀反\n[音釋-22]：罩。\n[釋文-23]：郭陟孝反，顧野王都角反，說文云：「草大也。」孫都耗反\n[音釋-24]：蒲板。\n[釋文-25]：沈旋蒲板反，此依詩讀也，孫、郭方滿反，字林方但、方旦二反，施乾蒲滿反，顧音板，又普姦、普練二反\n[音釋-26]：之日。\n[釋文-27]：舊音之日反，本又作至，又作胵，顧音充尸反\n[釋文-28]：音席\n[郭注-29]：詩曰：「我受命溥將。」又曰：「亂如此幠。」「爲下國駿厖。」「湯孫奏假。」「王公伊濯。」「訏謨定命。」「有壬有林。」「厥聲載路。」「旣有淫威。」「廢爲殘賊。」「爾土宇昄章。」「緇衣之蓆兮。」廓落、宇宙、穹隆、至極，亦爲大也。菿，義未聞。尸子曰：「此皆大，有十餘名而同一實。」\n[釋文-30]：謨，亡乎反。', '釋詁弟一', '爾雅', '2;5;6;7;8;16;17;18;21;25;26;28;29;30;33;36;37', DEFAULT, DEFAULT, '8493;4150;4567;7131;714;8521;3371;4898;5995;9141;1463;4;6667;7133;1645;8377;6168;5074;3329;5628;7419;1671;4564;4670;9762;1410;7221;2083;4211;5961;228;5813;2892;569;4239;7706;1983;1750;578;6596', '5938;6608;6611;1463;3847;7331;5876;1463;5222;5087;1463;1908;3329;6610;5007/0/3584;6604;228', 'null', 'null');
