INSERT INTO `t_han_nga` VALUES (2951, '迄;臻;極;到;赴;來;弔;艐;格;戾;懷;摧;詹;至', '𨑩;徦;瞻', '迄1、臻2、極3、到、赴、來、弔[𨑩]45、艐67、格[徦]8、戾9、懷、摧1011、詹[瞻]12，至也。1314', '[釋文-1]：許訖反\n[釋文-2]：側巾反\n[音釋-3]：紀力\n[音釋-4]：的。\n[釋文-5]：如字，又音的。\n[音釋-6]：宗。\n[釋文-7]：郭音届，孫云：「古届字。」顧子公反\n[釋文-8]：更伯反，字或作𢓜\n[釋文-9]：力帝反\n[音釋-10]：昨雷。\n[釋文-11]：昨雷反，又祖雷反\n[釋文-12]：音占\n[郭注-13]：齊 楚之會郊曰懷，宋曰届。詩曰：「先祖于摧。」又曰：「六日不詹。」詹、摧，皆楚語，方言云。\n[釋文-14]：屆，音界。', '釋詁弟一', '爾雅', '7;9;13', DEFAULT, DEFAULT, '1216;7708;3624;7707;981;3346;5183;5443;3601;6354;6762;7851;713;7706', '1208;1248;2157', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2952, '如;適;之;嫁;徂;逝;往', '', '如、適1、之、嫁、徂、逝，往也。2', '[釋文-1]：傷亦反\n[郭注-2]：方言云：「自家而出謂之嫁，猶女出爲嫁。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8243;1109;3859;8125;1106;1105;1229', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2953, '賚;貢;錫;畀;予;貺;賜', '賜', '賚1、貢2、錫[賜]、畀34、予56、貺7，賜也。8', '[釋文-1]：力代反，又力臺反\n[釋文-2]：字或作贛，同\n[音釋-3]：必寐。\n[釋文-4]：必寐反，下及注同\n[音釋-5]：羊汝。\n[釋文-6]：羊汝反，下同\n[釋文-7]：許誑反，本或作況\n[郭注-8]：皆賜與也。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '3957;3946;9264;3023;2509;3993;3959', '3959', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2954, '儀;若;祥;淑;鮮;省;臧;嘉;令;類;綝;彀;攻;穀;介;徽;善', '誼;俶;鱻;鱻;靈;闖/琳;工;价;美', '儀[誼]、若、祥、淑[俶]1、鮮[鱻]23、省[鱻]45、臧6、嘉、令[靈]78、類、綝[闖/琳]910、彀1112、攻[工]、穀、介[价]、徽[美]13，善也。1415', '[釋文-1]：市六反\n[音釋-2]：息淺。\n[釋文-3]：息淺反，又音仙，本或作𧬊，沈云：「古斯字。」郭音義云：「本或作尠，非古斯字。」案字書：𧬊，先奚反，亦訓善\n[音釋-4]：先郢。\n[釋文-5]：先郢反\n[釋文-6]：子郎反\n[音釋-7]：力政。\n[釋文-8]：力政反\n[音釋-9]：勑金。\n[釋文-10]：勑金反，郭勑淫反\n[音釋-11]：古豆。\n[釋文-12]：古豆反，一音古侯反\n[釋文-13]：音暉\n[郭注-14]：詩曰：「儀刑文王。」左傳曰：「禁禦不若。」詩曰：「永錫爾類。」「我車旣攻。」「介人維藩。」「大姒嗣徽音。」省、綝、彀，未詳其義。餘皆常語。\n[釋文-15]：藩，方元反。大，音泰。', '釋詁弟一', '爾雅', '1;4;5;6;9;11;13;15;16', DEFAULT, DEFAULT, '5082;599;18;7197;7646;2219;1955;3081;5777;6369;8585;8498;2052;4483;714;8689;1736', '1557;5015;7680;7680;206;7786/105;3029;5105;2355', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2955, '舒;業;順;敘;舒;業;順;敘;緒', '', '舒、業、順，敘也。1舒、業、順、敘，緒也。2', '[郭注-1]：皆謂次敘。\n[郭注-2]：四者又爲端緒。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2510;1750;5652;2065;2510;1750;5652;2065;8519', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2956, '怡;懌;悅;欣;衎;喜;愉;豫;愷;康;冘;般;樂;悅;懌;愉;釋;賓;協;服', '媅;昪;𠬝', '怡1、懌2、悅、欣、衎3、喜、愉4、豫、愷5、康、妉{冘}[媅]67、般[昪]89，樂也1011。12悅、懌、愉、釋、賓、協13，服[𠬝]也。14', '[釋文-1]：以之反\n[釋文-2]：音亦\n[釋文-3]：苦旦反\n[釋文-4]：羊朱反\n[釋文-5]：苦在反\n[音釋-6]：丁含。\n[釋文-7]：丁含反\n[音釋-8]：盤。\n[釋文-9]：蒲安反\n[音釋-10]：洛。\n[釋文-11]：音洛\n[郭注-12]：皆見詩。\n[釋文-13]：胡頰反\n[郭注-14]：皆謂喜而服從。', '釋詁弟一', '爾雅', '11;12;20', DEFAULT, DEFAULT, '6744;6978;1546;5536;1274;3074;6825;6134;3093;4457;3324;5446;3762;1546;6978;6825;724;3966;9258;5447', '8235;4236;1921', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2957, '遹;遵;率;循;由;從;自;遹;遵;率;循', '述;述', '遹12、遵、率[述]3、循4、由、從，自也。5遹、遵、率[述]，循也。67', '[音釋-1]：聿。\n[釋文-2]：孫云：「古述字，讀聿。」一音餘橘反\n[釋文-3]：所律反\n[釋文-4]：音旬\n[郭注-5]：自猶從也。\n[郭注-6]：三者又爲循行。\n[釋文-7]：行，下孟反。', '釋詁弟一', '爾雅', '3;10', DEFAULT, DEFAULT, '1157;1108;8783;1233;8514;5225;2223;1157;1108;8783;1233', '1107;1107', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2958, '靖;惟;漠;圖;詢;度;咨;諏;究;如;慮;謨;猷;肈;基;訪;謀', '謨;茹;𠧙;諅', '靖1、惟、漠[謨]2、圖、詢、度34、咨、諏56、究7、如[茹]、慮、謨8、猷9、肈[𠧙]10、基[諅]11、訪，謀也12。1314', '[釋文-1]：音靜\n[釋文-2]：孫音莫，舍人云：「心之謨也」\n[音釋-3]：鐸。\n[釋文-4]：徒洛反，注同\n[音釋-5]：子須。\n[釋文-6]：子須反\n[釋文-7]：九又反\n[釋文-8]：亡胡反\n[釋文-9]：音猶\n[釋文-10]：音趙\n[釋文-11]：本或作𧫠，音同\n[釋文-12]：莫浮反\n[郭注-13]：國語曰：「詢于八虞，咨于二虢，度于閎夭，謀于南宮，諏于蔡、原，訪于辛、尹。」通謂謀議耳。如、肈，所未詳。餘皆見詩。\n[釋文-14]：虢，古伯反。原，音元。閎夭，音宏，下於兆反。', '釋詁弟一', '爾雅', '3;10;14;15', DEFAULT, DEFAULT, '6684;6761;7130;3913;1726;1929;841;1511;4671;8243;6702;1509;6373;8374;9044;1510;1508', '1509;614;2080;1642', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2959, '典;彝;法;則;㓝;範;矩;庸;恆;律;戛;職;秩;常;柯;憲;㓝;範;辟;律;矩;則;法', '陳;笵;楷;豑;儀;笵', '典[陳]、彝12、法、則、㓝、範[笵]3、矩、庸、恆、律、戛[楷]4、職5、秩[豑]6，常也。7柯[儀]8、憲、㓝、範[笵]、辟910、律、矩、則，法也。1112', '[音釋-1]：夷。\n[釋文-2]：以而反\n[釋文-3]：音犯，字或作范，同\n[釋文-4]：居黠反，郭苦八反\n[釋文-5]：之力反\n[釋文-6]：長栗反\n[郭注-7]：庸、戛、職、秩，義見詩、書。餘皆謂常法耳。\n[釋文-8]：古河反\n[音釋-9]：婢亦。\n[釋文-10]：婢亦反，下同\n[郭注-11]：詩曰：「伐柯伐柯，其則不遠。」論語曰：「不踰矩。」\n[釋文-12]：踰，以朱反。矩，俱宇反。', '釋詁弟一', '爾雅', '1;6;11;13;15;18', DEFAULT, DEFAULT, '3022;8763;6260;2755;3193;9572;3032;2084;9015;1257;8376;7805;4466;4870;3744;6721;3193;9572;5796;1257;3032;2755;6260', '9686;2896;3427;3102;5082;2896', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2960, '辜;辟;戾;辠', '', '辜12、辟、戾，辠也34。5', '[音釋-1]：孤。\n[釋文-2]：古胡反\n[音釋-3]：罪。\n[釋文-4]：古罪字，秦始皇以其字似皇字，改從四，非\n[郭注-5]：皆㓝罪。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9756;5796;6354;9755', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2961, '黃髮;齯齒;鮐背;耇;老;壽', '', '黃髮、齯齒12、鮐背34、耇56、老，壽也7。89', '[音釋-1]：倪。\n[釋文-2]：兒，本今皆作齯，五兮反，一音如字\n[音釋-3]：台。\n[釋文-4]：天才反，一音夷。博內反\n[音釋-5]：狗。\n[釋文-6]：音苟\n[釋文-7]：𦓆，音壽，本又作壽\n[郭注-8]：黃髮，髮落更生黃者。齯齒，齒墮更生細者。鮐背，背皮如鮐魚。耇猶耆也。皆壽考之通稱。\n[釋文-9]：更，古孟反。隋，徒火反，又作墮，同。細，先計反。耆，巨伊反。稱，尺證反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9201/5728;1297/1278;7650/2616;5379;5375;5382', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2962, '允;孚;亶;展;諶;誠;亮;詢;信;展;諶;允;愼;亶;誠', '坦;諒;恂;坦', '允1、孚2、亶[坦]34、展、諶56、誠、亮[諒]、詢[恂]，信也。78展、諶、允、愼、亶[坦]，誠也。9', '[釋文-1]：音尹\n[釋文-2]：音敷\n[音釋-3]：丁但。\n[釋文-4]：丁但反\n[音釋-5]：市林。\n[釋文-6]：甚針反\n[郭注-7]：方言曰：「荆 吳 淮 汭之閒曰展，燕 岱 東齊曰諶，宋 衞曰詢。」亦皆見詩。\n[釋文-8]：汭，仁銳反。岱，音待。\n[郭注-9]：轉相訓也。詩曰：「愼爾優遊。」', '釋詁弟一', '爾雅', '3;7;8;14', DEFAULT, DEFAULT, '5458;1881;3342;5405;1523;1526;5461;1726;1524;5405;1523;5458;6712;3342;1526', '9070;1481;6759;9070', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2963, '戲謔', '', '「謔浪𥬇敖12」，戲謔也3。45', '[音釋-1]：五報。\n[釋文-2]：許虐反。蘇誚反。五報反\n[釋文-3]：虛寄反\n[郭注-4]：謂調戲也，見詩。\n[釋文-5]：調，徒弔反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8383/1647', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2964, '粤;于;爰;曰;爰;粤;于;爰;粤;于;那;都;繇;於', '', '粤12、于、爰3，曰也。45爰、粤，于也。6爰、粤、于、那7、都、繇89，於也1011。1213', '[音釋-1]：曰。\n[釋文-2]：音越\n[釋文-3]：音袁\n[郭注-4]：書曰：「土爰稼穡。」詩曰：「對越在天。」「王于出征。」\n[釋文-5]：稼，古乍反。穡，音色。征，之成反。\n[郭注-6]：轉相訓。\n[釋文-7]：乃河反\n[音釋-8]：由。\n[釋文-9]：除又反，注同，孫音由，又音遥\n[音釋-10]：烏。\n[釋文-11]：音烏，注同\n[郭注-12]：左傳曰：「棄甲則那。」那猶今人云那那也。書曰：「臯陶曰：都。」繇，辭。於乎。皆語之韻絶。\n[釋文-13]：陶，音遥。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3071;3069;2516;3044;2516;3071;3069;2516;3071;3069;4108;4005;8514;2487', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2965, '㪉;郃;盍;翕;仇;偶;妃;匹;會;合;仇;讎;敵;妃;儀;匹;妃;合;會;對;妃;媲', '雔;雔;雔', '㪉12、郃34、盍56、翕7、仇[雔]8、偶9、妃1011、匹、會，合也。12仇[雔]、讎[雔]13、敵、妃、知、儀，匹也。1415妃、合、會，對也。16妃，媲也1718。19', '[音釋-1]：閤。\n[釋文-2]：古荅反\n[音釋-3]：合。\n[釋文-4]：音合，又音洽\n[音釋-5]：胡臘。\n[釋文-6]：胡獵反\n[釋文-7]：許急反\n[釋文-8]：音求\n[釋文-9]：五口反\n[音釋-10]：配。\n[釋文-11]：音配，又芳非反，下同\n[郭注-12]：皆謂對合也。\n[釋文-13]：市周反\n[郭注-14]：詩云：「君子好仇。」「樂子之無知。」「實維我儀。」國語亦云：「丹朱馮身以儀之。」讎猶儔也。廣雅云：「讎，輩也。」\n[釋文-15]：樂，音洛。馮，皮氷反。\n[郭注-16]：皆相當對。\n[音釋-17]：普計。\n[釋文-18]：普計反，郭音譬，字林匹地反\n[郭注-19]：相偶媲也。', '釋詁弟一', '爾雅', '5;11;12', DEFAULT, DEFAULT, '2027;4029;3180;2257;5171;5182;8131;8422;3274;3269;5171;1488;2029;8131;5082;8422;8131;3269;3274;1752;8131;8132', '2362;2362;2362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2966, '紹;胤;嗣;續;纂;緌;績;武;係;繼', '纘', '紹1、胤2、嗣、續、纂[纘]34、緌5、績6、武、係7，繼也8。9', '[釋文-1]：市小反\n[釋文-2]：羊忍反，又以刃反\n[音釋-3]：子管。\n[釋文-4]：子管反\n[釋文-5]：汝誰反\n[釋文-6]：子狄反\n[釋文-7]：戸帝反\n[釋文-8]：音計\n[郭注-9]：詩曰：「下武維同。」緌，見釋水。餘皆常語。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '8550;2644;1434;8548;8650;8642;8733;8395;5164;8547', '8549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2967, '忥;侐;侐;蟄;愼;貉;謐;顗;頠;密;寧;靜', '訖;嗼;宓;寍', '忥[訖]12、謚{侐}34、溢{侐}5、蟄6、愼、貉[嗼]78、謐910、顗1112、頠1314、密[宓]、寧[寍]，靜也。15', '[音釋-1]：戲。\n[釋文-2]：本或作氣，同，許氣反\n[音釋-3]：侍。\n[釋文-4]：時至反\n[釋文-5]：以日反\n[釋文-6]：直立反\n[音釋-7]：陌。\n[釋文-8]：莫白反，又武博反\n[音釋-9]：密。\n[釋文-10]：彌畢反\n[音釋-11]：擬。\n[釋文-12]：魚豈反，又五愷反\n[音釋-13]：魚毀。\n[釋文-14]：魚毁反，沈五罪反，孫、郭五果反\n[郭注-15]：忥、顗、頠，未聞其義。餘皆見詩傳。', '釋詁弟一', '爾雅', '1;6;10;11', DEFAULT, DEFAULT, '6868;5055;5055;8910;6712;6129;1555;5668;5646;5874;3056;3189', '1578;941;4577;4573', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2968, '隕;磒;湮;下;降;墜;摽;蘦;落', '𠬪;零', '隕12、磒34、湮5、下、降6、墜7、摽[𠬪]89、蘦[零]1011，落也。1213', '[音釋-1]：于閔。\n[釋文-2]：于閔反\n[音釋-3]：于敏。\n[釋文-4]：于敏反，石落也\n[釋文-5]：郭音因，又音烟，又音翳\n[釋文-6]：古巷反\n[釋文-7]：本又作隊，同，直類反\n[音釋-8]：婢眇。\n[釋文-9]：婢眇反，又普交、符表二反\n[音釋-10]：零。\n[釋文-11]：力丁反，字或作苓，說文云：「草曰苓，木曰落」\n[郭注-12]：磒猶隕也，方俗語有輕重耳。湮，沈落也。摽、蘦，見詩。\n[釋文-13]：沈，直今反。', '釋詁弟一', '爾雅', '7;8', DEFAULT, DEFAULT, '9648;6023;7295;9;9647;9160;7929;465;549', '2515;7538', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2969, '命;令;禧;畛;祈;請;謁;訊;誥;告', '視;誥', '命、令12、禧3、畛[視]45、祈、請、謁、訊6、誥7，告[誥]也89。10', '[音釋-1]：力政。\n[釋文-2]：力政反，說文云：「發號也」\n[釋文-3]：許其反\n[音釋-4]：稹。\n[釋文-5]：之忍反\n[釋文-6]：誶，沈音粹，郭音碎，告也，本作訊，音信\n[釋文-7]：羔報反，又古酷反\n[音釋-8]：谷。\n[釋文-9]：古篤反\n[郭注-10]：禧，未聞。禮記曰：「畛於鬼神。」', '釋詁弟一', '爾雅', '4;10', DEFAULT, DEFAULT, '840;5777;13;9188;50;1483;1484;1519;1529;778', '5475;1529', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2970, '永;悠;迥;違;遐;逷;闊;遠;永;悠;迥;遠;遐', '攸;攸', '永、悠[攸]1、迥23、違、遐、逷45、闊6，遠也。7永、悠[攸]、迥、遠，遐也。8', '[釋文-1]：音由\n[音釋-2]：戸頂。\n[釋文-3]：戸頂反\n[音釋-4]：惕。\n[釋文-5]：說文云：「古逖字。」他歷反，郭湯革反\n[釋文-6]：苦活反\n[郭注-7]：書曰：「逷矣！西土之人。」\n[郭注-8]：遐亦遠也，轉相訓。', '釋詁弟一', '爾雅', '2;10', DEFAULT, DEFAULT, '7495;6927;1200;1159;1215;1199;7784;1198;7495;6927;1200;1198;1215', '2034;2034', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2971, '虧;壞;圮;垝;毀', '', '虧1、壞23、圮45、垝67，毀也。89', '[釋文-1]：字又作𧇾，袪危反，郭又許宜反\n[音釋-2]：怪。\n[釋文-3]：音怪，說文云：「敗也。籀文作𣀩。」字林云：「壞，自敗也，下怪反。」「𣀩，毁也，公壞反」\n[音釋-4]：房美。\n[釋文-5]：孫房美反，「岸毁也」\n[音釋-6]：鬼。\n[釋文-7]：古委反\n[郭注-8]：書曰：「方命圮族。」詩曰：「乗彼垝垣。」虧，通語耳。\n[釋文-9]：垣，音袁。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3070;9118;9110;9109;9116', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2972, '矢;雉;引;延;順;薦;劉;繹;尸;旅;陳', '荐', '矢1、雉、引、延、順、薦[荐]、劉、繹2、尸、旅3，陳也。4', '[釋文-1]：𡱁，本作矢，同，失耳反\n[釋文-2]：音亦\n[釋文-3]：音呂\n[郭注-4]：禮記曰：「尸，陳也。」雉、順、劉，皆未詳。', '釋詁弟一', '爾雅', '6', DEFAULT, DEFAULT, '3307;2288;8491;1265;5652;6259;9444;8518;5400;4309;9686', '580', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2973, '尸;職;主;尸;寀;寀;寮;官', '勞', '尸、職，主也。1尸，寀也23。4寀、寮[勞]5，官也。6', '[郭注-1]：左傳曰：「殺老牛，莫之敢尸。」詩曰：「誰其尸之。」又曰：「職爲亂階。」\n[音釋-2]：七代。\n[釋文-3]：李、孫、郭並七代反，下句及注同，樊七在反\n[郭注-4]：謂寀地。\n[釋文-5]：字又作僚，同，力彫反\n[郭注-6]：官地爲寀，同官爲寮。', '釋詁弟一', '爾雅', '7', DEFAULT, DEFAULT, '5400;7805;3183;5400;4627;4627;4641;9616', '9234', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2974, '績;緒;采;業;宜;貫;公;事', '遦', '績、緒、采1、業、服2、宜、貫[遦]3、公，事也。4', '[釋文-1]：七在反\n[釋文-2]：本或作𦩟，同，符福反，又作般字\n[釋文-3]：古玩反\n[郭注-4]：論語曰：「仍舊貫。」餘皆見詩、書。', '釋詁弟一', '爾雅', '6', DEFAULT, DEFAULT, '8733;8519;3792;1750;4597;4339;716;1934', '1111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2975, '永;羕;引;延;融;駿;長', '演;峻', '永、羕12、引[演]3、延、融、駿[峻]4，長也5。6', '[音釋-1]：㨾。\n[釋文-2]：羊讓反，字從永，說文云：「水長也」\n[釋文-3]：以忍反\n[釋文-4]：本或作俊，又作峻，同，子閏反\n[釋文-5]：直良反\n[郭注-6]：宋 衞 荆 吳之閒曰融。羕，所未詳。', '釋詁弟一', '爾雅', '3;6', DEFAULT, DEFAULT, '7495;7496;8491;1265;1863;6168;6067', '7146;5876', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2976, '喬;嵩;崇;高;崇;充', '', '喬1、嵩2、崇，高也。3崇，充也。4', '[釋文-1]：郭音橋，或音驕\n[釋文-2]：宿忠反\n[郭注-3]：皆高大貌。左傳曰：「師叔，楚之崇也。」\n[郭注-4]：亦爲充盛。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6622;5909;5880;3318;5880;5460', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2977, '犯;奢;果;毅;克;捷;功;肩;堪;勝;勝;肩;戡;劉;殺;克;劉;𤣐;斬;刺;殺', '', '犯、奢、果1、毅2、剋{克}、捷3、功、肩4、堪5，勝也6。78勝、肩、戡9、劉、殺，克也。10劉、獮{𤣐}1112、斬、刺1314，殺也。1516', '[釋文-1]：惈，音果，本今作果\n[音釋-2]：義\n[釋文-3]：才接反\n[釋文-4]：音堅\n[釋文-5]：字又作戡，同，苦含反\n[釋文-6]：尸證反，注及下同\n[郭注-7]：陵犯、誇奢、果毅，皆得勝也。左傳曰：「殺敵爲果。」肩卽剋耳。書曰：「西伯堪黎。」\n[釋文-8]：夸，口花反，或作誇，非。𨛫，郎兮反，國名，本作黎。殺，所黠反，舊所例反，說文云：「戮也。」\n[音釋-9]：堪\n[郭注-10]：轉相訓耳。公羊傳曰：「克之者何？殺之也。」\n[音釋-11]：尠。\n[釋文-12]：息淺反，說文或作𤣐\n[音釋-13]：次。\n[釋文-14]：七賜反，說文云：「君殺大夫曰刺。」刺，直傷也。周禮「司刺」掌三刺之法，以聽獄訟，或七亦反\n[郭注-15]：書曰：「咸劉厥敵。」秋獵爲獮，應殺氣也。公羊傳曰：「刺之者何？殺之也。」\n[釋文-16]：應，應對之應。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6341;6651;3566;1975;4412;8087;9212;2623;9052;9227;9227;2623;8390;9444;1979;4412;9444;9608;2805;1979', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2978, '釁釁;愐沒;敦;勖;釗;茂;劭;愐;勉', '忞忞;邵;懋', '亹亹{釁釁}[忞忞]12、蠠沒{愐沒}34、孟、敦5、勖6、釗[邵]78、茂[懋]9、劭1011、勔{愐}1213，勉也。1415', '[音釋-1]：尾。\n[釋文-2]：字或作斖，同，亡匪反\n[音釋-3]：蜜。\n[釋文-4]：彌畢反，又亡忍反，本或作𧖅，說文曰：「𧖅，古蜜字。」\n[釋文-5]：𣀦，丁門反，本今作敦\n[釋文-6]：許玉反\n[音釋-7]：招。\n[釋文-8]：古堯反，又之遥反\n[釋文-9]：字又作懋，亦作𢘅，同，亡候反\n[音釋-10]：邵。\n[釋文-11]：上照反，或上遥反\n[音釋-12]：泯。\n[釋文-13]：字本作僶，又作黽，或音泯，又彌兖反\n[郭注-14]：詩曰：「亹亹文王。」蠠沒猶黽勉。書曰：「茂哉茂哉。」方言云：「周 鄭之閒相勸勉爲勔釗。」孟，未聞。\n[釋文-15]：僶，字又作黽，亡忍反，又亡衍反。茂哉，或作茂才。', '釋詁弟一', '爾雅', '1;5;6', DEFAULT, DEFAULT, '1790/1790;6788/7297;2039;9222;2794;522;9221;6788;9220', '6786/6786;4049;6790', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2979, '騖;務;昏;暋;強', '敃/忞;敃/忞;彊', '騖12、務、昏[敃/忞]、暋[敃/忞]34，強[彊]也56。7', '[音釋-1]：務。\n[釋文-2]：音務\n[音釋-3]：閔。\n[釋文-4]：音閔，或作旻\n[音釋-5]：其丈。\n[釋文-6]：其丈反，注同\n[郭注-7]：馳騖、事務，皆自勉強。書曰：「不昏作勞。」「暋不畏死。」', '釋詁弟一', '爾雅', '3;4;5', DEFAULT, DEFAULT, '6209;9217;4221;8824', '2000/6786;2000/6786;8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2980, '卬;吾;台;予;朕;身;甫;余;言;我;朕;余;躬;身;台;朕;賚;畀;卜;陽;予', '姎;嬯;夫;僕;姎', '卬[姎]12、吾、台[嬯]34、予5、朕、身、甫[夫]、余6、言，我也。78朕、余、躬9，身也。10台、朕、賚、畀1112、卜[僕]、陽[姎]1314，予也1516。1718', '[音釋-1]：五剛。\n[釋文-2]：五剛反\n[音釋-3]：怡，下同。\n[釋文-4]：孫羊而反，下同\n[釋文-5]：羊如反\n[釋文-6]：羊如反\n[郭注-7]：卬猶姎也，語之轉耳。書曰：「非台小子。」古者貴賤皆自稱朕。禮記云：「授政任功，曰『予一人』，畛於鬼神，曰『有某甫』。」言，見詩。\n[釋文-8]：姎，烏郎、烏黨、烏浪三反，說文云：「女人稱我曰姎。」任，而鴆反。畛，之忍反。某，音𠭇。\n[釋文-9]：音弓\n[郭注-10]：今人亦自呼爲身。\n[音釋-11]：必二。\n[釋文-12]：必二反\n[音釋-13]：賜。\n[釋文-14]：音賜，又如字，本或作賜\n[音釋-15]：與。\n[釋文-16]：羊汝反\n[郭注-17]：賚、卜、畀，皆賜與也。與猶予也，因通其名耳。魯詩云：「陽如之何。」今巴 濮之人自呼「阿陽」。\n[釋文-18]：巴，伯家反。濮，音卜。', '釋詁弟一', '爾雅', '1;3;7;19;20', DEFAULT, DEFAULT, '5221;837;868;2509;5444;5248;2083;718;1475;8401;5444;718;4631;5248;868;5444;3957;3023;2074;9622;2509', '8307;8316;6674;1754;8307', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2981, '肅;延;誘;薦;炎;晉;寅;盡;進;羞;餞;迪;烝;進', '羞;荐;導;引;進', '肅[羞]、延、誘1、薦[荐]、餤{炎}[導]23、晉4、寅[引]、藎{盡}[進]56，進也。7羞、餞8、迪9、烝10，進也。11', '[釋文-1]：余九反\n[音釋-2]：談。\n[釋文-3]：沈大甘反，徐仙民詩音閻，餘占反，郭持鹽反\n[釋文-4]：本又作㬜\n[音釋-5]：燼。\n[釋文-6]：本又作燼，同，徐刃反\n[郭注-7]：禮記曰：「主人肅客。」詩曰：「亂是用餤。」「王之藎臣。」易曰：「晉，進也。」寅，未詳。\n[釋文-8]：徂淺反\n[釋文-9]：大的反\n[釋文-10]：之仍反\n[郭注-11]：皆見詩、禮。', '釋詁弟一', '爾雅', '1;4;5;7;8', DEFAULT, DEFAULT, '1939;1265;5845;6259;6532;4203;9791;3156;1113;9790;3247;1132;6429;1113', '9790;580;1987;8491;1113', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2982, '詔;亮;左;右;相;導;詔;相;導;左;右;助;𠣊;亮;介;尚;右;左;右;亮', '召;𢪇;召;𢪇;上;𢪇', '詔[召]、亮[𢪇]、左、右1、相23，導也4。5詔[召]、相、導、左、右、助，勴{𠣊}也67。8亮[𢪇]、介9、尚[上]，右也。10左、右，亮[𢪇]也。1112', '[釋文-1]：音佐佑，下同\n[音釋-2]：息亮。\n[釋文-3]：息亮反，下文同\n[釋文-4]：道，徒報反，本或作導，注及下同\n[郭注-5]：皆謂敎導之。\n[音釋-6]：慮。\n[釋文-7]：力庶反\n[郭注-8]：勴謂贊勉。\n[釋文-9]：音界\n[郭注-10]：紹介、勸尚，皆相佑助。\n[郭注-11]：反覆相訓，以盡其義。\n[釋文-12]：覆，芳服反。', '釋詁弟一', '爾雅', '1;2;7;14;16;20', DEFAULT, DEFAULT, '1530;5461;3027;874;2162;1987;1530;2162;1987;3027;874;9213;9214;5461;714;711;874;3027;874;5461', '842;7855;842;7855;6;7855', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2983, '緝熙;烈;顯;昭;晧;熲;光', '㬎熙', '緝熙[㬎熙]1、烈、顯、昭、晧2、熲34，光也。5', '[釋文-1]：七入反。許其反\n[釋文-2]：胡老反\n[音釋-3]：古迥。\n[釋文-4]：古迥反\n[郭注-5]：詩曰：「學有緝熙于光明。」又曰：「休有烈光。」', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '8731/6525;6425;5690;4197;4212;6437;6509', '4245/6525', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2984, '劼;鞏;堅;篤;掔;虔;膠;固', '硈;䈞;艮', '劼[硈]12、鞏3、堅、篤[䈞]4、掔56、虔[艮]7、膠8，固也。9', '[音釋-1]：苦黠。\n[釋文-2]：苦黠反，郭苦八反，或作硈字，古黠反\n[釋文-3]：九勇反\n[釋文-4]：丁毒反\n[音釋-5]：牽。\n[釋文-6]：音牽，又卻閑反，郭音義本與「慳惜物」同\n[釋文-7]：音乾\n[釋文-8]：音交\n[郭注-9]：劼、虔，皆見詩、書。易曰：「鞏用黃牛之革。」「固志也。」掔然亦牢固之意。', '釋詁弟一', '爾雅', '1;4;6', DEFAULT, DEFAULT, '9216;1801;1951;6196;7953;3111;2728;3927', '6029;3333;5223', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2985, '疇;孰;誰', '𠷎;𠷎', '疇[𠷎]12、孰[𠷎]，誰也。34', '[音釋-1]：直留。\n[釋文-2]：本又作𠷎，直留反\n[郭注-3]：易曰：「疇離祉。」\n[釋文-4]：祉，音恥。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '9173;1886;1706', '879;879', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2986, '暀暀;皇皇;𢤧𢤧;穆穆;休;嘉;珍;偉;懿;鑠;美', '㣎㣎;好;覣', '暀暀12、皇皇、藐藐{𢤧𢤧}34、穆穆[㣎㣎]、休[好]、嘉、珍、褘{偉}[覣]56、懿7、鑠8，美也。910', '[音釋-1]：旺。\n[釋文-2]：于況反，又于𣓈反\n[音釋-3]：邈。\n[釋文-4]：亡角反\n[音釋-5]：衣。\n[釋文-6]：於宜反\n[釋文-7]：音意\n[釋文-8]：舒灼反\n[郭注-9]：自「穆穆」已上，皆美盛之貌。其餘常語。\n[釋文-10]：上，時掌反。盛，或作晠，同。', '釋詁弟一', '爾雅', '4;5;8', DEFAULT, DEFAULT, '4238/4238;80/80;6715/6715;4428/4428;3816;3081;148;4980;6643;9279;2355', '5718/5718;8189;5477', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2987, '諧;輯;協;和;關關;邕邕;勰;燮', '饔饔', '諧1、輯23、協，和也。4關關、噰噰{邕邕}[饔饔]56，音聲和也。7勰89、燮10，和也。11', '[釋文-1]：下階反\n[音釋-2]：集。\n[釋文-3]：音集，又七入反\n[郭注-4]：書曰：「八音克諧。」左傳曰：「百姓輯睦。」\n[音釋-5]：於恭。\n[釋文-6]：於恭反\n[郭注-7]：皆鳥鳴相和。\n[音釋-8]：恊。\n[釋文-9]：本又作協，同，戸牒反\n[釋文-10]：蘇頰反\n[郭注-11]：書曰：「燮友柔克。」', '釋詁弟一', '爾雅', '6', DEFAULT, DEFAULT, '1548;9525;9258;846;7771/7771;7487/7487;1910', '3208/3208', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2988, '從;申;神;加;弼;崇;重', '陳;陳', '從、申[陳]、神[陳]、加、弼1、崇，重也23。45', '[釋文-1]：白筆反\n[音釋-2]：直龍。\n[釋文-3]：直龍反，注同\n[郭注-4]：隨從、弼輔、增崇，皆所以爲重疊。神，所未詳。\n[釋文-5]：疊，音牒。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '5225;9800;25;9241;8506;5880;5242', '9686;9686', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2989, '觳;悉;卒;泯;忽;滅;罄;空;畢;罊;殲;拔;殄;盡', '㱿;𣨛;欻;㪤;撥', '觳[㱿]12、悉、卒[𣨛]3、泯4、忽[欻]、滅、罄5、空、畢[㪤]、罊67、殱{殲}89、拔[撥]10、殄11，盡也。1213', '[音釋-1]：學。\n[釋文-2]：胡角反，又胡谷反\n[釋文-3]：子恤反，字或作𣨛\n[釋文-4]：亡忍反\n[釋文-5]：苦定反\n[音釋-6]：苦計。\n[釋文-7]：苦計反，說文口地反，云：「器中盡也。」本或作𢢞字，音同。廣雅云：「𢢞，劇也」\n[音釋-8]：尖。\n[釋文-9]：子廉反\n[釋文-10]：步八反\n[釋文-11]：大典反\n[郭注-12]：觳，今直語耳。忽然，盡貌。今江東呼厭極爲罊。餘皆見詩。\n[釋文-13]：厭，一豔反。', '釋詁弟一', '爾雅', '1;3;5;9;12', DEFAULT, DEFAULT, '2863;723;5359;7452;6843;7441;3303;4646;2491;3304;2552;7995;2551;3156', '1963;2534;5549;2047;7978', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2990, '苞;蕪;茂;豐', '葆/飽;𣞤', '苞[葆/飽]1、蕪[𣞤]2、茂，豐也3。4', '[釋文-1]：百交反\n[釋文-2]：音無，或亡甫反，蕃滋生長也，古本作隷\n[釋文-3]：敷馮反\n[郭注-4]：苞叢、繁蕪，皆豐盛。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '388;544;522;3103', '674/3242;3847', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2991, '揫;斂;屈;收;戢;蒐;襃;鳩;樓;聚', '詘;𢯱;勼;摟', '揫12、斂3、屈[詘]4、收、戢5、蒐[𢯱]67、裒{襃}8、鳩[勼]9、樓[摟]10，聚也。1112', '[音釋-1]：子由。\n[釋文-2]：孫子由反，郭音遒。案：遒，音子由、徂秋二反\n[釋文-3]：力儉反\n[釋文-4]：丘勿反\n[釋文-5]：側立反\n[音釋-6]：搜。\n[釋文-7]：所求反\n[釋文-8]：古字作襃，蒲侯反，下同，字本或作捊\n[釋文-9]：居牛反，說文作勼，音九尤反\n[釋文-10]：摟，力侯反，從手。本或作樓，非\n[郭注-11]：禮記曰：「秋之言揫。」「揫，斂也。」「春獵爲蒐。」「蒐者，以其聚人衆也。」詩曰：「屈此羣醜。」「原隰裒矣。」左傳曰：「以鳩其民。」樓，猶今言拘樓，聚也。\n[釋文-12]：隰，音習。拘，古侯反。', '釋詁弟一', '爾雅', '3;6;8;9', DEFAULT, DEFAULT, '3396;2024;5428;2048;8396;383;5301;2375;3651;5236', '1701;8090;5805;7943', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2992, '肅;齊;遄;速;亟;屢;數;迅;疾;寁;駿;肅;亟;遄;速', '齌;䞭', '肅、齊[齌]、遄12、速、亟34、屢5、數67、迅8，疾也。910寁1112、駿[䞭]、肅、亟1314、遄，速也。15', '[音釋-1]：船。\n[釋文-2]：市專反\n[音釋-3]：欺冀。\n[釋文-4]：欺冀反，又居力反\n[釋文-5]：力具反\n[音釋-6]：朔。\n[釋文-7]：色角反\n[釋文-8]：信、峻二音\n[郭注-9]：詩曰：「仲山甫徂齊。」\n[釋文-10]：徂，才孤反。\n[音釋-11]：昝。\n[釋文-12]：本或作疌，同，子感反\n[音釋-13]：居力。\n[釋文-14]：字又作茍，同，居力反。經典亦作棘，同\n[郭注-15]：詩曰：「不寁故也。」駿猶迅。速亦疾也。', '釋詁弟一', '爾雅', '2;11', DEFAULT, DEFAULT, '1939;4393;1120;1121;9014;5423;2011;1122;4694;4605;6168;1939;9014;1120;1121', '6457;1048', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2993, '壑;阬阬;滕;徵;隍;漮;虛', '盅', '壑1、阬阬23、滕[盅]4、徵、隍5、漮67，虛也8。910', '[釋文-1]：本或作㕡，同，許各反，郭胡郭反\n[音釋-2]：坑。\n[釋文-3]：苦衡反\n[釋文-4]：徒登反\n[釋文-5]：音皇，城池也\n[音釋-6]：康。\n[釋文-7]：字又作㱂，同，苦郎反。說文云：「水之空也。」方言作䆲，亦空也。郭云：「本或作荒」，荒亦丘墟之空無\n[釋文-8]：許居反\n[郭注-9]：壑，谿壑也。阬阬，謂阬壍也。隍，城池無水者。方言云：「漮之言空也。」皆謂丘墟耳。滕、徵，未詳。\n[釋文-10]：谿，音溪。壍，七豔反。墟，去魚反。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '9654/9654;7170;5239;9701;7344;5232', '3157', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2994, '黎;庶;烝;多;醜;師;旅;衆;洋;觀;襃;衆;那;多', '雔;讙;葆;夥', '𥠖{黎}、庶、烝1、多、醜[雔]、師、旅，衆也。2洋3、觀[讙]4、裒{襃}[葆]、衆、那[夥]5，多也。6', '[釋文-1]：之仍反\n[郭注-2]：皆見詩。\n[釋文-3]：音羊，洋溢多貌。讀羊者，或爲詳，非也\n[釋文-4]：顧、謝音官，施古喚反，注同\n[釋文-5]：奴多反，本或作𡖔\n[郭注-6]：詩曰：「薄言觀者。」又曰：「受福不那。」洋溢，亦多貌。', '釋詁弟一', '爾雅', '5;10;11;13', DEFAULT, DEFAULT, '4513;5956;6429;4334;5834;3862;4309;5235;7081;5484;5301;5235;4108;4334', '2362;1658;674;4335', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2995, '流;差;柬;擇', '', '流、差12、柬34，擇也。5', '[音釋-1]：叉。\n[釋文-2]：楚佳反\n[音釋-3]：𥳑。\n[釋文-4]：音𥳑\n[郭注-5]：皆選擇，見詩。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '7474;3028;3898;7897', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2996, '戰;栗;震;驚;戁;竦;恐;慴;懼', '', '戰、慄{栗}12、震、驚、戁34、竦5、恐67、慴89，懼也。10', '[音釋-1]：栗。\n[釋文-2]：六日反\n[音釋-3]：女板。\n[釋文-4]：女版反\n[釋文-5]：息勇反\n[音釋-6]：丘勇。\n[釋文-7]：丘勇反\n[音釋-8]：之涉。\n[釋文-9]：之涉反\n[郭注-10]：詩曰：「不戁不竦。」慴卽懾也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8382;4391;7531;6216;6723;6682;6942;6943;6772', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2997, '痡;瘏;虺穨;玄黃;劬勞;咎;顇;瘽;瘉;鰥;戮;鼠;臠;𢟤/悝;痒;疧;疵;閔;逐;疚;悔;瘥;痱;亶;瘵;瘼;齊;病', '齂嘳;悁/悹;𤻲;瘏;愍;疛;癉;膌', '痡12、瘏34、虺頽{虺穨}[齂嘳]56、玄黃、劬勞7、咎8、顇910、瘽1112、瘉1314、鰥[悁/悹]15、戮[𤻲]、癙{鼠}[瘏]1617、癵{臠}1819、㾖{𢟤/悝}2021、痒2223、疧2425、疵26、閔[愍]、逐[疛]、疚27、痗{悔}2829、瘥3031、痱3233、𤺺{亶}[癉]3435、瘵3637、瘼38、癠{齊}[膌]3940，病也。4142', '[音釋-1]：普胡。\n[釋文-2]：普胡、芳膚二反，詩作鋪\n[音釋-3]：徒。\n[釋文-4]：音徒，詩作屠\n[音釋-5]：灰。\n[釋文-6]：虎回反，又呼懷反。徒回反\n[釋文-7]：求于反\n[釋文-8]：音臼\n[音釋-9]：悴。\n[釋文-10]：字或作悴，在醉反\n[音釋-11]：勤。\n[釋文-12]：音勤，字亦作懃\n[音釋-13]：俞。\n[釋文-14]：羊朱反，又羊主反\n[釋文-15]：古頑反，注瘝同\n[音釋-16]：鼠。\n[釋文-17]：傷汝反，舍人云：「癙、癵、㾖、痒，皆心憂憊之病也。」孫炎云：「癙者，畏之病也。」字林音恕，詩作鼠\n[音釋-18]：力專。\n[釋文-19]：郭作拘攣，同，力專反\n[音釋-20]：里。\n[釋文-21]：音里\n[音釋-22]：羊。\n[釋文-23]：音羊\n[音釋-24]：祁。\n[釋文-25]：祈支反，或丁禮反，本作疲，字書云：「疲，病也。」聲類猶以爲疧字，又音支。孫炎云：「滯之病也」\n[釋文-26]：音慈\n[釋文-27]：音救\n[音釋-28]：妹。\n[釋文-29]：音昧，一音晦\n[音釋-30]：伹何。\n[釋文-31]：徂何反，又子衰反\n[音釋-32]：肥。\n[釋文-33]：符非反，又符沸反，說文「蒲愷反，風病也」\n[音釋-34]：亶。\n[釋文-35]：丁但反，又徒丹反，孫炎云：「疫病」\n[音釋-36]：債。\n[釋文-37]：側界反，字林側例反\n[釋文-38]：音莫\n[音釋-39]：徂細。\n[釋文-40]：徂細反，或徂犂反\n[郭注-41]：虺頽、玄黃，皆人病之通名，而說者便謂之馬病，失其義也。詩曰：「生我劬勞。」書曰：「智藏瘝在。」相戮辱，亦可恥病也。今江東呼病曰瘵，東齊曰瘼。禮記曰：「親癠，色容不盛。」戮、逐，未詳。餘皆見詩。\n[釋文-42]：㾯，呼回反，字林云：「病也。」今經注無此字。', '釋詁弟一', '爾雅', '3;10;11;12;18;19;24;27', DEFAULT, DEFAULT, '4696;4710;8800/5473;2506/9201;9250/9234;5173;5685;4700;4793;7593;8389;6391;2654;6930/6849;4717;4779;4708;7785;1178;4612;6887;4791;4734;3342;4701;4703;4393;4695', '2236/830;6873/6768;4788;4710;6906;4728;4770;2655', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2998, '恙;寫;悝;盱;恤;罹;憂', '獡;𢖳;𢝊', '恙1、寫[獡]2、悝34、盱[𢖳]56、繇78、慘9、恤、罹10，憂[𢝊]也。11', '[釋文-1]：羊讓反\n[釋文-2]：悉冶反\n[音釋-3]：里。\n[釋文-4]：音里\n[音釋-5]：吁。\n[釋文-6]：本或作𢖳，同，香于反\n[音釋-7]：遥。\n[釋文-8]：音遥\n[釋文-9]：七感反\n[釋文-10]：力知反\n[郭注-11]：今人云無恙，謂無憂也。寫，有憂者思散寫也。詩曰：「悠悠我悝。」「云何盱矣。」繇役，亦爲憂愁也。', '釋詁弟一', '爾雅', '2;4;7', DEFAULT, DEFAULT, '6917;4598;6849;2134;6799;4849;3366', '6331;6931;6935', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2999, '倫;勩;邛;敕;勤;愉;庸;癉;勞;勞;來;強;事;謂;翦;篲;勤', '掄;攻;輸;勑;習', '倫[掄]、勩12、邛[攻]3、敕4、勤、愉[輸]5、庸、癉67，勞也8。910勞11、來[勑]1213、強1415、事、謂、翦16、篲[習]17，勤也。1819', '[音釋-1]：與世。\n[釋文-2]：字或作𠡫，與世反，郭音謚，字亦作肄\n[釋文-3]：巨凶反\n[釋文-4]：本又作飭，竝恥力反。案說文、字林來旁作力，是勞來之字，束旁作攵，是治，音丑力反\n[釋文-5]：羊主反，又羊朱反\n[音釋-6]：丁賀。\n[釋文-7]：丁賀反，本或作憚，音同\n[音釋-8]：力報\n[郭注-9]：詩曰：「莫知我勩。」「維王之邛。」「哀我癉人。」國語曰：「無功庸者。」倫理事務，以相約敕，亦爲勞。勞苦者多情愉。今字或作窳，同。\n[釋文-10]：窳，羊主反，字林云：「污也，音烏。」說文云：「污窬也。」案：污窬猶污邪也。\n[釋文-11]：力報反，注同\n[音釋-12]：賚。\n[釋文-13]：本又作勑，力代反，本或作賚\n[音釋-14]：其丈。\n[釋文-15]：其丈反，注同\n[釋文-16]：子淺反\n[釋文-17]：本又作彗，同，息遂反，又徂歲反\n[郭注-18]：詩曰：「職勞不來。」自勉強者亦勤力者，由事事，故爲勤也。詩曰：「迨其謂之。」翦、篲，未詳。\n[釋文-19]：迨，音待。', '釋詁弟一', '爾雅', '1;3;6;11;16', DEFAULT, DEFAULT, '5036;9237;4124;2022;9240;6825;2084;4770;9234;9234;3346;8824;1934;1480;2247;1927;9240', '7896;2052;9576;9215;2239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3000, '悠;傷;憂;思;懷;惟;慮;願;念;惄;思', '慯;𢝊', '悠、傷[慯]1、憂[𢝊]，思也23。4懷、惟、慮、願、念、惄56，思也。78', '[釋文-1]：字書作慯，尸羊反\n[音釋-2]：司嗣。\n[釋文-3]：司嗣反，注同\n[郭注-4]：皆感思也。\n[音釋-5]：溺。\n[釋文-6]：乃歷反\n[郭注-7]：詩曰：「惄如調飢。」\n[釋文-8]：調，竹留反，毛詩傳云：「朝也。」', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6927;5156;3366;6701;6762;6761;6702;5633;6719;6803;6701', '6923;6935', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3001, '禄;祉;履;戩;祓;禧;禠;祜;福', '', '禄、祉1、履、戩2、祓34、禧5、禠67、祜8，福也。910', '[釋文-1]：音恥\n[釋文-2]：音翦，又章善反，孫音箭\n[音釋-3]：廢。\n[釋文-4]：音廢，又音拂，又方妹反\n[釋文-5]：許其反\n[音釋-6]：斯。\n[釋文-7]：音斯，郭常支、巨移二反\n[釋文-8]：音戸\n[郭注-9]：詩曰：「福履綏之。」「俾爾戩穀。」「祓禄康矣。」禠、禧，書傳不見，其義未詳。\n[釋文-10]：俾，必爾反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '15;19;5430;8393;49;13;16;11;20', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3002, '禋;祀;祠;蒸;甞;龠;祭', '烝;礿', '禋1、祀2、祠3、蒸[烝]4、甞5、禴{龠}[礿]67，祭也。8', '[釋文-1]：音因，注同\n[釋文-2]：音似\n[釋文-3]：音詞，周春祭名\n[釋文-4]：之升反，冬祭名\n[釋文-5]：字又作常，秋祭名\n[音釋-6]：藥。\n[釋文-7]：字又作礿，同，餘若反，夏祭名\n[郭注-8]：書曰：「禋于六宗。」餘者皆以爲四時祭名也。', '釋詁弟一', '爾雅', '4;6', DEFAULT, DEFAULT, '29;31;41;623;3043;1428;30', '6429;42', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3003, '儼;愙;祗;翼;禋;恭;欽;寅;熯;敬', '趩;趛;夤;戁', '儼1、恪{愙}23、祗4、翼[趩]、諲{禋}56、恭、欽[趛]、寅[夤]、熯[戁]78，敬也。9', '[釋文-1]：魚儉反\n[音釋-2]：墟各。\n[釋文-3]：口各反\n[釋文-4]：旨夷反\n[音釋-5]：因。\n[釋文-6]：音因，又音眞\n[音釋-7]：而善。\n[釋文-8]：而善反，郭音罕，說文、蒼頡篇皆同\n[郭注-9]：儼然，敬貌。書曰：「夙夜惟寅。」詩曰：「我孔熯矣。」諲，未詳。', '釋詁弟一', '爾雅', '4;7;8;9', DEFAULT, DEFAULT, '5002;6770;23;7693;29;6741;5524;9791;6432;5818', '1027;1020;4329;6723', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3004, '朝;旦;夙;晨;晙;早', '', '朝、旦、夙、晨、晙12，早也。3', '[音釋-1]：俊。\n[釋文-2]：子峻反，又音峻\n[郭注-3]：晙亦明也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4287;4283;4332;4316;4261;4190', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3005, '𩓣;竢;𤾕;戾;厎;止;徯;待', '底', '𩓣12、竢34、𤾕5、戾6、厎[底]78、止、徯910，待也。11', '[音釋-1]：須。\n[釋文-2]：音須\n[音釋-3]：士。\n[釋文-4]：音仕，字又作俟，亦作戺，音同\n[釋文-5]：他計反\n[釋文-6]：音麗\n[音釋-7]：止。\n[釋文-8]：之視反，字宜從厂，或作底，非也。底，音丁禮反\n[音釋-9]：胡禮。\n[釋文-10]：胡禮反\n[郭注-11]：書曰：「徯我后。」今河北人語亦然。𤾕、戾、厎者，皆止也，止亦相待。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '6689;6685;6354;5982;1064;1244;1245', '5950', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3006, '噊;幾;烖;殆;危;𧰙;汽', '', '噊12、幾3、烖4、殆，危也。5𧰙67，汽也89。1011', '[音釋-1]：聿。\n[釋文-2]：郭音聿，施音述\n[釋文-3]：音祈，又音機\n[音釋-4]：哉\n[郭注-5]：幾猶殆也。噊、烖，未詳。\n[音釋-6]：析。\n[釋文-7]：音祈，又音沂，郭音剴。案剴，音公哀反，說文云：「摩也」\n[音釋-8]：蓋。\n[釋文-9]：古愛反，施音旣，樊、孫虛乞反\n[郭注-10]：謂相摩近。\n[釋文-11]：摩，莫河反。近，附近之近。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '910;2502;6487;2548;6007;3094;7339', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3007, '治;肆;古;故;肆;故;今', '巳;自;自', '治[巳]1、肆[自]2、古，故也。3肆[自]、故，今也。4', '[釋文-1]：如字，施直吏反\n[釋文-2]：音四\n[郭注-3]：治，未詳。肆、古，見詩、書。\n[郭注-4]：肆旣爲故，又爲今，今亦爲故，故亦爲今，此義相反而兼通者，事例在下，而皆見詩。', '釋詁弟一', '爾雅', '1;2;5', DEFAULT, DEFAULT, '7087;6068;1462;2005;6068;2005;3272', '9795;2223;2223', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3008, '惇;亶;祜;篤;掔;仍;𣬈;埤;竺;腹;厚', '䈞;䚮;𤼤', '惇1、亶2、祜3、篤[䈞]、掔4、仍[䚮]5、肶{𣬈}[𤼤]67、埤89、竺1011、腹12，厚也。1314', '[釋文-1]：字又作𢤈，丁門反\n[釋文-2]：多但反\n[釋文-3]：音戸\n[釋文-4]：卻賢反，又苦閒、苦忍二反\n[釋文-5]：本或作扔，同，汝烝反\n[音釋-6]：毗。\n[釋文-7]：音毗，本或作脾，同\n[音釋-8]：脾。\n[釋文-9]：避支反，又音椑\n[音釋-10]：篤。\n[釋文-11]：字又作篤，同，丁毒反\n[釋文-12]：分伏反\n[郭注-13]：頻仍、埤益、肶輔，皆重厚。掔然，厚貌。餘皆見詩、書。\n[釋文-14]：重，直龍反。', '釋詁弟一', '爾雅', '4;6;7', DEFAULT, DEFAULT, '6727;3342;11;6196;7953;5047;6700;9091;9017;2630;3337', '3333;1522;4729', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3009, '載;謨;食;詐;僞', '戴', '載[戴]、謨1、食、詐2，僞也。34', '[釋文-1]：亡胡反，郭音慕\n[釋文-2]：側駕反\n[郭注-3]：載者，言而不信。謨者，謀而不忠。書曰：「朕不食言。」\n[釋文-4]：謀，亡侯反。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '9569;1509;3204;1670;5134', '1779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3010, '話;猷;載;行;化;言', '譌', '話1、猷2、載、行3、訛{化}[譌]4，言也。56', '[釋文-1]：胡快反\n[釋文-2]：音由，字亦從犭\n[釋文-3]：下庚反，郭下孟反，注同\n[釋文-4]：字又作吪，亦作譌，同，五戈反\n[郭注-5]：詩曰：「愼爾出話。」猷者，道，道亦言也。周禮曰：「作盟詛之載。」今江東通謂語爲行。世以妖言爲訛。\n[釋文-6]：盟，音明。詛，側慮反。祅，於喬反，本又作妖，同。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '1551;6373;9569;1266;5214;1475', '1661', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3011, '遘;逢;遇;遘;逢;遇;遻;遘;逢;遇;遻;見', '', '遘12、逢，遇也。3遘、逢、遇，遻也45。67遘、逢、遇、遻，見也。8', '[音釋-1]：搆。\n[釋文-2]：古豆反\n[郭注-3]：謂相遭遇。\n[音釋-4]：悟。\n[釋文-5]：字又作迕，同，五故反\n[郭注-6]：轉復爲相觸遻。\n[釋文-7]：復，扶又反。\n[郭注-8]：行而相值卽見。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1129;1130;1127;1129;1130;1127;1131;1129;1130;1127;1131;5474', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3012, '顯;昭;覲;釗;覿;見;監;瞻;臨;位;覜;相;視', '昭;䇐', '顯、昭、覲、釗[昭]1、覿，見也2。3監45、瞻、臨、涖{位}[䇐]67、覜89、相10，視也。11', '[釋文-1]：工堯反，又章堯反\n[釋文-2]：賢遍反，注同\n[郭注-3]：顯、昭，明見也。逸書曰：「釗我周王。」\n[音釋-4]：鑒。\n[釋文-5]：音鑒，又工杉反，字又作𥌈\n[音釋-6]：利。\n[釋文-7]：音利，又音類\n[音釋-8]：眺。\n[釋文-9]：他弔反\n[釋文-10]：施息亮反，又息良反\n[郭注-11]：皆謂察視也。', '釋詁弟一', '爾雅', '4;10', DEFAULT, DEFAULT, '5690;4197;5513;2794;5519;5474;5245;2157;5246;5029;5514;2162;5475', '4197;6678', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3013, '鞠;訩;溢;盈', '𡫬;洶', '鞠[𡫬]、訩[洶]1、溢，盈也。2', '[釋文-1]：音凶\n[郭注-2]：詩曰：「降此鞠訩。」', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '1810;7398;3155', '4616;7189', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3014, '孔;魄;哉;延;虛;無;之;言;閒', '㕣', '孔、魄、哉、延[㕣]、虛、無、之、言，閒也1。23', '[釋文-1]：古閑反，𨻶也，舊音閑\n[郭注-2]：孔穴、延魄、虛無，皆有閒隙。餘未詳。\n[釋文-3]：隙，去㦸反。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '7702;5822;856;1265;5232;8414;3859;1475;7760', '959', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3015, '瘞;幽;隱;匿;蔽;竄;微', '', '瘞12、幽、隱、匿34、蔽5、竄6，微也。7', '[音釋-1]：於計。\n[釋文-2]：猗例反，郭音翳\n[音釋-3]：女力。\n[釋文-4]：女力反\n[釋文-5]：必曳反\n[釋文-6]：麤亂反\n[郭注-7]：微謂逃藏也，左傳曰：「其徒微之」是也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9137;2501;9667;8418;550;4666;1236', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3016, '訖;徽;妥;懷;安;按;𤾕;戾;底;厎;尼;定;曷;遏;止', '褱', '訖、徽、妥12、懷[褱]、安、按3、𤾕、戾、底4、厎5、尼6、定、曷7、遏89，止也。1011', '[音釋-1]：他窠。\n[釋文-2]：孫他果反，郭他回反、又他罪反，劉昌宗音儀禮同，字林亦同\n[釋文-3]：一旦反\n[釋文-4]：丁禮反\n[釋文-5]：之視反\n[釋文-6]：施女乙反，謝羊而反\n[釋文-7]：何末反\n[音釋-8]：烏曷。\n[釋文-9]：烏割反\n[郭注-10]：妥者，坐也。懷者，至也。按，抑按也。𤾕，廢。皆止住也。戾、厎，義見詩傳。國語曰：「戾久將底。」孟子曰：「行或尼之。」今以逆相止爲遏。徽，未詳。\n[釋文-11]：抑，於力反。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '1578;8689;8347;6762;4576;7885;6354;5950;5982;5410;4574;3046;1185;1064', '5289', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3017, '豫;射;厭', '斁;猒', '豫、射[斁]12，厭[猒]也3。4', '[音釋-1]：亦。\n[釋文-2]：羊石反，字又作斁，同\n[釋文-3]：於豔反\n[郭注-4]：詩曰：「服之無斁。」豫，未詳。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6134;3308;6001', '2032;3040', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3018, '烈;績;業;績;勲;功', '', '烈、績，業也。1績、勲，功也。2', '[郭注-1]：謂功業也。\n[郭注-2]：謂功勞也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6425;8733;1750;8733;9211;9212', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3019, '功;績;質;登;平;明;考;就;成', '憕;攷', '功、績、質、登[憕]、平、明、考[攷]、就，成也。1', '[郭注-1]：功、績，皆有成。詩曰：「質爾民人。」禮記曰：「年穀不登。」穀梁傳曰：「平者，成也。」事有分明，亦成濟也。', '釋詁弟一', '爾雅', '4;7', DEFAULT, DEFAULT, '9212;8733;3970;1079;3073;4321;5383;3330;9747', '6722;2050', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3020, '梏;梗;較;頲;庭;道;直', '亢;𡈼;導', '梏12、梗[亢]3、較45、頲[𡈼]67、庭、道[導]，直也。8', '[音釋-1]：谷。\n[釋文-2]：古沃反，郭音角\n[釋文-3]：古杏反\n[音釋-4]：角。\n[釋文-5]：古學反\n[音釋-6]：他頂。\n[釋文-7]：他鼎反\n[郭注-8]：梏、梗、較、頲，皆正直也。詩曰：「旣庭且碩。」頲、道，無所屈。', '釋詁弟一', '爾雅', '2;4;6', DEFAULT, DEFAULT, '3821;3541;9531;5645;5926;1205;8410', '6653;5238;1987', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3021, '密;康;靜', '宓', '密[宓]1、康，靜也。2', '[釋文-1]：亡筆反\n[郭注-2]：皆安靜也。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5874;4457;3189', '4577', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3022, '豫;寧;綏;康;柔;安', '寍;妥', '豫、寧[寍]、綏[妥]、康、柔，安也。1', '[郭注-1]：皆見詩、書。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '6134;3056;8762;4457;3607;4576', '4573;8347', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3023, '平;均;夷;弟;易;矢;弛;弛;易', '𢓡;𢓡', '平、均、夷[𢓡]、弟[𢓡]，易也12。3矢，弛也45。6弛，易也7。8', '[音釋-1]：以豉。\n[釋文-2]：以豉反，注同\n[郭注-3]：皆謂易直。\n[音釋-4]：並音尸紙。\n[釋文-5]：尸紙反\n[郭注-6]：弛放。\n[釋文-7]：施、李音尸紙反，下音亦。顧、謝本弛作施，并「易」皆以豉反，注同\n[郭注-8]：相延易。', '釋詁弟一', '爾雅', '3;4', DEFAULT, DEFAULT, '3073;9028;6613;3399;6132;3307;8495;8495;6132', '1239;1239', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3024, '希;寡;鮮;罕;鮮;寡', '尟;尟;尟', '希、寡、鮮[尟]12，罕[尟]也3。4鮮[尟]，寡也。5', '[音釋-1]：息淺。\n[釋文-2]：息淺反，下同\n[釋文-3]：火旱反\n[郭注-4]：罕亦希也。\n[郭注-5]：謂少。', '釋詁弟一', '爾雅', '3;4;5', DEFAULT, DEFAULT, '4606;7646;4815;7646;4606', '1091;1091;1091', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3025, '酬;酢;侑;報', '讎;醋', '酬[讎]、酢[醋]12、侑3，報也。4', '[音釋-1]：昨。\n[釋文-2]：才各反\n[釋文-3]：本或作宥，同，于救反\n[郭注-4]：此通謂相報荅，不主于飲酒。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '9838;9860;8255;6649', '1488;9839', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3026, '毗劉;㬥樂;覭髳;茀離', '𣔵落', '毗劉1，㬥樂[𣔵落]也23。45覭髳67，茀離也8。910', '[釋文-1]：如字，樊光本作庇，云：「蔭也」\n[音釋-2]：剥。洛。\n[釋文-3]：本又作𤒺，同，邦角反。本又作爍，郭音洛，又力角反\n[郭注-4]：謂樹木葉缺落、蔭䟽。㬥樂，見詩。\n[釋文-5]：廕，於禁反，又作蔭，同。\n[音釋-6]：陌。蒙。\n[釋文-7]：郭亡革反，又莫經反。音蒙\n[釋文-8]：音弗\n[郭注-9]：謂草木之叢茸翳薈也。茀離卽彌離，彌離猶蒙蘢耳。孫叔然字別爲義，失矣。\n[釋文-10]：叢，才工反。茸，如容反，或如融反。薈，烏會反。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '6700/9444;4246/3762;5492/5739;571/2293', '3600/549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3027, '蠱;𩥅;貳;疑', '𠑹', '蠱[𠑹]1、謟{𩥅}23、貳，疑也。4', '[釋文-1]：音古\n[音釋-2]：叨。\n[釋文-3]：郭音縚，他刀反，字或作慆，沈勑檢反\n[郭注-4]：蠱惑、有貳心者，皆疑也。左傳曰：「天命不謟。」音縚。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '8976;6195;3965;9778', '5468', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3028, '楨;翰;儀;榦', '', '楨1、翰、儀，榦也2。3', '[釋文-1]：音貞\n[釋文-2]：本又作幹，胡旦反，又作翰\n[郭注-3]：詩曰：「維周之翰。」儀表亦體榦。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3606;2243;5082;3618', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3029, '弼;棐;輔;比;俌', '𠨒', '弼、棐12、輔、比[𠨒]34，俌也56。78', '[音釋-1]：匪。\n[釋文-2]：音匪\n[音釋-3]：毗志。\n[釋文-4]：毗志反，注同\n[音釋-5]：甫。\n[釋文-6]：音輔，郭方輔反，字林音甫\n[郭注-7]：書曰：「天畏棐忱。」易曰：「比，輔也。」俌猶輔也。\n[釋文-8]：諶，市林反，本今作忱。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '8506;3753;9565;5227;5044', '5778', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3030, '疆;界;邊;衞;圉;垂', '', '疆1、界、邊、衞、圉23，垂也。45', '[釋文-1]：壃，字又作畺，音姜。經典作疆，假借字\n[音釋-2]：語。\n[釋文-3]：魚呂反\n[郭注-4]：疆埸、竟界、邊旁、營衞、守圉，皆在外垂也。左傳曰：「聊以固吾圉也。」\n[釋文-5]：埸，羊石反，廣雅云：「界也。」旁，步郎反。聊，音遼。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9200;9185;1209;6647;9147', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3031, '昌;敵;彊;應;丁;當', '正', '昌、敵1、彊2、應3、丁[正]，當也。45', '[釋文-1]：音狄\n[釋文-2]：巨良反，下注同\n[釋文-3]：本或作膺，同，於矜反\n[郭注-4]：書曰：「禹拜昌言。」彊者，好與物相當值。\n[釋文-5]：好，呼報反。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '4237;2029;8489;6711;9745;9191', '1087', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3032, '孛;肩;揺;動;蠢;迪;俶;厲;作', '開;倏', '浡{孛}1、肩[開]、揺、動、蠢2、迪、俶[倏]3、厲，作也。4', '[釋文-1]：步忽反\n[釋文-2]：尺允反\n[釋文-3]：昌育反\n[郭注-4]：浡然，興作貌。蠢，動作。公羊傳曰：「俶，甚也。」穀梁傳曰：「始厲樂矣。」肩，見書。迪，未詳。', '釋詁弟一', '爾雅', '2;7', DEFAULT, DEFAULT, '3870;2623;7949;9231;8969;1132;5015;5983;5073', '7754;6348', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3033, '茲;斯;咨;呰;已;此', '', '兹{茲}、斯、咨、呰12、已3，此也。4', '[音釋-1]：紫。\n[釋文-2]：子爾反，或子移反，郭音些。案廣雅：「些，辭也。」息計反，又息賀反，謂語餘聲也\n[釋文-3]：音以\n[郭注-4]：呰、已，皆方俗異語。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '526;9483;841;898;9795;1083', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3034, '𧪰;咨;𨲠', '嗞', '嗟{𧪰}、咨[嗞]，𨲠{𨲠}也12。34', '[音釋-1]：嗟。\n[釋文-2]：本或作䦈，字林云：「皆古嗟字」\n[郭注-3]：今河北人云𨲠歎，音兔罝。\n[釋文-4]：罝，音嗟。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '1672;841', '922', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3035, '閑;狎;遦;貫;習', '遦', '閑、狎1、串{遦}23、貫[遦]45，習也。67', '[釋文-1]：乎甲反\n[音釋-2]：五患。\n[釋文-3]：郭音五患反，沈、謝古患反\n[音釋-4]：慣。\n[釋文-5]：慣，本又作貫，又作遦，同，古患反\n[郭注-6]：串，厭串。貫，貫忕也。今俗語皆然。\n[釋文-7]：厭，於豔反。忕，音逝，又時設反，張揖雜字音曳，云：「狃忕過度。」', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '7767;6338;1111;4339;2239', '1111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3036, '曩;塵;佇;淹;留;久', '陳;眝;腌', '曩1、塵[陳]、佇[眝]、淹[腌]、留，久也。2', '[釋文-1]：奴黨反\n[郭注-2]：塵垢、佇企、淹滯，皆稽久也。', '釋詁弟一', '爾雅', '2;3;4', DEFAULT, DEFAULT, '4232;6288;5209;7001;9195;3407', '9686;2176;2717', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3037, '逮;及;曁;與', '臮', '逮1、及、曁[臮]2，與也。3', '[釋文-1]：音代，一音大計反\n[釋文-2]：其器反\n[郭注-3]：公羊傳曰：「會、及、暨，皆與也。」逮亦及也。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '1147;1918;4284;1782', '5237', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3038, '騭;假;格;陟;躋;登;升', '陟;叚;登', '騭[陟]12、假[叚]34、格、陟、躋5、登，陞{升}[登]也6。7', '[音釋-1]：質。\n[釋文-2]：之實反，又音陟\n[音釋-3]：遐。\n[釋文-4]：音遐\n[釋文-5]：子兮反\n[釋文-6]：音升\n[郭注-7]：方言曰：「魯 衞之閒曰騭，梁 益曰格。」禮記曰：「天王登遐。」公羊傳曰：「躋者何？陞也。」', '釋詁弟一', '爾雅', '1;2;7', DEFAULT, DEFAULT, '6136;5074;3601;9641;1347;1079;9505', '9641;1928;1079', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3039, '揮;渌;歇;涸;竭', '渴', '揮1、盝{渌}2、歇3、涸45，竭[渴]也6。78', '[釋文-1]：許韋反\n[釋文-2]：音鹿\n[釋文-3]：虛謁反\n[音釋-4]：鶴。\n[釋文-5]：戸各反\n[釋文-6]：渴，音竭，本或作竭\n[郭注-7]：月令曰：「無漉陂池。」國語曰：「水涸而成梁。」揮振去水亦爲竭。歇，通語。\n[釋文-8]：毋，音無，本今作無。漉，音鹿。去，起呂反。', '釋詁弟一', '爾雅', '5', DEFAULT, DEFAULT, '8008;5534;7340;6688', '7343', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3040, '挋;式;刷;淸', '飾;㕞', '挋12、拭{式}[飾]3、刷[㕞]45，淸也6。78', '[音釋-1]：振。\n[釋文-2]：音震\n[釋文-3]：音式\n[音釋-4]：所劣。\n[釋文-5]：字又作㕞，所劣反，說文云：「刮也。」廣雅云：「削也」\n[釋文-6]：如字，劉音儀禮慈性反\n[郭注-7]：振訊、抆拭、掃刷，皆所以爲絜淸。\n[釋文-8]：抆，亡粉反。埽，素老反。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '7913;3030;2783;7200', '4894;1917', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3041, '鴻;昏;於;顯;閒;代', '', '鴻、昏、於1、顯、閒23，代也。4', '[音釋-1]：烏\n[音釋-2]：㵎。\n[釋文-3]：古莧反，注同，謝古閑反，郭古鴈反，施胡瞎反\n[郭注-4]：鴻鴈知運代。昏主代明，明亦代昏。顯卽明也。聞錯亦相代，於義未詳。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2414;4221;2487;5690;7760;5081', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3042, '饁;饟;饋', '', '饁12、饟34，饋也56。7', '[音釋-1]：叶。\n[釋文-2]：于輒反，字林于怯反\n[音釋-3]：餉。\n[釋文-4]：式亮反\n[音釋-5]：櫃。\n[釋文-6]：本或作餽，同，巨愧反\n[郭注-7]：國語曰：「其妻饁之。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3229;3230;3232', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3043, '遷;運;徙', '', '遷1、運，徙也2。3', '[釋文-1]：七延反，注同\n[釋文-2]：斯尒反\n[郭注-3]：今江東通言遷徙。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1137;1138;1135', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3044, '秉;拱;執', '廾', '秉、拱[廾]1，執也。2', '[釋文-1]：九勇反\n[郭注-2]：兩手持爲拱。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '1919;7840;6646', '1756', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3045, '廞;熙;興', '', '廞12、熙，興也3。4', '[音釋-1]：許金。\n[釋文-2]：郭音歆，又音欽，字林火欽反\n[釋文-3]：如字，又許應反\n[郭注-4]：書曰：「庶績咸熙。」廞，見周官。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '5968;6525;1783', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3046, '衞;蹶;假;嘉', '偉;話', '衞[偉]、蹶[話]12、假34，嘉也。5', '[音釋-1]：居衛。\n[釋文-2]：居衞反，下同\n[音釋-3]：暇。\n[釋文-4]：戸嫁反\n[郭注-5]：詩序曰：「假樂，嘉成王也。」餘未詳。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '1369;5074;3081', '4980;1551', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3047, '廢;稅;赦;舍', '脫;捨', '廢1、稅[脫]2、赦，舍[捨]也34。5', '[釋文-1]：字亦作癈，同，甫穢反\n[釋文-2]：始銳反\n[音釋-3]：捨。\n[釋文-4]：音捨，注同\n[郭注-5]：詩曰：「召伯所稅。」舍，放置。', '釋詁弟一', '爾雅', '2;4', DEFAULT, DEFAULT, '5961;4486;2033;3273', '2652;7883', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3048, '棲遟;舌;休;苦;𦳋;齂;呬;息', '屖遟;愒;夃;喟', '棲遟[屖遟]1、憩{舌}[愒]2、休、苦[夃]、㕟{𦳋}[喟]34、齂56、呬78，息也。9', '[釋文-1]：音西。直釐反\n[釋文-2]：起例反，本或作愒，同\n[音釋-3]：苦怪。\n[釋文-4]：苦怪反，又墟季反，字林以爲喟，丘愧反。孫本作快，郭音苦槩反。又作嘳，墟愧、苦怪二反\n[音釋-5]：訶誡。\n[釋文-6]：郭、施、謝海拜反，孫虛貴反，顧乎被反\n[音釋-7]：許四。\n[釋文-8]：郭許四反，孫許器反，施火季反\n[郭注-9]：棲遲，遊息也。苦勞者，宜止息。憩，見詩。㕟、齂、呬，皆氣息貌。今東齊呼息爲呬也。', '釋詁弟一', '爾雅', '1;2;4;5', DEFAULT, DEFAULT, '7712/1148;1445;3816;323;376;2236;824;6704', '5415/1148;6806;3405;830', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3049, '供;歭;共;具', '庤', '供1、歭[庤]23、共45，具也。6', '[釋文-1]：如字，又居用反\n[音釋-2]：直紀。\n[釋文-3]：直紀反\n[音釋-4]：恭。\n[釋文-5]：音恭\n[郭注-6]：皆謂備具。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '5025;1067;1776;1772', '5957', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3050, '𢜮;憐;惠;愛', '㤅', '𢜮12、憐3、惠，愛[㤅]也。4', '[音釋-1]：某。\n[釋文-2]：亡矩反，又音無\n[釋文-3]：力田反\n[郭注-4]：𢜮，韓 鄭語，今江東通呼爲憐。', '釋詁弟一', '爾雅', '4', DEFAULT, DEFAULT, '6785;6960;2504;3367', '6779', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3051, '娠;蠢;震;戁;妯;騷;感;訛;蹶;動', '蝡;慅;吪', '娠12、蠢3、震、戁[蝡]4、妯56、騷[慅]7、感、訛[吪]8、蹶，動也。910', '[音釋-1]：振。\n[釋文-2]：指愼反，又音身\n[釋文-3]：尺允反\n[釋文-4]：奴板反\n[音釋-5]：抽。\n[釋文-6]：顧依詩勑留反，郭盧篤反，又徒歷反\n[釋文-7]：蘇刀反\n[釋文-8]：五禾反\n[郭注-9]：娠猶震也。詩曰：「憂心且妯。」「無感我帨兮。」「或寢或訛。」蠢、戁、騷、蹶，皆揺動貌。\n[釋文-10]：帨，始銳反，毛詩傳云：「佩巾也。」', '釋詁弟一', '爾雅', '4;6;8', DEFAULT, DEFAULT, '8134;8969;7531;6723;8287;6229;6911;1369;9231', '8882;6910;930', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3052, '覆;察;副;審', '覆', '覆1、察、副[覆]2，審也。34', '[釋文-1]：芳福反，後注同\n[釋文-2]：音赴\n[郭注-3]：覆校、察視、副長，皆所爲審諦。\n[釋文-4]：校，音敎。長，丁丈反，下文并注同。諦，音帝。', '釋詁弟一', '爾雅', '3', DEFAULT, DEFAULT, '4852;4581;2765;722', '4852', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3053, '契;滅;殄;絶', '㓞', '契[㓞]12、滅、殄3，絶也。45', '[音釋-1]：苦結。\n[釋文-2]：郭苦計反，字又作挈，顧苦結反，注同。左傳云：「盡借邑人之車，契其軸」是也\n[釋文-3]：大典反\n[郭注-4]：今江東呼刻斷物爲契斷。\n[釋文-5]：刻，音克。斷，大管反。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '6612;7441;2551', '2814', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3054, '郡;臻;仍;迺;侯;乃', '屈/出;晉;幺', '郡[屈/出]、臻[晉]、仍、迺、侯[幺]，乃也。1', '[郭注-1]：迺卽乃。餘未詳。', '釋詁弟一', '爾雅', '1;2;5', DEFAULT, DEFAULT, '4004;7708;5047;3311;3051', '5428/3863;4203;2497', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3055, '迪;䍃;訓;道', '䌛;順', '迪1、繇{䍃}[䌛]23、訓[順]，道也4。5', '[釋文-1]：音狄\n[音釋-2]：由。\n[釋文-3]：音由\n[釋文-4]：如字，或徒報反，非\n[郭注-5]：義皆見詩、書。', '釋詁弟一', '爾雅', '2;3', DEFAULT, DEFAULT, '1132;3298;1496;1205', '8514;5652', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3056, '僉;咸;胥;皆', '', '僉12、咸、胥3，皆也。4', '[音釋-1]：七廉。\n[釋文-2]：七廉反\n[釋文-3]：息廬反\n[郭注-4]：東齊曰胥，見方言。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3270;872;2698;2226', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3057, '育;孟;耆;艾;正;伯;長', '', '育、孟、耆1、艾2、正、伯，長也3。4', '[釋文-1]：巨之反，禮記云：「六十曰耆」\n[釋文-2]：五蓋反，下同，禮記曰：「五十曰艾」\n[音釋-3]：丁丈\n[郭注-4]：育養亦爲長。正、伯，皆官長。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9786;9772;5378;389;1087;4965;6067', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3058, '艾;歷', '䢃;厤', '艾[䢃]，歷[厤]也。12', '[郭注-1]：長者多更歷。\n[釋文-2]：更，音庚。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '389;1070', '5798;5986', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3059, '歷;秭;算;數', '𣀷/秝;筭', '歷[𣀷/秝]、秭12、算[筭]34，數也5。6', '[音釋-1]：姊。\n[釋文-2]：音姊\n[音釋-3]：筭。\n[釋文-4]：字又作筭，素緩反，郭息轉反\n[釋文-5]：色具反，注同，謝色主反\n[郭注-6]：歷，歷數也。今以十億爲秭。論語云：「何足算也。」', '釋詁弟一', '爾雅', '1;3', DEFAULT, DEFAULT, '1070;4497;3009;2011', '2010/4503;3008', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3060, '歷;傅', '䕻', '歷[䕻]，傅也12。3', '[音釋-1]：附。\n[釋文-2]：符付反，注同\n[郭注-3]：傅，近。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '1070;5042', '577', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3061, '艾;歷;覛;胥;相', '䢃;厤;俾', '艾[䢃]、歷[厤]、覛[俾]12、胥，相也3。4', '[音釋-1]：脉。\n[釋文-2]：字又作脈，亡革反\n[釋文-3]：息羊反，讀者或息亮反，今不用\n[郭注-4]：覛謂相視也。公羊傳曰：「胥盟者何？相盟也。」艾、歷，未詳。', '釋詁弟一', '爾雅', '1;2;3', DEFAULT, DEFAULT, '389;1070;7499;2698;2162', '5798;5986;5096', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3062, '乂;亂;靖;神;弗;淈;治', '䢃;清;敒;弼', '乂[䢃]1、亂、靖[清]、神[敒]、弗[弼]、淈23，治也4。56', '[釋文-1]：字又作䢃，亦作刈，同，魚廢反\n[音釋-2]：骨。\n[釋文-3]：郭古沒反，又胡忽反\n[釋文-4]：直吏反，謝如字\n[郭注-5]：論語曰：「子有亂臣十人。」淈，書序作汩，音同耳。神，未詳。餘並見詩、書。\n[釋文-6]：汨，古沒反。', '釋詁弟一', '爾雅', '1;3;4;5', DEFAULT, DEFAULT, '8360;9742;6684;25;8361;7206;7087', '5798;7200;2018;8506', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3063, '頤;艾;育;養', '宧', '頤[宧]1、艾、育，養也。2', '[釋文-1]：以之反\n[郭注-2]：汝 潁 梁 宋之閒曰艾，方言云。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '7825;389;9786;3219', '4560', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3064, '犬;渾;隕;墜', '隕;隕', '汱{犬}[隕]12、渾[隕]34、隕5，墜也6。7', '[音釋-1]：古犬。\n[釋文-2]：姑犬反，施胡犬反，顧徒蓋反，字宜作汏\n[音釋-3]：胡本。\n[釋文-4]：胡本反\n[釋文-5]：于敏反\n[釋文-6]：直類反\n[郭注-7]：汱、渾，皆水落貌。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '6301;7195;9648;9160', '9648;9648', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3065, '際;接;翜;捷', '', '際、接、翜12，捷也3。4', '[音釋-1]：所甲。\n[釋文-2]：所甲反\n[釋文-3]：才接反\n[郭注-4]：捷謂相接續也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9695;7917;2262;8087', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3066, '毖;神;侐;愼', '', '毖12、神、溢{侐}3，愼也。4', '[音釋-1]：祕。\n[釋文-2]：音祕\n[釋文-3]：音𨓜\n[郭注-4]：神，未詳。餘見詩、書。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '5228;25;5055;6712', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3067, '鬱陶;䍃;喜', '䌛', '鬱陶1、繇{䍃}[䌛]23，喜也。4', '[釋文-1]：徒刀反\n[音釋-2]：由。\n[釋文-3]：音由，一音遥\n[郭注-4]：孟子曰：「鬱陶思君。」禮記曰：「人喜則斯陶，陶斯詠，詠斯猶。」猶卽繇也，古今字耳。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '3848/9687;3298;3074', '8514', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3068, '馘;穧;獲', '', '馘12、穧34，獲也。56', '[音釋-1]：古獲。\n[釋文-2]：古獲反，本又作聝，音同\n[音釋-3]：才細。\n[釋文-4]：本或作齊，同，才細反，依注字宜從禾\n[郭注-5]：今以獲賊耳爲馘，獲禾爲穧。並見詩。\n[釋文-6]：穫禾，一本作獲禾，戸郭反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '7819;4462;6362', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3069, '阻;艱;難', '', '阻、艱，難也12。3', '[音釋-1]：乃旦。\n[釋文-2]：奴旦反，注同，一音如字\n[郭注-3]：皆險難。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '9631;9166;2401', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3070, '剡;㗉;利', '', '剡12、㗉34，利也。56', '[音釋-1]：羊冉。\n[釋文-2]：羊冉反\n[音釋-3]：略。\n[釋文-4]：力約反，詩本作畧\n[郭注-5]：詩曰：「以我剡耜。」\n[釋文-6]：耜，音似。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '2752;2745;2751', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3071, '允;任;壬;佞', '俊', '允[俊]、任12、壬3，佞也。4', '[音釋-1]：壬鴆。\n[釋文-2]：而鴆反，又而淫反，注同\n[釋文-3]：而今反，注同\n[郭注-4]：書曰：「而難任人。」允信者，佞人似信。壬猶任也。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5458;5087;9762;8275', '4960', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3072, '俾;并;抨;使;俾;并;抨;使;從', '𢖊;𢖊;𢖊;𢖊', '俾1、拼{并}[𢖊]23、抨[𢖊]45，使也。67俾、拼{并}[𢖊]、抨[𢖊]、使，從也。8', '[釋文-1]：必尒反\n[音釋-2]：北萌。\n[釋文-3]：北萌反，以利使人曰拼，從手\n[音釋-4]：烹。\n[釋文-5]：普耕反，案字書拼、抨並音普耕、𥙷耕二反，訓義亦同，今旣二字相隨，故多互其讀也。亦從手，彈也。字又作伻，音同，使人也\n[郭注-6]：皆謂使令，見詩。\n[釋文-7]：令，力呈反。\n[郭注-8]：四者又爲隨從。', '釋詁弟一', '爾雅', '2;3;6;7', DEFAULT, DEFAULT, '5096;5226;8044;5099;5096;5226;8044;5099;5225', '1240;1240;1240;1240', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3073, '襄;仍;因', '相', '儴{襄}[相]12、仍，因也。3', '[音釋-1]：攘。\n[釋文-2]：樊、孫如羊反，引論語「其父攘羊」，釋之作攘，注云：「因來而盜曰攘。」施息羊反\n[郭注-3]：皆謂因緣。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '5322;5047;3922', '2162', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3074, '蕫;督;正', '中', '董{蕫}[中]、督1，正也。2', '[釋文-1]：多毒反\n[郭注-2]：皆謂御正。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '402;2172;1087', '231', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3075, '享;孝;珍;享;獻', '', '享1，孝也。2珍、享，獻也。3', '[釋文-1]：虛丈反，下注同\n[郭注-2]：享祀孝道。\n[郭注-3]：珍物宜獻。榖梁傳曰：「諸侯不享覲。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3331;5384;148;3331;6364', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3076, '縱;縮;亂', '', '縱1、縮2，亂也。34', '[釋文-1]：于用反\n[釋文-2]：所六反\n[郭注-3]：縱放、掣緒，皆亂法也。\n[釋文-4]：掣，昌世反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8553;8563;9742', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3077, '探;篡;俘;取', '', '探12、篡34、俘56，取也。78', '[音釋-1]：貪。\n[釋文-2]：吐南反\n[音釋-3]：初患。\n[釋文-4]：初患反\n[音釋-5]：孚。\n[釋文-6]：音孚\n[郭注-7]：書曰：「俘厥寳玉。」篡者，奪取也。探者，摸取也。\n[釋文-8]：摸，亡各、樓胡二反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8001;5844;5166;1926', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3078, '徂;在;存;在;存;省;士;察', '㡹', '徂[㡹]、在，存也。12在、存、省3、士，察也。45', '[郭注-1]：以徂爲存，猶以亂爲治、以曩爲曏、以故爲今。此皆詁訓，義有反覆旁通，美惡不嫌同名。\n[釋文-2]：治，直吏反。曩，乃朗反。嚮，許亮反，本今作曏。\n[釋文-3]：息井反\n[郭注-4]：書曰：「在璿璣玉衡。」士，理官，亦主聽察。存卽在。\n[釋文-5]：璿，音旋，又作璇。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '1106;9066;9776;9066;9776;2219;226;4581', '5965', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3079, '烈;𣡌;餘', '', '烈、枿{𣡌}12，餘也。3', '[音釋-1]：五割。\n[釋文-2]：五割反，本或作𣖂，又作𣎴，注作蘖，並同，說文作𣡌，以𣖂、𣎴爲古文，蘖爲或體，云：「伐木之餘」\n[郭注-3]：晉 衞之閒曰櫱，陳 鄭之閒曰烈。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '6425;3802;3245', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3080, '迓;迎', '', '迓1，迎也。23', '[釋文-1]：訝，五駕反，本又作迓\n[郭注-2]：公羊傳曰：「跛者迓跛者。」\n[釋文-3]：跛，布我反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '1580;1125', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3081, '元;良;首', '郎', '元、良[郎]，首也。12', '[郭注-1]：左傳曰：「狄人歸先軫之元。」良，未聞。\n[釋文-2]：軫，之忍反。', '釋詁弟一', '爾雅', '2', DEFAULT, DEFAULT, '2;3339;5701', '4137', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3082, '薦;摯;臻', '瀳;𡠗', '薦[瀳]12、摯[𡠗]3，臻也4。5', '[音釋-1]：曹練。\n[釋文-2]：遭練反\n[釋文-3]：之二反\n[釋文-4]：則巾反\n[郭注-5]：薦，進也。摯，至也。故皆爲臻。臻，至也。', '釋詁弟一', '爾雅', '1;2', DEFAULT, DEFAULT, '6259;7860;7708', '7215;8248', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3083, '賡;揚;續', '', '賡12、揚，續也3。4', '[音釋-1]：古孟。\n[釋文-2]：古孟反，沈、孫音庚，說文以爲古文續\n[釋文-3]：似欲反\n[郭注-4]：書曰：「乃賡載歌。」揚，未詳。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8548;7956;8548', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3084, '祔;祪;祖', '', '祔12、祪、祖也34。5', '[音釋-1]：附。\n[釋文-2]：音附，郭音付\n[音釋-3]：鬼。\n[釋文-4]：俱毁反\n[郭注-5]：祔，付也，付新死者於祖廟。祪，毁廟主。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '35;34;36', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3085, '卽;尼;尼;定;邇;幾;暱;近', '', '卽，尼也12。3尼，定也。4邇、幾56、暱78，近也。9', '[音釋-1]：女乙。\n[釋文-2]：本亦作昵，同，女乙反，謝羊而反，顧奴啓反，下同\n[郭注-3]：卽猶今也。尼者，近也。尸子曰：「悅尼而來遠。」\n[郭注-4]：尼者，止也。止亦定。\n[音釋-5]：機。\n[釋文-6]：音機，又音祈\n[音釋-7]：女乙。\n[釋文-8]：女乙反\n[郭注-9]：暱，親近也。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '3196;5410;5410;4574;1184;2502;4251;1180', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3086, '妥;安坐', '', '妥12，安坐也。34', '[音釋-1]：他回。\n[釋文-2]：郭他回反，沈他果反\n[郭注-3]：禮記曰：「妥而后傳命。」\n[釋文-4]：傳，直攣反。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '8347;4576/9067', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3087, '貉;縮;綸', '幕;摍;䜌', '貉[幕]12、縮[摍]，綸[䜌]也3。45', '[音釋-1]：陌。\n[釋文-2]：亡白反，下同，施胡各反\n[釋文-3]：音倫\n[郭注-4]：綸者，繩也，謂牽縛縮貉之。今俗語亦然。\n[釋文-5]：繩，音乗。', '釋詁弟一', '爾雅', '1;2;3', DEFAULT, DEFAULT, '6129;8563;8652', '4881;7990;1619', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3088, '貉;嗼;安;定', '嗼', '貉[嗼]、嗼12、安，定也。3', '[音釋-1]：莫。\n[釋文-2]：音莫，本亦作莫\n[郭注-3]：皆靜定。見詩。', '釋詁弟一', '爾雅', '1', DEFAULT, DEFAULT, '6129;941;4576;4574', '941', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3089, '伊;維;伊;維;侯', '', '伊，維也。1伊、維，侯也。2', '[郭注-1]：發語辭。\n[郭注-2]：詩曰：「侯誰在矣。」互相訓。', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4967;8701;4967;8701;3311', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3090, '時;寔;是', '', '時、寔1，是也。2', '[音釋-1]：石\n[郭注-2]：公羊傳曰：「寔來者何？是來也。」', '釋詁弟一', '爾雅', '', DEFAULT, DEFAULT, '4189;4575;1089', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3091, '卒;猷;假;輟;已;求;酋;在;卒;就;終;崩;薨;無禄;卒;徂落;殪;死', '𣨛;徦;柮;究;究;𣨛;𣨛;殂落', '卒[𣨛]12、猷3、假[徦]4、輟[柮]56，已也7。8求[究]9、酋[究]1011、在、卒[𣨛]、就12，終也13。14崩、薨1516、無禄、卒[𣨛]、徂落[殂落]17、殪1819，死也。2021', '[音釋-1]：子䘏。\n[釋文-2]：子恤反，又作𣨛，下同\n[釋文-3]：音由\n[釋文-4]：古雅反\n[音釋-5]：丁劣。\n[釋文-6]：丁劣反\n[釋文-7]：音以\n[郭注-8]：猷、假，未詳。\n[釋文-9]：殏，巨牛反，又作求\n[音釋-10]：在由。\n[釋文-11]：在由反，又子由反，郭音遒\n[釋文-12]：如字，或作噈，子六、子合二反，又作殧，同\n[釋文-13]：𣧩，音終，本又作終\n[郭注-14]：詩曰：「嗣先公爾酋矣。」成就，亦終也。其餘未詳。\n[音釋-15]：呼弘。\n[釋文-16]：火弘反\n[釋文-17]：殂，音徂，本又作徂。𣧳，音落，本又作落\n[音釋-18]：於計。\n[釋文-19]：於計反\n[郭注-20]：古者死亡尊卑同稱耳，故尚書堯曰「殂落」，舜曰「陟方乃死」。\n[釋文-21]：稱，尺證反。', '釋詁弟一', '爾雅', '1;3;4;6;7;9;15;16', DEFAULT, DEFAULT, '5359;6373;5074;4832;9795;5373;9878;9066;5359;3330;8588;5894;2562;8414/15;5359;1106/549;2540;2561', '2534;1248;3807;4671;4671;2534;2534;2538/549', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3092, '殷;齊;中', '𨽌', '殷[𨽌]、齊，中也1。23', '[釋文-1]：如字，又音仲，又知衆反\n[郭注-2]：書曰：「以殷仲春。」釋地曰：「岠齊州以南。」\n[釋文-3]：距，又作岠，同，音巨。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5251;4393;231', '3751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3093, '斯;誃;離', '澌', '斯[澌]1、誃23，離也。4', '[釋文-1]：私貲、所宜二反\n[音釋-2]：侈。\n[釋文-3]：尺氏反\n[郭注-4]：齊 陳曰斯。誃，見詩。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9483;1617;2293', '7338', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3094, '畟;興;起', '', '謖{畟}12、興，起也。3', '[音釋-1]：所六。\n[釋文-2]：所六反\n[郭注-3]：禮記曰：「尸謖。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3372;1783;1017', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3095, '還;復;返', '', '還12、復3，返也4。', '[音釋-1]：旋。\n[釋文-2]：音旋\n[釋文-3]：音服\n[釋文-4]：音反，說文同，云：「春秋傳作𢓉，從彳」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1142;1226;1141', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3096, '宣;徇;徧', '', '宣、徇12，徧也3。4', '[音釋-1]：辝峻。\n[釋文-2]：本又作侚，樊本作𢓈，並辭峻反，郭音巡。張揖字詁云：「徇，今巡。」施音詢進，本又爲均序\n[釋文-3]：古遍字\n[郭注-4]：皆周徧也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4558;1256;1247', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3097, '馹;遽;傳', '', '馹12、遽3，傳也45。67', '[音釋-1]：日。\n[釋文-2]：而實反，郭音義云：「本或作𨖹。」聲類云：「亦馹字。」同\n[釋文-3]：其據反\n[音釋-4]：張戀。\n[釋文-5]：張戀反，注同\n[郭注-6]：皆轉車、驛馬之名。\n[釋文-7]：車，音居。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6236;1206;5103', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3098, '蒙;荒;奄', '冡', '蒙[冡]1、荒，奄也。23', '[釋文-1]：莫公反，小爾雅云：「覆也」\n[郭注-2]：奄，奄覆也。皆見詩。\n[釋文-3]：覆，音副。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '649;546;6599', '4803', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3099, '告;謁;請', '誥', '告[誥]12、謁，請也。3', '[音釋-1]：谷。\n[釋文-2]：古毒反\n[郭注-3]：皆求請也。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '778;1484;1483', '1529', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3100, '肅;雍;聲', '嘯', '肅[嘯]、噰{雍}1，聲也。2', '[釋文-1]：於容反\n[郭注-2]：詩曰：「肅噰和鳴。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1939;2299;7808', '867', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3101, '格;懷;來', '', '格、懷，來也1。2', '[釋文-1]：徠，音來，本今作來\n[郭注-2]：書曰：「格尔衆庶。」懷，見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3601;6762;3346', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3102, '畛;厎;致', '致', '畛[致]1、厎2，致也。3', '[釋文-1]：之忍反\n[釋文-2]：之視反\n[郭注-3]：皆見詩傳。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9188;5982;3365', '3365', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3103, '恀;怙;恃', '垑', '恀[垑]12、怙3，恃也4。5', '[音釋-1]：是。\n[釋文-2]：音是\n[釋文-3]：音戸\n[釋文-4]：音市\n[郭注-5]：今江東呼母爲恀，音是。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6773;6774', '9107', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3104, '律;遹;述', '', '律、遹1，述也。2', '[釋文-1]：古述字，一音餘橘反\n[郭注-2]：皆敘述也，方俗語耳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1257;1157;1107', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3105, '俞;荅;然', '諭;嘫', '俞[諭]1、畣{荅}23，然[嘫]也。45', '[釋文-1]：羊朱反\n[音釋-2]：荅。\n[釋文-3]：古荅字，一本作荅\n[郭注-4]：禮記曰：「男唯女俞。」畣者，應也，亦爲然。\n[釋文-5]：唯，維癸反。應，音譍。', '釋言弟二', '爾雅', '1;3', DEFAULT, DEFAULT, '5437;247;6421', '1502;861', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3106, '豫;臚;敘', '', '豫、臚1，敘也。2', '[釋文-1]：呂居反\n[郭注-2]：皆陳敘也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6134;2598;2065', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3107, '庶幾;尚', '', '庶幾1，尚也。2', '[釋文-1]：音機\n[郭注-2]：詩曰：「不尚息焉。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5956/2502;711', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3108, '觀;指;示', '', '觀1、指，示也。2', '[釋文-1]：施音館，謝音官，注同\n[郭注-2]：國語曰：「且觀之兵。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5484;7830;10', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3109, '若;惠;順', '', '若、惠，順也。12', '[郭注-1]：詩曰：「惠然肯來。」\n[釋文-2]：肯，口等反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '599;2504;5652', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3110, '敖;幠;傲', '荒', '敖1、幠[荒]2，傲也3。4', '[釋文-1]：五刀反\n[釋文-2]：郭火孤反，沈亡甫反\n[釋文-3]：五報反\n[郭注-4]：禮記曰：「無幠無傲。」傲，慢也。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2513;4898;4999', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3111, '幼;鞠;稺', '毓', '幼、鞠[毓]，稺也1。2', '[釋文-1]：直利反，又音稚\n[郭注-2]：書曰：「不念鞠子哀。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2498;1810;4422', '9786', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3112, '逸;諐;過', '', '逸、諐12，過也。3', '[音釋-1]：𠍴。\n[釋文-2]：去虔反\n[郭注-3]：書曰：「汝則有逸罰。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6294;1110', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3113, '疑;休;戾', '', '疑、休，戾也1。2', '[釋文-1]：力細反\n[郭注-2]：戾，止也。疑者亦止。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9778;3816;6354', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3114, '疾;齊;壯', '齌', '疾、齊[齌]，壯也1。2', '[釋文-1]：阻亮反\n[郭注-2]：壯，壯事，謂速也。齊亦疾。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4694;4393;228', '6457', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3115, '悈;褊;急', '亟', '悈[亟]12、褊34，急也。56', '[音釋-1]：紀力。\n[釋文-2]：本或作極，又作亟，同，紀力反，或音戒\n[音釋-3]：必淺。\n[釋文-4]：必淺反，說文云：「小衣也。」廣雅云：「褊，狹𨹟也」\n[郭注-5]：皆急狹。\n[釋文-6]：狹，戸甲反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6753;5319;6809', '9014', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3116, '貿;賈;市', '', '貿1、賈23，市也。4', '[釋文-1]：音茂，字又作𧶝，同\n[音釋-2]：古。\n[釋文-3]：音古\n[郭注-4]：詩曰：「抱布貿絲。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3971;3975;3323', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3117, '厞;陋;隱', '㔷', '厞12、陋[㔷]，隱也。34', '[音釋-1]：符沸。\n[釋文-2]：符沸反，字又作陫，同\n[郭注-3]：禮記曰：「厞用席。」書曰：「揚側陋。」\n[釋文-4]：側，音仄。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '6000;9639;9667', '8419', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3118, '遏;筮;逮', '', '遏、遾{筮}12，逮也3。4', '[音釋-1]：誓。\n[釋文-2]：嘗例反\n[釋文-3]：音代，一音徒帝反\n[郭注-4]：東齊曰遏，北燕曰遾，皆相及逮。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1185;2899;1147', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3119, '征;邁;行', '', '征、邁，行也。1', '[郭注-1]：詩曰：「王于出征。」邁亦行。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1101;1096;1266', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3120, '圮;敗;覆', '', '圮12、敗，覆也3。4', '[音釋-1]：皮美。\n[釋文-2]：皮美反\n[釋文-3]：芳服反，注同\n[郭注-4]：謂毁覆。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9110;2041;4852', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3121, '荐;原;再', '謜', '荐12、原[謜]3，再也4。56', '[音釋-1]：賤。\n[釋文-2]：徂薦反，又徂遜反，郭徂很反\n[釋文-3]：舍人本作𧔞，音同\n[釋文-4]：子代反\n[郭注-5]：易曰：「水荐至。」今呼重蠶爲𧔞。\n[釋文-6]：重，直用反，又直龍反。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '580;7494;2495', '1500', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3122, '憮;敉;撫', '', '憮12、敉34，撫也。5', '[音釋-1]：武。\n[釋文-2]：亡甫反\n[音釋-3]：亡婢。\n[釋文-4]：亡婢反，郭敷靡反，孫敷是反\n[郭注-5]：憮，愛撫也。敉，義見書。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6778;2036;7920', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3123, '臞;脙;膌', '', '臞12、脙34，瘠{膌}也5。6', '[音釋-1]：衢。\n[釋文-2]：字又作癯，同，求俱反\n[音釋-3]：求。\n[釋文-4]：音求\n[釋文-5]：秦昔反\n[郭注-6]：齊人謂瘠瘦爲脙。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2651;2653;2655', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3124, '桄;熲;充', '', '桄1、熲2，充也。3', '[釋文-1]：孫作光，古黃反\n[釋文-2]：古迥反\n[郭注-3]：皆充盛也。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3796;6437;5460', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3125, '屢;暱;亟', '', '屢1、暱2，亟也34。56', '[釋文-1]：婁，本又作屢，同，力住反\n[釋文-2]：女乙反\n[音釋-3]：墟記。\n[釋文-4]：墟記反\n[郭注-5]：親暱者亦數。亟亦數也。\n[釋文-6]：數，色角反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5423;4251;9014', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3126, '靡;罔;無', '', '靡、罔，無也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7696;4813;8414', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3127, '爽;差;爽;忒', '', '爽，差也1。爽，忒也2。3', '[音釋-1]：初佳\n[釋文-2]：他得反，或作𠈸\n[郭注-3]：皆謂用心差錯，不專一。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2091;3028;2091;6823', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3128, '佴;貳', '', '佴12，貳也。3', '[音釋-1]：而志。\n[釋文-2]：而志反\n[郭注-3]：佴次爲副貳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5049;3965', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3129, '劑;翦;齊', '𣦃', '劑12、翦[𣦃]3，齊也。4', '[音釋-1]：卽隨。\n[釋文-2]：卽隨反\n[釋文-3]：子淺反\n[郭注-4]：南方人呼翦刀爲劑刀。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '2782;2247;4393', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3130, '饙;餾;稔', '餁', '饙12、餾34，稔[餁]也5。67', '[音釋-1]：紛。\n[釋文-2]：方云反，字又作餴，同，說文作𩞳，云：「脩飯也。」饙、餴並同𩞳也，字書云：「一蒸米」\n[音釋-3]：力又。\n[釋文-4]：力又反\n[釋文-5]：字又作餁，同，而審反\n[郭注-6]：今呼𩛢飯爲饙，饙熟爲餾。\n[釋文-7]：𩛢，音脩，又西九反，又所九反，一音孫。廣雅：「饙謂之𩛢也。」蒼頡篇云：「𩛢，饋也。」飰，字又作𩚳，俗作飯，同，符萬反。字林云：「飯，食也，扶晚反，飤也。」', '釋言弟二', '爾雅', '3', DEFAULT, DEFAULT, '3205;3206;4484', '3207', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3131, '㑞;將;送', '', '媵{㑞}12、將，送也。3', '[音釋-1]：以證。\n[釋文-2]：以證反，又繩證反。方言云：「媵，寄也、記也、送也」\n[郭注-3]：左傳曰：「以媵秦穆姬。」詩曰：「遠于將之。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1983;1144', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3132, '作;造;爲', '', '作、造1，爲也。', '[釋文-1]：才早反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5073;1114;1882', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3133, '餥;餱;食', '', '餥12、餱3，食也。45', '[音釋-1]：非。\n[釋文-2]：音非\n[釋文-3]：音侯\n[郭注-4]：方言云：「陳 楚之閒相呼食爲餥。」\n[釋文-5]：呼，或作𧦝，同。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3216;3215;3204', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3134, '𥷤;究;竆', '', '鞫{𥷤}1、究2，窮{竆}也。3', '[釋文-1]：居六反，字又作鞠，同\n[釋文-2]：音救\n[郭注-3]：皆窮盡也。見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6650;4671;4672', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3135, '鹵;矜;鹹;苦', '䰼', '滷{鹵}1、矜[䰼]2、鹹3，苦也。45', '[釋文-1]：音魯\n[釋文-2]：鹶，音矜，本又作矜\n[釋文-3]：音咸\n[郭注-4]：滷，苦地也。可矜憐者亦辛苦，苦卽大鹹。\n[釋文-5]：粦，力田反，本今作憐，同。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '7714;9510;7716;323', '7661', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3136, '干;流;求;流;覃;覃;延', '尋', '干、流，求也。1流，覃[尋]也2。覃，延也。34', '[郭注-1]：詩曰：「左右流之。」\n[釋文-2]：徒南反，本又作（⿰爻尋）字，孫叔然云：「古覃字，同」\n[郭注-3]：皆謂蔓延相被及。\n[釋文-4]：蔓，音萬。莚，以戰反，相連不斷，又音延，本今作延。', '釋言弟二', '爾雅', '5', DEFAULT, DEFAULT, '1442;7474;5373;7474;3336;3336;1265', '1984', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3137, '佻;愉', '', '佻12，偷{愉}也3。4', '[音釋-1]：挑。\n[釋文-2]：他雕反，郭庴了反\n[釋文-3]：他侯反\n[郭注-4]：謂苟且。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5127;6825', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3138, '濳;深;濳;深;測', '勘;探', '濳1，深也2。濳[勘]、深[探]，測也3。4', '[釋文-1]：捷鹽反\n[釋文-2]：如字，又戸鴆反\n[釋文-3]：初力反\n[郭注-4]：測亦水深之別名。', '釋言弟二', '爾雅', '3;4', DEFAULT, DEFAULT, '7288;7034;7288;7034;7183', '9252;8001', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3139, '穀;鞠;生', '毓', '穀、鞠[毓]1，生也。2', '[釋文-1]：居六反\n[郭注-2]：詩曰：「糓則異室。」', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4483;1810;3874', '9786', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3140, '啜;茹;茹;虞;度', '', '啜1，茹也23。4茹5、虞，度也67。8', '[釋文-1]：常悅反，郭音銳，顧豬芮反，施丑衞、尺銳二反，說文云：「啜，嘗也。」廣雅云：「食也」\n[音釋-2]：如庶。\n[釋文-3]：音汝，食也\n[郭注-4]：啜者，拾食。\n[釋文-5]：如庶反，注同\n[音釋-6]：鐸。\n[釋文-7]：待各反，注及下同\n[郭注-8]：皆測度也。詩曰：「不可以茹。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '804;614;614;3109;1929', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3141, '試;式;用', '', '試、式，用也。1', '[郭注-1]：見詩、書。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1541;3030;2082', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3142, '誥;誓;謹', '梏', '誥[梏]、誓，謹也。1', '[郭注-1]：皆所以約勒，謹戒衆。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1529;1531;1521', '3821', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3143, '競;逐;彊', '', '競、逐，彊也12。3', '[音釋-1]：巨丈。\n[釋文-2]：強，巨丈反，注同，本或作彊字，又其良反\n[郭注-3]：皆自勉彊。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1737;1178;8489', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3144, '禦;圉;禁', '', '禦1、圉，禁也。2', '[釋文-1]：本或作御，同，俱魚呂反\n[郭注-2]：禁制。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '56;6647;71', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3145, '窒;薶;塞', '', '窒12、薶34，塞也。5', '[音釋-1]：豬乙。\n[釋文-2]：豬乙反，又丁栗反\n[音釋-3]：埋。\n[釋文-4]：亡皆反\n[郭注-5]：謂塞孔穴。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4664;626;9093', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3146, '黼;黻;彰', '', '黼12、黻34，彰也。56', '[音釋-1]：甫。\n[釋文-2]：字或作𦓞，同，音甫，白與黑曰黼，爲斧形\n[音釋-3]：弗。\n[釋文-4]：字或作𦓗，同，音弗，靑與黑曰黻，黻，戾也\n[郭注-5]：黼文如斧。黻文如兩己相背。\n[釋文-6]：己，音紀。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4944;4945;5715', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3147, '膺;身;親', '', '膺1、身，親也。2', '[釋文-1]：於矜反\n[郭注-2]：謂躬親。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2614;5248;5512', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3148, '愷悌;發', '闓𠂆', '愷悌[闓𠂆]1，發也。2', '[釋文-1]：苦亥反。徒禮反\n[郭注-2]：發，發行也。詩曰：「齊子愷悌。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3093/6977;8502', '7755/8363', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3149, '髦士;官', '覒士/𢤧士', '髦士[覒士/𢤧士]1，官也。23', '[釋文-1]：音毛，毛中之長豪曰髦，士之俊傑者，借譬爲名\n[郭注-2]：取俊士，令居官。\n[釋文-3]：令，力呈反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5734/226;9616', '5515/226/6715/226', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3150, '畯', '', '畯12，農夫也。34', '[音釋-1]：俊。\n[釋文-2]：子峻反，主田大夫、嗇夫也\n[郭注-3]：今之嗇夫是也。\n[釋文-4]：嗇，音色。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9192', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3151, '蓋;割;裂', '害', '蓋[害]1、割，裂也2。3', '[釋文-1]：古害反，舍人本作害\n[釋文-2]：音列\n[郭注-3]：蓋，未詳。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '586;2777;5338', '4614', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3152, '邕;支;載', '', '邕1、支，載也。2', '[釋文-1]：字又作擁\n[郭注-2]：皆方俗語，亦未詳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7487;1935;9569', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3153, '諈諉;絫', '', '諈諉12，累{絫}也34。56', '[音釋-1]：竹睡。女睡。\n[釋文-2]：謝之睡反，郭置睡反。郭女睡反，顧汝恚反，謝音𣨙，孫云：「楚人曰諈，秦人曰諉」\n[音釋-3]：劣僞。\n[釋文-4]：本又作纍，劣僞反，字又作絫，同，注同\n[郭注-5]：以事相屬，累爲諈諉。\n[釋文-6]：屬，之欲反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1552/1553;9716', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3154, '漠;察;淸', '殺', '漠1、察[殺]，淸也。2', '[釋文-1]：音莫，樊光云：「漠然，淸貌」\n[郭注-2]：皆淸明。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '7130;4581;7200', '1979', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3155, '庇;庥;蔭', '', '庇1、庥2，廕{蔭}也3。4', '[釋文-1]：必寐反，又音祕\n[釋文-2]：字又作休，虛求反，郭許州反\n[釋文-3]：字亦作蔭，於鴆反\n[郭注-4]：今俗語呼樹蔭爲庥。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5955;3816;524', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3156, '穀;履;禄;履;禮', '', '穀、履，禄也。1履，禮也。2', '[郭注-1]：書曰：「旣富方穀。」詩曰：「福履將之。」\n[郭注-2]：禮可以履行，見易。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4483;5430;15;5430;12', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3157, '隱;占', '𨽌', '隱[𨽌]，占也。12', '[郭注-1]：隱度。\n[釋文-2]：度，大各反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9667;2079', '3751', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3158, '逆;迎', '', '逆，迎也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1124;1125', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3159, '㦧;曾', '朁', '㦧[朁]12，曾也。3', '[音釋-1]：慘。\n[釋文-2]：七感反\n[郭注-3]：發語辭，見詩。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6899;710', '3048', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3160, '增;益', '', '增，益也。1', '[郭注-1]：今江東通言增。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9090;3154', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3161, '寠;貧', '婁', '窶{寠}[婁]12，貧也。3', '[音釋-1]：求矩。\n[釋文-2]：求矩反，毛詩傳云：「窶者無禮」\n[郭注-3]：謂貧陋。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4611;3984', '8321', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3162, '𥴨;隱;僾;悒', '旡', '薆{𥴨}1，隱也。2僾[旡]3，唈{悒}也45。6', '[音釋-1]：愛\n[郭注-2]：謂隱蔽。\n[音釋-3]：愛\n[音釋-4]：烏合。\n[釋文-5]：薆、僾，皆音愛，烏槩反。烏合反\n[郭注-6]：嗚唈，短氣。皆見詩。', '釋言弟二', '爾雅', '3', DEFAULT, DEFAULT, '3005;9667;5017;6821', '5595', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3163, '基;經;基;設', '', '基，經也。1基，設也。2', '[郭注-1]：基業，所以自經營。\n[郭注-2]：亦爲造設。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9044;8530;9044;1562', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3164, '祺;祥;祺;吉', '', '祺1，祥也。2祺，吉也。34', '[釋文-1]：音其，下同\n[郭注-2]：謂徵祥。\n[郭注-3]：祥，吉之先見。\n[釋文-4]：見，賢遍反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '22;18;22;876', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3165, '兆;域', '垗', '兆[垗]1，域也。23', '[釋文-1]：廣雅云：「葬地也。」本又作垗，音同\n[郭注-2]：謂塋界。\n[釋文-3]：塋，音營。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '2081;8385', '9143', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3166, '肈;敏', '劭;誨', '肈[劭]，敏[誨]也。1', '[郭注-1]：書曰：「肈牽車牛。」', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8374;1999', '9221;1497', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3167, '挾;藏', '', '挾12，藏也。3', '[音釋-1]：叶。\n[釋文-2]：戸牒反\n[郭注-3]：今江東通言挾。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7869;695', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3168, '浹;徹', '', '浹12，徹也3。45', '[音釋-1]：接。\n[釋文-2]：子協反，郭音接\n[釋文-3]：直列反\n[郭注-4]：謂霑徹。\n[釋文-5]：霑，竹簾反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7468;1997', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3169, '𤾕;廢;替;滅', '', '𤾕，廢也。替，滅也。1', '[郭注-1]：亦爲滅絶。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5961;6697;7441', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3170, '速;徵;徵;召', '誎', '速[誎]，徵也。徵，召也。1', '[郭注-1]：易曰：「不速之客。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1121;5239;5239;842', '1535', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3171, '琛;寶', '', '琛12，寶也。3', '[音釋-1]：勑金。\n[釋文-2]：勑金反，郭舒金反\n[郭注-3]：詩曰：「來獻其琛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '210;4590', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3172, '探;試', '', '探1，試也。23', '[釋文-1]：吐南反\n[郭注-2]：刺探甞試。\n[釋文-3]：刺，七赤反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8001;1541', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3173, '髦;選;髦;俊', '覒;𢤧', '髦[覒]，選也1。2髦[𢤧]，俊也。3', '[釋文-1]：宣戀反\n[郭注-2]：俊士之選。\n[郭注-3]：士中之俊，如毛中之髦。', '釋言弟二', '爾雅', '1;3', DEFAULT, DEFAULT, '5734;1143;5734;4960', '5515;6715', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3174, '俾;職', '', '俾1，職也。2', '[釋文-1]：必爾反，沈方寐反\n[郭注-2]：使供職。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5096;7805', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3175, '紕;飾', '俾', '紕[俾]12，飾也3。45', '[音釋-1]：備。\n[釋文-2]：婢寐反，謝房彌反，郭方寐反\n[釋文-3]：申職反\n[郭注-4]：謂緣飾，見詩。\n[釋文-5]：緣，悅面反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8759;4894', '5096', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3176, '淩;栗;栗;慼', '凌', '淩[凌]12，慄{栗}也3。45慄{栗}，慼也6。7', '[音釋-1]：凌。\n[釋文-2]：力升反，案郭注，意當作陵，埤蒼云：「淩，慄也。」樊注作凌，「冰澟也。」力膺反\n[釋文-3]：音栗\n[郭注-4]：淩懅戰慄。\n[釋文-5]：懅，舊音遽，其據反，亦謂戰慄也。\n[釋文-6]：字又作戚，七歷反\n[郭注-7]：戰慄者，憂慼。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '7066;4391;4391;6934', '7511', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3177, '蠲;明;茅;明;明;朗', '炯;萌', '蠲[炯]1，明也。2茅，明[萌]也。3明，朗也。', '[釋文-1]：古玄反，又音圭\n[郭注-2]：蠲，淸明貌。\n[郭注-3]：左傳曰：「前茅慮無。」', '釋言弟二', '爾雅', '1;4', DEFAULT, DEFAULT, '8827;4321;326;4321;4321;4277', '6503;491', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3178, '猷;圖;猷;若', '', '猷，圖也。1猷，若也。2', '[郭注-1]：周官曰：「以猷鬼神祇。」謂圖畫。\n[郭注-2]：詩曰：「寔命不猷。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6373;3913;6373;599', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3179, '偁;舉;稱;好', '', '偁，舉也。1稱2，好也3。4', '[郭注-1]：書曰：「偁爾戈。」\n[釋文-2]：尺證反\n[釋文-3]：呼報反，又如字，注同\n[郭注-4]：物稱人意亦爲好。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5065;7957;4493;8189', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3180, '坎;律;銓', '勘', '坎[勘]1、律，銓也2。3', '[釋文-1]：字又作欿，苦感反\n[釋文-2]：七全反，銓卽稱\n[郭注-3]：易坎卦主法。法、律，皆所以銓量輕重。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '9084;1257;9371', '9252', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3181, '矢;誓', '', '矢，誓也。1', '[郭注-1]：相約誓。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3307;1531', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3182, '舫;舟', '方', '舫[方]1，舟也。23', '[釋文-1]：方訪反，又音方\n[郭注-2]：並兩船。\n[釋文-3]：併，步頂反，字又作並。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '5445;5436', '5453', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3183, '泳;游', '', '泳1，游也。23', '[釋文-1]：音詠\n[郭注-2]：濳行游水底。\n[釋文-3]：底，丁禮反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7287;4304', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3184, '𨽿;及', '', '迨{𨽿}1，及也。2', '[釋文-1]：音待\n[郭注-2]：東齊曰迨。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1947;1918', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3185, '冥;幼', '窈', '冥1，幼[窈]也。2', '[釋文-1]：覓經反，字林亡經、亡定二反\n[郭注-2]：幼稚者，冥昧。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '4311;2498', '4676', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3186, '降;下', '', '降1，下也2。', '[釋文-1]：古巷反，一讀戸江反\n[釋文-2]：如字，一讀戸嫁反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9647;9', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3187, '傭;均', '', '傭12，均也3。4', '[音釋-1]：勑容。\n[釋文-2]：勑恭反\n[釋文-3]：音鈞\n[郭注-4]：齊等。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5016;9028', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3188, '強;暴', '彊;虣', '強[彊]，暴[虣]也1。2', '[釋文-1]：字又作虣，同，蒲報反\n[郭注-2]：強梁，淩暴。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8824;4246', '8489', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3189, '窕;肆;肆;力', '', '窕1，肆也。23肆，力也。4', '[釋文-1]：吐彫反\n[郭注-2]：輕窕者，好放肆。\n[釋文-3]：好，呼報反。\n[郭注-4]：肆，極力。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4669;6068;6068;9210', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3190, '俅;戴', '', '俅1，戴也。23', '[釋文-1]：音求\n[郭注-2]：詩曰：「戴弁俅俅。」\n[釋文-3]：弁，音卞。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4957;1779', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3191, '瘞;幽', '', '瘞1，幽也。23', '[釋文-1]：於例反，又於計反，或作𨻊，同\n[郭注-2]：幽亦薶也。\n[釋文-3]：薶，莫皆反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9137;2501', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3192, '氂;罽', '毳', '氂[毳]12，罽也34。5', '[音釋-1]：離。\n[釋文-2]：力知反，又力才反，李本作毳，呂銳反\n[音釋-3]：計。\n[釋文-4]：居例反\n[郭注-5]：毛氂，所以爲罽。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '776;4823', '5398', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3193, '烘;燎', '', '烘1，燎也23。4', '[釋文-1]：沈、顧火公反，郭巨凶反，孫音恭，字林巨凶、甘凶二反\n[音釋-2]：料。\n[釋文-3]：力皎反，又力召、力弔二反\n[郭注-4]：謂燒燎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6456;6483', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3194, '煁;烓', '', '煁12，烓也34。56', '[音釋-1]：市針。\n[釋文-2]：市針反\n[音釋-3]：頃。\n[釋文-4]：郭音恚，字林口穎反，說文云：「行竈也。」顧口井、烏攜二反\n[郭注-5]：今之三隅竈，見詩。\n[釋文-6]：隅，音虞。竈，則到反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6453;6452', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3195, '陪;朝', '', '陪1，朝也2。3', '[釋文-1]：蒲回反\n[釋文-2]：直遥反，注同\n[郭注-3]：陪位爲朝。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9697;4287', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3196, '康;苛', '', '康，苛也12。3', '[音釋-1]：何。\n[釋文-2]：音何\n[郭注-3]：謂苛刻。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4457;543', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3197, '樊;藩', '棥', '樊[棥]1，藩也2。34', '[釋文-1]：音煩\n[釋文-2]：方元反\n[郭注-3]：謂藩籬。\n[釋文-4]：籬，力支反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1774;590', '2088', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3198, '賦;量', '', '賦，量也1。23', '[釋文-1]：音良，一音亮\n[郭注-2]：賦稅，所以評量。\n[釋文-3]：評，音病。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3981;5243', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3199, '粻;糧', '', '粻1，糧也2。3', '[釋文-1]：音張，字林又丈庚反\n[釋文-2]：音良\n[郭注-3]：今江東通言粻。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4382;4369', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3200, '庶;侈;庶;幸', '', '庶，侈也1。2庶，幸也。34', '[釋文-1]：昌氏反，又尸氏反\n[郭注-2]：庶者衆多爲奢侈。\n[郭注-3]：庶幾，僥倖。\n[釋文-4]：幾，音機。僥，古堯反。倖，胡耿反，與幸通用。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5956;5131;5956;6623', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3201, '筑;拾', '叔', '筑[叔]12，拾也。34', '[音釋-1]：竹。\n[釋文-2]：音竹\n[郭注-3]：謂拾掇。\n[釋文-4]：掇，丁活反，說文云：「拾取。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '2997;7986', '1924', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3202, '奘;駔', '', '奘12，駔也34。56', '[音釋-1]：徂朗。\n[釋文-2]：徂朗反，秦 晉謂大爲奘\n[音釋-3]：粗。\n[釋文-4]：在魯反，又子朗反，沈集注本作𤖛，音同，孫、樊二本並作將且，而無奘、駔，沈集衆本合爲一字\n[郭注-5]：今江東呼大爲駔，駔猶麤也。\n[釋文-6]：𧦝，火乎反，或作呼，同。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6668;6233', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3203, '集;會', '', '集，會也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2367;3274', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3204, '舫;泭', '', '舫1，泭也23。45', '[釋文-1]：謝音方，詩亦作方，施甫訪反，樊本作坊，符方反，又音方\n[音釋-2]：孚。\n[釋文-3]：郭音孚，字或作𣻜，同。樊本作坿，沈音附\n[郭注-4]：水中𥴖筏。\n[釋文-5]：𥴖，皮佳反，本又作𥱼，同。筏，音伐，方言云：「泭謂之𥴖，𥴖謂之筏，秦 晉通語。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5445;7282', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3205, '洵;均;洵;龕', '旬;咸', '洵[旬]1，均也。2洵，龕[咸]也3。4', '[釋文-1]：郭音巡，注同，謝音荀，下句放此\n[郭注-2]：謂調均。\n[釋文-3]：苦南反，字或作含，本今作龕\n[郭注-4]：未詳。', '釋言弟二', '爾雅', '1;4', DEFAULT, DEFAULT, '7111;9028;7111;7689', '5806;872', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3206, '逮;遝', '眔', '逮1，遝[眔]也23。45', '[釋文-1]：音代，一音大計反，下同\n[音釋-2]：沓。\n[釋文-3]：孫、郭徒荅反\n[郭注-4]：今荆楚人皆云遝，音沓。\n[釋文-5]：沓，與上同，亦徒荅反。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1147;1116', '2142', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3207, '是;則', '', '是，則也。1', '[郭注-1]：是事可法則。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1089;2755', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3208, '畫;形', '', '畫12，形也。3', '[音釋-1]：𦘕。\n[釋文-2]：胡卦反\n[郭注-3]：畫者爲形象。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1944;5712', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3209, '賑;富', '', '賑12，富也。3', '[音釋-1]：之忍。\n[釋文-2]：之忍反，又之人、之刃二反，字林云：「富也，刃引反」\n[郭注-3]：謂隱賑富有。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3942;4584', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3210, '局;分', '', '局1，分也23。4', '[釋文-1]：彊六反\n[音釋-2]：符閒。\n[釋文-3]：符問反，注同\n[郭注-4]：謂分部。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '958;708', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3211, '齌;怒', '', '懠{齌}12，怒也。3', '[音釋-1]：才細。\n[釋文-2]：才計反\n[郭注-3]：詩曰：「天之方懠。」音薺。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6457;6877', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3212, '僁;聲', '', '僁12，聲也。3', '[音釋-1]：屑。\n[釋文-2]：音屑，動草聲也，郭音與稷、契同，施私秩反，字又作𠋱\n[郭注-3]：謂聲音。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5020;7808', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3213, '葵;揆;揆;度', '', '葵1，揆也2。3揆，度也45。6', '[釋文-1]：求維反\n[釋文-2]：其水反\n[郭注-3]：詩曰：「天子葵之。」\n[音釋-4]：徒各。\n[釋文-5]：徒各反，注同\n[郭注-6]：商度。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '260;7973;7973;1929', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3214, '逮;及', '', '逮，及也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1147;1918', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3215, '惄;飢', '', '惄1，飢也。2', '[釋文-1]：本又作（⿱𠦑心），同，奴歷反\n[郭注-2]：惄然，飢意。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6803;3259', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3216, '眕;重', '', '眕12，重也。3', '[音釋-1]：之忍。\n[釋文-2]：之忍反\n[郭注-3]：謂厚重，見左傳。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2138;5242', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3217, '獵;虐', '邋', '獵[邋]，虐也。1', '[郭注-1]：淩獵，暴虐。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6358;3114', '1181', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3218, '土;田', '', '土，田也。12', '[郭注-1]：別二名。\n[釋文-2]：別，彼列反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9019;9170', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3219, '戍;遏', '', '戍1，遏也。23', '[釋文-1]：式喻反\n[郭注-2]：戍守，所以止寇賊。\n[釋文-3]：𡨥，苦侯反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8381;1185', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3220, '師;人', '', '師，人也。1', '[郭注-1]：謂人衆。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3862;4948', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3221, '硈;鞏', '', '硈12，鞏也3。4', '[音釋-1]：苦角。\n[釋文-2]：苦角反\n[釋文-3]：九勇反\n[郭注-4]：硈然，堅固。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6029;1801', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3222, '棄;忘', '', '棄1，忘也2。', '[釋文-1]：丘異反\n[釋文-2]：音亡', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2493;6844', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3223, '嚻;閑', '悠;閒', '嚻[悠]12，閑[閒]也。3', '[音釋-1]：五刀。\n[釋文-2]：五刀反，又許嬌反\n[郭注-3]：嚻然，閑暇貌。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '1438;7767', '6927;7760', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3224, '謀;心', '', '謀，心也。1', '[郭注-1]：謀慮以心，', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1508;6703', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3225, '獻;聖', '虔', '獻[虔]，聖也。1', '[郭注-1]：謚法曰：「聰明睿智曰獻。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6364;7801', '3111', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3226, '里;邑', '', '里，邑也。1', '[郭注-1]：謂邑居。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9167;4002', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3227, '襄;除', '', '襄1，除也。2', '[釋文-1]：四羊反，或而羊反\n[郭注-2]：詩曰：「不可襄也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5322;9690', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3228, '振;古', '陳', '振[陳]，古也。1', '[郭注-1]：詩曰：「振古如兹。」猶云久若此。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '7961;1462', '9686', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3229, '懟;怨', '', '懟12，怨也。', '[音釋-1]：隊。\n[釋文-2]：直類反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6886;6876', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3230, '縭;介', '', '縭12，介也3。45', '[音釋-1]：離。\n[釋文-2]：力知反\n[釋文-3]：音界，李、孫、顧、舍人本並云：「䋠，羅也。介，別也」\n[郭注-4]：縭者，繫。介猶閡。\n[釋文-5]：閡，五代反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '8685;714', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3231, '號;謼', '', '號12，謼也34。5', '[音釋-1]：毫。\n[釋文-2]：戸羔反，又胡到反\n[音釋-3]：火故。\n[釋文-4]：火故反，又如字，又作呼\n[郭注-5]：今江東皆言謼。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3068;1577', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3232, '凶;咎', '', '凶，咎也1。', '[釋文-1]：求九反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4527;5173', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3233, '苞;稹', '', '苞1，稹也2。34', '[釋文-1]：補茅反\n[釋文-2]：謝之忍反，郭振、眞二音\n[郭注-3]：今人呼物叢緻者爲稹。\n[釋文-4]：緻，直吏反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '388;4423', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3234, '吾;寤', '午', '逜{吾}[午]12，寤也3。4', '[音釋-1]：悟。\n[釋文-2]：五故反，孫本吾字作午，吾補反\n[釋文-3]：五故反\n[郭注-4]：相干寤。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '837;4686', '9797', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3235, '定;題', '頂', '顁{定}[頂]12，題也3。45', '[音釋-1]：丁佞。\n[釋文-2]：丁佞反，字又作定，注同\n[釋文-3]：徒兮反\n[郭注-4]：題，額也。詩曰：「麟之定。」\n[釋文-5]：麟，力仁反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '4574;5609', '5607', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3236, '猷;肎;可', '', '猷、肎1，可也。2', '[釋文-1]：肯，苦等反，或作古𦚋字，字林作𦚋，並同\n[郭注-2]：詩曰：「猷來無棄。」肎，今通言。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6373;2733;3058', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3237, '務;侮', '', '務，侮也1。2', '[釋文-1]：亡甫反\n[郭注-2]：詩曰：「外禦其侮。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9217;5148', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3238, '貽;遺', '', '貽1，遺也23。45', '[釋文-1]：以之反\n[音釋-2]：唯季。\n[釋文-3]：唯季反，注同\n[郭注-4]：相歸遺。\n[釋文-5]：歸，巨位反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3997;1174', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3239, '貿;買', '', '貿1，買也。2', '[釋文-1]：音茂\n[郭注-2]：廣二名。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3971;3978', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3240, '賄;財', '', '賄1，財也。', '[釋文-1]：火罪反', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3936;3937', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3241, '甲;狎', '', '甲，狎也1。2', '[釋文-1]：乎甲反\n[郭注-2]：謂習狎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9739;6338', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3242, '菼;騅;菼;薍', '', '菼12，騅也3。菼，薍也45。67', '[音釋-1]：他敢。\n[釋文-2]：字又作𦵹，他敢反，或待感反\n[釋文-3]：章誰反，如騅馬色也\n[音釋-4]：五患。\n[釋文-5]：五患反\n[郭注-6]：詩曰：「毳衣如菼。」菼，草色如騅，在靑白之閒。\n[釋文-7]：毳，昌銳反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '422;6147;422;421', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3243, '粲;餐', '', '粲1，餐也23。4', '[釋文-1]：七旦反\n[音釋-2]：孫。\n[釋文-3]：飱，謝素昆反，說文云：「餔也。」字林云：「水澆飯也。」本又作䬸，施七丹反，字林作飱，云：「吞食」\n[郭注-4]：今河北人呼食爲餐。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4349;3227', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3244, '渝;變', '', '渝1，變也。2', '[釋文-1]：音榆，舍人作䞕，同\n[郭注-2]：謂變易。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7439;2020', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3245, '宜;肴', '', '宜，肴也1。2', '[釋文-1]：音爻\n[郭注-2]：詩曰：「與子宜之。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4597;2673', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3246, '夷', '咦', '夷[咦]1，悅也。2', '[釋文-1]：恞，本又作夷，音同\n[郭注-2]：詩曰：「我心則夷。」', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '6613', '823', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3247, '顚;頂', '', '顚1，頂也。2', '[釋文-1]：丁田反\n[郭注-2]：頭上。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5606;5607', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3248, '耋;老', '', '耋12，老也。3', '[音釋-1]：迭。\n[釋文-2]：田結反，孫他結反，云：「老人面如鐵色」\n[郭注-3]：八十爲耋。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5376;5375', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3249, '輶;輕', '', '輶12，輕也。3', '[音釋-1]：酋。\n[釋文-2]：由久反，又餘周反\n[郭注-3]：詩曰：「德輶如毛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9520;9519', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3250, '俴;淺', '', '俴12，淺也。3', '[音釋-1]：踐。\n[釋文-2]：慈瞚反\n[郭注-3]：詩曰：「小戎俴收。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5123;7226', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3251, '匋;絞', '絛;筊', '綯{匋}[絛]12，絞[筊]也3。45', '[音釋-1]：陶。\n[釋文-2]：徒刀反\n[釋文-3]：古卯反\n[郭注-4]：糾絞繩索。\n[釋文-5]：糾，吉黝反。索，悉各反。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '3287;6627', '8664;2952', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3252, '化', '', '訛，化也。1', '[郭注-1]：詩曰：「四國是訛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5214', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3253, '跋;獵', '', '跋1，躐{獵}也23。45', '[釋文-1]：蒲末反，郭音貝，又補葛反\n[音釋-2]：獵。\n[釋文-3]：力輒反\n[郭注-4]：詩曰：「狼跋其胡。」\n[釋文-5]：狼，音郎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1383;6358', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3254, '疐;跲', '', '疐12，跲也34。5', '[音釋-1]：致。\n[釋文-2]：竹利反，又得異反，又竹季反，說文云：「礙足不行。」與躓同\n[音釋-3]：其葉。\n[釋文-4]：其業反，又居業反，郭又音甲，廣雅云：「跲，代也」\n[郭注-5]：詩曰：「載疐其尾。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2505;1380', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3255, '烝;塵', '', '烝1，塵也。23', '[釋文-1]：之仍反\n[郭注-2]：人衆，所以生塵埃。\n[釋文-3]：埃，音哀。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6429;6288', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3256, '戎;相', '胥', '戎[胥]1，相也2。3', '[釋文-1]：如字，本或作𢫨，顧如勇反，沈如升反\n[釋文-2]：如字，又息亮反\n[郭注-3]：相佐助。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8377;2162', '2698', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3257, '飫;私', '醧;厶', '飫[醧]1，私[厶]也。2', '[釋文-1]：於庶反\n[郭注-2]：宴飲之私。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '4429', '9844;1905', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3258, '孺;屬', '', '孺1，屬也2。3', '[釋文-1]：𡦗，如戍反，本今作孺\n[釋文-2]：雛欲反\n[郭注-3]：謂親屬。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9770;5427', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3259, '幕;暮', '', '幕1，暮也。2', '[釋文-1]：音莫\n[郭注-2]：幕然，暮夜。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4881', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3260, '煽;熾;熾;盛', '', '煽1，熾也2。熾，盛也3。4', '[釋文-1]：音扇\n[釋文-2]：昌至反\n[釋文-3]：晠，市正反，本作盛，同\n[郭注-4]：互相訓。煽，義見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6527;6511;6511;3140', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3261, '柢;本', '', '柢12，本也。3', '[音釋-1]：帝。\n[釋文-2]：謝音帝\n[郭注-3]：謂根本。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3560;3559', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3262, '窕;閒', '', '窕1，閒也23。45', '[釋文-1]：郭徒了反，舍人本作跳，云：「跳者，躍之閒」\n[音釋-2]：閑。\n[釋文-3]：音閑，或如字\n[郭注-4]：窈窕，閒隙。\n[釋文-5]：𥥆，音杳。𨻶，去逆反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4669;7760', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3263, '淪;率', '', '淪，率也。1', '[郭注-1]：相率使。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7176;8783', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3264, '罹;毒', '', '罹1，毒也。23', '[釋文-1]：力知反\n[郭注-2]：憂思慘毒。\n[釋文-3]：思，息吏反。慘，七感反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4849;236', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3265, '檢;同', '僉', '檢[僉]1，同也。23', '[釋文-1]：李、郭居儉反\n[郭注-2]：模範同等。\n[釋文-3]：模，亡胡反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3769;4801', '3270', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3266, '郵;過', '', '郵1，過也2。3', '[釋文-1]：音尤\n[釋文-2]：古臥反\n[郭注-3]：道路所經過。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4012;1110', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3267, '遜;遯', '遁', '遜，遯[遁]也1。2', '[釋文-1]：字又作遁，又作𨔡，同，徒頓反\n[郭注-2]：謂逃去。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1140;1172', '1139', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3268, '斃;踣;僨;僵', '', '斃12，踣也34。56僨78，僵也910。11', '[音釋-1]：弊。\n[釋文-2]：獘，字亦作弊，又作斃，婢世反，又婢設反，郭步計反\n[音釋-3]：蒲北。\n[釋文-4]：蒲北反，又音赴，或孚豆、蒲侯二反\n[郭注-5]：前覆。\n[釋文-6]：覆，芳服反。\n[音釋-7]：糞。\n[釋文-8]：甫問反\n[音釋-9]：姜。\n[釋文-10]：居良反\n[郭注-11]：卻偃。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6363;1392;5152;5153', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3269, '畛;殄', '', '畛1，殄也2。3', '[釋文-1]：之引反，又之人反\n[釋文-2]：大典反\n[郭注-3]：謂殄絶。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9188;2551', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3270, '曷;盍', '', '曷，盍也1。2', '[釋文-1]：戸臘反\n[郭注-2]：盍，何不。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3046;3180', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3271, '虹;潰', '訌;䜋', '虹[訌]1，潰[䜋]也23。4', '[釋文-1]：音洪，顧作訌，音同。李本作降，下江反\n[音釋-2]：會。\n[釋文-3]：乎內反\n[郭注-4]：謂潰敗。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8933;7224', '1649;1650', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3272, '音;闇', '晻', '隌{音}[晻]12，闇也3。456', '[音釋-1]：唵。\n[釋文-2]：字林或作晻，同，烏感反\n[釋文-3]：音暗\n[郭注-4]：隌然，冥貌。\n[音釋-5]：冥，莫定。\n[釋文-6]：冥，莫定反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1739;7770', '4223', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3273, '䵑;膠', '', '䵑12，膠也3。45', '[音釋-1]：女乙。\n[釋文-2]：字又作䵒，同，女乙反，郭音馹\n[釋文-3]：音交\n[郭注-4]：膠，黏䵑。\n[釋文-5]：黏，女廉反，字林云：「相著也。」字書云：「糊也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2728', NULL, 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3274, '孔;甚', '', '孔，甚也。', '', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7702;3041', NULL, 'null', 'null');
