INSERT INTO `t_han_nga` VALUES (3279, '攸;所', '𠧴;疋', '攸[𠧴]，所[疋]也。', '', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '2034;9482', '1422', 'null', 'null');
