INSERT INTO `t_han_nga` VALUES (3281, '鬱;氣', '气', '鬱，氣[气]也。1', '[郭注-1]：鬱然，氣出。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '3848;4374', '224', 'null', 'null');
