INSERT INTO `t_han_nga` VALUES (3284, '祈;叫', '', '祈，叫也1。23', '[釋文-1]：古弔反\n[郭注-2]：祈𥙊者，叫呼而請事。\n[釋文-3]：呼，火故反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '50;924', NULL, 'null', 'null');
