INSERT INTO `t_han_nga` VALUES (4640, '諸;之;乎;旃;焉;惡乎;於何;烏乎;吁𧪰', '', '諸，之也、乎也。旃，焉也。惡乎1，於何也。烏乎，吁嗟{吁𧪰}也。吁嗟，嗚呼也。有所歎美，有所傷痛，随事爲義也。', '[音義-1]：烏', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '1489;3859;3066;4297;2489;6880/3066;2487/5023;2487/3066;913/1672', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4641, '無念;無寧;不顯;不承', '', '無念，念也。無寧，寧也。不顯，顯也。不承，承也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '8414/6719;8414/3056;7704/5690;7704/7912', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4642, '不肖;不似', '', '不肖，不似也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '7704/2643;7704/5084', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4643, '繩;譽', '偁', '繩[偁]之，譽之也。', '', '廣訓第三', '小爾雅', '1', DEFAULT, DEFAULT, '8692;1569', '5065', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4644, '詰朝;明旦', '', '詰朝，明旦也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '1697/4287;4321/4283', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4645, '黃耇;壽考', '', '「遐不黃耇。」言壽考也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '9201/5379;5382/5383', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4646, '碩膚;瑕;大美;遠', '嘏', '「公孫碩膚，徳音不瑕[嘏]。」道成王大美，聲稱遠也。', '', '廣訓第三', '小爾雅', '2', DEFAULT, DEFAULT, '5628/2598;144;6596/2355;1198', '1463', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4647, '韡', '', '「鄂不韡韡1。」言韡韡也。', '[音義-1]：羽鬼切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '3883', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4648, '賢', '', '「我從事獨賢。」勞事獨多也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '3943', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4649, '甫甫;大', '', '「魴鱮甫甫1。」語其大也。', '[音義-1]：符方切。象吕切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '2083/2083;6596', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4650, '吳吳;衆', '', '「麀鹿麌麌{吳吳}1。」語其衆也。', '[音義-1]：於丘切。虞矩切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '6619/6619;5235', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4651, '錯;雜', '', '「海物維錯。」錯，雜也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '9327;5333', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4652, '氂;繪;哤', '', '雜毛曰氂，雜彩曰繪，雜言曰哤1。', '[音義-1]：莫江切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '776;8607;923', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4653, '寡;㷀;嫠', '赹', '凡無妻、無夫通謂之寡。寡夫曰㷀[赹]1，寡婦曰嫠2。', '[音義-1]：渠營切，亦作惸\n[音義-2]：陵之切', '廣義第四', '小爾雅', '2', DEFAULT, DEFAULT, '4606;0;8353', '1015', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4654, '屬婦;逮', '', '妾婦之賤者謂之屬婦。屬1，逮也。逮婦之名，言其微也。', '[音義-1]：時欲切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '5427/8130;1147', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4655, '幸', '', '非分而得謂之幸1。', '[音義-1]：扶問切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '6623', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4656, '讓', '', '詰責以辭謂之讓。', '', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '1693', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4657, '淫;烝;報;通', '勽', '男女不以禮交謂之淫。上淫曰烝，下淫曰報[勽]，旁淫曰通。', '', '廣義第四', '小爾雅', '3', DEFAULT, DEFAULT, '7221;6429;6649;1134', '5807', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4658, '慙;愧;戁;恧;逡', '夋', '不直失節謂之慙，慙，愧也。面慙曰戁1，心慙曰恧2，體慙曰逡[夋]。', '[音義-1]：乃版切\n[音義-2]：女六切', '廣義第四', '小爾雅', '5', DEFAULT, DEFAULT, '6957;8344;6723;6958;1161', '3362', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4659, '大行', '', '諱死謂之大行。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '6596/1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4660, '蘇', '', '死而復生謂之蘇。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4661, '阽', '', '疾甚謂之阽1。', '[音義-1]：余廉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '9689', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4662, '戚;近;從', '', '請天子命曰「未可以戚先王」，請諸侯命曰「未可以近先君」，請大夫命曰「未可以從先子1」。', '[音義-1]：牆容切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '8400;1180;5225', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4663, '櫬;柩', '', '空棺謂之櫬，有屍謂之柩。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3828;8440', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4664, '賵;襚', '', '饋死者謂之賵1，衣服謂之襚2。', '[音義-1]：撫鳯切\n[音義-2]：徐醉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3994;5363', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4665, '殔;池;壙;竁;窆;封', '', '埋柩謂之殔1。殔坎謂之池。壙謂之竁2，下棺謂之窆。塡竁謂之封。', '[音義-1]：羊至切\n[音義-2]：昌縜切，又充芮切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2543;7257;9114;4678;4679;9074', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4666, '宰;冢;壟;塋', '', '宰，塚{冢}也。壟，塋也。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '4593;5813;9142;9139', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4667, '殤', '', '無主之鬼謂之殤。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2537', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4668, '織;繒', '', '治絲曰織，織，繒也1。', '[音義-1]：慈陵切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8531;8590', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4669, '布', '', '麻、紵、葛曰布，布，通名也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4910', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4670, '纊;緜;絮;纊', '', '纊，綿{緜}也。絮之細者曰纊。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8726;8513;8724;8726', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4671, '繒;縞;素', '', '繒之精者曰縞1，縞之麄者曰素。', '[音義-1]：古老切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8590;8599;8774', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4672, '葛;絺;綌', '', '葛之精者曰絺，麄者曰綌。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '458;8737;8738', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4673, '元服', '', '在首謂之元服。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2/5447', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4674, '弁髦', '', '弁髦，大古冠，布冠而敝之者也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5467/5734', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4675, '題;定;顚;顔;顙;頟', '頂', '題、定[頂]、顚、顔、䫙{顙}，額{頟}也。', '', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '5609;4574;5606;5600;5608;5610', '5607', 'null', 'null');
