INSERT INTO `t_han_nga` VALUES (4677, '紱;綬', '', '紱謂之綬1。', '[音義-1]：分物切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4925;8646', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4678, '襜褕;童容', '', '襜褕謂之童容1。', '[音義-1]：蚩占切。容朱切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5290/5256;1747/4587', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4679, '藍縷', '襤褸', '布褐而紩之謂之藍縷[襤褸]1。', '[音義-1]：直質切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '282/8674', '5281/5267', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4680, '絝;裳', '', '袴{絝}謂之裳。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8659;4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4681, '冉', '襜', '蔽膝謂之袡{冉}[襜]1。', '[音義-1]：蚩廉切', '廣服第六', '小爾雅', '1', DEFAULT, DEFAULT, '6073', '5290', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4682, '厲', '', '帶之垂者謂之厲。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5983', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4683, '幎', '', '大巾謂之幂{幎}1。', '[音義-1]：莫狄切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4875', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4684, '楃;幕', '', '覆帳謂之幄，幄{楃}，幕也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3672;4881', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4685, '簀;笫', '', '簀，牀笫也1。', '[音義-1]：士莊切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2908;2909', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4686, '扇;䈉', '', '大扇謂之䈉。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '7722;0', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4687, '杖;梃', '', '杖謂之梃。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '3739;3578', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4688, '鍵;籥', '𨷲', '鍵謂之籥[𨷲]1。', '[音義-1]：巨偃切', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '9312;2890', '7772', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4689, '弈', '', '棊局謂之弈。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '1771', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4690, '屨;逹屨', '', '在足謂之屨。屨，尊者曰逹屨。逹，謂之金舄，舄而金絇也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5431;1163/5431', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4691, '侯;鵠;正;執', '臬', '射有張布謂之侯。侯中者謂之鵠1。鵠中者謂之正，正方二尺。正中者謂之𣙗{執}[臬]2，𣙗方六寸。', '[音義-1]：古毒切\n[音義-2]：諸城切。倪結切，亦作臬', '廣器第七', '小爾雅', '4', DEFAULT, DEFAULT, '3311;2413;1087;6646', '3759', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4692, '棘;戟', '', '棘，戟也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '4397;8375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4693, '戚;鉞;斧', '戉', '鏚{戚}1、鉞[戉]，斧也。', '[顏注-1]：鎗歷切', '廣器第七', '小爾雅', '2', DEFAULT, DEFAULT, '8400;9420;9475', '8399', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4694, '干;瞂;盾', '𢧀', '干[𢧀]、瞂1，盾也。', '[音義-1]：房越切，又徒本切', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '1442;2221;2220', '8379', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4695, '戈;戟', '', '戈，句孑戟也1。', '[音義-1]：古侯切。吉列切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8373;8375', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4696, '室;鞞;棓;珌', '琫', '刀之韒謂之室，室謂之鞞1。鞛{棓}[琫]2、珌3，鞞之飾也。', '[音義-1]：布頂切\n[音義-2]：布孔切\n[音義-3]：壁吉切', '廣器第七', '小爾雅', '3', DEFAULT, DEFAULT, '4557;1813;3741;128', '127', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4697, '弢', '', '矢服謂之弢1。', '[音義-1]：他刀切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8496', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4698, '艇;符;舳;艫;楫;橈', '', '小船謂之艇1，艇之小者曰䒀{符}2。船頭謂之舳，尾謂之艫。楫謂之橈。', '[音義-1]：待鼎切\n[音義-2]：薄故切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '5450;2898;5440;5441;3788;3591', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4699, '龍;轅;輈;軫;枕;較;榦;衡;軶;烏啄', '', '車轅上者謂之𨏠{龍}1，轅謂之輈，軫謂之枕，較謂之幹{榦}2。衡，軶也，軶上謂之烏啄。', '[音義-1]：力公切\n[音義-2]：訖岳切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7687;9556;9557;9540;3678;9531;3618;2843;9560;2487/953', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4700, '纍;綆;繘', '', '纍1、綆，繘也2。', '[音義-1]：力追切\n[音義-2]：以律切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8684;8719;8718', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4701, '韜;索;繩', '絛', '縚{韜}[絛]1，索也。大者謂之索，小者謂之繩。', '[音義-1]：他刀切，亦作絛', '廣器第七', '小爾雅', '1', DEFAULT, DEFAULT, '3386;3871;8692', '8664', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4702, '䋫;索', '', '詘而戾之爲䋫1，繆而紾之爲索2。', '[音義-1]：側莖切\n[音義-2]：章忍切，或作縝', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '8693;3871', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4703, '坰', '', '坰，地也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '3322', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4704, '陴', '', '墉牆謂之陴1。', '[音義-1]：頻彌切', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '9700', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4705, '邍', '', '高平謂之大原。{邍}', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '1204', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4706, '汪;池', '', '汪，池也。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7157;7257', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4707, '汭', '', '水之北謂之汭。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7144', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4708, '衍', '', '澤之廣者謂之衍。', '', '廣器第七', '小爾雅', '', DEFAULT, DEFAULT, '7136', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4709, '稾;稈;芻', '犓', '藁{稾}謂之稈1，稈謂之芻[犓]，生曰芻。', '[音義-1]：古旱切', '廣物第八', '小爾雅', '3', DEFAULT, DEFAULT, '4474;4473;611', '758', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4710, '穀;粒;菜;蔬;穗;穎;銍', '', '穀謂之粒1，菜謂之蔬。禾穗謂之穎，截穎謂之銍2。', '[音義-1]：（⿰禾立）力入切，亦作粒\n[音義-2]：陟栗切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4483;4356;558;691;4447;4446;9357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4711, '揠;擢', '', '拔心曰揠1，拔根曰擢。', '[音義-1]：烏轄切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7996;7994', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4712, '把;秉;筥;稯;秅', '', '把謂之秉，秉四曰筥1，筥十曰稯2。〔稯十曰秅。〕', '[音義-1]：居許切\n[音義-2]：於求切', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '7875;1919;2921;4496;4498', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4713, '棗', '', '棘之實謂之棗。', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '4396', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4714, '葚', '', '桑之實謂之葚1。', '[音義-1]：食荏切，又作（⿱艸⿰禾甚）、椹', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '480', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4715, '柞;象', '樣', '柞之實謂之橡{象}[樣]1。', '[音義-1]：徐兩切', '廣物第八', '小爾雅', '2', DEFAULT, DEFAULT, '3481;6133', '3477', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4716, '桑土;桑根', '', '〔桑土謂之桑根。〕', '', '廣物第八', '小爾雅', '', DEFAULT, DEFAULT, '3858/9019;3858/3562', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4717, '陽鳥;鴻;鴈', '', '去陰就陽謂之陽鳥，鴻、鴈是也。', '', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '9622/2368;2414;2422', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4718, '慈烏;雅烏;鸒;燕烏;蒼烏', '', '純黑而反哺者謂之慈烏。小而腹下白、不反哺者謂之雅烏，鸒也1。白項而群飛者謂之燕烏，白脰烏也2。〔大而白脰者謂之蒼烏。〕', '[音義-1]：羊茹切\n[音義-2]：田候切', '廣鳥第九', '小爾雅', '', DEFAULT, DEFAULT, '6745/2487;2279/2487;2385;7686/2487;538/2487', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4719, '豕;彘;豬;豚;豜;豵', '', '豕，彘也。彘，猪{豬}也，其子曰豚。豕之大者謂之豜1，小者謂之豵2。', '[音義-1]：古賢切\n[音義-2]：子紅切', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '6076;6104;6077;6108;6082;6080', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4720, '巢;窠', '', '鳥之所乳謂之巢，鷄雉所乳謂之窠。', '', '廣獸第十', '小爾雅', '', DEFAULT, DEFAULT, '3892;4650', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4721, '場;堀;朁;槮', '罧;滲', '鹿之所息謂之場，兔之所息謂之窟{堀}，魚之所息謂之潜{朁}[罧]，潜，槮[滲]也1，積柴水中而魚舍焉2。', '[音義-1]：桑感切\n[音義-2]：如字', '廣獸第十', '小爾雅', '3;4', DEFAULT, DEFAULT, '9145;9053;3048;3596', '4829;7203', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4722, '䞨;步;仞;尋;常', '', '跬{䞨}1，一舉足也。倍跬謂之步。四尺謂之仞，倍仞謂之尋，尋，舒兩肱也。倍尋謂之常。', '[音義-1]：丘弭切', '度', '小爾雅', '', DEFAULT, DEFAULT, '1050;1081;4953;1984;4870', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4723, '墨;丈;端;兩;匹;束', '', '五尺謂之墨，倍墨謂之丈，倍丈謂之端，倍端謂之兩1，倍兩謂之匹，匹有五謂之束。', '[音義-1]：力讓切', '度', '小爾雅', '', DEFAULT, DEFAULT, '9076;1465;6680;4811;8422;3897', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4724, '溢;匊', '', '一手之盛謂之溢1，兩手謂之掬{匊}。', '[音義-1]：時征切。弋質切', '量', '小爾雅', '', DEFAULT, DEFAULT, '7398;5803', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4725, '匊;豆;區;釜;藪;缶;鍾;秉;斛', '', '掬，一升也。掬{匊}四謂之豆，豆四謂之區，區四謂之釜，釜二有半謂之藪，藪二有半謂之缶，二缶謂之鍾，二鍾謂之秉。秉，十六斛也。', '', '量', '小爾雅', '', DEFAULT, DEFAULT, '5803;3095;8417;1861;563;3285;9292;1919;9490', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4726, '銖;兩;捷;舉;鋝;鍰;斤;衡;稱;鈞;石;鼓', '䄷', '二十四銖曰兩1，兩有半曰捷，倍捷曰舉，倍舉曰鋝2，鋝謂之鍰3，二鍰四兩謂之斤，斤十謂之衡，衡有半謂之秤{稱}，秤二謂之鈞，鈞四謂之石[䄷]，石四謂之鼓。然則鼓四百八十斤也。', '[音義-1]：慵朱切\n[音義-2]：龍輟切\n[音義-3]：胡關切', '衡', '小爾雅', '11', DEFAULT, DEFAULT, '9372;4811;8087;7957;9373;9374;9474;2843;4493;9377;6009;3082', '4499', 'null', 'null');
