  `word_real_semicolon_count` int(11) GENERATED ALWAYS AS (((length(`word_real`) - length(replace(`word_real`,';',''))) + if((isnull(`word_real`) or (`word_real` = '')),0,1))) STORED NULL,
  `shuowen_fk_word_id` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT 'word索引（方便后续高效查询）（触发器更新）',
