delimiter ;;
CREATE TRIGGER `update_shuowen_fk_word_id_before_update` BEFORE UPDATE ON `t_han_nga` FOR EACH ROW BEGIN
    DECLARE result VARCHAR(255) DEFAULT '';
    DECLARE temp_char VARCHAR(1);
    DECLARE i INT DEFAULT 1;
    DECLARE found_index VARCHAR(10);
    -- 此触发器是为了解决区外码汉字查询效率低下的问题
		-- 采用数字索引，比单纯的采用正则全局匹配效率要高得多
    -- 如果 word 字段没有变化，不需要更新
		-- 此会话标识，用以强制更新该字段（强制更新时，需要额外添加一句：@disable_shuowen_trigger = 1）
    IF NEW.word <=> OLD.word AND @disable_shuowen_trigger is null THEN
        SET NEW.shuowen_fk_word_id = OLD.shuowen_fk_word_id;
    ELSE
        -- 如果 word 为空，设置为空字符串
        IF NEW.word IS NULL THEN
            SET NEW.shuowen_fk_word_id = '';
        ELSE
            -- 遍历 word 中的每个字符
            WHILE i <= CHAR_LENGTH(NEW.word) DO
                SET temp_char = SUBSTRING(NEW.word, i, 1);
                
                -- 检查当前字符是否为分号
                IF temp_char = ';' THEN
                    -- 如果是分号，直接添加到结果中
                    IF result = '' THEN
                        SET result = ';';
                    ELSE
                        SET result = CONCAT(result, '/;');
                    END IF;
                ELSE
                    -- 不是分号，正常查找索引
                    SET found_index = NULL;
                    
                    -- 在 shuowen_voice_revel 表中查找包含该字符的 word
                    SELECT id INTO found_index FROM shuowen_voice_revel 
                    WHERE word REGEXP CONCAT('.*', temp_char, '.*') 
                    LIMIT 1;
                    
                    -- 如果找到，添加到结果中
                    IF found_index IS NOT NULL THEN
                        IF result = '' THEN
                            SET result = found_index;
                        ELSE
                            SET result = CONCAT(result, '/', found_index);
                        END IF;
                    ELSE
                        -- 如果没找到，添加 '0'
                        IF result = '' THEN
                            SET result = '0';
                        ELSE
                            SET result = CONCAT(result, '/0');
                        END IF;
                    END IF;
                END IF;
                
                SET i = i + 1;
            END WHILE;
            
            -- 替换连续的分号标记
            SET result = REPLACE(result, '/;/', ';');
            
            -- 设置最终的 shuowen_fk_word_id 值
            SET NEW.shuowen_fk_word_id = result;
        END IF;
    END IF;
END
;;
