INSERT INTO `t_han_nga` VALUES (611, '盬;雜;猝', '疌', '盬、1雜[疌]，猝也。2', '[郭注-1]：音古；\n[郭注-2]：皆倉卒也。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '7717;5332;6315', '1074', 'null', 'null');
