INSERT INTO `t_han_nga` VALUES (617, '埤;予', '', '埤，予也。12', '[郭注-1]：予猶與也。\n[音義-2]：埤音畀。', '卷十三', '方言', '', DEFAULT, DEFAULT, '9090;2508', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (618, '镾;縫', '', '彌{镾}，縫也。', '', '卷十三', '方言', '', DEFAULT, DEFAULT, '6068;8676', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (619, '譯;傳;見', '', '譯，傳也。譯，見也。1', '[郭注-1]：傳宣語，卽相見。', '卷十三', '方言', '', DEFAULT, DEFAULT, '1722;5102;5473', '', 'null', 'null');
