INSERT INTO `t_han_nga` VALUES (624, '珇;好;美', '袓', '珇[袓]，好也。珇，美也。12', '[郭注-1]：美好等互見義耳。\n[音義-2]：珇音祖。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '132;8188;2354', '5329', 'null', 'null');
