INSERT INTO `t_han_nga` VALUES (626, '閻;開', '', '閻，開也。1', '[郭注-1]：謂開門也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '7739;7753', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (627, '摩;滅', '', '摩，滅也。1', '[音義-1]：或作攠滅字，音麋。', '卷十三', '方言', '', DEFAULT, DEFAULT, '8008;7440', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (628, '菲;薄', '', '菲，薄也。12', '[郭注-1]：謂微薄也。\n[音義-2]：菲音翡。', '卷十三', '方言', '', DEFAULT, DEFAULT, '641;561', NULL, 'null', 'null');
