INSERT INTO `t_han_nga` VALUES (631, '芋;大', '', '芋，大也。12', '[郭注-1]：芋猶訏耳。\n[音義-2]：芋，香于反；', '卷十三', '方言', '', DEFAULT, DEFAULT, '270;6595', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (632, '煬;翕;炙;烈;暴', '', '煬、12翕，炙也。煬、烈，暴也。', '[郭注-1]：今江東呼火熾猛爲煬。\n[音義-2]：音恙；', '卷十三', '方言', '', DEFAULT, DEFAULT, '6465;2256;6581;6424;4245', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (633, '馺;馳', '𢕬', '馺[𢕬]，馬馳也。12', '[郭注-1]：馺馺，疾貌也。\n[音義-2]：馺，索荅反。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '6199;6207', '1235', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (634, '選;延;徧', '宣', '選[宣]、延，徧也。', '', '卷十三', '方言', '1', DEFAULT, DEFAULT, '1143;1265;1247', '4557', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (635, '澌;索', '', '澌，索也。1', '[郭注-1]：盡也。', '卷十三', '方言', '', DEFAULT, DEFAULT, '7337;3870', '', 'null', 'null');
