INSERT INTO `t_han_nga` VALUES (638, '萃;集', '', '萃，集也。', '', '卷十三', '方言', '', DEFAULT, DEFAULT, '540;2366', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (639, '睨;澤;明', '', '睨、1𥋭{澤}，2明也。', '[音義-1]：音俾倪。\n[音義-2]：音亦。', '卷十三', '方言', '', DEFAULT, DEFAULT, '2128;7219;4320', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (640, '闓;臨;照;美', '', '暟{闓}、臨，照也。暟，美也。12', '[郭注-1]：暟暟，美德也；\n[音義-2]：暟，呼凱反。', '卷十三', '方言', '', DEFAULT, DEFAULT, '7754;5245;6495;2354', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (641, '箄;簍;與;舀;𥴧;筐', '', '箄、簍、籅{與}、䈱{舀}，𥴧也。1江沔之閒謂之籅，2趙代之間謂之䈱，3淇衞之閒謂之牛筐。4𥴧，其通語也。𥴧小者，南楚謂之簍，5自關而西秦晉之閒謂之箄。67', '[郭注-1]：古筥字；\n[音義-2]：音餘；\n[音義-3]：音弓弢；\n[郭注-4]：淇，水名也；\n[音義-5]：音縷；\n[郭注-6]：今江南亦名籠爲箄。\n[音義-7]：方氏反。', '卷十三', '方言', '', DEFAULT, DEFAULT, '2925;2928;1781;4524;2960;2959', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (642, '籠;旁;笯', '', '籠，南楚江沔之閒謂之篣{旁}，12或謂之笯。34', '[郭注-1]：今零陵人呼籠爲篣。\n[音義-2]：音彭。\n[郭注-3]：亦呼籃。\n[音義-4]：那墓反。', '卷十三', '方言', '', DEFAULT, DEFAULT, '2955;8;2947', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (643, '䈰;𥬔旅', '𥬔盧', '𥰠，1南楚謂之筲{䈰}，23趙魏之郊謂之𥬔𥰠{𥬔旅}[𥬔盧]。4', '[郭注-1]：盛餅筥也；\n[郭注-2]：今建平人呼筲。\n[音義-3]：音鞭鞘；\n[郭注-4]：今通語也。', '卷十三', '方言', '2', DEFAULT, DEFAULT, '2919;3162/4308', '3162/3142', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (644, '錐;苕', '', '錐謂之鍣{苕}。1', '[郭注-1]：廣雅作鉊字。', '卷十三', '方言', '', DEFAULT, DEFAULT, '9364;664', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (645, '刀斗', '鐎斗', '無[緣之]升[斗]謂之刁斗{刀斗}[鐎斗]。12', '[郭注-1]：謂小鈴也。\n[音義-2]：音貂。見漢書。', '卷十三', '方言', '1', DEFAULT, DEFAULT, '2742/9488', '9308/9488', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (646, '匕;匙', '', '匕謂之匙。1', '[郭注-1]：匙音祇。', '卷十三', '方言', '', DEFAULT, DEFAULT, '5214;5215', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (647, '盂;盡;安殘;盌;喬;銚銳;肙夬', '', '盂謂之㯸{盡}。1河濟之閒謂之䀂𥂫{安殘}。椀{盌}謂之䀉{喬}，盂謂之銚銳，2椀謂之梋㭈{肙夬}。34', '[音義-1]：子殄反；\n[音義-2]：銚，音謠；\n[郭注-3]：椀亦盂屬，江東名盂爲凱，亦曰甌也。\n[音義-4]：蠲決兩音。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3137;3155;4575/2549;3138;6621;9306/9366;2730/1912', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (648, '餌;餻;餈;令;奄;元', '', '餌謂之糕{餻}，或謂之餈，或謂之𩚹{令}，1或謂之餣{奄}，2或謂之䬧{元}。3', '[音義-1]：音鈴；\n[音義-2]：央怯反；\n[音義-3]：音元。', '卷十三', '方言', '', DEFAULT, DEFAULT, '1874;3266;3212;5776;6598;2', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (649, '餅;屯;長昆', '', '餅謂之飥{飩}[屯]，1或謂之餦餛{長昆}。2', '[音義-1]：音託；\n[音義-2]：餦餛，長渾兩音。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3211;234;6066/4253', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (650, '餳;長皇;飴;亥;𧯡;隓;鎕', '', '𩛿{餳}謂之餦餭{張皇}。1飴謂之䬵{亥}。2𩜌{𧯡}謂之䭉{隓}。34𩛿謂之餹{鎕}。56凡飴謂之𩛿，自關而東陳楚宋衞之閒通語也。', '[郭注-1]：卽乾飴也；\n[音義-2]：音該；\n[郭注-3]：以豆屑雜𩛿也。\n[音義-4]：音髓；\n[郭注-5]：江東皆言餹。\n[音義-6]：音唐。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3209;6066/80;3208;9880;3098;9650;9446', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (651, '𪍠;䴭;䴳;麰;稗;𨢊;稞;𥶶', '', '𪍠、䴭、䴳、麰、䴽{稗}、䵆{𨢊}、12䴹{稞}，34麴{𥶶}也。自關而西秦豳之閒曰𪍠，56晉之舊都曰䴭，78齊右河濟曰䴳，9或曰麰，1011北鄙曰䴽。1213麴，其通語也。', '[郭注-1]：有衣麴。\n[音義-2]：音蒙。\n[郭注-3]：小麥麴爲䴹，卽䴷也；\n[音義-4]：音𩸄。\n[郭注-5]：豳卽邠，\n[音義-6]：音斌。𪍠音哭。\n[郭注-7]：今江東人呼麴爲䴭。\n[音義-8]：䴭音才；\n[音義-9]：于八反；\n[郭注-10]：大麥麴。\n[音義-11]：音牟。\n[郭注-12]：細餅麴。\n[音義-13]：音脾。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3357;3359;3358;3348;4443;9805;4467;4362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (652, '梠;櫺', '', '屋梠謂之欞{櫺}。12', '[郭注-1]：雀梠卽屋檐也，亦呼爲連綿。\n[音義-2]：音鈴。', '卷十三', '方言', '', DEFAULT, DEFAULT, '3641;3653', '', 'null', 'null');
