INSERT INTO `t_han_nga` VALUES (654, '冢;墳;培;俞;采;良;壠;丘;塿;墓;無', '宰;摹', '冢，秦晉之閒謂之墳，1或謂之培，2或謂之堬{俞}，3或謂之埰{采}[宰]，4或謂之埌{良}，5或謂之壠。6自關而東謂之丘，小者謂之塿，78大者謂之丘，9凡葬而無墳謂之墓，10所以墓謂之墲{無}[摹]。11', '[郭注-1]：取名於大防也；\n[音義-2]：音部；\n[音義-3]：音臾；\n[郭注-4]：古者卿大夫有采地，死葬之，因名也；\n[音義-5]：音波浪；\n[郭注-6]：有界埓似耕壠，以名之；\n[郭注-7]：培塿，亦堆高之貌。\n[音義-8]：洛口反；\n[郭注-9]：又呼冢爲墳也；\n[郭注-10]：言不封也，墓猶慕也；\n[郭注-11]：墲謂規度墓地也，漢書曰：「初陵之墲」是也。', '卷十三', '方言', '5;11', DEFAULT, DEFAULT, '5812;9140;9100;5436;3791;3338;9141;5230;9123;9139;8413', '4592;8023', 'null', 'null');
