INSERT INTO `t_han_nga` VALUES (2423, '休流;扶持;下謀;雲門;六莖;五英;大章;簫韶;大夏;大護;大武;勺;大予', '酌', '休流、扶持、下謀、雲門、六䪫{六莖}1、五韺{五英}2、大章3、簫韶4、〔大夏〕5、大頀{大護}6、大武7、勺[酌]8、大予9。', '[音義-1]：莖。顓頊樂\n[音義-2]：英。帝俈樂\n[音義-3]：堯樂\n[音義-4]：舜樂\n[音義-5]：禹樂\n[音義-6]：湯樂\n[音義-7]：武王樂\n[音義-8]：只藥。周公樂也，斟酌文 武之道\n[音義-9]：漢明帝 永平三年秋八月戊辰，改大樂爲大予樂', '釋樂', '廣雅', '12', DEFAULT, DEFAULT, '3815/7473;7853/7855;9/1507;7575/7729;9725/493;9724/502;6595/1742;2989/1741;6595/3370;6595/1562;6595/8394;9464;6595/2508', '9833', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2424, '足鼓;植鼓;縣鼓;雷鼓;靈鼓;路鼓;鼖鼓;鼛鼓;晉鼓;𧒕鼓;鼙鼓;鞀鼓;應;朄;搏拊', '皋鼓', '足鼓1、植鼓2、縣鼓3、雷鼓4、靈鼓5、路鼓6、鼖鼓7、鼛鼓[皋鼓]8、晉鼓9、鼜鼓{𧒕鼓}10、鼙鼓11、鞀鼓12、應、朄13、搏拊14。', '[音義-1]：夏后氏鼓\n[音義-2]：見禮明堂記。詩「植我鞉鼓」\n[音義-3]：禮記曰「周縣鼓」，鄭注云「縣於栒虡也」\n[音義-4]：周禮「雷鼓，鼓神祀」，鄭注曰「雷鼓八面」\n[音義-5]：周禮「靈鼓，鼓社祭」，鄭注曰「靈鼓六面也」\n[音義-6]：周禮「路鼓,鼓鬼享」，鄭注「路鼓四面」\n[音義-7]：周禮「鼖鼓，鼓軍事」，鄭注「大鼓也，長八尺」\n[音義-8]：周禮「鼛鼓，鼓役事」，考工記「長尋有四尺也」\n[音義-9]：周禮「晉鼓，鼓金奏」，鄭注「長六尺六寸也」\n[音義-10]：周禮「凡軍旅，夜鼓鼜」，鄭云「夜戒守鼓」。音「造次」之次\n[音義-11]：周禮「旅帥執鼙」\n[音義-12]：周禮「小師之職掌鼓鼗」。釋名云「鞀，導也」\n[音義-13]：詩云「應朄縣鼓」\n[音義-14]：禮記「拊搏」，鄭注「以韋爲之，充之以糠，形如小鼓，以節樂」', '釋樂', '廣雅', '8', DEFAULT, DEFAULT, '1326/3081;3647/3081;5704/3081;7525/3081;206/3081;1410/3081;3083/3081;3082/3081;4202/3081;3078/3081;3084/3081;1810/3081;6710;9800;7863/7889', '6659/3081', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2425, '琴;鳴廉;脩營;藍脅;號鍾;空中;自鳴;焦尾', '攸況;濫協;號鐘', '神農氏琴長三尺六寸六分，上有五弦，曰宮商角徵羽。文王增二弦，曰少宮、〔少〕商。鳴廉、脩營[攸況]。藍脅[濫協]、號鍾[號鐘]、空中、自鳴、焦尾。', '', '釋樂', '廣雅', '3;4;5', DEFAULT, DEFAULT, '8404;2479/5946;2684/4628;282/2616;3067/9291;4645/231;2222/2479;6485/5425', '2033/7159;7178/9257;3067/9385', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2426, '瑟', '', '伏羲氏瑟長七尺二寸，上有〔二十七〕弦1。', '[音義-1]：見世本', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '8405', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2427, '柷', '', '柷，象桶1，方三尺五寸，深尺八寸，四角有陞{升}[登]鼠2。', '[音義-1]：動\n[音義-2]：升', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '3765', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2428, '敔', '', '敔1，象伏虎，背上有二十七刻。', '[音義-1]：魚呂', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2058', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2429, '鍾', '鐘', '倕[垂]氏鍾[鐘]十六枚1。', '[音義-1]：世本「倕造鍾」。倕，舜臣', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '9291', '9385', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2430, '磬', '', '毋句氏磬十六枚1。', '[音義-1]：世本「毋句作磬」。毋句，堯臣也', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6038', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2431, '員', '壎', '塤{員}[壎]1，象稱錘，以土爲之，有六孔2。', '[音義-1]：許圓\n[音義-2]：古史考曰：「有塤尙矣。周幽王時，暴辛公善塤」', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '3931', '9072', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2432, '䶵', '', '䶵1，以竹爲之，長尺四寸，有八孔，〔一孔上出寸三分〕2。', '[音義-1]：池\n[音義-2]：前有一孔，上有三孔，後有四孔，頭有一孔', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1429', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2433, '籟;簫', '', '籟1謂之簫，大者二十四管，小者十六管，有底。', '[音義-1]：賴', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2991;2989', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2434, '笙', '', '笙，以瓠爲之，十三管，宮管在左方。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2986', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2435, '竽', '', '竽，象笙，三十六管，宮管在中央。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2985', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2436, '龠;笛', '', '龠謂之笛，有七孔。', '', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '1427;2995', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2437, '管', '', '管，象䶵1，長尺圍寸，〔有〕六孔，無底。', '[音義-1]：池', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '2993', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2438, '佾', '䏌', '{佾}[䏌]天子樂八佾，諸公六佾，諸侯四佾。', '', '釋樂', '廣雅', '1', DEFAULT, DEFAULT, '5198', '2645', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2439, '慟;歈;謳;詠;吟;歌', '', '㗢{慟}1、歈2、謳、詠、吟，歌也。', '[音義-1]：洞\n[音義-2]：頭', '釋樂', '廣雅', '', DEFAULT, DEFAULT, '6973;5587;1572;1573;921;5540', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2440, '大初;大始;大素;九頭;五龍;攝提;合雒;連通;序命;循蜚;因提;禪通;疏訖', '合洛;循飛', '太初{大初}，氣之始也，生於酉仲1，清濁未分也。太始{大始}，形之始也，生於戌仲，清者爲精、濁者爲形也。太素{大素}，質之始也，生於亥仲，已有素朴而未散也。三氣相接，至於子仲，剖判分離，輕清者上爲天，重濁者下爲地，中和爲萬物2。天地辟設人皇以來，至魯哀公十有四年，積二百七十六萬歲，分爲十紀，曰九頭、五龍、攝提、合雒[合洛]，連通、序命、循蜚[循飛]、因提、禪通、疏訖3。', '[音義-1]：八月酉仲，號爲太初，屬雄；九月戌仲，號爲太始，屬雌\n[音義-2]：詩緯曰：「陽本爲雄，陰本爲雌，物本爲魂。雄雌俱行三節，而雄合物魂，號曰太素也。三氣未分别，號曰渾淪」\n[音義-3]：帝王世紀：「自天地闢設人皇以來，迄魏 咸熙二年，凡二百七十二代，積二百七十六萬七百四十五年，分爲十紀，一曰九頭，至十曰疏訖」', '釋天', '廣雅', '7;10', DEFAULT, DEFAULT, '6595/2752;6595/8181;6595/8773;9727/5598;9724/7686;7865/7878;3268/2280;1168/1134;5935/840;1233/8974;3921/7878;55/1134;9786/1577', '3268/7014;1233/7691', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2441, '昦天;陽天;赤天;朱天;成天;幽天;玄天;變天;鈞天', '', '東方昦天、東南陽天、南方赤天、西南朱天、西方成天、西北幽天、北方玄天、東北變天、中央鈞天。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6663/3;9621/3;6584/3;3560/3;9746/3;2500/3;2505/3;2019/3;9376/3', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2442, '天度', '', '{天度}天圜廣南北二億三萬三千五百里七十五步，東西短減四步，周六億十萬七百里二十五步。從地至天，一億一萬六千七百八十七里〔半〕，下度地之厚，與天高等。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/1928', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2443, '七宿度', '', '{七宿度}東方七宿，七十五度；南方七宿，百一十二度；西方七宿，八十度；北方七宿，九十八度四分度之一。四方凡三百六十五度四分度之一。一度二千九百三十二里，二十八宿閒相距積一百七萬九百一十三里，徑三十五萬六千九百七十〔一〕里。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9726/4599/1928', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2444, '條風;明庶風;清明風;景風;涼風;閶闔風;不周風;廣莫風', '調風/融風', '東北條風[調風/融風]、東方明庶風、東南清明風、南方景風、西南涼風、西方閶闔風、西北不周風、北方廣莫風。', '', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '3569/8976;4320/5955/8976;7199/4320/8976;4210/8976;7388/8976;7730/7746/8976;7703/877/8976;5937/701/8976', '1549/8976/0/1862/8976', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2445, '昌光;握譽;可錯;持勝;履予', '', '昌光、握譽、可錯、持勝、履予。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4236/6508;7872/1568;3057/9326;7855/9226;5429/2508', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2446, '格擇;旬始;天狗;枉矢;氛;祲;倍;譎;冠;珥', '格澤;背;鐍/璚/穴', '格擇[格澤]1、旬始、天狗、枉矢、氛、祲2、倍[背]、譎[鐍/璚/穴]、冠、珥。', '[音義-1]：乎格。宅\n[音義-2]：子枕', '釋天', '廣雅', '1;7;8', DEFAULT, DEFAULT, '3600/7896;5805/8181;3/6301;3589/3306;225;66;5113;1668;4796;125', '3600/7219;2615;2858/0/94/0/4631', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2447, '赤霄;濛澒;朝霞;正陽;淪陰;沆瀣;列缺;倒景', '厖鴻;陯陰', '赤霄、濛澒[厖鴻]1、朝霞、正陽、淪陰[陯陰]2、沆瀣3、列缺、倒景。', '[音義-1]：莫孔。乎孔\n[音義-2]：倫\n[音義-3]：乎朗。乎戒', '釋天', '廣雅', '2;5', DEFAULT, DEFAULT, '6584/7532;7316/7444;4286/7570;1087/9621;7175/9620;7164/7452;2770/3300;5199/4210', '5994/2413;9705/9620', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2448, '歉;饑;饉;㱂;大侵', '荒', '一穀不升曰歉1。二穀不升曰饑。三穀不升曰饉。四穀不升曰㱂[荒]2。五穀不升曰大侵。', '[音義-1]：苦簟反\n[音義-2]：康', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '5573;3254;3255;5584;6595/5075', '546', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2449, '靈威仰;赤熛怒;含樞紐;白招歫;叶光紀', '', '蒼曰靈威仰。赤曰赤熛怒。黃曰含樞紐。白曰白招矩{白招歫}。黑曰叶光紀。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '206/8144/5060;6584/6438/6876;815/3648/8650;4928/7918/1068;9257/6508/8539', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2450, '青道;黃道;赤道;白道;黑道', '', '立春、春分，東從青道二出黃道東，交於房二度中；立夏、夏至，南從赤道二出黃道南，交於七星四度中；立秋、秋分，西從白道二出黃道西，交於胃十二度中；立冬、冬至，北從黑道二出黃道北，交於虛二度中。四季之月，還從黃道。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3187/1205;9200/1205;6584/1205;4928/1205;6539/1205', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2451, '月𧘂', '', '{月𧘂}正月不溫，七月不涼。二月不風，八月雷不藏。三月風不衰，九月無降霜。四月雷不見，十月蟄蟲行。五月陽暑不蒸，十一月不合凍。六月浮雲不布，十二月草不喪。七月白露不降，正月有微霜。八月浮雲不歸，二月雷不行。九月物不凋1，三月草木傷。十月流火不定，四月䖵蟲不育。十一月寒不降，五月雨雹。十二月萌類不見，六月五穀不實。', '[音義-1]：彫', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4272/1270', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2452, '營室;東壁;奎;婁;胃;昴;畢;觜觿;參;東井;輿鬼;柳;七星;張;翼;軫;角;亢;貫氐;房;心;尾;箕;斗;牽牛;須女;虛;危', '觜蠵;嬃女', '日月五星行黃道，始營室、東壁、奎、婁、胃之陽，入昴、畢閒，行觜觿[觜蠵]、參之陰，度東井、輿鬼，行柳、七星、張、翼、軫之陰，入角、〔亢〕閒，貫氐、房，出心、尾、箕之陰，入斗、牽牛閒，行須女[嬃女]、虛、危之陽，復至營室。', '', '釋天', '廣雅', '8;26', DEFAULT, DEFAULT, '4628/4556;3843/9047;6596;8320;2608;4229;2490;2848/2850;4314;3843/3189;9525/5818;3511;9726/4313;8485;7692;9539;2825;6652;4338/8368;7722;6702;5425;3017;9488;755/728;5705/8108;5231;6006', '2848/8914;8169/8108', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2453, '离;馮夷;奇相;鬽;賁羊;罔象;畢方;游光;清明', '馮遲;羒羊;必方', '山神謂之离1。河伯謂之馮夷[馮遲]。江神謂之奇相。物神謂之鬽。土神謂之羵羊{賁羊}[羒羊]2。水神謂之冈象{罔象}。木神謂之畢方[必方]。火神謂之游光。金神謂之清明。', '[音義-1]：勑支\n[音義-2]：墳', '釋天', '廣雅', '2;5;7', DEFAULT, DEFAULT, '9731;6200/6612;3058/2161;5825;3943/2331;4812/6132;2490/5452;4303/6508;7199/4320', '6200/1148;2339/2331;717/5452', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2454, '朱明;燿靈;東君;日', '', '朱明、曜靈{燿靈}、東君，日也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3560/4320;6500/206;3843/839;4186', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2455, '夜光;月', '', '夜光謂之月。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4325/6508;4272', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2456, '天河;天漢', '', '天河謂之天漢。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3/6983;3/7006', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2457, '震;霣;追;并;雷', '啍;旁', '震、霣1、䨨{追}[啍]2、𩂦{并}[旁]，雷也。', '[音義-1]：于慜\n[音義-2]：追', '釋天', '廣雅', '3;4', DEFAULT, DEFAULT, '7530;7526;1177;5225;7525', '831;8', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2458, '雲;運;雨;榘', '秬', '〔雲〕，運也；雨，榘[秬]也1。', '[音義-1]：俱雨反', '釋天', '廣雅', '4', DEFAULT, DEFAULT, '7575;1138;7524;3031', '3201', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2459, '晷;柱;景', '', '晷、柱，景也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4217;3624;4210', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2460, '飛廉;荓翳;豐隆', '蜚螊', '風師謂之飛廉[蜚螊]。雨師謂之荓翳1。雲師謂之豐隆。', '[音義-1]：蒲形', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '7691/5946;358/2272;3102/3876', '8974/8897', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2461, '羲和;望舒', '朢朔', '日御謂之羲和。月御謂之望舒[朢朔]。', '', '釋天', '廣雅', '2', DEFAULT, DEFAULT, '3064/846;8412/2509', '5239/4273', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2462, '青龍;天一;大侌;大歲', '', '青龍、天一、太陰{大侌}，太歲{大歲}也。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3187/7686;3/1;6595/7576;6595/1082', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2463, '榦枝', '', '{榦枝}甲乙爲榦。榦者，日之神也。寅卯爲枝。枝〔者〕，月之靈也。甲剛乙柔、丙剛丁柔、戊剛己柔、庚剛辛柔、壬剛癸柔。甲齊、乙東夷、丙楚、丁南夷、戊魏、己韓、庚秦、辛西夷、壬衛、癸北夷、子周、丑狄、寅楚、卯鄭、辰晉、巳衞、午秦、未宋、申齊、酉魯、戌趙、亥燕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3617/3568', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2464, '星宿', '', '{星宿}角、亢，鄭；氐、房、心，宋；尾、箕，燕；斗、牽牛、嬃女，吳〔越〕；虛、危，齊；營室、東壁，衞；奎、婁，魯；胃、昴、畢，趙；觜〔觿〕、參，魏；東井、輿鬼，秦；柳、七星、張，周；翼、軫，楚。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4313/4599', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2465, '樞;旋;機;權;衡;開陽;揺光', '', '北斗七星：一爲樞、二爲旋、三爲機、四爲權、五爲衡、六爲開陽、七爲揺光。樞爲雍州，旋爲冀州，機爲青、兖州，權爲徐、揚州，衡爲荆州，開陽爲梁州，揺光爲豫州。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3648;4305;3722;3518;2842;7753/9621;7948/6508', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2466, '歲星;重華;應星', '', '歲星謂之重華，或謂之應星1。', '[音義-1]：木宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '1082/4313;5241/3883;6710/4313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2467, '營惑;罰星;執法', '䁝惑', '營惑[䁝惑]謂之罰星，或謂之執法1。', '[音義-1]：火宿也', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '4628/6862;2796/4313;6645/6259', '2203/6862', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2468, '鎭星;地侯', '', '鎭星謂之地侯1。', '[音義-1]：土宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9357/4313;9019/3310', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2469, '大白;長庚;大囂', '', '太白{大白}謂之長庚，或謂之大囂1。', '[音義-1]：金宿也。晨見東方爲啟明，昏見西方爲長庚', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6595/4928;6066/9752;6595/1437', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2470, '辰星;爨星;兔星;鉤星', '', '辰星謂之爨星，〔或謂之〕兔星，或謂之鉤星1。', '[音義-1]：水宿也', '釋天', '廣雅', '', DEFAULT, DEFAULT, '9792/4313;1787/4313;6292/4313;1460/4313', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2471, '大角;棟星', '', '大角謂之棟星。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '6595/2825;3622/4313', '', 'null', 'null');
