INSERT INTO `t_han_nga` VALUES (2498, '昔;曹;卒;䬽;婁;臘;祓;絜;餟;祼;軷;䄟;𥛻;夌;禖;禪;祧;醮;禬;卷;望;𩴪;祥;禫;禱;禜;禳;祭', '', '䄍{昔}1、䄚{曹}2、祽{卒}3、祱{䬽}4、䄛{婁}5、臘、祓、禊{絜}6、餟7、祼8、軷9、䄟10、𥛻11、祾{夌}12、禖13、禪、祧14、醮15、禬16、飬{卷}、望、禨{𩴪}17、祥、禫、禱18、禜、禳，祭也。', '[音義-1]：士駕反\n[音義-2]：曹\n[音義-3]：七外\n[音義-4]：税\n[音義-5]：力侯切\n[音義-6]：乎計\n[音義-7]：加税\n[音義-8]：古奐反\n[音義-9]：步末\n[音義-10]：毳\n[音義-11]：布庚\n[音義-12]：陵，又力登\n[音義-13]：梅\n[音義-14]：他聊\n[音義-15]：子咲反\n[音義-16]：古外\n[音義-17]：巨衣\n[音義-18]：倒', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4249;3049;5358;3262;8320;2665;49;8751;3261;45;9570;46;37;3363;58;55;74;9834;54;5783;8412;5828;18;72;51;52;53;30', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2499, '臘;獵/接;昔;索;清祀;嘉平;大昔;臘', '', '臘，〔□{獵/接}也〕。〔䄍{昔}〕，索也。夏曰清祀1，殷曰嘉平2，周曰大䄍{大昔}，秦曰臘。', '[音義-1]：以清潔而祭祀\n[音義-2]：嘉，善也；平，成也。以歲終萬物善成，就而報其功', '釋天', '廣雅', '', DEFAULT, DEFAULT, '2665;6357/0/7916;4249;3870;7199/31;3080/3072;6595/4249;2665', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2500, '鬯;薰;茝蘭;蕭;艾', '', '天子祭以鬯，諸侯以薰，卿、大夫以茝蘭，士以蕭，庶人〔以〕艾。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3198;294;292/286;443;389', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2501, '肆兵', '肄兵', '{肆兵}[肄兵]王者以四時畋，以奉宗廟，因簡戎事。刈草爲防，敺1而射之，不題禽，不垝[恑]遇2，不捷草，越防不追。天子取三十焉，一爲乾梪，二爲賓客，三曰充君之庖，其餘以與士。', '[音義-1]：欺于\n[音義-2]：車美', '釋天', '廣雅', '1', DEFAULT, DEFAULT, '6067/1768', '1937/1768', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2502, '旞;旌;旗', '', '全羽曰旞。析羽曰旌。熊虎曰旗。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4294;4291;4288', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2503, '杠', '', '天子杠高九仞。諸侯七仞。〔卿〕大夫五仞。〔士三仞〕。', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '3673', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2504, '游', '', '{游}天子十二斿，至地。諸侯九斿，至軫。卿大夫七斿，至軹1。士三斿，至肩。', '[音義-1]：紙', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4303', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2505, '年;稔;秋;穀熟', '', '〔年、稔、秋，穀熟也。〕', '', '釋天', '廣雅', '', DEFAULT, DEFAULT, '4481;4483;4490;4482/1885', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2506, '四海九州', '', '{四海九州}神農度四海內，東西九十萬里，南北八十一萬里。帝堯所治九州，地二千四百三十萬八千二十四頃，其墾者九百一十萬八千二十四頃。夏禹所治四海內，地東西二萬八千里、南北二萬六千里，出水者八千里，受水者八千里。', '', '釋地', '廣雅', '', DEFAULT, DEFAULT, '9717/7128/9727/7489', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2507, '湖;藪;陂;塘;都;㽘;斥澤;埏;衍;皋;沼;池', '唐;沆;㚖', '湖、藪、陂、塘[唐]、都、㽘[沆]1、斥澤、埏2、衍、皋[㚖]3、沼，池也。', '[音義-1]：古朗\n[音義-2]：延\n[音義-3]：古豪', '釋地', '廣雅', '4;6;10', DEFAULT, DEFAULT, '7257;563;9624;9156;4004;9185;5966/7219;9151;7135;6659;7255;7256', '878;7164;6668', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2508, '都野;孟豬;彭蠡;少原;振澤;諸毗;沛澤;雷澤;幽都', '孟都/望諸', '都野、孟豬[孟都/望諸]、彭蠡、少原、振澤、諸毗1、沛澤2、雷澤、幽都。', '[音義-1]：符夷\n[音義-2]：盃妹', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '4004/9168;9771/6076;3079/8964;705/7493;7960/7219;1488/6699;7097/7219;7525/7219;2500/4004', '9771/4004/0/8412/1488', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2509, '瓊支;瑾瑜;昭華;白珩;琁;璜;弁和;璵璠;垂棘;碧黸;藍田;琜𧶠;琬;琰;璐;唐;璑;囷;赤瑕', '苕華', '瓊支、瑾瑜、昭華[苕華]、白珩1、璇{琁}2、璜、弁和、璵璠、垂棘、碧瓐{碧黸}、藍田、琜瓄{琜𧶠}3、琬、琰、璐4、瑭{唐}5、璑、㻒{囷}6、赤瑕。', '[音義-1]：衡\n[音義-2]：旋\n[音義-3]：來瀆\n[音義-4]：音路\n[音義-5]：唐\n[音義-6]：渠慜', '釋地', '廣雅', '3', DEFAULT, DEFAULT, '94/1934;90/91;4196/3883;4928/123;94;109;5466/846;89/88;9146/4396;185/6540;282/9169;93/3990;113;115;98;878;101;3916;6584/144', '664/3883', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2510, '水精;石英;𤥗離;珊瑚;玟瑰;夜光;隨侯;虎魄;金精;璣', '石瑛', '水精謂之石英[石瑛]。瑠璃{𤥗離}、珊瑚、玟瑰1、夜光、隋侯{隨侯}2、虎魄、金精、璣。', '[音義-1]：梅。古回\n[音義-2]：隋侯見虵傷治之，後虵銜珠以報', '釋地', '廣雅', '2', DEFAULT, DEFAULT, '6981/4350;6008/502;202/2292;200/201;195/196;4325/6508;1102/3310;3116/5821;9258/4350;197', '6008/100', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2511, '蜀石;碝;玟;車渠;馬匘;武夫;琨吾;珉石;咸力;珂', '玪𤨙', '蜀石、碝1、玟2、硨磲{車渠}3、碼碯{馬匘}4、武夫、琨珸{琨吾}5、瑉石{珉石}、瑊玏{咸力}[玪𤨙]6、珂。', '[音義-1]：而兗\n[音義-2]：忙巾\n[音義-3]：車渠\n[音義-4]：馬。奴道\n[音義-5]：昆吾\n[音義-6]：古咸，又咸。勒', '釋地', '廣雅', '9', DEFAULT, DEFAULT, '8825/6008;6011;195;9511/7262;6134/5219;8394/6673;186/837;187/6008;872/9209;213', '158/159', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2512, '合;敝付;玃如;相繇;化虵', '', '東方有魚焉，如鯉、六足、鳥尾，其名曰鮯{合}1。南方有鳥焉，三首、六目、六足、三翼，其名曰鷩𩿧{敝付}2。西方有獸焉，如鹿、白尾、馬足、人手、四角，其名曰玃如3。北方有民焉，九首、虵身，其名曰相繇4。中央有虵焉，人面、豺身、鳥翼、虵行，其名曰化虵。此五方之異物也。', '[音義-1]：古合\n[音義-2]：必舌。付于反\n[音義-3]：九縳\n[音義-4]：由', '釋地', '廣雅', '', DEFAULT, DEFAULT, '3268;4940/5055;6371/8242;2161/8513;5213/8992', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2513, '鄰;朋;里;邑;都;師', '', '八家爲鄰，三鄰爲朋，三朋爲里，五里爲邑，十邑爲都，十都爲師，州十有二師焉1。', '[音義-1]：見尙書', '釋地', '廣雅', '', DEFAULT, DEFAULT, '4005;2368;9166;4001;4004;3861', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2514, '㽥;𤲬;堅;甄;埴;塿;㙚;壚;墳;陚;田;地;土', '堅;肥', '㽥1、𤲬2、堅、甄[堅]3、埴4、塿5、㙚6、壚7、墳[肥]、陚8、田、地，土也。', '[音義-1]：柔\n[音義-2]：奴戈\n[音義-3]：古賢\n[音義-4]：時識\n[音義-5]：樓\n[音義-6]：息營\n[音義-7]：來乎\n[音義-8]：付', '釋地', '廣雅', '4;9', DEFAULT, DEFAULT, '9175;9171;1950;8451;9033;9123;9032;9031;9140;9680;9169;9019;9018', '1950;2648', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2515, '耦;㹃;鈂;講;異;突;葘;穮;披;合;䥯;掊;局;暵;𤛿;營;墾;𦓯;包;耕', '鎣;掊', '耦、㹃1、𣜣{鈂}2、耩{講}3、𦔜{異}4、𦔅{突}5、䎩{葘}6、藨{穮}7、耚{披}8、耠{合}9、䥯10、䎧{掊}11、䎤{局}12、䎯{暵}13、犁{𤛿}、營[鎣]、墾、𦓯、〔𡂟{包}[掊]〕，耕也。', '[音義-1]：沸\n[音義-2]：才心\n[音義-3]：講\n[音義-4]：弋\n[音義-5]：突\n[音義-6]：側基\n[音義-7]：布苗\n[音義-8]：披\n[音義-9]：乎荅\n[音義-10]：碑\n[音義-11]：步侯\n[音義-12]：局\n[音義-13]：漢', '釋地', '廣雅', '16;19', DEFAULT, DEFAULT, '2820;762;9342;1581;1777;4664;564;4458;7944;3268;9352;7890;958;4247;761;4628;9155;2822;5813;2819', '9314;7890', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2516, '肖;曼;堫;啻;奄;責;埶;植;樹;耆;㵺;投;蒔;種', '', '𦓴{肖}1、𦔔{曼}2、䎫{堫}3、䎮{啻}4、䎨{奄}5、耫{責}6、埶7、植、樹、𦔌{耆}8、㵺9、投、蒔10，種也11。', '[音義-1]：所交反\n[音義-2]：亡旦\n[音義-3]：叉江\n[音義-4]：他戾\n[音義-5]：一劫\n[音義-6]：壯責\n[音義-7]：魚世\n[音義-8]：祇\n[音義-9]：派\n[音義-10]：時志\n[音義-11]：之用', '釋地', '廣雅', '', DEFAULT, DEFAULT, '2642;1910;9039;875;6598;3973;1884;3647;3557;5377;7044;7924;541;4419', '', 'null', 'null');
