INSERT INTO `t_han_nga` VALUES (2935, '𪊺', '', '𪊺，狼題肉角，含仁懷義，音中鐘呂，行步中規，折還中榘，遊必擇土，翔必後處，不履生蟲，不折生草，不羣居，不旅行，不入陷穽，不羅罘罔，文章彬彬。', '', '釋獸', '廣雅', '', DEFAULT, DEFAULT, '6268', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (2936, '隹鼠;䶂鼠;鼫鼠;偃鼠;鼢鼠;鼠狼;鼬;陽鼠;𪕎鼠;䶄鼠;𪕶鼠;韱𪕱;尃唐;冋令;白𪖇;㱿;業攴;夋鼠;耳鼠;鼲鼠;䶃鼠;𪕚鼠', '斬𪕱', '𪕪鼠{隹鼠}1。䶂鼠2。鼫鼠。鼴鼠{偃鼠}，鼢鼠3。鼠狼，鼬4。陽鼠。𪕎鼠5。䶄鼠6。𪕶鼠7。𪖎𪕱{韱𪕱}[斬𪕱]8。䶈𪕹{尃唐}9。𪕍𪕌{冋令}10。白𪖇11。𪕷{㱿}12。𪖊𪔿{業攴}13。𪕞鼠{夋鼠}14。𪕔〔鼠〕{耳鼠}15。鼲鼠16。𪕛鼠{䶃鼠}17。𪕚鼠18。', '[音義-1]：隹\n[音義-2]：爵\n[音義-3]：墳\n[音義-4]：由溜\n[音義-5]：如勇\n[音義-6]：瓶\n[音義-7]：於革\n[音義-8]：讒乎\n[音義-9]：博唐\n[音義-10]：古熒。零\n[音義-11]：煩\n[音義-12]：谷\n[音義-13]：卜攴\n[音義-14]：音俊\n[音義-15]：耳\n[音義-16]：古門\n[音義-17]：胡貪\n[音義-18]：柳', '釋獸', '廣雅', '12', DEFAULT, DEFAULT, '2277/6390;6405/6390;6397/6390;5153/6390;6393/6390;6390/6376;6404;9621/6390;6406/6390;6394/6390;6399/6390;4542/6409;1985/878;3321/5776;4928/6391;1962;1749/1994;3361/6390;7791/6390;6408/6390;6403/6390;6396/6390', '9607/6409', 'null', 'null');
