INSERT INTO `t_han_nga` VALUES (2939, '驙;馼;飛黃;騶吾;吉量;朱駮;飛兔;金喙;要褭;走狐;駃騠;飛鴻;野麋;腹丹;騏驥;緑耳;華駵;兆驪;汗血;驒騱;巨虛;騀鹿', '騶虞;盜驪', '白馬黑脊，驙1。白馬朱鬛，馼。飛黃。騶吾[騶虞]。吉𣊹{吉量}。朱駮。飛兔。金喙、騕褭{要褭}。走狐。駃騠2。飛鴻。野麋。腹丹。騏驥。騄駬{緑耳}。驊騮{華駵}。駣𩥴{兆驪}[盜驪]3。汗血。驒騱4。巨虛。騀鹿。', '[音義-1]：大安、知連\n[音義-2]：決啼\n[音義-3]：力兮\n[音義-4]：顛', '釋嘼', '廣雅', '4;18', DEFAULT, DEFAULT, '6223;6176;7691/9200;6233/837;876/5242;3560/6240;7691/6292;9258/783;1784/5367;979/6379;6241/6242;7691/2413;9168/6269;2629/3184;6140/6166;8610/7791;3883/6144;2080/6141;7433/3166;6246/6247;3031/5231;6193/6260', '6233/3108;5593/6141', 'null', 'null');
