INSERT INTO `t_han_nga` VALUES (3248, '耋;老', '', '耋12，老也。3', '[音釋-1]：迭。\n[釋文-2]：田結反，孫他結反，云：「老人面如鐵色」\n[郭注-3]：八十爲耋。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5375;5374', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3249, '輶;輕', '', '輶12，輕也。3', '[音釋-1]：酋。\n[釋文-2]：由久反，又餘周反\n[郭注-3]：詩曰：「德輶如毛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9519;9518', '', 'null', 'null');
