INSERT INTO `t_han_nga` VALUES (3251, '匋;絞', '絛;筊', '綯{匋}[絛]12，絞[筊]也3。45', '[音釋-1]：陶。\n[釋文-2]：徒刀反\n[釋文-3]：古卯反\n[郭注-4]：糾絞繩索。\n[釋文-5]：糾，吉黝反。索，悉各反。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '3286;6626', '8663;2951', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3252, '化', '', '訛，化也。1', '[郭注-1]：詩曰：「四國是訛。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '5213', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3253, '跋;獵', '', '跋1，躐{獵}也23。45', '[釋文-1]：蒲末反，郭音貝，又補葛反\n[音釋-2]：獵。\n[釋文-3]：力輒反\n[郭注-4]：詩曰：「狼跋其胡。」\n[釋文-5]：狼，音郎。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '1383;6357', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3254, '疐;跲', '', '疐12，跲也34。5', '[音釋-1]：致。\n[釋文-2]：竹利反，又得異反，又竹季反，說文云：「礙足不行。」與躓同\n[音釋-3]：其葉。\n[釋文-4]：其業反，又居業反，郭又音甲，廣雅云：「跲，代也」\n[郭注-5]：詩曰：「載疐其尾。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '2504;1380', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3255, '烝;塵', '', '烝1，塵也。23', '[釋文-1]：之仍反\n[郭注-2]：人衆，所以生塵埃。\n[釋文-3]：埃，音哀。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6428;6287', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3256, '戎;相', '胥', '戎[胥]1，相也2。3', '[釋文-1]：如字，本或作𢫨，顧如勇反，沈如升反\n[釋文-2]：如字，又息亮反\n[郭注-3]：相佐助。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '8376;2161', '2697', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3257, '飫;私', '醧;厶', '飫[醧]1，私[厶]也。2', '[釋文-1]：於庶反\n[郭注-2]：宴飲之私。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '0;4428', '9843;5842', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3258, '孺;屬', '', '孺1，屬也2。3', '[釋文-1]：𡦗，如戍反，本今作孺\n[釋文-2]：雛欲反\n[郭注-3]：謂親屬。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '9769;5426', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3259, '幕;暮', '', '幕1，暮也。2', '[釋文-1]：音莫\n[郭注-2]：幕然，暮夜。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4880;0', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3260, '煽;熾;熾;盛', '', '煽1，熾也2。熾，盛也3。4', '[釋文-1]：音扇\n[釋文-2]：昌至反\n[釋文-3]：晠，市正反，本作盛，同\n[郭注-4]：互相訓。煽，義見詩。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6526;6510;6510;3139', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3261, '柢;本', '', '柢12，本也。3', '[音釋-1]：帝。\n[釋文-2]：謝音帝\n[郭注-3]：謂根本。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3559;3558', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3262, '窕;閒', '', '窕1，閒也23。45', '[釋文-1]：郭徒了反，舍人本作跳，云：「跳者，躍之閒」\n[音釋-2]：閑。\n[釋文-3]：音閑，或如字\n[郭注-4]：窈窕，閒隙。\n[釋文-5]：𥥆，音杳。𨻶，去逆反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4668;7759', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3263, '淪;率', '', '淪，率也。1', '[郭注-1]：相率使。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '7175;8782', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3264, '罹;毒', '', '罹1，毒也。23', '[釋文-1]：力知反\n[郭注-2]：憂思慘毒。\n[釋文-3]：思，息吏反。慘，七感反。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4848;236', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3265, '檢;同', '僉', '檢[僉]1，同也。23', '[釋文-1]：李、郭居儉反\n[郭注-2]：模範同等。\n[釋文-3]：模，亡胡反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '3768;4800', '3269', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3266, '郵;過', '', '郵1，過也2。3', '[釋文-1]：音尤\n[釋文-2]：古臥反\n[郭注-3]：道路所經過。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '4011;1110', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3267, '遜;遯', '遁', '遜，遯[遁]也1。2', '[釋文-1]：字又作遁，又作𨔡，同，徒頓反\n[郭注-2]：謂逃去。', '釋言弟二', '爾雅', '2', DEFAULT, DEFAULT, '1140;1172', '1139', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3268, '斃;踣;僨;僵', '', '斃12，踣也34。56僨78，僵也910。11', '[音釋-1]：弊。\n[釋文-2]：獘，字亦作弊，又作斃，婢世反，又婢設反，郭步計反\n[音釋-3]：蒲北。\n[釋文-4]：蒲北反，又音赴，或孚豆、蒲侯二反\n[郭注-5]：前覆。\n[釋文-6]：覆，芳服反。\n[音釋-7]：糞。\n[釋文-8]：甫問反\n[音釋-9]：姜。\n[釋文-10]：居良反\n[郭注-11]：卻偃。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '6362;1392;5151;5152', '', 'null', 'null');
