INSERT INTO `t_han_nga` VALUES (3270, '曷;盍', '', '曷，盍也1。2', '[釋文-1]：戸臘反\n[郭注-2]：盍，何不。', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '3045;3179', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3271, '虹;潰', '訌;䜋', '虹[訌]1，潰[䜋]也23。4', '[釋文-1]：音洪，顧作訌，音同。李本作降，下江反\n[音釋-2]：會。\n[釋文-3]：乎內反\n[郭注-4]：謂潰敗。', '釋言弟二', '爾雅', '1;2', DEFAULT, DEFAULT, '8932;7223', '1648;1649', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3272, '音;闇', '晻', '隌{音}[晻]12，闇也3。456', '[音釋-1]：唵。\n[釋文-2]：字林或作晻，同，烏感反\n[釋文-3]：音暗\n[郭注-4]：隌然，冥貌。\n[音釋-5]：冥，莫定。\n[釋文-6]：冥，莫定反。', '釋言弟二', '爾雅', '1', DEFAULT, DEFAULT, '1738;7769', '4222', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (3273, '䵑;膠', '', '䵑12，膠也3。45', '[音釋-1]：女乙。\n[釋文-2]：字又作䵒，同，女乙反，郭音馹\n[釋文-3]：音交\n[郭注-4]：膠，黏䵑。\n[釋文-5]：黏，女廉反，字林云：「相著也。」字書云：「糊也。」', '釋言弟二', '爾雅', '', DEFAULT, DEFAULT, '0;2727', '', 'null', 'null');
