INSERT INTO `t_han_nga` VALUES (4640, '諸;之;乎;旃;焉;惡乎;於何;烏乎;吁𧪰', '', '諸，之也、乎也。旃，焉也。惡乎1，於何也。烏乎，吁嗟{吁𧪰}也。吁嗟，嗚呼也。有所歎美，有所傷痛，随事爲義也。', '[音義-1]：烏', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '1488;3858;3065;4296;2488;6879/3065;2486/5022;2486/3065;913/1671', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4641, '無念;無寧;不顯;不承', '', '無念，念也。無寧，寧也。不顯，顯也。不承，承也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '8413/6718;8413/3055;7703/5689;7703/7911', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4642, '不肖;不似', '', '不肖，不似也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '7703/2642;7703/5083', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4643, '繩;譽', '偁', '繩[偁]之，譽之也。', '', '廣訓第三', '小爾雅', '1', DEFAULT, DEFAULT, '8691;1568', '5064', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4644, '詰朝;明旦', '', '詰朝，明旦也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '1696/4286;4320/4282', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4645, '黃耇;壽考', '', '「遐不黃耇。」言壽考也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '9200/5378;5381/5382', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4646, '碩膚;瑕;大美;遠', '嘏', '「公孫碩膚，徳音不瑕[嘏]。」道成王大美，聲稱遠也。', '', '廣訓第三', '小爾雅', '2', DEFAULT, DEFAULT, '5627/2597;144;6595/2354;1198', '1462', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4647, '韡', '', '「鄂不韡韡1。」言韡韡也。', '[音義-1]：羽鬼切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '3882', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4648, '賢', '', '「我從事獨賢。」勞事獨多也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '3942', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4649, '甫甫;大', '', '「魴鱮甫甫1。」語其大也。', '[音義-1]：符方切。象吕切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '2082/2082;6595', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4650, '吳吳;衆', '', '「麀鹿麌麌{吳吳}1。」語其衆也。', '[音義-1]：於丘切。虞矩切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '6618/6618;5234', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4651, '錯;雜', '', '「海物維錯。」錯，雜也。', '', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '9326;5332', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4652, '氂;繪;哤', '', '雜毛曰氂，雜彩曰繪，雜言曰哤1。', '[音義-1]：莫江切', '廣訓第三', '小爾雅', '', DEFAULT, DEFAULT, '776;8606;923', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4653, '寡;㷀;嫠', '赹', '凡無妻、無夫通謂之寡。寡夫曰㷀[赹]1，寡婦曰嫠2。', '[音義-1]：渠營切，亦作惸\n[音義-2]：陵之切', '廣義第四', '小爾雅', '2', DEFAULT, DEFAULT, '4605;0;8352', '1015', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4654, '屬婦;逮', '', '妾婦之賤者謂之屬婦。屬1，逮也。逮婦之名，言其微也。', '[音義-1]：時欲切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '5426/8129;1147', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4655, '幸', '', '非分而得謂之幸1。', '[音義-1]：扶問切', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '6622', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4656, '讓', '', '詰責以辭謂之讓。', '', '廣義第四', '小爾雅', '', DEFAULT, DEFAULT, '1692', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4657, '淫;烝;報;通', '勽', '男女不以禮交謂之淫。上淫曰烝，下淫曰報[勽]，旁淫曰通。', '', '廣義第四', '小爾雅', '3', DEFAULT, DEFAULT, '7220;6428;6648;1134', '5806', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4658, '慙;愧;戁;恧;逡', '夋', '不直失節謂之慙，慙，愧也。面慙曰戁1，心慙曰恧2，體慙曰逡[夋]。', '[音義-1]：乃版切\n[音義-2]：女六切', '廣義第四', '小爾雅', '5', DEFAULT, DEFAULT, '6956;8343;6722;6957;1161', '3361', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4659, '大行', '', '諱死謂之大行。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '6595/1266', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4660, '蘇', '', '死而復生謂之蘇。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '256', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4661, '阽', '', '疾甚謂之阽1。', '[音義-1]：余廉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '9688', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4662, '戚;近;從', '', '請天子命曰「未可以戚先王」，請諸侯命曰「未可以近先君」，請大夫命曰「未可以從先子1」。', '[音義-1]：牆容切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '8399;1180;5224', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4663, '櫬;柩', '', '空棺謂之櫬，有屍謂之柩。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3827;8439', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4664, '賵;襚', '', '饋死者謂之賵1，衣服謂之襚2。', '[音義-1]：撫鳯切\n[音義-2]：徐醉切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '3993;5362', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4665, '殔;池;壙;竁;窆;封', '', '埋柩謂之殔1。殔坎謂之池。壙謂之竁2，下棺謂之窆。塡竁謂之封。', '[音義-1]：羊至切\n[音義-2]：昌縜切，又充芮切', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2542;7256;9113;4677;4678;9073', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4666, '宰;冢;壟;塋', '', '宰，塚{冢}也。壟，塋也。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '4592;5812;9141;9138', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4667, '殤', '', '無主之鬼謂之殤。', '', '廣名第五', '小爾雅', '', DEFAULT, DEFAULT, '2536', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4668, '織;繒', '', '治絲曰織，織，繒也1。', '[音義-1]：慈陵切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8530;8589', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4669, '布', '', '麻、紵、葛曰布，布，通名也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '4909', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4670, '纊;緜;絮;纊', '', '纊，綿{緜}也。絮之細者曰纊。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8725;8512;8723;8725', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4671, '繒;縞;素', '', '繒之精者曰縞1，縞之麄者曰素。', '[音義-1]：古老切', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '8589;8598;8773', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4672, '葛;絺;綌', '', '葛之精者曰絺，麄者曰綌。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '458;8736;8737', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4673, '元服', '', '在首謂之元服。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '2/5446', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4674, '弁髦', '', '弁髦，大古冠，布冠而敝之者也。', '', '廣服第六', '小爾雅', '', DEFAULT, DEFAULT, '5466/5733', '', 'null', 'null');
INSERT INTO `t_han_nga` VALUES (4675, '題;定;顚;顔;顙;頟', '頂', '題、定[頂]、顚、顔、䫙{顙}，額{頟}也。', '', '廣服第六', '小爾雅', '2', DEFAULT, DEFAULT, '5608;4573;5605;5599;5607;5609', '5606', 'null', 'null');
