INSERT INTO `t_han_dialect` VALUES (572, '鼻;息', '', '䑄{鼻}，𦞜{息}也。12', '[郭注-1]：謂息肉也。\n[音義-2]：䑄，魚自反。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (573, '讟;痛', '', '讟，痛也。12', '[郭注-1]：謗誣怨痛也。\n[音義-2]：亦音讀。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (574, '鼻;始;首;初;祖;居', '', '鼻，始也，嘼之初生謂之鼻，人之初生謂之首。梁益之閒謂鼻爲初，或謂之祖；祖，居也。1', '[郭注-1]：鼻、祖皆始之别名也，轉復訓以爲居，所謂代語者也。', '卷十三', '方言', '', 'null', 'null');
