INSERT INTO `t_han_dialect` VALUES (580, '兆;理', '䍃', '恌{兆}[䍃]，理也。12', '[郭注-1]：謂情理也。\n[音義-2]：桃音遙。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (581, '薀;盛', '', '蘊{薀}，晠{盛}也。1', '[郭注-1]：蘊藹，茂貌。', '卷十三', '方言', '', 'null', 'null');
