INSERT INTO `t_han_dialect` VALUES (597, '禔;福;喜', '', '禔，福也。12禔，喜也。3', '[郭注-1]：謂福祚也。\n[音義-2]：禔音衹。\n[郭注-3]：有福卽喜。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (598, '嬾;隓;壞', '', '攋{嬾}、1隓，2壞也。', '[音義-1]：洛旱反；\n[音義-2]：許規反。', '卷十三', '方言', '', 'null', 'null');
