INSERT INTO `t_han_dialect` VALUES (604, '豈;陭', '', '隑{豈}，陭也。12', '[郭注-1]：江南人呼梯爲隑，所以隑物而登者也；\n[音義-2]：隑，音剴切也。', '卷十三', '方言', '', 'null', 'null');
