INSERT INTO `t_han_dialect` VALUES (608, '芬;和', '', '芬，和也。1', '[郭注-1]：芬香和調。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (609, '擣;依', '㠀', '擣[㠀]，依也。1依祿也。2', '[郭注-1]：謂可依倚之也；\n[郭注-2]：祿位可依憑也。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (610, '盛;腯', '', '𦛙{盛}，腯也。12', '[郭注-1]：腯腯，肥充也；\n[音義-2]：腯音腞，亦突。', '卷十三', '方言', '', 'null', 'null');
