INSERT INTO `t_han_dialect` VALUES (619, '譯;傳;見', '', '譯，傳也。譯，見也。1', '[郭注-1]：傳宣語，卽相見。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (620, '梗;略', '', '梗，略也。1', '[郭注-1]：梗槪，大略也。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (621, '臆;滿', '𢡃', '臆[𢡃]，滿也。1', '[郭注-1]：愊臆，氣滿之也。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (622, '罵;益', '', '䧞{罵}，益也。12', '[郭注-1]：謂增益也。\n[音義-2]：䧞音罵。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (623, '空;待', '控', '空[控]，待也。1', '[郭注-1]：來則實也。', '卷十三', '方言', '1', 'null', 'null');
