INSERT INTO `t_han_dialect` VALUES (625, '嫗;色', '煦', '嫗[煦]，色也。1', '[郭注-1]：嫗煦[姁]，好色貌。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (626, '閻;開', '', '閻，開也。1', '[郭注-1]：謂開門也。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (627, '摩;滅', '', '摩，滅也。1', '[音義-1]：或作攠滅字，音麋。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (628, '菲;薄', '', '菲，薄也。12', '[郭注-1]：謂微薄也。\n[音義-2]：菲音翡。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (629, '腆;厚', '', '腆，厚也。', '', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (630, '媟;狎', '', '媟，狎也。1', '[郭注-1]：相親狎也。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (631, '芋;大', '', '芋，大也。12', '[郭注-1]：芋猶訏耳。\n[音義-2]：芋，香于反；', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (632, '煬;翕;炙;烈;暴', '', '煬、12翕，炙也。煬、烈，暴也。', '[郭注-1]：今江東呼火熾猛爲煬。\n[音義-2]：音恙；', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (633, '馺;馳', '𢕬', '馺[𢕬]，馬馳也。12', '[郭注-1]：馺馺，疾貌也。\n[音義-2]：馺，索荅反。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (634, '選;延;徧', '宣', '選[宣]、延，徧也。', '', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (635, '澌;索', '', '澌，索也。1', '[郭注-1]：盡也。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (636, '晞;燥', '', '晞，燥也。', '', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (637, '梗;覺', '亢', '梗[亢]，覺也。1', '[郭注-1]：謂直也。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (638, '萃;集', '', '萃，集也。', '', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (639, '睨;澤;明', '', '睨、1𥋭{澤}，2明也。', '[音義-1]：音俾倪。\n[音義-2]：音亦。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (640, '闓;臨;照;美', '', '暟{闓}、臨，照也。暟，美也。12', '[郭注-1]：暟暟，美德也；\n[音義-2]：暟，呼凱反。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (641, '箄;簍;與;舀;𥴧;筐', '', '箄、簍、籅{與}、䈱{舀}，𥴧也。1江沔之閒謂之籅，2趙代之間謂之䈱，3淇衞之閒謂之牛筐。4𥴧，其通語也。𥴧小者，南楚謂之簍，5自關而西秦晉之閒謂之箄。67', '[郭注-1]：古筥字；\n[音義-2]：音餘；\n[音義-3]：音弓弢；\n[郭注-4]：淇，水名也；\n[音義-5]：音縷；\n[郭注-6]：今江南亦名籠爲箄。\n[音義-7]：方氏反。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (642, '籠;旁;笯', '', '籠，南楚江沔之閒謂之篣{旁}，12或謂之笯。34', '[郭注-1]：今零陵人呼籠爲篣。\n[音義-2]：音彭。\n[郭注-3]：亦呼籃。\n[音義-4]：那墓反。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (643, '䈰;𥬔旅', '𥬔盧', '𥰠，1南楚謂之筲{䈰}，23趙魏之郊謂之𥬔𥰠{𥬔旅}[𥬔盧]。4', '[郭注-1]：盛餅筥也；\n[郭注-2]：今建平人呼筲。\n[音義-3]：音鞭鞘；\n[郭注-4]：今通語也。', '卷十三', '方言', '2', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (644, '錐;苕', '', '錐謂之鍣{苕}。1', '[郭注-1]：廣雅作鉊字。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (645, '刀斗', '鐎斗', '無[緣之]升[斗]謂之刁斗{刀斗}[鐎斗]。12', '[郭注-1]：謂小鈴也。\n[音義-2]：音貂。見漢書。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (646, '匕;匙', '', '匕謂之匙。1', '[郭注-1]：匙音祇。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (647, '盂;盡;安殘;盌;喬;銚銳;肙夬', '', '盂謂之㯸{盡}。1河濟之閒謂之䀂𥂫{安殘}。椀{盌}謂之䀉{喬}，盂謂之銚銳，2椀謂之梋㭈{肙夬}。34', '[音義-1]：子殄反；\n[音義-2]：銚，音謠；\n[郭注-3]：椀亦盂屬，江東名盂爲凱，亦曰甌也。\n[音義-4]：蠲決兩音。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (648, '餌;餻;餈;令;奄;元', '', '餌謂之糕{餻}，或謂之餈，或謂之𩚹{令}，1或謂之餣{奄}，2或謂之䬧{元}。3', '[音義-1]：音鈴；\n[音義-2]：央怯反；\n[音義-3]：音元。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (649, '餅;屯;長昆', '', '餅謂之飥{飩}[屯]，1或謂之餦餛{長昆}。2', '[音義-1]：音託；\n[音義-2]：餦餛，長渾兩音。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (650, '餳;長皇;飴;亥;𧯡;隓;鎕', '', '𩛿{餳}謂之餦餭{張皇}。1飴謂之䬵{亥}。2𩜌{𧯡}謂之䭉{隓}。34𩛿謂之餹{鎕}。56凡飴謂之𩛿，自關而東陳楚宋衞之閒通語也。', '[郭注-1]：卽乾飴也；\n[音義-2]：音該；\n[郭注-3]：以豆屑雜𩛿也。\n[音義-4]：音髓；\n[郭注-5]：江東皆言餹。\n[音義-6]：音唐。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (651, '𪍠;䴭;䴳;麰;卑;𨢊;果;𥶶', '', '𪍠、䴭、䴳、麰、䴽{卑}、䵆{𨢊}、12䴹{果}，34麴{𥶶}也。自關而西秦豳之閒曰𪍠，5678晉之舊都曰䴭，910齊右河濟曰䴳，11或曰麰，1213北鄙曰䴽。1415麴，其通語也。', '[郭注-1]：有衣麴。\n[音義-2]：音蒙。\n[郭注-3]：小麥麴爲䴹，卽䴷也；\n[音義-4]：音𩸄。\n[郭注-5]：豳卽邠，\n[音義-6]：音斌\n[郭注-7]：；\n[音義-8]：𪍠音哭；\n[郭注-9]：今江東人呼麴爲䴭。\n[音義-10]：䴭音才；\n[音義-11]：于八反；\n[郭注-12]：大麥麴。\n[音義-13]：音牟。\n[郭注-14]：細餅麴。\n[音義-15]：音脾。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (652, '梠;櫺', '', '屋梠謂之欞{櫺}。12', '[郭注-1]：雀梠卽屋檐也，亦呼爲連綿。\n[音義-2]：音鈴。', '卷十三', '方言', '', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (653, '每;霤', '甍', '𤭐{每}[甍]謂之㽌{霤}。12', '[郭注-1]：卽屋檼也，今字作甍。\n[音義-2]：音萌；㽌音霤。', '卷十三', '方言', '1', 'null', 'null');
INSERT INTO `t_han_dialect` VALUES (654, '冢;墳;培;俞;采;良;壠;丘;塿;墓;無', '宰;摹', '冢，秦晉之閒謂之墳，1或謂之培，2或謂之堬{俞}，3或謂之埰{采}[宰]，4或謂之埌{良}，5或謂之壠。6自關而東謂之丘，小者謂之塿，78大者謂之丘，9凡葬而無墳謂之墓，10所以墓謂之墲{無}[摹]。11', '[郭注-1]：取名於大防也；\n[音義-2]：音部；\n[音義-3]：音臾；\n[郭注-4]：古者卿大夫有采地，死葬之，因名也；\n[音義-5]：音波浪；\n[郭注-6]：有界埓似耕壠，以名之；\n[郭注-7]：培塿，亦堆高之貌。\n[音義-8]：洛口反；\n[郭注-9]：又呼冢爲墳也；\n[郭注-10]：言不封也，墓猶慕也；\n[郭注-11]：墲謂規度墓地也，漢書曰：「初陵之墲」是也。', '卷十三', '方言', '5;11', 'null', 'null');
