DROP TRIGGER IF EXISTS `update_shuowen_fk_word_and_word_real_id`;
delimiter ;;
CREATE TRIGGER `update_shuowen_fk_word_and_word_real_id` AFTER UPDATE ON `shuowen_voice_revel` FOR EACH ROW BEGIN
-- 1、此触发器用以更新ID时，同步更新t_han_nga外键字段
-- 2、更新场景：
-- |-随着阅读量的提升，比如出土文献，此时《说文》框架无法向上兼容，就需要动态调整；
-- |-根据传世文献进行比勘校对时，有必要进行调序。
-- 3、最好不要同时更新id、word
    -- 检查是否更新了 id
    IF OLD.id != NEW.id THEN
		-- 更新 t_han_nga 表中的 shuowen_fk_word_id 字段
		-- 此会话标识，用以暂时禁止t_han_nga的触发器（否则无法更新成功）
				SET @disable_shuowen_trigger = 1;
				
        UPDATE t_han_nga SET shuowen_fk_word_id = F_PLACE_MATCH_ALL(OLD.id, shuowen_fk_word_id, ';', NEW.id) WHERE FIND_IN_SET(OLD.id, REPLACE(REPLACE(shuowen_fk_word_id, '/', ';'), ';', ',')) > 0;
				UPDATE t_han_nga SET shuowen_fk_word_real_id = F_PLACE_MATCH_ALL(OLD.id, shuowen_fk_word_real_id, ';', NEW.id) WHERE FIND_IN_SET(OLD.id, REPLACE(REPLACE(shuowen_fk_word_real_id, '/', ';'), ';', ',')) > 0;
				
				SET @disable_shuowen_trigger = NULL;
		-- 若word变更，则需要更新旧有的、新添加的，通过”空更新“，达到调用触发器的目的		
		ELSEIF NEW.word != OLD.word THEN
				SET @disable_shuowen_trigger = 1;
				
				UPDATE t_han_nga h
        JOIN (
            SELECT DISTINCT id 
            FROM t_han_nga 
            WHERE FIND_IN_SET(NEW.id, REPLACE(REPLACE(shuowen_fk_word_id, '/', ';'), ';', ',')) > 0 
               OR FIND_IN_SET(NEW.id, REPLACE(REPLACE(shuowen_fk_word_real_id, '/', ';'), ';', ',')) > 0 
               OR word REGEXP concat('.*', REPLACE(NEW.word, ';', '|'), '.*') 
               OR word_real REGEXP concat('.*', REPLACE(NEW.word, ';', '|'), '.*')
        ) AS t ON h.id = t.id
        SET h.word = h.word;
				
				SET @disable_shuowen_trigger = null;
    END IF;
END
;;
delimiter ;

