INSERT INTO `shuowen_voice_revel` VALUES (1, '一;弌', 'yī', '第一卷', '一部', '惟初大極，道立於一，造分天地，化成萬物。凡一之屬皆从一。古文一[弋]。', '弋', '', 'null', NULL, '弋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (2, '元', 'yuán', '第一卷', '一部', '始也。从一，兀聲。', '一;兀', '兀', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (3, '天', 'tiān', '第一卷', '一部', '顚也。至高無上。从一大。', '一;大;顚', '顚', 'null', NULL, '一;大', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (4, '丕;㔻', 'pī', '第一卷', '一部', '大也。从一，不聲。', '一;不', '不', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (5, '吏', 'lì', '第一卷', '一部', '治人者也。从一，从史，史亦聲。', '一;史', '史', 'null', NULL, '一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (6, '𠄞;上;丄', 'shànɡ', '第一卷', '一部', '高也。此古文丄。指事也。凡𠄞之屬皆从𠄞。篆文上。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (7, '帝', 'dì', '第一卷', '一部', '諦也，王天下之號。从𠄞，朿聲。古文帝[一]。古文諸丄字皆从一，篆文皆从𠄞。𠄞，古文上字。䇂、示、辰、龍、童、音、章，皆从古文上。', '𠄞;朿;一', '朿', 'null', NULL, '𠄞;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (8, '旁;㫄;雱', 'pánɡ', '第一卷', '一部', '溥也。从𠄞[上]。闕，方聲。古文㫄。亦古文㫄。雱籒文[雨]。', '上;方;雨', '方', 'null', NULL, '上;雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (9, '𠄟;下;丅', 'xià', '第一卷', '一部', '底也。指事。篆文丅。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (10, '示', 'shì', '第一卷', '示部', '天𠂹象，見吉凶，所㠯示人也。从𠄞，三𠂹——日、月、星也。觀乎天文，㠯察時變，示神事也。凡示之屬皆从示。古文示。', '𠄞;神', '神', 'null', NULL, '𠄞', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (11, '祜', 'hù', '第一卷', '示部', '上諱。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (12, '禮', 'lǐ', '第一卷', '示部', '履也。所㠯事神致福也。从示，从豊。豊亦聲。古文禮[𠂆]。', '示;豊;𠂆;履', '豊;履', 'null', NULL, '示;𠂆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (13, '禧', 'xī', '第一卷', '示部', '禮吉也。从示，喜聲。', '示;喜', '喜', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (14, '禛', 'zhēn', '第一卷', '示部', '㠯眞受福也。从示，眞聲。', '示;眞', '眞', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (15, '祿;禄', 'lù', '第一卷', '示部', '福也。从示，录聲。', '示;录', '录', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (16, '禠', 'sī', '第一卷', '示部', '福也。从示，虒聲。', '示;虒', '虒', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (17, '禎', 'zhēn', '第一卷', '示部', '祥也。从示，貞聲。', '示;貞', '貞', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (18, '祥', 'xiánɡ', '第一卷', '示部', '福也。从示，羊聲。', '示;羊', '羊', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (19, '祉', 'zhǐ', '第一卷', '示部', '福也。从示，止聲。', '示;止', '止', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (20, '福', 'fú', '第一卷', '示部', '備也。从示，畐聲。', '示;畐;備', '畐;備', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (21, '祐', 'yòu', '第一卷', '示部', '助也。从示，右聲。', '示;右', '右', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (22, '祺', 'qí', '第一卷', '示部', '吉也。从示，其聲。籒文从基。', '示;其;基', '其;基', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (23, '祗', 'zhī', '第一卷', '示部', '敬也。从示，氐聲。', '示;氐', '氐', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (24, '禔', 'zhī', '第一卷', '示部', '安也。从示，是聲。易曰「禔[祇]旣平」。', '示;是;祇', '是;祇', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (25, '神', 'shén', '第一卷', '示部', '天神，引出萬物者也。从示，申聲。', '示;申;引', '申;引', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (26, '祇', 'qí', '第一卷', '示部', '地祇，提出萬物者也。从示，氏聲。', '示;氏;提;地', '氏;提;地', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (27, '祕', 'mì', '第一卷', '示部', '神也。从示，必聲。', '示;必', '必', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (28, '齋', 'zhāi', '第一卷', '示部', '戒絜也。从示，齊省聲。籒文齋从𥜹[禱]省。', '示;齊;禱', '齊', 'null', NULL, '示;禱', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (29, '禋', 'yīn', '第一卷', '示部', '絜祀也。一曰精意㠯享爲禋。从示，垔聲。籒文从宀。', '示;垔;宀', '垔', 'null', NULL, '示;宀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (30, '祭', 'jì', '第一卷', '示部', '祭祀也。从示，㠯手持肉。', '示;手;肉', '', 'null', NULL, '示;手;肉', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (31, '祀', 'sì', '第一卷', '示部', '祭無巳也。从示，巳聲。祀或从異。', '示;巳;異', '巳;異', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (32, '祡', 'chái', '第一卷', '示部', '燒柴尞祭天也。从示，此聲。虞書曰「至于岱宗，祡」。古文祡从隋省。', '示;此;隋', '此;隋', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (33, '禷', 'lèi', '第一卷', '示部', '㠯事類祭天神。从示，類聲。', '示;類', '類', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (34, '祪', 'ɡuǐ', '第一卷', '示部', '祔、祪，祖也。从示，危聲。', '示;危', '危', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (35, '祔', 'fù', '第一卷', '示部', '後死者合食於先祖。从示，付聲。', '示;付', '付', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (36, '祖', 'zǔ', '第一卷', '示部', '始廟也。从示，且聲。', '示;且', '且', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (37, '𥛱;祊;𥛻', 'bēnɡ', '第一卷', '示部', '門內祭——先祖所㫄皇也。从示，彭聲。詩曰「祝祭于𥛱」。祊𥛱或从方。', '示;彭;方', '彭;方', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (38, '祰', 'kǎo', '第一卷', '示部', '告祭也。从示，告聲。', '示;告', '告', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (39, '祏', 'shí', '第一卷', '示部', '宗廟主也。周禮有郊、宗、石室。一曰大夫㠯石爲主。从示石，石亦聲。', '示;石', '石', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (40, '䃾', 'bǐ', '第一卷', '示部', '㠯豚祠司命也。从示，比聲。漢律曰「祠䃾司命」。', '示;比', '比', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (41, '祠', 'cí', '第一卷', '示部', '春祭曰祠——品物少，多文詞也。从示，司聲。仲春之月，祠[祀]，不用犧牲，用圭璧及皮幣。', '示;司;祀', '司;祀', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (42, '礿', 'yuè', '第一卷', '示部', '夏祭也。从示，勺聲。', '示;勺', '勺', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (43, '禘', 'dì', '第一卷', '示部', '諦祭也。从示，帝聲。周禮五歲一禘。', '示;帝', '帝', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (44, '祫', 'xiá', '第一卷', '示部', '大合祭先祖親疏遠近也。从示合。周禮三歲一祫。', '示;合', '合', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (45, '祼', 'ɡuàn', '第一卷', '示部', '灌祭也。从示，果聲。', '示;果', '果', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (46, '䄟', 'cuì', '第一卷', '示部', '數祭也。从示，毳聲。讀若「舂麥爲㯔」之㯔[臿]。', '示;毳;臿', '毳;臿', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (47, '祝', 'zhù', '第一卷', '示部', '祭主贊䛐者。从示，从儿口。一曰从兌省。易曰「兌爲口、爲巫」。', '示;儿;口;兌;主', '主', 'null', NULL, '示;儿;口;兌', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (48, '𥛽', 'liù', '第一卷', '示部', '祝𥛽也。从示，畱聲。', '示;畱', '畱', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (49, '祓', 'fú', '第一卷', '示部', '除惡祭也。从示，犮聲。', '示;犮', '犮', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (50, '祈', 'qí', '第一卷', '示部', '求福也。从示，斤聲。', '示;斤', '斤', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (51, '禱;𥜹', 'dǎo', '第一卷', '示部', '告事求福也。从示，壽聲。禱或省。籒文禱[眞夂]。', '示;壽;眞;夂', '壽', 'null', NULL, '示;眞;夂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (52, '禜', 'yǒnɡ', '第一卷', '示部', '設緜蕝爲營，㠯禳風雨、雪霜、水旱、厲疫于日月星辰山川也。从示，从營省聲。一曰禜，衛，使災不生。', '示;營', '營', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (53, '禳', 'ránɡ', '第一卷', '示部', '磔禳祀——除厲殃也。古者燧人禜子所造。从示，襄聲。', '示;襄', '襄', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (54, '禬', 'ɡuì', '第一卷', '示部', '會福祭也。从示，會聲。周禮曰「禬之祝號」。', '示;會', '會', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (55, '禪', 'shàn', '第一卷', '示部', '祭天也。从示，單聲。', '示;單', '單', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (56, '禦', 'yù', '第一卷', '示部', '祀也。从示，御聲。', '示;御', '御', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (57, '䄆', 'huó', '第一卷', '示部', '祀也。从示，𠯑聲。', '示;𠯑', '𠯑', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (58, '禖', 'méi', '第一卷', '示部', '祭也。从示，某聲。', '示;某', '某', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (59, '𥚩', 'xǔ', '第一卷', '示部', '祭具也。从示，胥聲。', '示;胥', '胥', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (60, '祳', 'shèn', '第一卷', '示部', '社肉。盛之㠯蜃，故謂之祳。天子所㠯親遺同姓。从示，辰聲。春秋傳曰「石尙來歸祳」。', '示;辰', '辰', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (61, '祴', 'ɡāi', '第一卷', '示部', '宗廟奏祴樂。从示，戒聲。', '示;戒', '戒', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (62, '禡', 'mà', '第一卷', '示部', '師行所止，恐有慢其神，下而祀之曰禡。从示，馬聲。周禮「禡於所征之地」。', '示;馬', '馬', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (63, '禂', 'dǎo', '第一卷', '示部', '禱牲馬祭也。从示，周聲。或从馬，壽省聲。', '示;周;馬;壽', '周;壽', 'null', NULL, '示;馬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (64, '社', 'shè', '第一卷', '示部', '地主也。从示，土聲。春秋傳曰「共工之子句龍爲社神」。周禮「二十五家爲社」，各樹其土所宜木。古文社[木]。', '示;土;木', '土', 'null', NULL, '示;木', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (65, '禓', 'yánɡ', '第一卷', '示部', '道上祭。从示，昜聲。', '示;昜', '昜', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (66, '祲', 'jìn', '第一卷', '示部', '精气感祥。从示，侵省聲。春秋傳曰「見赤黑之祲」是。', '示;侵', '侵', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (67, '禍', 'huò', '第一卷', '示部', '害也。神不福也。从示，咼聲。', '示;咼', '咼', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (68, '祟', 'suì', '第一卷', '示部', '神禍也。从示出。籒文祟从𥜹[禱]省。', '示;出;禱', '', 'null', NULL, '示;出;禱', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (69, '䄏', 'yāo', '第一卷', '示部', '地反物爲䄏也。从示，芺聲。', '示;芺', '芺', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (70, '祘', 'suàn', '第一卷', '示部', '明視㠯筭之。从二示。逸周書曰「士分民之祘」，均分㠯祘之也。讀若筭。', '示;筭', '筭', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (71, '禁', 'jìn', '第一卷', '示部', '吉凶之忌也。从示，林聲。', '示;林', '林', 'null', NULL, '示', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (72, '禫', 'dàn', '第一卷', '示部', '除服祭也。从示，覃聲。', '示;覃', '覃', 'null', NULL, '示', 'null', 'null', 'null', 'null', '侵部', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (73, '禰', 'nǐ', '第一卷', '示部', '親廟也。从示，爾聲。', '示;爾', '爾', 'null', NULL, '示', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (74, '祧', 'tiāo', '第一卷', '示部', '遷廟也。从示，兆聲。', '示;兆', '兆', 'null', NULL, '示', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (75, '祆', 'xiān', '第一卷', '示部', '胡神也。从示，天聲。', '示;天', '天', 'null', NULL, '示', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (76, '祚', 'zuò', '第一卷', '示部', '福也。从示，乍聲。', '示;乍', '乍', 'null', NULL, '示', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (77, '三', 'sān', '第一卷', '三部', '數名。天、地、人之道也。於文一耦二爲三，成數也。凡三之屬皆从三。古文三[弋]。', '一;二;弋', '', 'null', NULL, '一;二;弋', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (78, '王', 'wánɡ', '第一卷', '王部', '天下所歸往也。董仲舒曰「古之造文者，三畫而連其中謂之王」。三者，天、地、人也，而參通之者，王也。孔子曰「一貫三爲王」。凡王之屬皆从王。古文王。', '往', '往', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (79, '閏', 'rùn', '第一卷', '王部', '餘分之月，五歲再閏也。告朔之禮，天子居宗廟，閏月居門中。从王在門中。周禮「閏月王居門中，終月也」。', '王;門', '', 'null', NULL, '王;門', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (80, '皇;𦤃', 'huánɡ', '第一卷', '王部', '大也。从自王。自，始也。始王者——三皇，大君也。自，讀若鼻，今俗㠯作始生子爲「鼻子」是。', '自;王', '王', 'null', NULL, '自', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (81, '玉', 'yù', '第一卷', '玉部', '石之美者。有「五德」——潤澤㠯溫，仁之方也；䚡理自外，可㠯知中，義之方也；其聲舒揚，專㠯遠聞，智之方也；不撓而折，勇之方也；銳廉而不忮，絜之方也。象三玉之連。丨，其貫也。凡玉之屬皆从玉。古文玉。', '丨', '', 'null', NULL, '丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (82, '璙', 'liáo', '第一卷', '玉部', '玉也。从玉，尞聲。', '玉;尞', '尞', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (83, '瓘', 'ɡuàn', '第一卷', '玉部', '玉也。从玉，雚聲。春秋傳曰「瓘斝」。', '玉;雚', '雚', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (84, '璥', 'jǐnɡ', '第一卷', '玉部', '玉也。从玉，敬聲。', '玉;敬', '敬', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (85, '琠', 'tiǎn', '第一卷', '玉部', '玉也。从玉，典聲。', '玉;典', '典', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (86, '𤫕', 'náo', '第一卷', '玉部', '玉也。从玉，夒聲。讀若柔。', '玉;夒;柔', '夒;柔', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (87, '𤪢', 'lì', '第一卷', '玉部', '玉也。从玉，毄聲。讀若鬲。', '玉;毄;鬲', '毄;鬲', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (88, '璠', 'fán', '第一卷', '玉部', '璠與，魯之寶玉。从玉，番聲。孔子曰「美哉璠與！遠而望之，奐若也；近而視之，瑟若也。一則理勝，二則孚勝」。', '玉;番', '番', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (89, '璵', 'yú', '第一卷', '玉部', '璵璠也。从玉，與聲。', '玉;與', '與', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '大徐补 | 应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (90, '瑾', 'jǐn', '第一卷', '玉部', '瑾瑜，美玉也。从玉，堇聲。', '玉;堇', '堇', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (91, '瑜', 'yú', '第一卷', '玉部', '瑾瑜也。从玉，俞聲。', '玉;俞', '俞', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (92, '玒', 'hónɡ', '第一卷', '玉部', '玉也。从玉，工聲。', '玉;工', '工', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (93, '𤦃;琜', 'lái', '第一卷', '玉部', '赤玉也。从玉，來聲。', '玉;來', '來', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (94, '瓊;璚;瓗;琁', 'qiónɡ', '第一卷', '玉部', '玉也。从玉，敻聲。璚瓊或从矞。瓗瓊或从巂。琁瓊或从旋省。', '玉;敻;矞;巂;旋', '敻;矞;巂;旋', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '重文应调', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (95, '珦', 'xiànɡ', '第一卷', '玉部', '玉也。从玉，向聲。', '玉;向', '向', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (96, '㻝', 'là', '第一卷', '玉部', '玉也。从玉，剌聲。', '玉;剌', '剌', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (97, '珣', 'xún', '第一卷', '玉部', '醫無閭之珣㺮璂。周書所謂「夷玉」也。从玉，旬聲。一曰玉器。讀若宣。', '玉;旬;宣', '旬;宣', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (98, '璐', 'lù', '第一卷', '玉部', '玉也。从玉，路聲。', '玉;路', '路', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (99, '瓚', 'zàn', '第一卷', '玉部', '三玉二石也。从玉，贊聲。禮「天子用全，純玉也；上公用駹，四玉一石；侯用瓚；伯用埒，玉石半相埒也」。', '玉;贊', '贊', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (100, '瑛', 'yīnɡ', '第一卷', '玉部', '玉光也。从玉，英聲。', '玉;英', '英', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '△', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (101, '璑', 'wú', '第一卷', '玉部', '三采玉也。从玉，無聲。', '玉;無', '無', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (102, '玊', 'xiù', '第一卷', '玉部', '朽玉也。从玉有點。讀若「畜牧」之畜。', '玉;畜', '畜', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (103, '璿', 'xuán', '第一卷', '玉部', '美玉也。从玉，睿聲。春秋傳曰「璿弁玉纓」。琁璿或从旋省。籒文璿。古文璿。', '玉;睿;旋', '睿;旋', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '重文应调', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (104, '球;璆', 'qiú', '第一卷', '玉部', '玉也。从玉，求聲。璆球或从翏。', '玉;求;翏', '求;翏', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (105, '琳', 'lín', '第一卷', '玉部', '美玉也。从玉，林聲。', '玉;林', '林', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (106, '璧', 'bì', '第一卷', '玉部', '瑞玉圜也。从玉，辟聲。', '玉;辟', '辟', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (107, '瑗', 'yuàn', '第一卷', '玉部', '大孔璧。人君上除陛㠯相引。爾雅曰「好倍肉謂之瑗，肉倍好謂之璧」。从玉，爰聲。', '玉;爰', '爰', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (108, '環;𤪹', 'huán', '第一卷', '玉部', '璧肉好若一謂之環。从玉，瞏聲。', '玉;瞏', '瞏', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (109, '璜', 'huánɡ', '第一卷', '玉部', '半璧也。从玉，黃聲。', '玉;黃', '黃', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (110, '琮', 'cónɡ', '第一卷', '玉部', '瑞玉。大八寸，似車釭。从玉，宗聲。', '玉;宗', '宗', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (111, '琥', 'hǔ', '第一卷', '玉部', '發兵瑞玉，爲虎文。从玉，虎聲。春秋傳曰「賜子家子雙琥」是。', '玉;虎', '虎', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (112, '瓏', 'lónɡ', '第一卷', '玉部', '禱旱玉也。爲龍文。从玉，龍聲。', '玉;龍', '龍', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (113, '琬', 'wǎn', '第一卷', '玉部', '圭有琬者。从玉，宛聲。', '玉;宛', '宛', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (114, '璋', 'zhānɡ', '第一卷', '玉部', '剡上爲圭，半圭爲璋。从玉，章聲。禮「六幣」——圭㠯馬，璋㠯皮，璧㠯帛，琮㠯錦，琥㠯繡，璜㠯黼。', '玉;章', '章', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (115, '琰', 'yǎn', '第一卷', '玉部', '璧上起美色也。从玉，炎聲。', '玉;炎', '炎', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (116, '玠', 'jiè', '第一卷', '玉部', '大圭也。从玉，介聲。周書曰「稱奉介圭」。', '玉;介', '介', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (117, '瑒', 'chànɡ', '第一卷', '玉部', '圭。尺二寸，有瓚，㠯祠宗廟者也。从玉，昜聲。', '玉;昜', '昜', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (118, '瓛', 'huán', '第一卷', '玉部', '桓圭。公所執。从玉，獻聲。', '玉;獻', '獻', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (119, '珽', 'tǐnɡ', '第一卷', '玉部', '大圭。長三尺，抒上，終葵首。从玉，廷聲。', '玉;廷', '廷', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (120, '瑁', 'mào', '第一卷', '玉部', '諸矦執圭朝天子，天子執玉㠯冒之，似𤛿冠。周禮曰「天子執瑁四寸」。从玉冒。冒亦聲。古文从冃。', '玉;冒;冃', '冒;冃', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (121, '瑞', 'ruì', '第一卷', '玉部', '㠯玉爲信也。从玉，耑聲。', '玉;耑', '耑', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (122, '璬', 'jiǎo', '第一卷', '玉部', '玉佩。从玉，敫聲。', '玉;敫', '敫', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (123, '珩', 'hénɡ', '第一卷', '玉部', '佩上玉也。从玉。行，所㠯節行止也。', '玉;行', '行', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (124, '玦', 'jué', '第一卷', '玉部', '玉佩也。从玉，夬聲。', '玉;夬', '夬', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (125, '珥', 'ěr', '第一卷', '玉部', '瑱也。从玉耳。耳亦聲。', '玉;耳', '耳', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (126, '瑱', 'tiàn', '第一卷', '玉部', '㠯玉充耳也。从玉，眞聲。詩曰「玉之瑱兮」。瑱或从耳。', '玉;眞;耳', '眞', 'null', NULL, '玉;耳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (127, '琫', 'běnɡ', '第一卷', '玉部', '佩刀上飾也。天子㠯玉，諸矦㠯金。从玉，奉聲。', '玉;奉', '奉', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (128, '珌', 'bì', '第一卷', '玉部', '佩刀下飾。天子㠯玉。从玉，必聲。古文珌[畢]。', '玉;必;畢', '必;畢', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (129, '璏', 'zhì', '第一卷', '玉部', '劒鼻玉也。从玉，彘聲。', '玉;彘', '彘', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (130, '瑵', 'zhǎo', '第一卷', '玉部', '車葢玉瑵。从玉，蚤聲。', '玉;蚤', '蚤', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (131, '瑑', 'zhuàn', '第一卷', '玉部', '圭璧上起兆瑑也。从玉，彖聲。周禮曰「瑑圭璧」。', '玉;彖', '彖', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (132, '珇', 'zǔ', '第一卷', '玉部', '琮玉之瑑。从玉，且聲。', '玉;且', '且', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (133, '𤪌;璂', 'qí', '第一卷', '玉部', '弁飾也，往往冒玉也。从玉，綦聲。𤪌或从基。', '玉;綦;基', '綦', 'null', NULL, '玉;基', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (134, '璪', 'zǎo', '第一卷', '玉部', '玉飾。如水藻之文。从玉，喿聲。虞書曰「璪火粉米」。', '玉;喿', '喿', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (135, '瑬', 'liú', '第一卷', '玉部', '𠂹玉也，冕飾。从玉，流聲。', '玉;流', '流', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (136, '璹', 'shú', '第一卷', '玉部', '玉器也。从玉，𠷎聲。讀若淑。', '玉;𠷎;淑', '𠷎;淑', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (137, '瓃', 'léi', '第一卷', '玉部', '玉器也。从玉，畾聲。', '玉;畾', '畾', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (138, '瑳', 'cuō', '第一卷', '玉部', '玉色鮮白。从玉，差聲。', '玉;差', '差', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (139, '玼', 'cǐ', '第一卷', '玉部', '新玉色鮮也。从玉，此聲。詩曰「新臺有玼[漼]」。', '玉;此;漼', '此;漼', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (140, '璱', 'sè', '第一卷', '玉部', '玉英華相帶如瑟弦也。从玉，瑟聲。詩曰「璱彼玉瓚」。', '玉;瑟', '瑟', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (141, '瑮', 'lì', '第一卷', '玉部', '玉英華羅列秩秩。从玉，栗聲。逸論語曰「玉粲之璱兮，其瑮猛也」。', '玉;栗', '栗', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (142, '瑩', 'yínɡ', '第一卷', '玉部', '玉色也。从玉，熒省聲。一曰石之次玉者。逸論語曰「如玉之瑩」。', '玉;熒', '熒', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (143, '璊', 'mén', '第一卷', '玉部', '玉䞓色也。从玉，㒼聲。禾之赤苗謂之𥡹，言璊玉色如之。璊或从允。', '玉;㒼;允', '㒼;允', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (144, '瑕', 'xiá', '第一卷', '玉部', '玉小赤也。从玉，叚聲。', '玉;叚', '叚', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (145, '琢;𤥨', 'zhuó', '第一卷', '玉部', '治玉也。从玉，豖聲。', '玉;豖', '豖', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (146, '琱', 'diāo', '第一卷', '玉部', '治玉也。一曰石似玉。从玉，周聲。', '玉;周', '周', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (147, '理', 'lǐ', '第一卷', '玉部', '治玉也。从玉，里聲。', '玉;里', '里', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (148, '珍', 'zhēn', '第一卷', '玉部', '寶也。从玉，㐱聲。', '玉;㐱', '㐱', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (149, '玩', 'wán', '第一卷', '玉部', '弄也。从玉，元聲。玩或从貝。', '玉;元;貝', '元', 'null', NULL, '玉;貝', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (150, '玲', 'línɡ', '第一卷', '玉部', '玉聲也。从玉，令聲。', '玉;令', '令', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (151, '瑲', 'qiānɡ', '第一卷', '玉部', '玉聲也。从玉，倉聲。詩曰「攸革有瑲」。', '玉;倉', '倉', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (152, '玎', 'dīnɡ', '第一卷', '玉部', '玉聲也。从玉，丁聲。齊大公子伋謚曰「玎公」。', '玉;丁', '丁', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (153, '琤', 'chēnɡ', '第一卷', '玉部', '玉聲也。从玉，爭聲。', '玉;爭', '爭', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (154, '瑣', 'suǒ', '第一卷', '玉部', '玉聲。从玉，𧴪聲。', '玉;𧴪', '𧴪', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (155, '瑝', 'huánɡ', '第一卷', '玉部', '玉聲。从玉，皇聲。', '玉;皇', '皇', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (156, '瑀', 'yǔ', '第一卷', '玉部', '石之次玉者。从玉，禹聲。', '玉;禹', '禹', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (157, '玤', 'bànɡ', '第一卷', '玉部', '石之次玉者，㠯爲系璧。从玉，丰聲。讀若詩曰「瓜瓞菶菶」。一曰若「蛤蚌」。', '玉;丰;菶;蚌', '丰;菶;蚌', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (158, '玪', 'jiān', '第一卷', '玉部', '玪𤨙，石之次玉者。从玉，今聲。', '玉;今', '今', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (159, '𤨙', 'lè', '第一卷', '玉部', '玪𤨙也。从玉，勒聲。', '玉;勒', '勒', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (160, '琚', 'jū', '第一卷', '玉部', '佩玉石也。从玉，居聲。詩曰「報之㠯瓊琚」。', '玉;居', '居', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (161, '璓', 'xiù', '第一卷', '玉部', '石之次玉者。从玉，莠聲。詩曰「充耳璓瑩」。', '玉;莠', '莠', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (162, '玖', 'jiǔ', '第一卷', '玉部', '石之次玉黑色者。从玉，久聲。詩曰「貽我佩玖」。讀若芑。或曰若「人句脊」之句。', '玉;久;芑;句', '久;芑;句', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (163, '㺿', 'yí', '第一卷', '玉部', '石之似玉者。从玉，𦣞聲。讀若貽。', '玉;𦣞;貽', '𦣞;貽', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
