INSERT INTO `shuowen_voice_revel` VALUES (165, '𤤺', 'yì', '第一卷', '玉部', '石之似玉者。从玉，曳聲。', '玉;曳', '曳', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
