INSERT INTO `shuowen_voice_revel` VALUES (167, '璡', 'jīn', '第一卷', '玉部', '石之似玉者。从玉，進聲。讀若津。', '玉;進;津', '進;津', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (168, '㻸', 'zēn', '第一卷', '玉部', '石之似玉者。从玉，朁聲。', '玉;朁', '朁', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (169, '璁', 'cōnɡ', '第一卷', '玉部', '石之似玉者。从玉，悤聲。讀若蔥。', '玉;悤;蔥', '悤;蔥', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
