INSERT INTO `shuowen_voice_revel` VALUES (172, '𤧩', 'wàn', '第一卷', '玉部', '石之似玉者。从玉，𥄗聲。', '玉;𥄗', '𥄗', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (173, '𤫉', 'xiè', '第一卷', '玉部', '石之玉。从玉，燮聲。', '玉;燮', '燮', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (174, '玽', 'ɡǒu', '第一卷', '玉部', '石之似玉者。从玉，句聲。讀若苟。', '玉;句;苟', '句;苟', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (175, '琂', 'yán', '第一卷', '玉部', '石之似玉者。从玉，言聲。', '玉;言', '言', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (176, '璶', 'jìn', '第一卷', '玉部', '石之似玉者。从玉，盡聲。', '玉;盡', '盡', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (177, '琟', 'wéi', '第一卷', '玉部', '石之似玉者。从玉，隹聲。讀若維。', '玉;隹;維', '隹;維', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (178, '瑦', 'wǔ', '第一卷', '玉部', '石之似玉者。从玉，烏聲。', '玉;烏', '烏', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (179, '瑂', 'méi', '第一卷', '玉部', '石之似玉者。从玉，眉聲。讀若眉。', '玉;眉', '眉', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (180, '璒', 'dēnɡ', '第一卷', '玉部', '石之似玉者。从玉，登聲。', '玉;登', '登', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (181, '㺨', 'sī', '第一卷', '玉部', '石之似玉者。从玉，厶聲。讀與私同。', '玉;厶;私', '厶;私', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (182, '玗;㺮', 'yú', '第一卷', '玉部', '石之似玉者。从玉，于聲。', '玉;于', '于', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (183, '𤣻', 'mò', '第一卷', '玉部', '玉屬也。从玉，𠬛聲。讀若沒。', '玉;𠬛;沒', '𠬛;沒', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (184, '瑎', 'xié', '第一卷', '玉部', '黑石似玉者。从玉，皆聲。讀若諧。', '玉;皆;諧', '皆;諧', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (185, '碧', 'bì', '第一卷', '玉部', '石之靑美者。从玉石，白聲。', '玉;石;白', '白', 'null', NULL, '玉;石', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (186, '琨', 'kūn', '第一卷', '玉部', '石之美者。从玉，昆聲。夏書曰「楊州貢瑤琨」。琨或从貫。', '玉;昆;貫', '昆;貫', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (187, '珉', 'mín', '第一卷', '玉部', '石之美者。从玉，民聲。', '玉;民', '民', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (188, '瑤', 'yáo', '第一卷', '玉部', '石之美者。从玉，䍃聲。詩曰「報之㠯瓊瑤」。', '玉;䍃', '䍃', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (189, '珠', 'zhū', '第一卷', '玉部', '蜯中陰精也。从玉，朱聲。春秋國語曰「珠足㠯御火災」是也。', '玉;朱', '朱', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (190, '玓', 'dì', '第一卷', '玉部', '玓瓅，明珠光也。从玉，勺聲。', '玉;勺', '勺', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (191, '瓅', 'lì', '第一卷', '玉部', '玓瓅也。从玉，樂聲。', '玉;樂', '樂', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (192, '玭;蠙', 'pín', '第一卷', '玉部', '珠也。从玉，比聲。宋宏曰「淮水中出玭珠」。玭珠，珠之有聲者。蠙夏書玭从虫賓。', '玉;比;虫;賓', '比;賓', 'null', NULL, '玉;虫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (193, '珕', 'lì', '第一卷', '玉部', '蜃屬。从玉，劦聲。禮記曰「佩刀，士珕琫而珧珌」。', '玉;劦', '劦', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (194, '珧', 'yáo', '第一卷', '玉部', '蜃甲也，所㠯飾物也。从玉，兆聲。禮記曰「佩刀，天子玉琫而珧珌」。', '玉;兆', '兆', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (195, '玟', 'méi', '第一卷', '玉部', '玟瑰，火齊珠。一曰石之美者。从玉，文聲。', '玉;文', '文', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (196, '瑰', 'ɡuī', '第一卷', '玉部', '玟瑰也。从玉，鬼聲。一曰圜好。', '玉;鬼', '鬼', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (197, '璣', 'jī', '第一卷', '玉部', '珠不圜者。从玉，幾聲。', '玉;幾', '幾', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (198, '琅', 'lánɡ', '第一卷', '玉部', '琅玕，似珠者。从玉，良聲。', '玉;良', '良', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (199, '玕', 'ɡān', '第一卷', '玉部', '琅玕也。从玉，干聲。禹貢「雝州璆琳琅玕」。古文玕从玉旱。', '玉;干;旱', '干;旱', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (200, '珊', 'shān', '第一卷', '玉部', '珊瑚，色赤，生於海，或生於山。从玉，刪省聲。', '玉;刪', '刪', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (201, '瑚', 'hú', '第一卷', '玉部', '珊瑚也。从玉，胡聲。', '玉;胡', '胡', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (202, '𤥗;珋', 'liú', '第一卷', '玉部', '石之有光者，璧𤥗也。出西胡中。从玉，丣聲。', '玉;丣', '丣', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (203, '琀', 'hán', '第一卷', '玉部', '送死口中玉也。从玉含。含亦聲。', '玉;含', '含', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (204, '𤪎', 'yǒu', '第一卷', '玉部', '遺玉也。从玉，𣣸聲。', '玉;𣣸', '𣣸', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (205, '璗', 'dànɡ', '第一卷', '玉部', '金之美者。與玉同色。从玉，湯聲。禮記曰「佩刀，諸矦璗琫而璆珌」。', '玉;湯', '湯', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (206, '𩆜;𤫊;靈', 'línɡ', '第一卷', '玉部', '巫也。㠯玉事神。从玉，霝聲。靈𤫊或从巫。', '玉;霝;巫', '霝', 'null', NULL, '玉;巫', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (207, '珈', 'jiā', '第一卷', '玉部', '婦人首飾。从玉，加聲。詩曰「副笄六珈」。', '玉;加', '加', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (208, '璩', 'qú', '第一卷', '玉部', '環屬。从玉，豦聲。', '玉;豦', '豦', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (209, '琖;盞', 'zhǎn', '第一卷', '玉部', '玉爵也。夏曰琖，殷曰斝，周曰爵。从玉，戔聲。[盞]或从皿。', '玉;戔;皿', '戔', 'null', NULL, '玉;皿', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (210, '琛', 'chēn', '第一卷', '玉部', '寶也。从玉，深省聲。', '玉;深', '深', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (211, '璫', 'dānɡ', '第一卷', '玉部', '華飾也。从玉，當聲。', '玉;當', '當', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (212, '琲', 'bèi', '第一卷', '玉部', '珠五百枚也。从玉，非聲。', '玉;非', '非', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (213, '珂', 'kē', '第一卷', '玉部', '玉也。从玉，可聲。', '玉;可', '可', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (214, '玘', 'qǐ', '第一卷', '玉部', '玉也。从玉，己聲。', '玉;己', '己', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (215, '珝', 'xǔ', '第一卷', '玉部', '玉也。从玉，羽聲。', '玉;羽', '羽', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (216, '璀', 'cuǐ', '第一卷', '玉部', '璀璨，玉光也。从玉，崔聲。', '玉;崔', '崔', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (217, '璨', 'càn', '第一卷', '玉部', '玉光也。从玉，粲聲。', '玉;粲', '粲', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (218, '琡', 'chù', '第一卷', '玉部', '玉也。从玉，叔聲。', '玉;叔', '叔', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (219, '瑄', 'xuān', '第一卷', '玉部', '璧六寸也。从玉，宣聲。', '玉;宣', '宣', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (220, '珙', 'ɡǒnɡ', '第一卷', '玉部', '玉也。从玉，共聲。', '玉;共', '共', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (221, '玨;𤤴', 'jué', '第一卷', '玨部', '二玉相合爲一玨。凡玨之屬皆从玨。玨或从㱿。', '玉;㱿', '㱿', 'null', NULL, '玉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (222, '班', 'bān', '第一卷', '玨部', '分瑞玉。从玨刀。', '玨;刀', '', 'null', NULL, '玨;刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (223, '𨌥', 'fú', '第一卷', '玨部', '車笭閒皮匧也。古者使奉玉所㠯盛之。从車玨。讀與服同。', '車;玨;服', '服', 'null', NULL, '車;玨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (224, '气', 'qì', '第一卷', '气部', '雲气也。象形。凡气之屬皆从气。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (225, '氛;雰', 'fēn', '第一卷', '气部', '祥气也。从气，分聲。雰氛或从雨。', '气;分;雨', '分', 'null', NULL, '气;雨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (226, '士', 'shì', '第一卷', '士部', '事也。數始於一，終於十。从一十。孔子曰「推十合一爲士」。凡士之屬皆从士。', '一;十', '', 'null', NULL, '一;十', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (227, '壻;婿', 'xù', '第一卷', '士部', '夫也。从士胥。詩曰「女也不爽，士貳其行」。士者，夫也。讀與細同。婿壻或从女。', '士;胥;細;女', '細', 'null', NULL, '士;胥;女', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (228, '壯', 'zhuànɡ', '第一卷', '士部', '大也。从士，爿聲。', '士;爿', '爿', 'null', NULL, '士', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (229, '壿;墫', 'cūn', '第一卷', '士部', '士舞也。从士，𢍜聲。詩曰「墫墫舞我」。', '士;𢍜', '𢍜', 'null', NULL, '士', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (230, '丨', 'ɡǔn', '第一卷', '丨部', '下上通也。引而上行，讀若𦥓。引而下行，讀若退。凡丨之屬皆从丨。', '𦥓;退', '𦥓;退', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (231, '中', 'zhōnɡ', '第一卷', '丨部', '和也。从囗丨。下上通也。古文中。籒文中。', '囗;丨', '', 'null', NULL, '囗;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (232, '𣃘', 'chǎn', '第一卷', '丨部', '旌旗杠皃。从丨，从㫃，㫃亦聲。', '丨;㫃', '㫃', 'null', NULL, '丨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (233, '屮', 'chè', '第一卷', '屮部', '艸木初生也。象丨出形有枝莖也。古文或㠯爲艸字。讀若徹。凡屮之屬皆从屮，尹彤說。', '丨;徹', '徹', 'null', NULL, '丨', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (234, '屯', 'zhūn', '第一卷', '屮部', '難也。屯象艸木之初生，屯然而難。从屮貫一，屈曲之也。一，地也。易曰「屯剛柔始交而難生」。', '屮;一', '', 'null', NULL, '屮;一', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (235, '每', 'měi', '第一卷', '屮部', '艸盛上出也。从屮，母聲。', '屮;母', '母', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (236, '毒', 'dú', '第一卷', '屮部', '厚也。害人之艸，往往而生。从屮，毐聲。古文毒从刀䈞。', '屮;毐;刀;䈞', '毐;䈞', 'null', NULL, '屮;刀', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (237, '㞣;芬', 'fēn', '第一卷', '屮部', '艸初生其香分布也。从屮，分聲。芬㞣或从艸。', '屮;分;艸', '分', 'null', NULL, '屮;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (238, '𡴆', 'lù', '第一卷', '屮部', '菌𡴆，地蕈。叢生田中。从屮，六聲。籒文𡴆，从三𡴆。', '屮;六;𡴆', '六', 'null', NULL, '屮;𡴆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (239, '熏', 'xūn', '第一卷', '屮部', '火煙上出也。从屮，从黑。屮黑，熏象。', '屮;黑', '', 'null', NULL, '屮;黑', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (240, '艸', 'cǎo', '第一卷', '艸部', '百卉也。从二屮。凡艸之屬皆从艸。', '屮', '', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (241, '莊', 'zhuānɡ', '第一卷', '艸部', '上諱。[从艸，壯聲。]古文莊[爿歺丌]。', '艸;壯;爿;歺;丌', '壯;爿', 'null', NULL, '艸;歺;丌', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (242, '蓏', 'luǒ', '第一卷', '艸部', '在木曰果，在艸曰蓏。从艸㼌。', '艸;㼌', '', 'null', NULL, '艸;㼌', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (243, '芝', 'zhī', '第一卷', '艸部', '神艸也。从艸，之聲。', '艸;之', '之', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (244, '萐', 'shà', '第一卷', '艸部', '萐莆，瑞艸也。堯時生於庖廚，扇暑而涼。从艸，疌聲。', '艸;疌', '疌', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (245, '莆', 'fǔ', '第一卷', '艸部', '萐莆也。从艸，甫聲。', '艸;甫', '甫', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (246, '虋', 'mén', '第一卷', '艸部', '赤苗嘉穀也。从艸，釁聲。', '艸;釁', '釁', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (247, '荅', 'dá', '第一卷', '艸部', '小尗也。从艸，合聲。', '艸;合', '合', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (248, '萁', 'qí', '第一卷', '艸部', '尗莖也。从艸，其聲。', '艸;其', '其', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (249, '𧆑;藿', 'huò', '第一卷', '艸部', '尗之少也。从艸，靃聲。', '艸;靃', '靃', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (250, '莥', 'niǔ', '第一卷', '艸部', '鹿藿之實名也。从艸，狃聲。', '艸;狃', '狃', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (251, '蓈;稂', 'lánɡ', '第一卷', '艸部', '禾粟之莠生而不成者，謂之「童蓈」。从艸，郎聲。稂蓈或从禾[良]。', '艸;郎;禾;良', '郎;良', 'null', NULL, '艸;禾', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (252, '莠', 'yǒu', '第一卷', '艸部', '禾粟下揚生者，謂之莠。从艸，秀聲。讀若酉。', '艸;秀;酉', '秀;酉', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (253, '萉;𪎰', 'fèi', '第一卷', '艸部', '枲實也。从艸，肥聲。萉或从麻賁。', '艸;肥;麻;賁', '肥;賁', 'null', NULL, '艸;麻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (254, '芓', 'zì', '第一卷', '艸部', '麻母也。从艸，子聲。一曰芓卽枲也。', '艸;子;枲', '子;枲', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (255, '䔬', 'yì', '第一卷', '艸部', '芓也。从艸，異聲。', '艸;異', '異', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (256, '蘇;蘓', 'sū', '第一卷', '艸部', '桂荏也。从艸，穌聲。', '艸;穌', '穌', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (257, '荏', 'rěn', '第一卷', '艸部', '桂荏，蘇也。从艸，任聲。', '艸;任', '任', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (258, '䒨', 'shǐ', '第一卷', '艸部', '䒨菜也。从艸，矢聲。', '艸;矢', '矢', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (259, '䔇', 'qǐ', '第一卷', '艸部', '菜之美者，雲夢之䔇[芹]。从艸，豈聲。', '芹;艸;豈', '芹;豈', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (260, '葵', 'kuí', '第一卷', '艸部', '葵菜也。从艸，癸聲。', '艸;癸', '癸', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (261, '䕬', 'jiānɡ', '第一卷', '艸部', '御溼之菜也。从艸，彊聲。', '艸;彊', '彊', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (262, '蓼', 'liǎo', '第一卷', '艸部', '辛菜，薔虞也。从艸，翏聲。', '艸;翏', '翏', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (263, '䔃', 'zǔ', '第一卷', '艸部', '䔃菜也。从艸，祖聲。', '艸;祖', '祖', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (264, '𦼫', 'qú', '第一卷', '艸部', '𦼫菜也，佀蘇者。从艸，豦聲。', '艸;豦', '豦', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
