INSERT INTO `shuowen_voice_revel` VALUES (404, '𦺋', 'sǎo', '第一卷', '艸部', '艸也。从艸，㛮聲。', '艸;㛮', '㛮', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (405, '芐', 'hù', '第一卷', '艸部', '地黃也。从艸，下聲。禮記「鈃毛：牛𧆑、羊芐、豕薇」是。', '艸;下', '下', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
