INSERT INTO `shuowen_voice_revel` VALUES (407, '菳', 'qín', '第一卷', '艸部', '黃菳也。从艸，金聲。', '艸;金', '金', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (408, '芩', 'qín', '第一卷', '艸部', '艸也。从艸，今聲。詩曰「食野之芩」。', '艸;今', '今', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (409, '藨', 'biāo', '第一卷', '艸部', '鹿𧆑也。从艸，麃聲。讀若剽。一曰𦳋之屬。', '艸;麃;剽', '麃;剽', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
