INSERT INTO `shuowen_voice_revel` VALUES (412, '芰', 'jì', '第一卷', '艸部', '蔆也。从艸，支聲。杜林說芰从多。', '艸;支;多', '支;多', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
