INSERT INTO `shuowen_voice_revel` VALUES (416, '䕮', 'jú', '第一卷', '艸部', '日精也，㠯秋華。从艸，𥶶省聲。䕮或省[]。', '艸;𥶶', '𥶶', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
