INSERT INTO `shuowen_voice_revel` VALUES (542, '苗', 'miáo', '第一卷', '艸部', '艸生於田者。从艸田。', '艸;田', '', 'null', NULL, '艸;田', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (543, '苛', 'kē', '第一卷', '艸部', '小艸也。从艸，可聲。', '艸;可', '可', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (544, '蕪', 'wú', '第一卷', '艸部', '薉也。从艸，無聲。', '艸;無', '無', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (545, '薉;穢', 'huì', '第一卷', '艸部', '蕪也。从艸，歲聲。', '艸;歲', '歲', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (546, '荒', 'huānɡ', '第一卷', '艸部', '蕪也。从艸，巟聲。一曰艸掩地也。', '艸;巟', '巟', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (547, '𦱊', 'zhēnɡ', '第一卷', '艸部', '𦱊𦺝，艸亂也。从艸，爭聲。杜林說「𦱊𦺝，艸皃」。', '艸;爭', '爭', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (548, '𦺝', 'nínɡ', '第一卷', '艸部', '𦱊𦺝也。从艸，寧聲。', '艸;寧', '寧', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (549, '落', 'luò', '第一卷', '艸部', '凡艸曰零，木曰落。从艸，洛聲。', '艸;洛', '洛', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '舍≌落', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (550, '蔽', 'bì', '第一卷', '艸部', '蔽蔽，小艸也。从艸，敝聲。', '艸;敝', '敝', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (551, '蘀', 'tuò', '第一卷', '艸部', '艸木凡皮葉落陊地爲蘀。从艸，擇聲。詩曰「十月殞蘀」。', '艸;擇', '擇', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (552, '蕰;薀', 'yùn', '第一卷', '艸部', '積也。从艸，溫聲。春秋傳曰「薀利生孽」。', '艸;溫', '溫', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (553, '蔫', 'yān', '第一卷', '艸部', '菸也。从艸，焉聲。', '艸;焉', '焉', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (554, '菸', 'yū', '第一卷', '艸部', '𩰪也。从艸，於聲。一曰𣨙也。', '艸;於', '於', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (555, '𦾵', 'yínɡ', '第一卷', '艸部', '艸旋皃也。从艸，榮聲。詩曰「葛藟𦾵之」。', '艸;榮', '榮', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (556, '蔡', 'cài', '第一卷', '艸部', '艸丯也。从艸，祭聲。', '艸;祭', '祭', 'null', NULL, '艸', '𥻦', 'null', 'null', 'null', '', 0x31, '𢄉', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (557, '茷', 'fá', '第一卷', '艸部', '艸葉多。从艸，伐聲。春秋傳曰「晉糴茷」。', '艸;伐', '伐', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (558, '菜', 'cài', '第一卷', '艸部', '艸之可食者。从艸，采聲。', '艸;采', '采', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (559, '荋', 'ér', '第一卷', '艸部', '艸多葉皃。从艸，而聲。沛 城父有揚荋亭。', '艸;而', '而', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (560, '䒦', 'fàn', '第一卷', '艸部', '艸浮水中皃。从艸，乏聲。', '艸;乏', '乏', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (561, '薄', 'bó', '第一卷', '艸部', '林薄也。一曰蠶薄。从艸，溥聲。', '艸;溥', '溥', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (562, '苑', 'yuàn', '第一卷', '艸部', '所㠯養禽獸。从艸，夗聲。', '艸;夗', '夗', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (563, '藪', 'sǒu', '第一卷', '艸部', '大澤也。从艸，數聲。九州之藪——楊州 具區，荆州 雲夢，豫州 甫田，靑州 孟諸，兖州 大野，雝州 弦圃，幽州 奚養，冀州 楊紆，并州 昭餘祁是也。', '艸;數', '數', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (564, '葘;菑', 'zī', '第一卷', '艸部', '不耕田也。从艸田，𡿧聲。易曰「不葘畬」。葘或省艸。', '艸;田;𡿧', '𡿧', 'null', NULL, '艸;田', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (565, '𧄎', 'yáo', '第一卷', '艸部', '艸盛皃。从艸，䌛聲。夏書曰「厥艸惟䌛」。', '艸;䌛', '䌛', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (566, '薙', 'tì', '第一卷', '艸部', '除艸也。朙堂月令曰「季夏燒薙」。从艸，雉聲。', '艸;雉', '雉', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (567, '䒹', 'lèi', '第一卷', '艸部', '耕多艸。从艸耒。耒亦聲。', '艸;耒', '耒', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (568, '𦳙', 'zhì', '第一卷', '艸部', '艸大也。从艸，致聲。', '艸;致', '致', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (569, '菿', 'dào', '第一卷', '艸部', '艸大也。从艸，到聲。', '艸;到', '到', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '调序', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (570, '蔪', 'jiàn', '第一卷', '艸部', '艸相蔪苞也。从艸，斬聲。蔪或从槧。', '艸;斬;槧', '斬;槧', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (571, '茀', 'fú', '第一卷', '艸部', '道多艸，不可行。从艸，弗聲。', '艸;弗', '弗', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (572, '苾', 'bì', '第一卷', '艸部', '馨香也。从艸，必聲。', '艸;必', '必', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (573, '蔎', 'shè', '第一卷', '艸部', '香艸也。从艸，設聲。', '艸;設', '設', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (574, '芳', 'fānɡ', '第一卷', '艸部', '香艸也。从艸，方聲。', '艸;方', '方', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (575, '蕡', 'fén', '第一卷', '艸部', '襍香艸。从艸，賁聲。', '艸;賁', '賁', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (576, '藥', 'yào', '第一卷', '艸部', '治疾之艸總名。从艸，樂聲。', '艸;樂', '樂', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (577, '䕻', 'lí', '第一卷', '艸部', '艸木生箸土。从艸，麗聲。易曰「百穀艸木麗於地」。', '艸;麗', '麗', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (578, '蓆', 'xí', '第一卷', '艸部', '廣多也。从艸，席聲。', '艸;席', '席', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (579, '芟', 'shān', '第一卷', '艸部', '刈艸也。从艸殳。', '艸;殳', '', 'null', NULL, '艸;殳', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (580, '荐', 'jiàn', '第一卷', '艸部', '薦席也。从艸，存聲。', '艸;存', '存', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (581, '藉', 'jiè', '第一卷', '艸部', '祭藉也。一曰艸不編——「狼藉」。从艸，耤聲。', '艸;耤', '耤', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (582, '蒩', 'zū', '第一卷', '艸部', '茅藉也。从艸，租聲。禮曰「封諸侯㠯土，蒩㠯白茅」。', '艸;租', '租', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (583, '蕝', 'jué', '第一卷', '艸部', '朝會束茅表位曰蕝。从艸，絕聲。春秋國語曰「致茅蕝，表坐」。[纂]', '艸;絕;纂', '絕;纂', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (584, '茨', 'cí', '第一卷', '艸部', '茅葢屋。从艸，次聲。', '艸;次', '次', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (585, '葺', 'qì', '第一卷', '艸部', '茨也。从艸，咠聲。', '艸;咠', '咠', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (586, '葢;蓋', 'ɡài', '第一卷', '艸部', '苫也。从艸，盇聲。', '艸;盇', '盇', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (587, '苫', 'shān', '第一卷', '艸部', '葢也。从艸，占聲。', '艸;占', '占', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (588, '䔽', 'ài', '第一卷', '艸部', '葢也。从艸，渴聲。', '艸;渴', '渴', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (589, '䓛', 'qū', '第一卷', '艸部', '㕞也。从艸，屈聲。', '艸;屈', '屈', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (590, '藩', 'fān', '第一卷', '艸部', '屏也。从艸，潘聲。', '艸;潘', '潘', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (591, '菹;𦼬', 'zū', '第一卷', '艸部', '酢菜也。从艸，沮聲。 或从血。或从缶。', '艸;沮;血;缶', '沮', 'null', NULL, '艸;血;缶', 'null', 'null', 'null', 'null', '二重文应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (592, '荃', 'quán', '第一卷', '艸部', '芥脃也。从艸，全聲。', '艸;全', '全', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (593, '䔯', 'kù', '第一卷', '艸部', '韭鬱也。从艸，酤聲。', '艸;酤', '酤', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (594, '蘫', 'lán', '第一卷', '艸部', '瓜菹也。从艸，濫聲。', '艸;濫', '濫', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (595, '䓜;𥁼', 'zhī', '第一卷', '艸部', '菹也。从艸，泜聲。䓜或从皿。皿，器。', '艸;泜;皿', '泜', 'null', NULL, '艸;皿', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (596, '䕩', 'lǎo', '第一卷', '艸部', '乾梅之屬。从艸，橑聲。周禮曰「饋食之籩，其實『乾䕩』」。後漢 長沙王始煮艸爲䕩。䕩或从潦。', '艸;橑;潦', '橑;潦', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (597, '𧆈', 'yì', '第一卷', '艸部', '煎茱萸。从艸，𩕺聲。漢律「會稽獻𧆈一斗」。', '艸;𩕺', '𩕺', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (598, '䔂', 'zǐ', '第一卷', '艸部', '羹菜也。从艸，宰聲。', '艸;宰', '宰', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (599, '若', 'ruò', '第一卷', '艸部', '擇菜也。从艸右。右，手也。一曰杜若，香艸。', '艸;右', '', 'null', NULL, '艸;右', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (600, '蓴', 'tuán', '第一卷', '艸部', '蒲叢也。从艸，專聲。', '艸;專', '專', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (601, '𦭮', 'zhì', '第一卷', '艸部', '㠯艸補缺。从艸，㐁聲。讀若俠。或㠯爲綴。一曰約空也。', '艸;㐁;俠;綴', '㐁;俠;綴', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (602, '䔿', 'zǔn', '第一卷', '艸部', '叢艸也。从艸，尊聲。', '艸;尊', '尊', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (603, '莜', 'diào', '第一卷', '艸部', '𦶮田器。从艸，攸聲。論語曰「㠯杖荷莜」。', '艸;攸', '攸', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (604, '萆', 'pì', '第一卷', '艸部', '雨衣。一曰衰衣。从艸，卑聲。一曰萆歷，佀烏韭。', '艸;卑', '卑', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (605, '𦳚', 'chí', '第一卷', '艸部', '艸也。从艸，是聲。', '艸;是', '是', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (606, '苴', 'jū', '第一卷', '艸部', '履中艸。从艸，且聲。', '艸;且', '且', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (607, '𧆓', 'cū', '第一卷', '艸部', '艸履也。从艸，麤聲。', '艸;麤', '麤', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (608, '蕢;臾', 'kuì', '第一卷', '艸部', '艸器也。从艸，貴聲。古文蕢，象形。論語曰「有荷臾而過孔氏之門」。', '艸;貴', '貴', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (609, '𦯈', 'qǐn', '第一卷', '艸部', '覆也。从艸，侵省聲。', '艸;侵', '侵', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (610, '茵;鞇', 'yīn', '第一卷', '艸部', '車重席也。从艸，因聲。司馬相如說茵从革。', '艸;因;革', '因', 'null', NULL, '艸;革', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
