INSERT INTO `shuowen_voice_revel` VALUES (680, '草;皁', 'zào', '第一卷', '艸部', '草斗，櫟實也。一曰象斗。从艸，早聲。', '艸;早', '早', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (681, '菆', 'zōu', '第一卷', '艸部', '麻蒸也。从艸，取聲。一曰蓐也。', '艸;取', '取', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (682, '蓄', 'xù', '第一卷', '艸部', '積也。从艸，畜聲。', '艸;畜', '畜', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (683, '春', 'chūn', '第一卷', '艸部', '推也。从日艸屯。屯亦聲。', '日;艸;屯', '屯', 'null', NULL, '日;艸', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (684, '𦱄', 'ɡū', '第一卷', '艸部', '艸多皃。从艸，狐聲。江夏 平春有𦱄亭。', '艸;狐', '狐', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '应删', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (685, '芙', 'fú', '第一卷', '艸部', '芙蓉也。从艸，夫聲。', '艸;夫', '夫', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (686, '蓉', 'rónɡ', '第一卷', '艸部', '芙蓉也。从艸，容聲。', '艸;容', '容', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (687, '薳', 'wěi', '第一卷', '艸部', '艸也。从艸，遠聲。', '艸;遠', '遠', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (688, '荀', 'xún', '第一卷', '艸部', '艸也。从艸，旬聲。', '艸;旬', '旬', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (689, '莋', 'zuó', '第一卷', '艸部', '越嶲縣名。从艸，作聲。', '艸;作', '作', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (690, '蓀', 'sūn', '第一卷', '艸部', '香艸也。从艸，孫聲。', '艸;孫', '孫', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (691, '蔬', 'shū', '第一卷', '艸部', '菜也。从艸，疏聲。', '艸;疏', '疏', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (692, '芊', 'qiān', '第一卷', '艸部', '艸盛也。从艸，千聲。', '艸;千', '千', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (693, '茗', 'mínɡ', '第一卷', '艸部', '荼芽也。从艸，名聲。', '艸;名', '名', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (694, '薌', 'xiānɡ', '第一卷', '艸部', '榖气也。从艸，鄉聲。', '艸;鄉', '鄉', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (695, '藏', 'cánɡ', '第一卷', '艸部', '匿也。从艸，臧聲。', '艸;臧', '臧', 'null', NULL, '艸', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (696, '蕆', 'chǎn', '第一卷', '艸部', '左氏傳「㠯蕆陳事」。从艸。[茂貝]', '茂;貝', '', 'null', NULL, '茂;貝', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (697, '蘸', 'zhàn', '第一卷', '艸部', '㠯物沒水也。从艸。[醮]', '艸;醮', '', 'null', NULL, '艸;醮', 'null', 'null', 'null', 'null', '新', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (698, '蓐', 'rù', '第一卷', '蓐部', '陳艸復生也。从艸，辱聲。一曰蔟也。籒文蓐从茻。凡蓐之屬皆从蓐。', '艸;辱;茻', '辱', 'null', NULL, '艸;茻', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (699, '薅;茠', 'hāo', '第一卷', '蓐部', '披田艸也。从蓐，好省聲。籒文薅省。茠薅或从休。詩曰「旣茠荼蓼」。', '蓐;好;休', '好;休', 'null', NULL, '蓐', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (700, '茻', 'mǎnɡ', '第一卷', '茻部', '眾艸也。从四屮。凡茻之屬皆从茻。讀若與罔同。', '屮;罔', '罔', 'null', NULL, '屮', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (701, '莫', 'mù', '第一卷', '茻部', '日且冥也。从日在茻中。茻亦聲。', '日;茻', '茻', 'null', NULL, '日', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (702, '莽', 'mǎnɡ', '第一卷', '茻部', '南昌謂犬善逐兔艸中爲莽。从犬茻。茻亦聲。', '犬;茻', '茻', 'null', NULL, '犬', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (703, '葬', 'zànɡ', '第一卷', '茻部', '臧也。从死在茻中。一，其中所㠯荐之。易曰「古者葬，厚衣之㠯薪」。茻亦聲。', '死;茻;一', '茻', 'null', NULL, '死;一', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (704, '小', 'xiǎo', '第二卷', '小部', '物之微也。从八，丨見而八分之。凡小之屬皆从小。', '八;丨', '', 'null', NULL, '八;丨', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (705, '少', 'shǎo', '第二卷', '小部', '不多也。从少，丿聲。', '少;丿', '丿', 'null', NULL, '少', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (706, '尐', 'jié', '第二卷', '小部', '少也。从小，乀聲。讀若輟。', '小;乀;輟', '乀;輟', 'null', NULL, '小', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (707, '八', 'bā', '第二卷', '八部', '別也。象分別相背之形。凡八之屬皆从八。', '別', '別', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (708, '分', 'fēn', '第二卷', '八部', '別也。从八刀。刀㠯分別物也。', '八;刀', '', 'null', NULL, '八;刀', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (709, '尒;尔', 'ěr', '第二卷', '八部', '䛐之必然也。从丨八。八，象气之分散。入聲。', '丨;八;入', '入', 'null', NULL, '丨;八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (710, '曾', 'zēnɡ', '第二卷', '八部', '䛐之舒也。从八，从曰，𡆧聲。', '八;曰;𡆧', '𡆧', 'null', NULL, '八;曰', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (711, '尙;尚', 'shànɡ', '第二卷', '八部', '曾也。庶幾也。从八，向聲。', '八;向', '向', 'null', NULL, '八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (712, '㒸', 'suì', '第二卷', '八部', '从意也。从八，豕聲。', '八;豕', '豕', 'null', NULL, '八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (713, '詹', 'zhān', '第二卷', '八部', '多言也。从言，从八，从厃。', '言;八;厃', '厃', 'null', NULL, '言;八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (714, '介', 'jiè', '第二卷', '八部', '畫也。从人，从八。', '人;八', '', 'null', NULL, '人;八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (715, '𠔁', 'bié', '第二卷', '八部', '分也。从重八。八，別也。亦聲。孝經說曰「故上下有別」。', '八;別', '八;別', 'null', NULL, '八', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (716, '公', 'ɡōnɡ', '第二卷', '八部', '平分也。从八厶。八猶背也。韓非曰「背厶爲公」。', '八;厶', '', 'null', NULL, '八;厶', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (717, '必', 'bì', '第二卷', '八部', '分極也。从八弋，八亦聲。', '八;弋', '八', 'null', NULL, '弋', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (718, '余', 'yú', '第二卷', '八部', '語之舒也。从八，舍省聲。', '八;舍', '舍', 'null', NULL, '八', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (719, '𠎳', 'yú', '第二卷', '八部', '二余也。讀與余同。', '余', '余', 'null', NULL, '', 'null', 'null', 'null', 'null', '衍文', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (720, '釆', 'biàn', '第二卷', '釆部', '辨別也。象獸指爪分別也。凡釆之屬皆从釆。讀若辨。古文釆。', '辨', '辨', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (721, '番;𨆌', 'fán', '第二卷', '釆部', '獸足謂之番。从釆，田象其掌。番或从足，从煩。古文番。', '釆;田;足;煩', '煩', 'null', NULL, '釆;田;足', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (722, '宷;審', 'shěn', '第二卷', '釆部', '悉也，知宷諦也。从宀釆。篆文宷从番。', '宀;釆;番', '', 'null', NULL, '宀;釆;番', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (723, '悉', 'xī', '第二卷', '釆部', '詳盡也。从心釆。古文悉[心囧]。', '心;釆;囧', '', 'null', NULL, '心;釆;囧', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (724, '釋', 'shì', '第二卷', '釆部', '解也。从釆。釆，取其分別。从睪聲。', '釆;睪', '睪', 'null', NULL, '釆', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (725, '半', 'bàn', '第二卷', '半部', '物中分也。从八牛。牛爲物大，可㠯分也。凡半之屬皆从半。', '八;牛', '', 'null', NULL, '八;牛', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (726, '胖', 'pàn', '第二卷', '半部', '半體也。一曰廣肉。从肉半。半亦聲。', '肉;半', '半', 'null', NULL, '肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (727, '叛', 'pàn', '第二卷', '半部', '半反也。从半反。半亦聲。', '半;反', '反', 'null', NULL, '半', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (728, '牛', 'niú', '第二卷', '牛部', '事也，理也。像角頭三，封尾之形也。凡牛之屬皆从牛。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (729, '牡', 'mǔ', '第二卷', '牛部', '畜父也。从牛，土聲。', '牛;土', '', 'null', NULL, '牛;土', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (730, '犅', 'ɡānɡ', '第二卷', '牛部', '特也。从牛，岡聲。', '牛;岡', '岡', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (731, '特', 'tè', '第二卷', '牛部', '特牛也。从牛，寺聲。', '牛;寺', '寺', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (732, '牝', 'pìn', '第二卷', '牛部', '畜母也。从牛，匕聲。易曰「畜牝牛吉」。', '牛;匕', '匕', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (733, '犢', 'dú', '第二卷', '牛部', '牛子也。从牛，𧷏聲。', '牛;𧷏', '𧷏', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (734, '㸬', 'bèi', '第二卷', '牛部', '二歲牛。从牛，𣎵聲。', '牛;𣎵', '𣎵', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (735, '犙', 'sān', '第二卷', '牛部', '三歲牛。从牛，參聲。', '牛;參', '參', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (736, '牭', 'sì', '第二卷', '牛部', '四歲牛。从牛四。四亦聲。籒文牭从貳。', '牛;四;貳', '四', 'null', NULL, '牛;貳', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (737, '犗', 'jiè', '第二卷', '牛部', '騬牛也。从牛，害聲。', '牛;害', '害', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (738, '牻', 'mánɡ', '第二卷', '牛部', '白黑襍毛牛。从牛，尨聲。', '牛;尨', '尨', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (739, '㹁', 'liánɡ', '第二卷', '牛部', '牻牛也。从牛，京聲。春秋傳曰「牻㹁」。', '牛;京', '京', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (740, '犡', 'lì', '第二卷', '牛部', '牛白脊也。从牛，厲聲。', '牛;厲', '厲', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (741, '𤙛', 'tú', '第二卷', '牛部', '黃牛虎文。从牛，余聲。讀若塗。', '牛;余;塗', '余;塗', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (742, '犖', 'luò', '第二卷', '牛部', '駁牛也。从牛，勞省聲。', '牛;勞', '勞', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (743, '㸹', 'liè', '第二卷', '牛部', '牛白脊也。从牛，寽聲。', '牛;寽', '寽', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (744, '𤘾', 'pēnɡ', '第二卷', '牛部', '牛駁如星。从牛，平聲。', '牛;平', '平', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (745, '犥', 'piāo', '第二卷', '牛部', '牛黃白色。从牛，麃聲。', '牛;麃', '麃', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (746, '犉', 'rún', '第二卷', '牛部', '黃牛黑脣也。从牛，𦎫聲。詩曰「九十其犉」。', '牛;𦎫', '𦎫', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (747, '㹊', 'yuè', '第二卷', '牛部', '白牛也。从牛，隺聲。', '牛;隺', '隺', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (748, '㹔', 'jiānɡ', '第二卷', '牛部', '牛長脊。从牛，畺聲。', '牛;畺', '畺', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (749, '𤘸', 'tāo', '第二卷', '牛部', '牛徐行也。从牛，𠬢聲。讀若滔。', '牛;𠬢;滔', '𠬢;滔', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (750, '犫', 'chōu', '第二卷', '牛部', '牛息聲。从牛，雔聲。一曰牛名。', '牛;雔', '雔', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (751, '牟', 'móu', '第二卷', '牛部', '牛鳴也。从牛厶。象其聲气從口出。', '牛;厶', '', 'null', NULL, '牛;厶', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (752, '㹌', 'chǎn', '第二卷', '牛部', '畜㹌，畜牲也。从牛，產聲。', '牛;產', '產', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (753, '牲', 'shēnɡ', '第二卷', '牛部', '牛完也。从牛，生聲。', '牛;生', '生', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (754, '牷', 'quán', '第二卷', '牛部', '牛純色。禮「祭祀牷牲」。从牛，全聲。', '牛;全', '全', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (755, '牽', 'qiān', '第二卷', '牛部', '引而前也。从牛。冂象引牛之縻也。玄聲。', '牛;冂;玄', '玄', 'null', NULL, '牛;冂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (756, '牿', 'ɡù', '第二卷', '牛部', '牛馬牢也。从牛，告聲。周書曰「今惟[淫舍]牿牛馬」。', '牛;告', '告', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (757, '牢', 'láo', '第二卷', '牛部', '閑也。養牛馬圈也。从牛，冬省。取其四周帀。', '牛;冬', '', 'null', NULL, '牛;冬', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (758, '犓', 'chú', '第二卷', '牛部', '㠯芻莝養圈牛也。从牛芻。芻亦聲。春秋國語曰「犓豢幾何」。', '牛;芻', '芻', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (759, '㹛;犪', 'rǎo', '第二卷', '牛部', '牛柔謹也。从牛，夒聲。', '牛;夒', '夒', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (760, '𤛟;犕', 'bèi', '第二卷', '牛部', '易曰「犕牛乘馬」。从牛，𤰈聲。', '牛;𤰈', '𤰈', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (761, '𤛿', 'lí', '第二卷', '牛部', '耕也。从牛，黎聲。', '牛;黎', '黎', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (762, '㹃', 'fèi', '第二卷', '牛部', '㒳壁耕也。从牛，非聲。一曰覆耕穜也。', '牛;非', '非', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (763, '㹗', 'tāo', '第二卷', '牛部', '牛羊無子也。从牛，𠷎聲。讀若「糗糧」之糗。', '牛;𠷎;糗', '𠷎;糗', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (764, '牴', 'dǐ', '第二卷', '牛部', '觸也。从牛，氐聲。', '牛;氐', '氐', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (765, '𤜂', 'wèi', '第二卷', '牛部', '牛踶𤜂也。从牛，衛聲。', '牛;衛', '衛', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (766, '㹂', 'qiǎn', '第二卷', '牛部', '牛很不從牽也。从牛臤。臤亦聲。一曰大皃。讀若賢。', '牛;臤;賢', '臤;賢', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (767, '牼', 'kēnɡ', '第二卷', '牛部', '牛厀下骨也。从牛，巠聲。春秋傳宋 司馬牼字牛。', '牛;巠', '巠', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (768, '𤘡', 'jìn', '第二卷', '牛部', '牛舌病也。从牛，今聲。', '牛;今', '今', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (769, '犀', 'xī', '第二卷', '牛部', '徼外牛。一角在鼻，一角在頂，佀豕。从牛，尾聲。', '牛;尾', '尾', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (770, '牣', 'rèn', '第二卷', '牛部', '牣，滿也。从牛，刃聲。詩曰「於牣魚躍」。', '牛;刃', '刃', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (771, '物', 'wù', '第二卷', '牛部', '萬物也。牛爲大物，天地之數起於牽牛。故从牛，勿聲。', '牛;勿', '勿', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (772, '犧', 'xī', '第二卷', '牛部', '宗廟之牲也。从牛，羲聲。賈侍中說，此非古字。', '牛;羲', '羲', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (773, '犍', 'jiān', '第二卷', '牛部', '犗牛也。从牛，建聲。', '牛;建', '建', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (774, '犝', 'tónɡ', '第二卷', '牛部', '無角牛也。从牛，童聲。', '牛;童', '童', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '新', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (775, '犛', 'lí', '第二卷', '犛部', '西南夷長髦牛也。从牛，𠩺聲。凡犛之屬皆从犛。', '牛;𠩺', '𠩺', 'null', NULL, '牛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (776, '氂', 'máo', '第二卷', '犛部', '犛牛尾也。从犛省，从毛。', '犛;毛', '犛', 'null', NULL, '毛', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (777, '斄', 'lái', '第二卷', '犛部', '彊曲毛也。可㠯箸起衣。从犛省，來聲。古文斄省[厂]。', '犛;來;厂', '來', 'null', NULL, '犛;厂', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (778, '告', 'ɡào', '第二卷', '告部', '牛觸人，角箸橫木，所㠯告人也。从口，从牛。易曰「僮牛之告」。凡告之屬皆从告。', '口;牛', '', 'null', NULL, '口;牛', 'null', 'null', 'null', 'null', '经', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (779, '嚳', 'kù', '第二卷', '告部', '急告之甚也。从告，學省聲。', '告;學', '學', 'null', NULL, '告', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (780, '口', 'kǒu', '第二卷', '口部', '人所㠯言食也。象形。凡口之屬皆从口。', '', '', 'null', NULL, '', 'null', 'null', 'null', 'null', '', 0x30, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (781, '噭', 'jiào', '第二卷', '口部', '口也。从口，敫聲。一曰噭，呼也。', '口;敫', '敫', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (782, '噣', 'zhòu', '第二卷', '口部', '喙也。从口，蜀聲。', '口;蜀', '蜀', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (783, '喙', 'huì', '第二卷', '口部', '口也。从口，彖聲。', '口;彖', '彖', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (784, '吻', 'wěn', '第二卷', '口部', '口邊也。从口，勿聲。吻或从肉，从昏。', '口;勿;肉;昏', '勿;昏', 'null', NULL, '口;肉', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (785, '嚨', 'lónɡ', '第二卷', '口部', '喉也。从口，龍聲。', '口;龍', '龍', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (786, '喉;㗋', 'hóu', '第二卷', '口部', '咽也。从口，矦聲。', '口;矦', '矦', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (787, '噲', 'kuài', '第二卷', '口部', '咽也。从口，會聲。或讀若快。一曰噲，嚵也。', '口;會;快', '會;快', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (788, '吞', 'tūn', '第二卷', '口部', '咽也。从口，天聲。', '口;天', '天', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (789, '咽', 'yān', '第二卷', '口部', '嗌也。从口，因聲。', '口;因', '因', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (790, '嗌;𠍳', 'yì', '第二卷', '口部', '咽也。从口，益聲。籒文嗌上象口，下象頸脈理也。', '口;益', '益', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (791, '喗', 'yǔn', '第二卷', '口部', '大口也。从口，軍聲。', '口;軍', '軍', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (792, '哆', 'chǐ', '第二卷', '口部', '張口也。从口，多聲。', '口;多', '多', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (793, '呱', 'ɡū', '第二卷', '口部', '小兒嗁聲。从口，瓜聲。詩曰「后稷呱矣」。', '口;瓜', '瓜', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (794, '啾', 'jiū', '第二卷', '口部', '小兒聲也。从口，秋聲。', '口;秋', '秋', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (795, '喤', 'huánɡ', '第二卷', '口部', '小兒聲。从口，皇聲。詩曰「其泣喤喤」。', '口;皇', '皇', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (796, '咺', 'xuǎn', '第二卷', '口部', '朝鮮謂兒泣不止曰咺。从口，亘聲。', '口;亘', '亘', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (797, '唴', 'qiànɡ', '第二卷', '口部', '秦、晉調兒泣不止曰唴。从口，羌聲。', '口;羌', '羌', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (798, '咷', 'táo', '第二卷', '口部', '楚謂兒泣不止曰「噭咷」。从口，兆聲。', '口;兆', '兆', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (799, '喑', 'yīn', '第二卷', '口部', '宋、齊謂兒泣不止曰喑。从口，音聲。', '口;音', '音', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (800, '㘈', 'yì', '第二卷', '口部', '小兒有知也从口，疑聲。詩曰「克岐克㘈」。', '口;疑', '疑', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (801, '咳;孩', 'hái', '第二卷', '口部', '小兒𥬇也。从口，亥聲。孩古文咳从子。', '口;亥;子', '亥', 'null', NULL, '口;子', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (802, '嗛', 'xián', '第二卷', '口部', '口有所銜也。从口，兼聲。', '口;兼', '兼', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (803, '咀', 'jǔ', '第二卷', '口部', '含味也。从口，且聲。', '口;且', '且', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (804, '啜', 'chuò', '第二卷', '口部', '嘗也。从口，叕聲。一曰喙也。', '口;叕', '叕', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (805, '㗱', 'jí', '第二卷', '口部', '噍也。从口，集聲。讀若集。', '口;集', '集', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (806, '嚌', 'jì', '第二卷', '口部', '嘗也。从口，齊聲。周書曰「大保受同祭嚌」。', '口;齊', '齊', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (807, '噍;嚼', 'jiào', '第二卷', '口部', '齧也。从口，焦聲。噍或从爵。', '口;焦;爵', '焦;爵', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (808, '吮', 'shǔn', '第二卷', '口部', '欶也。从口，允聲。', '口;允', '允', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (809, '𠻜', 'shuì', '第二卷', '口部', '小㱃也。从口，率聲。讀若㕞。', '口;率;㕞', '率;㕞', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (810, '嚵', 'chán', '第二卷', '口部', '小𠻜也。从口，毚聲。一曰喙也。', '口;毚', '毚', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (811, '簭;噬', 'shì', '第二卷', '口部', '啗也，喙也。从口，筮聲。', '口;筮', '筮', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (812, '啗', 'dàn', '第二卷', '口部', '食也。从口，臽聲。讀與含同。', '口;臽;含', '臽;含', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (813, '嘰', 'jī', '第二卷', '口部', '小食也。从口，幾聲。', '口;幾', '幾', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (814, '㗘', 'bó', '第二卷', '口部', '噍皃。从口，尃聲。', '口;尃', '尃', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (815, '含', 'hán', '第二卷', '口部', '嗛也。从口，今聲。', '口;今', '今', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (816, '哺', 'bǔ', '第二卷', '口部', '哺咀也。从口，甫聲。', '口;甫', '甫', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (817, '味', 'wèi', '第二卷', '口部', '滋味也。从口，未聲。', '口;未', '未', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (818, '嚛', 'huò', '第二卷', '口部', '食辛嚛也。从口，樂聲。', '口;樂', '樂', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
INSERT INTO `shuowen_voice_revel` VALUES (819, '𠿡', 'zhuó', '第二卷', '口部', '口滿食。从口，窡聲。', '口;窡', '窡', 'null', NULL, '口', 'null', 'null', 'null', 'null', '', 0x31, 'null', 'null', 'null', DEFAULT);
